            )}
            
            <button
              onClick={onLogout}
              style={{
                padding: '8px 14px',
                background: 'rgba(255,255,255,0.2)',
                backdropFilter: 'blur(10px)',
                color: 'white',
                border: '1px solid rgba(255,255,255,0.3)',
                borderRadius: '8px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '600'
              }}
            >
              Salir
            </button>
          </div>
        </div>
      </div>

      {/* Panel lateral - Contenido simplificado sin notificaciones inline */}
      <div style={styles.panel}>
        <div style={{ padding: isMobile ? '16px' : '20px' }}>
          
          {/* Panel de notificaciones desplegable */}
          {showNotifications && notificaciones.length > 0 && (
            <div style={{
              ...styles.card,
              background: 'linear-gradient(135deg, #fef3c7 0%, #fed7aa 100%)',
              border: '2px solid #fbbf24',
              marginBottom: '20px'
            }}>
              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                marginBottom: '12px'
              }}>
                <h4 style={{ margin: 0, fontSize: '15px', color: '#92400e', fontWeight: '700' }}>
                  Notificaciones
                </h4>
                <button
                  onClick={clearAllNotifications}
                  style={{
                    padding: '4px 8px',
                    background: '#dc2626',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    fontSize: '12px',
                    cursor: 'pointer'
                  }}
                >
                  Limpiar
                </button>
              </div>
              <div style={{ maxHeight: '150px', overflowY: 'auto' }}>
                {notificaciones.slice(0, 3).map(notif => (
                  <div key={notif.id} style={{
                    padding: '8px',
                    background: 'rgba(255,255,255,0.8)',
                    borderRadius: '6px',
                    marginBottom: '6px',
                    fontSize: '13px',
                    cursor: 'pointer'
                  }} onClick={() => markNotificationAsRead(notif.id)}>
                    {notif.mensaje}
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Resto del contenido del panel... */}
          {/* ActivaciÃ³n GPS */}
          <button
            onClick={toggleGPS}
            style={{
              ...styles.button,
              background: isTracking 
                ? 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)' 
                : 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
              color: 'white'
            }}
          >
            {isTracking ? 'ðŸ“ Desactivar GPS' : 'ðŸ“ Activar GPS'}
          </button>

          {/* Selector de zona */}
          {isTracking && workStatus === 'idle' && (
            <div style={styles.card}>
              <label style={{ 
                display: 'block', 
                marginBottom: '10px', 
                fontSize: '14px', 
                fontWeight: '600',
                color: '#374151'
              }}>
                ðŸ—ï¸ Selecciona tu zona de trabajo
              </label>
              {zonasAsignadas.length > 0 ? (
                <select
                  value={selectedZona?.id || ''}
                  onChange={(e) => {
                    const zona = zonasAsignadas.find(z => z.id === e.target.value);
                    if (zona) selectZona(zona);
                  }}
                  style={{
                    width: '100%',
                    padding: '12px',
                    borderRadius: '8px',
                    border: '2px solid #e5e7eb',
                    fontSize: '14px',
                    background: 'white'
                  }}
                >
                  <option value="">-- Seleccionar zona --</option>
                  {zonasAsignadas.map(zona => (
                    <option key={zona.id} value={zona.id}>
