            <div style={styles.card}>
              <div style={{
                background: 'linear-gradient(135deg, #fef3c7 0%, #fde68a 100%)',
                margin: '-16px -16px 16px -16px',
                padding: '16px',
                borderRadius: '12px 12px 0 0'
              }}>
                <div style={{ fontSize: '17px', fontWeight: '700', color: '#92400e' }}>
                  ðŸš— En camino a la zona
                </div>
                <div style={{ fontSize: '14px', marginTop: '6px', color: '#78350f' }}>
                  ðŸ“ {selectedZona?.nombre}
                </div>
                {distance && duration && (
                  <div style={{ 
                    marginTop: '12px', 
                    padding: '8px',
                    background: 'rgba(255,255,255,0.8)',
                    borderRadius: '6px',
                    fontSize: '14px',
                    display: 'flex',
                    gap: '16px'
                  }}>
                    <span>ðŸ“ {distance}</span>
                    <span>â±ï¸ {duration}</span>
                  </div>
                )}
              </div>
              
              <button
                onClick={handleArrival}
                style={{
                  ...styles.button,
                  background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                  color: 'white',
                  marginBottom: '12px'
                }}
              >
                ðŸ“ Ya lleguÃ© a la zona
              </button>
              
              <button
                onClick={() => {
                  if (window.confirm('Â¿Cancelar el recorrido?')) {
                    cancelNavigation();
