          {/* Estado: Trabajando */}
          {workStatus === 'working' && (
            <div style={styles.card}>
              <div style={{
                background: 'linear-gradient(135deg, #dcfce7 0%, #bbf7d0 100%)',
                margin: '-16px -16px 16px -16px',
                padding: '20px',
                borderRadius: '12px 12px 0 0',
                border: '2px solid #22c55e'
              }}>
                <div style={{ 
                  fontSize: '17px', 
                  fontWeight: '700', 
                  color: '#14532d',
                  animation: 'pulse 2s infinite'
                }}>
