              <button
                onClick={() => {
                  if (window.confirm('Â¿Cancelar el trabajo?')) {
                    cancelNavigation();
                  }
                }}
                style={{
                  ...styles.button,
                  background: 'white',
                  color: '#ef4444',
                  border: '2px solid #ef4444',
                  marginBottom: 0
                }}
              >
                âŒ Cancelar
              </button>
            </div>
          )}

          {/* Estado: Completado */}
          {workStatus === 'completed' && (
            <div style={{
              ...styles.card,
              background: 'linear-gradient(135deg, #e0e7ff 0%, #c7d2fe 100%)',
              border: '2px solid #6366f1'
            }}>
              <div style={{ 
                fontSize: '17px', 
                fontWeight: '700', 
                color: '#312e81',
                textAlign: 'center',
                marginBottom: '16px'
              }}>
                âœ… Trabajo Completado
              </div>
              <button
                onClick={resetWork}
