                  <div style={{ marginBottom: '20px' }}>
                    <h3 style={{ margin: '0 0 10px 0', color: '#374151', fontSize: '16px' }}>
                      DescripciÃ³n
                    </h3>
                    <p style={{
                      padding: '10px',
                      background: '#f9fafb',
                      borderRadius: '6px',
                      fontSize: '14px',
                      lineHeight: '1.5'
                    }}>
                      {showSolicitudDetails.descripcion}
                    </p>
                  </div>

                  {showSolicitudDetails.ubicacion && (
                    <div style={{ marginBottom: '20px' }}>
                      <h3 style={{ margin: '0 0 10px 0', color: '#374151', fontSize: '16px' }}>
                        ðŸ“ UbicaciÃ³n
                      </h3>
                      <div style={{
                        padding: '10px',
                        background: '#f0f9ff',
                        borderRadius: '6px',
                        fontSize: '14px'
                      }}>
                        Lat: {showSolicitudDetails.ubicacion.lat.toFixed(6)}, 
                        Lng: {showSolicitudDetails.ubicacion.lng.toFixed(6)}
                        <a 
                          href={`https://www.google.com/maps?q=${showSolicitudDetails.ubicacion.lat},${showSolicitudDetails.ubicacion.lng}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          style={{
                            marginLeft: '10px',
                            color: '#3b82f6',
                            textDecoration: 'none'
                          }}
                        >
                          Ver en Google Maps â†’
                        </a>
                      </div>
                    </div>
                  )}

                  {showSolicitudDetails.estado === 'pendiente' && (
                    <div style={{
                      borderTop: '1px solid #e5e7eb',
                      paddingTop: '20px'
                    }}>
                      <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                        Responder Solicitud
                      </h3>
                      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
                        <button
                          onClick={() => {
                            const respuesta = prompt('Ingrese un mensaje para el solicitante (opcional):');
                            handleResponseSolicitud(
                              showSolicitudDetails.id, 
                              'aprobada', 
                              respuesta || 'Su solicitud ha sido aprobada. Nos pondremos en contacto para coordinar.'
                            );
                          }}
                          style={{
                            padding: '10px 20px',
                            background: '#22c55e',
                            color: 'white',
                            border: 'none',
                            borderRadius: '6px',
                            fontSize: '14px',
                            fontWeight: '600',
                            cursor: 'pointer'
                          }}
                        >
                          âœ… Aprobar
                        </button>

                        <button
                          onClick={() => {
                            const respuesta = prompt('Ingrese el motivo del rechazo:');
                            if (respuesta) {
                              handleResponseSolicitud(showSolicitudDetails.id, 'rechazada', respuesta);
                            }
                          }}
                          style={{
                            padding: '10px 20px',
                            background: '#ef4444',
                            color: 'white',
                            border: 'none',
                            borderRadius: '6px',
                            fontSize: '14px',
                            fontWeight: '600',
                            cursor: 'pointer'
                          }}
                        >
                          âŒ Rechazar
                        </button>

                        <button
                          onClick={() => {
                            handleResponseSolicitud(
                              showSolicitudDetails.id, 
                              'en_proceso', 
                              'Su solicitud estÃ¡ siendo procesada. Le informaremos cuando la maquinaria estÃ© asignada.'
                            );
                          }}
                          style={{
                            padding: '10px 20px',
                            background: '#3b82f6',
                            color: 'white',
                            border: 'none',
                            borderRadius: '6px',
                            fontSize: '14px',
                            fontWeight: '600',
                            cursor: 'pointer'
                          }}
                        >
                          ðŸ”§ En Proceso
                        </button>
                      </div>
                    </div>
                  )}

                  {showSolicitudDetails.estado !== 'pendiente' && showSolicitudDetails.respuesta && (
                    <div style={{
                      borderTop: '1px solid #e5e7eb',
                      paddingTop: '20px'
                    }}>
                      <h3 style={{ margin: '0 0 10px 0', color: '#374151', fontSize: '16px' }}>
                        Respuesta del Administrador
                      </h3>
                      <div style={{
                        padding: '10px',
                        background: showSolicitudDetails.estado === 'aprobada' ? '#dcfce7' : 
                                   showSolicitudDetails.estado === 'rechazada' ? '#fee2e2' : '#dbeafe',
                        borderRadius: '6px',
                        fontSize: '14px'
                      }}>
                        <p style={{ margin: '0 0 5px 0' }}>
                          {showSolicitudDetails.respuesta}
                        </p>
                        <p style={{ margin: 0, fontSize: '12px', color: '#6b7280' }}>
                          Respondido el: {new Date(showSolicitudDetails.fechaRespuesta).toLocaleString('es-CL')}
                        </p>
                      </div>
                    </div>
                  )}

                  {showSolicitudDetails.estado === 'aprobada' && (
                    <div style={{ marginTop: '20px' }}>
                      <button
                        onClick={() => {
                          handleResponseSolicitud(
                            showSolicitudDetails.id, 
                            'completada', 
                            'El trabajo ha sido completado exitosamente.'
                          );
                        }}
                        style={{
                          padding: '10px 20px',
                          background: '#8b5cf6',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          fontSize: '14px',
                          fontWeight: '600',
                          cursor: 'pointer'
                        }}
                      >
                        âœ”ï¸ Marcar como Completada
                      </button>
                    </div>
                  )}
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default SuperAdmin;
