// src/components/PasswordRecovery.js
import React, { useState } from 'react';
import { firestore, auth } from '../config/firebase';
import { collection, query, where, getDocs, doc, updateDoc } from 'firebase/firestore';
import { sendPasswordResetEmail } from 'firebase/auth';

const PasswordRecovery = ({ onClose, onSuccess }) => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    email: '',
    pin: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [userData, setUserData] = useState(null);

  // Verificar email y PIN y enviar email de reset
  const handleVerifyPin = async (e) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }
    
    console.log('Verificando PIN...'); 
    
    if (!formData.email || !formData.pin) {
      setMessage({ 
        type: 'error', 
        text: '‚ùå Por favor completa todos los campos' 
      });
      return;
    }

    if (formData.pin.length !== 4) {
      setMessage({ 
        type: 'error', 
        text: '‚ùå El PIN debe tener 4 d√≠gitos' 
      });
      return;
    }

    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Buscar usuario por email
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', formData.email.toLowerCase().trim()));
      const querySnapshot = await getDocs(q);

      if (querySnapshot.empty) {
        setMessage({ 
          type: 'error', 
          text: '‚ùå No existe un usuario con ese correo electr√≥nico' 
        });
        setLoading(false);
        return;
      }

      const userDoc = querySnapshot.docs[0];
      const user = { id: userDoc.id, ...userDoc.data() };
      
      // Verificar PIN
      let pinCorrecto = false;
      
      if (user.recoveryPin) {
        try {
          const storedPin = atob(user.recoveryPin);
          pinCorrecto = (formData.pin === storedPin);
        } catch (e) {
          pinCorrecto = (formData.pin === user.recoveryPin);
        }
      } else {
        setMessage({ 
          type: 'error', 
          text: '‚ùå Este usuario no tiene PIN configurado.' 
        });
        setLoading(false);
        return;
      }

      if (!pinCorrecto) {
        setMessage({ 
          type: 'error', 
          text: '‚ùå PIN incorrecto. Intenta nuevamente.' 
        });
        setLoading(false);
        return;
      }

      console.log('PIN correcto! Enviando email de recuperaci√≥n...'); 

      // PIN CORRECTO - Enviar email de reset de Firebase
      await sendPasswordResetEmail(auth, formData.email);
      
      // Marcar en Firestore que se solicit√≥ reset
      await updateDoc(doc(firestore, 'users', user.id), {
        passwordResetRequested: true,
        passwordResetAt: new Date().toISOString(),
        resetMethod: 'PIN_VERIFIED'
      });

      // Actualizar estado
      setUserData(user);
      setStep(2);
      setMessage({ 
        type: 'success', 
        text: '‚úÖ PIN verificado correctamente' 
      });

    } catch (error) {
      console.error('Error:', error);
      
      // Si el error es porque el usuario no existe en Firebase Auth
      if (error.code === 'auth/user-not-found') {
        // Crear una contrase√±a temporal como fallback
        await handleTemporaryPassword();
      } else {
        setMessage({ 
          type: 'error', 
          text: '‚ùå Error al procesar la solicitud' 
        });
      }
    } finally {
      setLoading(false);
    }
  };

  // Fallback: Generar contrase√±a temporal si el usuario no existe en Auth
  const handleTemporaryPassword = async () => {
    try {
      const tempPass = generateTemporaryPassword();
      
      // Buscar el usuario nuevamente
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', formData.email.toLowerCase().trim()));
      const querySnapshot = await getDocs(q);
      
      if (!querySnapshot.empty) {
        const userDoc = querySnapshot.docs[0];
        const user = { id: userDoc.id, ...userDoc.data() };
        
        // Guardar contrase√±a temporal
        await updateDoc(doc(firestore, 'users', user.id), {
          tempPassword: btoa(tempPass),
          passwordResetAt: new Date().toISOString(),
          requirePasswordChange: true,
          resetMethod: 'TEMP_PASSWORD'
        });
        
        setUserData(user);
        setStep(3); // Paso especial para mostrar contrase√±a temporal
        setMessage({ 
          type: 'info', 
          text: '‚ö†Ô∏è Se gener√≥ una contrase√±a temporal' 
        });
      }
    } catch (error) {
      console.error('Error generando contrase√±a temporal:', error);
    }
  };

  const generateTemporaryPassword = () => {
    const chars = 'ABCDEFGHJKLMNPQRSTUVWXYZabcdefghjkmnpqrstuvwxyz23456789';
    let password = 'Temp';
    for (let i = 0; i < 6; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return password;
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000,
      padding: '20px'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        padding: '30px',
        maxWidth: '450px',
        width: '100%',
        boxShadow: '0 10px 40px rgba(0,0,0,0.2)',
        position: 'relative'
      }}>
        {/* Bot√≥n cerrar */}
        <button
          type="button"
          onClick={(e) => {
            e.preventDefault();
            e.stopPropagation();
            onClose();
          }}
          style={{
            position: 'absolute',
            top: '15px',
            right: '15px',
            background: 'transparent',
            border: 'none',
            fontSize: '24px',
            cursor: 'pointer',
            color: '#6b7280',
            padding: '5px'
          }}
        >
          ‚úñ
        </button>

        {/* Header */}
        <div style={{ marginBottom: '25px' }}>
          <h2 style={{ 
            margin: 0, 
            fontSize: '24px', 
            color: '#1f2937' 
          }}>
            üîê Recuperar Contrase√±a
          </h2>
        </div>

        {/* Mensajes */}
        {message.text && (
          <div style={{
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            background: message.type === 'success' ? '#dcfce7' : 
                       message.type === 'error' ? '#fee2e2' : '#e0f2fe',
            border: `1px solid ${
              message.type === 'success' ? '#86efac' : 
              message.type === 'error' ? '#fecaca' : '#7dd3fc'
            }`,
            color: message.type === 'success' ? '#166534' : 
                   message.type === 'error' ? '#991b1b' : '#075985',
            fontSize: '14px'
          }}>
            {message.text}
          </div>
        )}

        {/* PASO 1: Verificar Email y PIN */}
        {step === 1 && (
          <div>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Correo Electr√≥nico
              </label>
              <input
                type="email"
                required
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                placeholder="tu@correo.com"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px',
                  outline: 'none'
                }}
                autoFocus
              />
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                PIN de Seguridad (4 d√≠gitos)
              </label>
              <input
                type="text"
                required
                maxLength="4"
                value={formData.pin}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, '');
                  setFormData({...formData, pin: value});
                }}
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '24px',
                  textAlign: 'center',
                  letterSpacing: '15px',
                  fontWeight: 'bold'
                }}
              />
              <small style={{ 
                color: '#6b7280', 
                fontSize: '12px',
                display: 'block',
                marginTop: '5px'
              }}>
                Ingresa tu PIN de 4 d√≠gitos
              </small>
            </div>

            <button
              type="button"
              onClick={handleVerifyPin}
              disabled={loading || formData.pin.length !== 4 || !formData.email}
              style={{
                width: '100%',
                padding: '14px',
                background: loading || formData.pin.length !== 4 || !formData.email ? '#9ca3af' : 
                          'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: loading || formData.pin.length !== 4 || !formData.email ? 'not-allowed' : 'pointer'
              }}
            >
              {loading ? 'Verificando...' : 'Verificar PIN ‚Üí'}
            </button>
          </div>
        )}

        {/* PASO 2: Email enviado */}
        {step === 2 && (
          <div style={{ textAlign: 'center' }}>
            <div style={{
              fontSize: '64px',
              marginBottom: '20px'
            }}>
              üìß
            </div>
            
            <h3 style={{
              margin: '0 0 20px 0',
              fontSize: '20px',
              color: '#1f2937'
            }}>
              ¬°Correo Enviado!
            </h3>

            <div style={{
              marginBottom: '25px',
              padding: '20px',
              background: '#f0f9ff',
              borderRadius: '8px',
              border: '1px solid #0284c7'
            }}>
              <p style={{ 
                margin: '0 0 10px 0', 
                fontSize: '14px', 
                color: '#075985' 
              }}>
                Hemos enviado un enlace de recuperaci√≥n a:
              </p>
              <p style={{ 
                margin: 0, 
                fontSize: '16px', 
                color: '#0c4a6e',
                fontWeight: 'bold'
              }}>
                {formData.email}
              </p>
            </div>

            <div style={{
              background: '#fef3c7',
              padding: '15px',
              borderRadius: '8px',
              marginBottom: '20px',
              textAlign: 'left'
            }}>
              <h4 style={{ 
                margin: '0 0 10px 0', 
                color: '#92400e', 
                fontSize: '14px' 
              }}>
                üìù Instrucciones:
              </h4>
              <ol style={{ 
                margin: 0, 
                paddingLeft: '20px', 
                fontSize: '13px', 
                color: '#78350f' 
              }}>
                <li>Revisa tu bandeja de entrada</li>
                <li>Busca el correo de "MapaQuill√≥n"</li>
                <li>Haz clic en el enlace del correo</li>
                <li>Crea tu nueva contrase√±a</li>
                <li>Vuelve aqu√≠ para iniciar sesi√≥n</li>
              </ol>
            </div>

            <p style={{
              fontSize: '12px',
              color: '#6b7280',
              marginBottom: '20px'
            }}>
              Si no ves el correo, revisa tu carpeta de spam
            </p>

            <button
              type="button"
              onClick={() => {
                onSuccess();
                onClose();
              }}
              style={{
                width: '100%',
                padding: '14px',
                background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer'
              }}
            >
              ‚úÖ Entendido
            </button>
          </div>
        )}

        {/* PASO 3: Contrase√±a temporal (fallback) */}
        {step === 3 && (
          <div>
            <div style={{
              marginBottom: '20px',
              padding: '15px',
              background: '#fef3c7',
              borderRadius: '8px',
              border: '1px solid #f59e0b'
            }}>
              <p style={{ 
                margin: 0, 
                fontSize: '14px', 
                color: '#92400e',
                fontWeight: '600'
              }}>
                ‚ö†Ô∏è Usuario sin cuenta de autenticaci√≥n
              </p>
              <p style={{ 
                margin: '8px 0 0 0', 
                fontSize: '13px', 
                color: '#78350f' 
              }}>
                Se ha generado una contrase√±a temporal. 
                Contacta al administrador para una soluci√≥n permanente.
              </p>
            </div>

            <button
              type="button"
              onClick={() => {
                onSuccess();
                onClose();
              }}
              style={{
                width: '100%',
                padding: '14px',
                background: '#f59e0b',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer'
              }}
            >
              Contactar Administrador
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default PasswordRecovery;
