// src/components/PreventiveMaintenanceModule.js

import React, { useState, useEffect, useCallback } from 'react';
import { 
  ref as databaseRef,
  set as databaseSet,
  push,
  update,
  remove,
  onValue,
  off
} from 'firebase/database';
import { database } from '../config/firebase';

const PreventiveMaintenanceModule = ({ vehiculos, currentUser, isMobile }) => {
  // Estados principales
  const [maintenanceRecords, setMaintenanceRecords] = useState([]);
  const [showMaintenanceForm, setShowMaintenanceForm] = useState(false);
  const [selectedVehicle, setSelectedVehicle] = useState('');
  const [editingMaintenance, setEditingMaintenance] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  
  // Estado para tipo de repuesto
  const [tipoRepuesto, setTipoRepuesto] = useState('original');
  
  // Estado para servicios preventivos
  const [serviciosPreventivos, setServiciosPreventivos] = useState([{
    id: Date.now(),
    numero: 1,
    servicio: '',
    codigo: '',
    detalle: '',
    manoObra: 0,
    costoRepuesto: 0,
    cantidadRepuesto: 1,
    repuestoDescripcion: ''
  }]);

  // C√°lculos autom√°ticos
  const calcularTotales = useCallback(() => {
    const subtotalManoObra = serviciosPreventivos.reduce((sum, s) => sum + (parseFloat(s.manoObra) || 0), 0);
    const subtotalRepuestos = serviciosPreventivos.reduce((sum, s) => sum + ((parseFloat(s.costoRepuesto) || 0) * (parseInt(s.cantidadRepuesto) || 1)), 0);
    const valorNeto = subtotalManoObra + subtotalRepuestos;
    const iva = valorNeto * 0.19;
    const total = valorNeto + iva;
    
    return {
      subtotalManoObra,
      subtotalRepuestos,
      valorNeto,
      iva,
      total
    };
  }, [serviciosPreventivos]);

  // Cargar registros de mantenimiento
  useEffect(() => {
    const maintenanceRef = databaseRef(database, 'mantenimientos');
    const unsubscribe = onValue(maintenanceRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const maintenanceArray = Object.entries(data).map(([id, maintenance]) => ({
          id,
          ...maintenance
        }));
        setMaintenanceRecords(maintenanceArray);
      } else {
        setMaintenanceRecords([]);
      }
    });

    return () => off(maintenanceRef);
  }, []);

  // Agregar nuevo servicio preventivo
  const agregarServicio = () => {
    const nuevoNumero = serviciosPreventivos.length + 1;
    setServiciosPreventivos([...serviciosPreventivos, {
      id: Date.now(),
      numero: nuevoNumero,
      servicio: '',
      codigo: '',
      detalle: '',
      manoObra: 0,
      costoRepuesto: 0,
      cantidadRepuesto: 1,
      repuestoDescripcion: ''
    }]);
  };

  // Actualizar servicio preventivo
  const actualizarServicio = (id, campo, valor) => {
    setServiciosPreventivos(serviciosPreventivos.map(s => 
      s.id === id ? { ...s, [campo]: valor } : s
    ));
  };

  // Eliminar servicio preventivo
  const eliminarServicio = (id) => {
    if (serviciosPreventivos.length > 1) {
      const nuevosServicios = serviciosPreventivos.filter(s => s.id !== id);
      // Renumerar servicios
      const serviciosRenumerados = nuevosServicios.map((s, index) => ({
        ...s,
        numero: index + 1
      }));
      setServiciosPreventivos(serviciosRenumerados);
    }
  };

  // Guardar mantenimiento
  const handleSaveMaintenance = async (e) => {
    e.preventDefault();
    
    if (!selectedVehicle) {
      setMessage({ type: 'error', text: '‚ùå Debe seleccionar un veh√≠culo' });
      return;
    }

    setLoading(true);
    
    try {
      const totales = calcularTotales();
      const vehiculo = vehiculos.find(v => v.id === selectedVehicle);
      
      const maintenanceData = {
        vehiculoId: selectedVehicle,
        vehiculoNombre: vehiculo?.nombre || '',
        vehiculoPatente: vehiculo?.patente || '',
        tipoRepuesto,
        serviciosPreventivos,
        totales,
        fechaMantenimiento: new Date().toISOString(),
        kilometrajeActual: vehiculo?.kilometraje || 0,
        createdBy: currentUser.uid,
        createdAt: new Date().toISOString()
      };

      if (editingMaintenance) {
        const maintenanceRef = databaseRef(database, `mantenimientos/${editingMaintenance.id}`);
        await update(maintenanceRef, {
          ...maintenanceData,
          updatedAt: new Date().toISOString(),
          updatedBy: currentUser.uid
        });
        
        setMessage({ type: 'success', text: '‚úÖ Mantenimiento actualizado exitosamente' });
      } else {
        const maintenanceRef = databaseRef(database, 'mantenimientos');
        const newMaintenanceRef = push(maintenanceRef);
        await databaseSet(newMaintenanceRef, maintenanceData);
        
        // Actualizar kilometraje y fecha del veh√≠culo
        const vehicleRef = databaseRef(database, `vehiculos/${selectedVehicle}`);
        await update(vehicleRef, {
          ultimoMantenimiento: new Date().toISOString().split('T')[0],
          kilometrajeUltimoMantenimiento: vehiculo?.kilometraje || 0
        });
        
        setMessage({ type: 'success', text: '‚úÖ Mantenimiento registrado exitosamente' });
      }

      // Resetear formulario
      setShowMaintenanceForm(false);
      setEditingMaintenance(null);
      setSelectedVehicle('');
      setTipoRepuesto('original');
      setServiciosPreventivos([{
        id: Date.now(),
        numero: 1,
        servicio: '',
        codigo: '',
        detalle: '',
        manoObra: 0,
        costoRepuesto: 0,
        cantidadRepuesto: 1,
        repuestoDescripcion: ''
      }]);
      
    } catch (error) {
      console.error('Error al guardar mantenimiento:', error);
      setMessage({ type: 'error', text: '‚ùå Error al guardar mantenimiento' });
    } finally {
      setLoading(false);
    }
  };

  // Editar mantenimiento existente
  const handleEditMaintenance = (maintenance) => {
    setEditingMaintenance(maintenance);
    setSelectedVehicle(maintenance.vehiculoId);
    setTipoRepuesto(maintenance.tipoRepuesto);
    setServiciosPreventivos(maintenance.serviciosPreventivos);
    setShowMaintenanceForm(true);
  };

  // Eliminar mantenimiento
  const handleDeleteMaintenance = async (maintenanceId) => {
    if (window.confirm('¬øEst√° seguro de eliminar este registro de mantenimiento?')) {
      try {
        const maintenanceRef = databaseRef(database, `mantenimientos/${maintenanceId}`);
        await remove(maintenanceRef);
        setMessage({ type: 'success', text: '‚úÖ Mantenimiento eliminado' });
      } catch (error) {
        setMessage({ type: 'error', text: '‚ùå Error al eliminar mantenimiento' });
      }
    }
  };

  const totales = calcularTotales();

  return (
    <div>
      <h1 style={{
        margin: '0 0 30px 0',
        color: '#1e293b',
        fontSize: '28px',
        fontWeight: '600'
      }}>
        üîß Gesti√≥n de Mantenimientos Preventivos
      </h1>

      {/* Mensajes */}
      {message.text && (
        <div style={{
          padding: '12px',
          borderRadius: '8px',
          marginBottom: '20px',
          background: message.type === 'success' ? '#dcfce7' : '#fee2e2',
          border: `1px solid ${message.type === 'success' ? '#86efac' : '#fecaca'}`,
          color: message.type === 'success' ? '#166534' : '#991b1b'
        }}>
          {message.text}
        </div>
      )}

      {/* Estad√≠sticas */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: `repeat(auto-fit, minmax(${isMobile ? '150px' : '200px'}, 1fr))`,
        gap: '15px',
        marginBottom: '30px'
      }}>
        <div style={{
          background: 'white',
          padding: '20px',
          borderRadius: '12px',
          boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
          borderLeft: '4px solid #3b82f6'
        }}>
          <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#1f2937' }}>
            {maintenanceRecords.length}
          </div>
          <div style={{ color: '#6b7280', fontSize: '13px' }}>Total Mantenimientos</div>
        </div>

        <div style={{
          background: 'white',
          padding: '20px',
          borderRadius: '12px',
          boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
          borderLeft: '4px solid #22c55e'
        }}>
          <div style={{ fontSize: '28px', fontWeight: 'bold', color: '#1f2937' }}>
            {maintenanceRecords.filter(m => {
              const fecha = new Date(m.fechaMantenimiento);
              const hoy = new Date();
              return fecha.getMonth() === hoy.getMonth() && fecha.getFullYear() === hoy.getFullYear();
            }).length}
          </div>
          <div style={{ color: '#6b7280', fontSize: '13px' }}>Este Mes</div>
        </div>

        <div style={{
          background: 'white',
          padding: '20px',
          borderRadius: '12px',
          boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
          borderLeft: '4px solid #f59e0b'
        }}>
          <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#1f2937' }}>
            ${maintenanceRecords.reduce((sum, m) => sum + (m.totales?.total || 0), 0).toLocaleString('es-CL')}
          </div>
          <div style={{ color: '#6b7280', fontSize: '13px' }}>Costo Total</div>
        </div>
      </div>

      {/* Bot√≥n para nuevo mantenimiento */}
      <button
        onClick={() => {
          setShowMaintenanceForm(!showMaintenanceForm);
          setEditingMaintenance(null);
          setSelectedVehicle('');
          setTipoRepuesto('original');
          setServiciosPreventivos([{
            id: Date.now(),
            numero: 1,
            servicio: '',
            codigo: '',
            detalle: '',
            manoObra: 0,
            costoRepuesto: 0,
            cantidadRepuesto: 1,
            repuestoDescripcion: ''
          }]);
        }}
        style={{
          padding: '12px 24px',
          background: showMaintenanceForm ? '#6b7280' : 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          fontSize: '16px',
          fontWeight: '500',
          cursor: 'pointer',
          marginBottom: '20px'
        }}
      >
        {showMaintenanceForm ? '‚úñ Cancelar' : '‚ûï Nuevo Mantenimiento Preventivo'}
      </button>

      {/* Formulario de mantenimiento */}
      {showMaintenanceForm && (
        <div style={{
          background: 'white',
          padding: '30px',
          borderRadius: '12px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
          marginBottom: '30px'
        }}>
          <form onSubmit={handleSaveMaintenance}>
            {/* Selecci√≥n de veh√≠culo */}
            <div style={{
              marginBottom: '30px',
              padding: '20px',
              background: '#f0f9ff',
              borderRadius: '8px',
              border: '2px solid #0284c7'
            }}>
              <h3 style={{
                margin: '0 0 15px 0',
                color: '#0c4a6e',
                fontSize: '18px',
                fontWeight: '600'
              }}>
                üöõ Informaci√≥n del Veh√≠culo
              </h3>
              
              <div style={{
                display: 'grid',
                gridTemplateColumns: isMobile ? '1fr' : '2fr 1fr',
                gap: '15px'
              }}>
                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '8px',
                    color: '#374151',
                    fontSize: '14px',
                    fontWeight: '500'
                  }}>
                    Seleccionar Veh√≠culo *
                  </label>
                  <select
                    required
                    value={selectedVehicle}
                    onChange={(e) => setSelectedVehicle(e.target.value)}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  >
                    <option value="">-- Seleccionar veh√≠culo --</option>
                    {vehiculos.map(v => (
                      <option key={v.id} value={v.id}>
                        {v.nombre} - {v.marca} {v.modelo} - Patente: {v.patente}
                      </option>
                    ))}
                  </select>
                </div>

                {selectedVehicle && (
                  <div style={{
                    padding: '10px',
                    background: 'white',
                    borderRadius: '6px'
                  }}>
                    <div style={{ fontSize: '12px', color: '#6b7280', marginBottom: '5px' }}>
                      Kilometraje actual:
                    </div>
                    <div style={{ fontSize: '18px', fontWeight: 'bold', color: '#1f2937' }}>
                      {vehiculos.find(v => v.id === selectedVehicle)?.kilometraje?.toLocaleString('es-CL') || 0} km
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Tipo de repuestos */}
            <div style={{
              marginBottom: '30px',
              padding: '20px',
              background: '#fef3c7',
              borderRadius: '8px',
              border: '2px solid #f59e0b'
            }}>
              <h3 style={{
                margin: '0 0 15px 0',
                color: '#78350f',
                fontSize: '18px',
                fontWeight: '600'
              }}>
                üî© Tipo de Repuestos
              </h3>
              
              <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
                <label style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  cursor: 'pointer',
                  padding: '10px 20px',
                  background: tipoRepuesto === 'original' ? '#22c55e' : 'white',
                  color: tipoRepuesto === 'original' ? 'white' : '#374151',
                  borderRadius: '8px',
                  border: '2px solid',
                  borderColor: tipoRepuesto === 'original' ? '#22c55e' : '#d1d5db',
                  fontWeight: '500'
                }}>
                  <input
                    type="radio"
                    name="tipoRepuesto"
                    value="original"
                    checked={tipoRepuesto === 'original'}
                    onChange={(e) => setTipoRepuesto(e.target.value)}
                    style={{ display: 'none' }}
                  />
                  ‚úÖ Repuestos Originales
                </label>

                <label style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  cursor: 'pointer',
                  padding: '10px 20px',
                  background: tipoRepuesto === 'alternativo' ? '#f59e0b' : 'white',
                  color: tipoRepuesto === 'alternativo' ? 'white' : '#374151',
                  borderRadius: '8px',
                  border: '2px solid',
                  borderColor: tipoRepuesto === 'alternativo' ? '#f59e0b' : '#d1d5db',
                  fontWeight: '500'
                }}>
                  <input
                    type="radio"
                    name="tipoRepuesto"
                    value="alternativo"
                    checked={tipoRepuesto === 'alternativo'}
                    onChange={(e) => setTipoRepuesto(e.target.value)}
                    style={{ display: 'none' }}
                  />
                  üîÑ Repuestos Alternativos
                </label>
              </div>

              <div style={{
                marginTop: '15px',
                padding: '10px',
                background: 'white',
                borderRadius: '6px',
                fontSize: '13px',
                color: '#6b7280'
              }}>
                {tipoRepuesto === 'original' 
                  ? '‚úÖ Usando repuestos originales del fabricante con garant√≠a oficial'
                  : 'üîÑ Usando repuestos alternativos compatibles (menor costo, garant√≠a limitada)'
                }
              </div>
            </div>

            {/* REPARACIONES PREVENTIVAS */}
            <div style={{
              marginBottom: '30px',
              padding: '20px',
              background: '#f9fafb',
              borderRadius: '8px',
              border: '1px solid #e5e7eb'
            }}>
              <h3 style={{
                margin: '0 0 20px 0',
                color: '#1f2937',
                fontSize: '18px',
                fontWeight: '600',
                textAlign: 'center',
                padding: '10px',
                background: '#e0f2fe',
                borderRadius: '6px'
              }}>
                REPARACIONES PREVENTIVAS
              </h3>

              {/* Tabla de servicios */}
              <div style={{ overflowX: 'auto' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr style={{ background: '#1e293b', color: 'white' }}>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>N¬∞</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>Servicio</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>C√≥digo</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px', minWidth: '200px' }}>Detalle</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>Mano de Obra</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>Repuesto</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>Cant.</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>Costo Unit.</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>Subtotal</th>
                      <th style={{ padding: '10px', border: '1px solid #475569', fontSize: '13px' }}>Acci√≥n</th>
                    </tr>
                  </thead>
                  <tbody>
                    {serviciosPreventivos.map((servicio, index) => (
                      <tr key={servicio.id}>
                        <td style={{ 
                          padding: '8px', 
                          border: '1px solid #e5e7eb', 
                          textAlign: 'center',
                          background: '#f9fafb',
                          fontWeight: 'bold'
                        }}>
                          {servicio.numero}
                        </td>
                        <td style={{ padding: '4px', border: '1px solid #e5e7eb' }}>
                          <input
                            type="text"
                            value={servicio.servicio}
                            onChange={(e) => actualizarServicio(servicio.id, 'servicio', e.target.value)}
                            placeholder="Tipo de servicio"
                            style={{
                              width: '100%',
                              padding: '6px',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '13px'
                            }}
                          />
                        </td>
                        <td style={{ padding: '4px', border: '1px solid #e5e7eb' }}>
                          <input
                            type="text"
                            value={servicio.codigo}
                            onChange={(e) => actualizarServicio(servicio.id, 'codigo', e.target.value)}
                            placeholder="C√≥digo"
                            style={{
                              width: '100%',
                              padding: '6px',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '13px'
                            }}
                          />
                        </td>
                        <td style={{ padding: '4px', border: '1px solid #e5e7eb' }}>
                          <textarea
                            value={servicio.detalle}
                            onChange={(e) => actualizarServicio(servicio.id, 'detalle', e.target.value)}
                            placeholder="Descripci√≥n detallada del servicio"
                            rows={2}
                            style={{
                              width: '100%',
                              padding: '6px',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '13px',
                              resize: 'none'
                            }}
                          />
                        </td>
                        <td style={{ padding: '4px', border: '1px solid #e5e7eb' }}>
                          <input
                            type="number"
                            value={servicio.manoObra}
                            onChange={(e) => actualizarServicio(servicio.id, 'manoObra', e.target.value)}
                            placeholder="$"
                            min="0"
                            style={{
                              width: '100%',
                              padding: '6px',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '13px'
                            }}
                          />
                        </td>
                        <td style={{ padding: '4px', border: '1px solid #e5e7eb' }}>
                          <input
                            type="text"
                            value={servicio.repuestoDescripcion}
                            onChange={(e) => actualizarServicio(servicio.id, 'repuestoDescripcion', e.target.value)}
                            placeholder="Nombre repuesto"
                            style={{
                              width: '100%',
                              padding: '6px',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '13px'
                            }}
                          />
                        </td>
                        <td style={{ padding: '4px', border: '1px solid #e5e7eb' }}>
                          <input
                            type="number"
                            value={servicio.cantidadRepuesto}
                            onChange={(e) => actualizarServicio(servicio.id, 'cantidadRepuesto', e.target.value)}
                            min="1"
                            style={{
                              width: '60px',
                              padding: '6px',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '13px',
                              textAlign: 'center'
                            }}
                          />
                        </td>
                        <td style={{ padding: '4px', border: '1px solid #e5e7eb' }}>
                          <input
                            type="number"
                            value={servicio.costoRepuesto}
                            onChange={(e) => actualizarServicio(servicio.id, 'costoRepuesto', e.target.value)}
                            placeholder="$"
                            min="0"
                            style={{
                              width: '100%',
                              padding: '6px',
                              border: '1px solid #d1d5db',
                              borderRadius: '4px',
                              fontSize: '13px'
                            }}
                          />
                        </td>
                        <td style={{ 
                          padding: '8px', 
                          border: '1px solid #e5e7eb',
                          textAlign: 'right',
                          fontWeight: 'bold',
                          background: '#f0fdf4'
                        }}>
                          ${((parseFloat(servicio.manoObra) || 0) + ((parseFloat(servicio.costoRepuesto) || 0) * (parseInt(servicio.cantidadRepuesto) || 1))).toLocaleString('es-CL')}
                        </td>
                        <td style={{ padding: '4px', border: '1px solid #e5e7eb', textAlign: 'center' }}>
                          <button
                            type="button"
                            onClick={() => eliminarServicio(servicio.id)}
                            disabled={serviciosPreventivos.length === 1}
                            style={{
                              padding: '4px 8px',
                              background: serviciosPreventivos.length === 1 ? '#e5e7eb' : '#ef4444',
                              color: 'white',
                              border: 'none',
                              borderRadius: '4px',
                              fontSize: '12px',
                              cursor: serviciosPreventivos.length === 1 ? 'not-allowed' : 'pointer'
                            }}
                          >
                            üóëÔ∏è
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Bot√≥n agregar servicio */}
              <button
                type="button"
                onClick={agregarServicio}
                style={{
                  marginTop: '15px',
                  padding: '8px 16px',
                  background: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  cursor: 'pointer'
                }}
              >
                ‚ûï Agregar L√≠nea de Servicio
              </button>
            </div>

            {/* RESUMEN DE COSTOS */}
            <div style={{
              marginBottom: '30px',
              display: 'grid',
              gridTemplateColumns: isMobile ? '1fr' : '1fr 1fr',
              gap: '20px'
            }}>
              {/* Columna Repuestos Originales/Alternativos */}
              <div style={{
                padding: '20px',
                background: tipoRepuesto === 'original' ? '#f0fdf4' : '#fef3c7',
                borderRadius: '8px',
                border: `2px solid ${tipoRepuesto === 'original' ? '#22c55e' : '#f59e0b'}`
              }}>
                <h4 style={{
                  margin: '0 0 15px 0',
                  color: tipoRepuesto === 'original' ? '#166534' : '#78350f',
                  fontSize: '16px',
                  fontWeight: '600',
                  textAlign: 'center'
                }}>
                  {tipoRepuesto === 'original' 
                    ? '‚úÖ Valor Servicio + Repuestos ORIGINALES'
                    : 'üîÑ Valor Servicio + Repuestos ALTERNATIVOS'
                  }
                </h4>

                <div style={{ 
                  display: 'grid',
                  gridTemplateColumns: '2fr 1fr',
                  gap: '10px',
                  fontSize: '14px'
                }}>
                  <div style={{ color: '#6b7280' }}>Subtotal Mano de Obra:</div>
                  <div style={{ textAlign: 'right', fontWeight: '500' }}>
                    ${totales.subtotalManoObra.toLocaleString('es-CL')}
                  </div>

                  <div style={{ color: '#6b7280' }}>Subtotal Repuestos:</div>
                  <div style={{ textAlign: 'right', fontWeight: '500' }}>
                    ${totales.subtotalRepuestos.toLocaleString('es-CL')}
                  </div>

                  <div style={{ 
                    paddingTop: '10px',
                    borderTop: '1px solid #e5e7eb',
                    fontWeight: '600'
                  }}>
                    VALOR NETO:
                  </div>
                  <div style={{ 
                    paddingTop: '10px',
                    borderTop: '1px solid #e5e7eb',
                    textAlign: 'right',
                    fontWeight: '600'
                  }}>
                    ${totales.valorNeto.toLocaleString('es-CL')}
                  </div>

                  <div style={{ color: '#6b7280' }}>IVA (19%):</div>
                  <div style={{ textAlign: 'right' }}>
                    ${totales.iva.toLocaleString('es-CL')}
                  </div>

                  <div style={{
                    paddingTop: '10px',
                    borderTop: '2px solid',
                    borderColor: tipoRepuesto === 'original' ? '#22c55e' : '#f59e0b',
                    fontSize: '18px',
                    fontWeight: 'bold',
                    color: tipoRepuesto === 'original' ? '#166534' : '#78350f'
                  }}>
                    TOTAL:
                  </div>
                  <div style={{
                    paddingTop: '10px',
                    borderTop: '2px solid',
                    borderColor: tipoRepuesto === 'original' ? '#22c55e' : '#f59e0b',
                    textAlign: 'right',
                    fontSize: '18px',
                    fontWeight: 'bold',
                    color: tipoRepuesto === 'original' ? '#166534' : '#78350f'
                  }}>
                    ${totales.total.toLocaleString('es-CL')}
                  </div>
                </div>
              </div>

              {/* Columna de comparaci√≥n de ahorro */}
              {tipoRepuesto === 'alternativo' && (
                <div style={{
                  padding: '20px',
                  background: '#eff6ff',
                  borderRadius: '8px',
                  border: '2px solid #3b82f6'
                }}>
                  <h4 style={{
                    margin: '0 0 15px 0',
                    color: '#1e40af',
                    fontSize: '16px',
                    fontWeight: '600',
                    textAlign: 'center'
                  }}>
                    üí∞ Comparaci√≥n de Ahorro
                  </h4>

                  <div style={{
                    padding: '15px',
                    background: 'white',
                    borderRadius: '6px',
                    marginBottom: '10px'
                  }}>
                    <div style={{ fontSize: '13px', color: '#6b7280', marginBottom: '5px' }}>
                      Costo estimado con repuestos originales:
                    </div>
                    <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#6b7280', textDecoration: 'line-through' }}>
                      ${(totales.total * 1.3).toLocaleString('es-CL')}
                    </div>
                  </div>

                  <div style={{
                    padding: '15px',
                    background: '#dcfce7',
                    borderRadius: '6px'
                  }}>
                    <div style={{ fontSize: '13px', color: '#166534', marginBottom: '5px' }}>
                      Tu ahorro usando repuestos alternativos:
                    </div>
                    <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#166534' }}>
                      ${(totales.total * 0.3).toLocaleString('es-CL')}
                    </div>
                    <div style={{ fontSize: '12px', color: '#166534', marginTop: '5px' }}>
                      (Aproximadamente 30% menos)
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Botones de acci√≥n */}
            <div style={{ display: 'flex', gap: '10px', justifyContent: 'flex-end' }}>
              <button
                type="button"
                onClick={() => {
                  setShowMaintenanceForm(false);
                  setEditingMaintenance(null);
                }}
                style={{
                  padding: '12px 24px',
                  background: '#6b7280',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '16px',
                  cursor: 'pointer'
                }}
              >
                Cancelar
              </button>

              <button
                type="submit"
                disabled={loading}
                style={{
                  padding: '12px 24px',
                  background: loading ? '#9ca3af' : '#22c55e',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '16px',
                  fontWeight: '500',
                  cursor: loading ? 'not-allowed' : 'pointer'
                }}
              >
                {loading ? 'Guardando...' : editingMaintenance ? '‚úÖ Actualizar Mantenimiento' : '‚úÖ Guardar Mantenimiento'}
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Lista de mantenimientos */}
      <div style={{
        background: 'white',
        borderRadius: '12px',
        boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
        overflow: 'hidden'
      }}>
        <div style={{
          padding: '20px',
          borderBottom: '1px solid #e5e7eb',
          background: 'linear-gradient(135deg, #f9fafb 0%, #f3f4f6 100%)'
        }}>
          <h2 style={{ margin: 0, color: '#1f2937', fontSize: '20px' }}>
            üìã Historial de Mantenimientos
          </h2>
        </div>

        <div style={{ overflowX: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ background: '#f9fafb' }}>
                <th style={{ padding: '12px 20px', textAlign: 'left', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                  Fecha
                </th>
                <th style={{ padding: '12px 20px', textAlign: 'left', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                  Veh√≠culo
                </th>
                <th style={{ padding: '12px 20px', textAlign: 'left', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                  Tipo Repuesto
                </th>
                <th style={{ padding: '12px 20px', textAlign: 'left', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                  Servicios
                </th>
                <th style={{ padding: '12px 20px', textAlign: 'right', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                  Total
                </th>
                <th style={{ padding: '12px 20px', textAlign: 'center', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                  Acciones
                </th>
              </tr>
            </thead>
            <tbody>
              {maintenanceRecords.map((record) => (
                <tr key={record.id} style={{ borderBottom: '1px solid #e5e7eb' }}>
                  <td style={{ padding: '16px 20px', fontSize: '14px', color: '#1f2937' }}>
                    {new Date(record.fechaMantenimiento).toLocaleDateString('es-CL')}
                  </td>
                  <td style={{ padding: '16px 20px', fontSize: '14px', color: '#6b7280' }}>
                    <div>{record.vehiculoNombre}</div>
                    <div style={{ fontSize: '12px' }}>Patente: {record.vehiculoPatente}</div>
                  </td>
                  <td style={{ padding: '16px 20px' }}>
                    <span style={{
                      padding: '4px 8px',
                      borderRadius: '4px',
                      fontSize: '12px',
                      fontWeight: '500',
                      background: record.tipoRepuesto === 'original' ? '#dcfce7' : '#fef3c7',
                      color: record.tipoRepuesto === 'original' ? '#166534' : '#78350f'
                    }}>
                      {record.tipoRepuesto === 'original' ? '‚úÖ Original' : 'üîÑ Alternativo'}
                    </span>
                  </td>
                  <td style={{ padding: '16px 20px', fontSize: '14px', color: '#6b7280' }}>
                    {record.serviciosPreventivos?.length || 0} servicios
                  </td>
                  <td style={{ 
                    padding: '16px 20px', 
                    fontSize: '16px', 
                    fontWeight: 'bold',
                    color: '#1f2937',
                    textAlign: 'right' 
                  }}>
                    ${record.totales?.total?.toLocaleString('es-CL') || 0}
                  </td>
                  <td style={{ padding: '16px 20px', textAlign: 'center' }}>
                    <div style={{ display: 'flex', gap: '8px', justifyContent: 'center' }}>
                      <button
                        onClick={() => handleEditMaintenance(record)}
                        style={{
                          padding: '6px 12px',
                          background: '#3b82f6',
                          color: 'white',
                          border: 'none',
                          borderRadius: '4px',
                          fontSize: '12px',
                          cursor: 'pointer'
                        }}
                      >
                        ‚úèÔ∏è Editar
                      </button>
                      <button
                        onClick={() => handleDeleteMaintenance(record.id)}
                        style={{
                          padding: '6px 12px',
                          background: '#ef4444',
                          color: 'white',
                          border: 'none',
                          borderRadius: '4px',
                          fontSize: '12px',
                          cursor: 'pointer'
                        }}
                      >
                        üóëÔ∏è
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {maintenanceRecords.length === 0 && (
            <div style={{
              padding: '40px',
              textAlign: 'center',
              color: '#6b7280'
            }}>
              <div style={{ fontSize: '48px', marginBottom: '10px' }}>üîß</div>
              <p style={{ fontSize: '16px' }}>No hay mantenimientos registrados</p>
              <p style={{ fontSize: '14px' }}>Haz clic en "Nuevo Mantenimiento Preventivo" para comenzar</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PreventiveMaintenanceModule;
