    },
    subtitle: {
      margin: '4px 0 0 0',
      fontSize: isMobile ? '12px' : '14px',
      color: '#6b7280'
    },
    logoutButton: {
      padding: isMobile ? '6px 12px' : '8px 16px',
      background: '#ef4444',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      fontSize: isMobile ? '12px' : '14px',
      fontWeight: '500',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '4px'
    },
    content: {
      padding: isMobile ? '10px' : '0'
    },
    newSolicitudButton: {
      width: '100%',
      padding: isMobile ? '12px' : '15px',
      background: 'white',
      color: '#667eea',
      border: '2px dashed #667eea',
      borderRadius: isMobile ? '8px' : '12px',
      fontSize: isMobile ? '14px' : '16px',
      fontWeight: '600',
      cursor: 'pointer',
      marginBottom: isMobile ? '15px' : '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '8px'
    },
    formContainer: {
      background: 'white',
      borderRadius: isMobile ? '8px' : '12px',
      padding: isMobile ? '15px' : '20px',
      marginBottom: isMobile ? '15px' : '20px',
