      padding: isMobile ? '8px' : '10px',
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      fontSize: isMobile ? '14px' : '14px',
      WebkitAppearance: 'none',
      background: 'white'
    },
    textarea: {
      width: '100%',
      padding: isMobile ? '8px' : '10px',
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      fontSize: isMobile ? '14px' : '14px',
      resize: 'vertical',
      minHeight: isMobile ? '80px' : '100px'
    },
    gridTwoColumns: {
      display: 'grid',
      gridTemplateColumns: isMobile ? '1fr' : '1fr 1fr',
      gap: isMobile ? '12px' : '10px',
      marginBottom: isMobile ? '12px' : '15px'
    },
    mapContainer: {
      width: '100%',
      height: isMobile ? '250px' : '300px',
      borderRadius: '8px',
      border: '2px solid #e5e7eb',
      marginBottom: '10px'
    },
    locationInfo: {
      marginTop: '10px',
      padding: isMobile ? '8px' : '10px',
      background: '#f0f9ff',
      borderRadius: '6px',
      fontSize: isMobile ? '12px' : '13px',
      color: '#0369a1'
    },
    buttonGroup: {
      display: 'flex',
      gap: '10px',
      marginTop: isMobile ? '15px' : '20px',
      flexDirection: isMobile ? 'column' : 'row'
    },
    submitButton: {
      flex: 1,
      padding: isMobile ? '10px' : '12px',
      background: loading || !selectedLocation ? '#9ca3af' : '#22c55e',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      fontSize: isMobile ? '14px' : '16px',
      fontWeight: '600',
      cursor: loading || !selectedLocation ? 'not-allowed' : 'pointer'
    },
    cancelButton: {
      padding: '5px 10px',
      background: '#6b7280',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      fontSize: isMobile ? '12px' : '14px',
      cursor: 'pointer'
    },
    solicitudesList: {
      background: 'white',
      borderRadius: isMobile ? '8px' : '12px',
      padding: isMobile ? '15px' : '20px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.1)'
    },
    solicitudCard: {
      border: '1px solid #e5e7eb',
      borderRadius: '8px',
      padding: isMobile ? '12px' : '15px',
      background: '#f9fafb',
      marginBottom: '12px',
      cursor: 'pointer',
      transition: 'all 0.2s'
    },
    solicitudHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      marginBottom: '10px',
      flexWrap: isMobile ? 'wrap' : 'nowrap',
      gap: '8px'
    },
    solicitudTitle: {
      margin: '0 0 5px 0',
      fontSize: isMobile ? '14px' : '16px',
      color: '#1f2937'
    },
    solicitudDate: {
      margin: '0',
      fontSize: isMobile ? '11px' : '13px',
      color: '#6b7280'
    },
    solicitudDetails: {
      fontSize: isMobile ? '12px' : '14px',
      color: '#374151',
      marginBottom: '10px',
      lineHeight: '1.5'
    },
    estadoBadge: {
      padding: '4px 8px',
      borderRadius: '4px',
      fontSize: isMobile ? '11px' : '12px',
      fontWeight: '600',
      whiteSpace: 'nowrap'
    },
    respuestaBox: {
      marginTop: '10px',
      padding: isMobile ? '8px' : '10px',
      borderRadius: '6px',
      fontSize: isMobile ? '12px' : '13px'
    },
    emptyState: {
      textAlign: 'center',
      padding: isMobile ? '30px 20px' : '40px 20px',
      color: '#6b7280'
    },
    messageAlert: {
      padding: isMobile ? '10px' : '12px',
      borderRadius: '8px',
      marginBottom: '15px',
      fontSize: isMobile ? '13px' : '14px'
    }
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <div>
            <h1 style={styles.title}>
              üèòÔ∏è Junta de Vecinos
            </h1>
            <p style={styles.subtitle}>
              {currentUser.localidad || currentUser.name || currentUser.email}
            </p>
          </div>
          
          <button onClick={onLogout} style={styles.logoutButton}>
            {isMobile ? 'üö™' : 'üö™ Salir'}
          </button>
        </div>
      </div>

      <div style={styles.content}>
        {/* Mensajes */}
        {message.text && (
          <div style={{
            ...styles.messageAlert,
            background: message.type === 'success' ? '#dcfce7' : '#fee2e2',
            border: `1px solid ${message.type === 'success' ? '#86efac' : '#fecaca'}`,
            color: message.type === 'success' ? '#166534' : '#991b1b'
          }}>
            {message.text}
          </div>
        )}

        {/* Bot√≥n Nueva Solicitud */}
        {!showNewSolicitud && (
          <button onClick={() => setShowNewSolicitud(true)} style={styles.newSolicitudButton}>
            <span style={{ fontSize: isMobile ? '20px' : '24px' }}>‚ûï</span>
            {isMobile ? 'Nueva Solicitud' : 'Nueva Solicitud de Maquinaria'}
          </button>
        )}

        {/* Formulario de Nueva Solicitud */}
        {showNewSolicitud && (
          <div style={styles.formContainer}>
            <div style={styles.formHeader}>
              <h2 style={styles.formTitle}>
                üìù Nueva Solicitud
              </h2>
              <button
                onClick={() => {
                  setShowNewSolicitud(false);
                  setFormData({
                    asunto: '',
                    tipoMaquinaria: '',
                    descripcion: '',
                    nombreSolicitante: '',
                    telefonoContacto: '',
                    fechaNecesaria: '',
                    duracionEstimada: '1',
                    prioridad: 'normal'
                  });
                  setSelectedLocation(null);
                  if (markerRef.current) {
                    markerRef.current.setMap(null);
                    markerRef.current = null;
                  }
                }}
                style={styles.cancelButton}
              >
                ‚úñ {isMobile ? '' : 'Cancelar'}
              </button>
            </div>

            <form onSubmit={handleSubmitSolicitud}>
              {/* Informaci√≥n del Solicitante */}
              <div style={styles.formSection}>
                <h3 style={styles.sectionTitle}>
                  üë§ Informaci√≥n del Solicitante
                </h3>
                
                <div style={styles.formGroup}>
                  <label style={styles.label}>
                    Nombre Completo *
                  </label>
                  <input
                    type="text"
                    required
                    value={formData.nombreSolicitante}
                    onChange={(e) => setFormData({...formData, nombreSolicitante: e.target.value})}
                    placeholder="Ej: Juan P√©rez Gonz√°lez"
                    style={styles.input}
                  />
                </div>

                <div style={styles.formGroup}>
                  <label style={styles.label}>
                    Tel√©fono de Contacto *
                  </label>
                  <input
                    type="tel"
                    required
                    value={formData.telefonoContacto}
                    onChange={(e) => setFormData({...formData, telefonoContacto: e.target.value})}
                    placeholder="+56 9 XXXX XXXX"
                    style={styles.input}
                  />
                </div>
              </div>

              {/* Detalles de la Solicitud */}
              <div style={styles.formSection}>
                <h3 style={styles.sectionTitle}>
                  üöú Detalles de la Solicitud
                </h3>

                <div style={styles.formGroup}>
                  <label style={styles.label}>
                    Asunto *
                  </label>
                  <input
                    type="text"
                    required
                    value={formData.asunto}
                    onChange={(e) => setFormData({...formData, asunto: e.target.value})}
                    placeholder="Ej: Limpieza de canal"
                    style={styles.input}
                  />
                </div>

                <div style={styles.formGroup}>
                  <label style={styles.label}>
                    Tipo de Maquinaria *
                  </label>
                  <select
                    required
                    value={formData.tipoMaquinaria}
                    onChange={(e) => setFormData({...formData, tipoMaquinaria: e.target.value})}
                    style={styles.select}
                  >
                    <option value="">-- Seleccionar --</option>
                    <option value="retroexcavadora">üèóÔ∏è Retroexcavadora</option>
                    <option value="motoniveladora">üöß Motoniveladora</option>
                    <option value="camion">üöõ Cami√≥n</option>
                    <option value="excavadora">‚öíÔ∏è Excavadora</option>
                    <option value="bulldozer">üöú Bulldozer</option>
                    <option value="otro">üìã Otro</option>
                  </select>
                </div>

                <div style={styles.gridTwoColumns}>
                  <div>
                    <label style={styles.label}>
                      Fecha Necesaria *
                    </label>
                    <input
                      type="date"
                      required
                      value={formData.fechaNecesaria}
                      onChange={(e) => setFormData({...formData, fechaNecesaria: e.target.value})}
                      min={new Date().toISOString().split('T')[0]}
                      style={styles.input}
                    />
                  </div>

                  <div>
                    <label style={styles.label}>
                      Duraci√≥n (d√≠as) *
                    </label>
                    <input
                      type="number"
                      required
                      min="1"
                      max="30"
                      value={formData.duracionEstimada}
                      onChange={(e) => setFormData({...formData, duracionEstimada: e.target.value})}
                      style={styles.input}
                    />
                  </div>
                </div>

                <div style={styles.formGroup}>
                  <label style={styles.label}>
                    Prioridad *
                  </label>
                  <select
                    required
                    value={formData.prioridad}
                    onChange={(e) => setFormData({...formData, prioridad: e.target.value})}
                    style={styles.select}
                  >
                    <option value="baja">üü¢ Baja</option>
                    <option value="normal">üü° Normal</option>
                    <option value="alta">üü† Alta</option>
                    <option value="urgente">üî¥ Urgente</option>
                  </select>
                </div>

                <div style={styles.formGroup}>
                  <label style={styles.label}>
                    Descripci√≥n Detallada *
                  </label>
                  <textarea
                    required
                    rows={4}
                    value={formData.descripcion}
                    onChange={(e) => setFormData({...formData, descripcion: e.target.value})}
                    placeholder="Describa el trabajo a realizar..."
                    style={styles.textarea}
                  />
                </div>
              </div>

              {/* Ubicaci√≥n */}
              <div style={styles.formSection}>
                <h3 style={styles.sectionTitle}>
                  üìç Ubicaci√≥n del Trabajo *
                </h3>
                
                <p style={{
                  fontSize: isMobile ? '12px' : '13px',
                  color: '#6b7280',
                  marginBottom: '10px'
                }}>
                  Toca el mapa para marcar la ubicaci√≥n
                </p>

                <div ref={mapRef} style={styles.mapContainer} />

                {selectedLocation && (
                  <div style={styles.locationInfo}>
                    üìç Ubicaci√≥n seleccionada: {selectedLocation.lat.toFixed(6)}, {selectedLocation.lng.toFixed(6)}
                  </div>
                )}
              </div>

              {/* Botones */}
              <div style={styles.buttonGroup}>
                <button
                  type="submit"
                  disabled={loading || !selectedLocation}
                  style={styles.submitButton}
                >
                  {loading ? '‚è≥ Enviando...' : 'üì§ Enviar Solicitud'}
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Lista de Solicitudes */}
        <div style={styles.solicitudesList}>
          <h2 style={{
            margin: `0 0 ${isMobile ? '15px' : '20px'} 0`,
            fontSize: isMobile ? '16px' : '18px',
            color: '#1f2937'
          }}>
            üìã Mis Solicitudes
          </h2>

          {solicitudes.length === 0 ? (
            <div style={styles.emptyState}>
              <div style={{ fontSize: isMobile ? '36px' : '48px', marginBottom: '10px' }}>üì≠</div>
              <p>No tienes solicitudes registradas</p>
              <p style={{ fontSize: isMobile ? '12px' : '14px' }}>
                Haz clic en "Nueva Solicitud" para comenzar
              </p>
            </div>
          ) : (
            <div>
              {solicitudes.map((solicitud) => (
                <div
                  key={solicitud.id}
                  style={styles.solicitudCard}
                  onClick={() => setExpandedSolicitud(
                    expandedSolicitud === solicitud.id ? null : solicitud.id
                  )}
                >
                  <div style={styles.solicitudHeader}>
                    <div style={{ flex: 1 }}>
                      <h3 style={styles.solicitudTitle}>
                        {solicitud.asunto}
                      </h3>
                      <p style={styles.solicitudDate}>
                        {new Date(solicitud.fechaSolicitud).toLocaleDateString('es-CL')}
                      </p>
                    </div>
                    <span style={{
                      ...styles.estadoBadge,
                      background: getEstadoColor(solicitud.estado) + '20',
                      color: getEstadoColor(solicitud.estado)
                    }}>
                      {getEstadoTexto(solicitud.estado)}
                    </span>
                  </div>

                  {expandedSolicitud === solicitud.id && (
                    <div style={styles.solicitudDetails}>
                      <strong>Tipo:</strong> {solicitud.tipoMaquinaria}<br/>
                      <strong>Fecha necesaria:</strong> {new Date(solicitud.fechaNecesaria).toLocaleDateString('es-CL')}<br/>
                      <strong>Duraci√≥n:</strong> {solicitud.duracionEstimada} d√≠a(s)<br/>
                      <strong>Prioridad:</strong> {solicitud.prioridad}
                    </div>
                  )}

                  {solicitud.respuesta && (
                    <div style={{
                      ...styles.respuestaBox,
                      background: solicitud.estado === 'aprobada' ? '#dcfce7' : '#fee2e2'
                    }}>
                      <strong>Respuesta:</strong><br/>
                      {solicitud.respuesta}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default JuntaVecinosPanel;
