// src/components/Login.js
import React, { useState } from 'react';
import PasswordRecovery from './PasswordRecovery';
import {
  signInWithEmailAndPassword,
  updatePassword,
  EmailAuthProvider,
  reauthenticateWithCredential,
  createUserWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import { auth, firestore } from '../config/firebase';
import { doc, getDoc, updateDoc, query, where, collection, getDocs, setDoc } from 'firebase/firestore';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Estados para cambio de contraseÃ±a obligatorio
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [oldPassword, setOldPassword] = useState('');
  const [showPasswordRecovery, setShowPasswordRecovery] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccessMessage('');

    const normalizedEmail = email.toLowerCase().trim();
    console.log('ðŸ” Intentando login con email:', normalizedEmail);
    console.log('ðŸ”‘ ContraseÃ±a ingresada:', password);

    try {
      // PRIMERO: Verificar si existe una contraseÃ±a temporal en Firestore
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', normalizedEmail));
      const querySnapshot = await getDocs(q);

      if (!querySnapshot.empty) {
        const userDoc = querySnapshot.docs[0];
        const userFirestoreData = userDoc.data();
        const userDocId = userDoc.id;

        console.log('ðŸ“‹ Usuario encontrado en Firestore:');
        console.log('  - ID documento:', userDocId);
        console.log('  - Email en DB:', userFirestoreData.email);
        console.log('  - Tiene contraseÃ±a temporal?:', !!userFirestoreData.tempPassword);
        console.log('  - Requiere cambio?:', userFirestoreData.requirePasswordChange);

        // Verificar si hay contraseÃ±a temporal
        if (userFirestoreData.tempPassword) {
          try {
            const tempPasswordDecoded = atob(userFirestoreData.tempPassword);
            console.log('ðŸ” ContraseÃ±a temporal decodificada:', tempPasswordDecoded);
            console.log('ðŸ”‘ ContraseÃ±a ingresada:', password);
            console.log('âœ… Coinciden?:', password === tempPasswordDecoded);
            
            if (password === tempPasswordDecoded) {
              console.log('âœ… Login con contraseÃ±a temporal exitoso!');
              console.log('ðŸ” Requiere cambio de contraseÃ±a?:', userFirestoreData.requirePasswordChange);
              
              // Login exitoso con contraseÃ±a temporal
              // Actualizar Ãºltima conexiÃ³n
              await updateDoc(doc(firestore, 'users', userDocId), {
                lastLogin: new Date().toISOString()
              });
              
              // SOLO marcar para cambio de contraseÃ±a si realmente lo requiere
              if (userFirestoreData.requirePasswordChange === true) {
                console.log('âž¡ï¸ Redirigiendo a cambio de contraseÃ±a obligatorio');
                setCurrentUser({
                  uid: userDocId,
                  email: userFirestoreData.email,
                  role: userFirestoreData.role,
                  name: userFirestoreData.name,
                  ...userFirestoreData,
                  isTempPasswordLogin: true
                });
                setOldPassword(password);
                setShowChangePassword(true);
                setLoading(false);
                return;
              } else {
                console.log('âœ… ContraseÃ±a ya fue cambiada, continuando con login normal');
                // La contraseÃ±a ya fue cambiada, continuar con login normal
                onLogin({
                  uid: userDocId,
                  email: userFirestoreData.email,
                  role: userFirestoreData.role,
                  name: userFirestoreData.name,
                  ...userFirestoreData
                });
                setLoading(false);
                return;
              }
            } else {
              console.log('âŒ Las contraseÃ±as NO coinciden');
              console.log('  - Esperada:', tempPasswordDecoded);
              console.log('  - Recibida:', password);
            }
          } catch (e) {
            console.log('âŒ Error decodificando contraseÃ±a temporal:', e);
          }
        } else {
          console.log('â„¹ï¸ Usuario no tiene contraseÃ±a temporal, intentando login normal...');
        }
      } else {
        console.log('âš ï¸ No se encontrÃ³ usuario en Firestore con email:', normalizedEmail);
      }

      // SEGUNDO: Si no es contraseÃ±a temporal o no coincide, intentar login normal con Firebase Auth
      console.log('ðŸ” Intentando login normal con Firebase Auth...');
      
      try {
        const userCredential = await signInWithEmailAndPassword(auth, normalizedEmail, password);
        const user = userCredential.user;
        console.log('âœ… Login exitoso con Firebase Auth, UID:', user.uid);

        // Obtener datos del usuario desde Firestore usando el UID de Auth
        const userDoc = await getDoc(doc(firestore, 'users', user.uid));

        if (userDoc.exists()) {
          const userData = userDoc.data();

          // Verificar si debe cambiar contraseÃ±a
          if (userData.requirePasswordChange || userData.firstLogin !== false) {
            // Limpiar flags de contraseÃ±a temporal si existen
            if (userData.tempPassword) {
              await updateDoc(doc(firestore, 'users', user.uid), {
                tempPassword: null,
                lastTempPassword: userData.tempPassword // Guardar para referencia
              });
            }

            setCurrentUser({
              uid: user.uid,
              email: user.email,
              role: userData.role,
              name: userData.name,
              ...userData
            });
            setOldPassword(password);
            setShowChangePassword(true);
          } else {
            // Login exitoso - actualizar Ãºltima conexiÃ³n
            await updateDoc(doc(firestore, 'users', user.uid), {
              lastLogin: new Date().toISOString()
            });

            // Login normal
            onLogin({
              uid: user.uid,
              email: user.email,
              role: userData.role,
              name: userData.name,
              ...userData
            });
          }
        } else {
          console.log('âŒ Usuario autenticado pero no encontrado en Firestore, UID:', user.uid);
          setError('No se encontraron datos del usuario');
        }
      } catch (authError) {
        console.log('âŒ Error en Firebase Auth:', authError.code);
        
        // Si falla el login con Firebase Auth, verificar si la contraseÃ±a estÃ¡ en Firestore como fallback
        if (authError.code === 'auth/invalid-credential' || authError.code === 'auth/wrong-password') {
          console.log('ðŸ”„ Verificando contraseÃ±a en Firestore como fallback...');
          
          // Buscar el usuario en Firestore nuevamente
          const usersRef = collection(firestore, 'users');
          const q = query(usersRef, where('email', '==', normalizedEmail));
          const querySnapshot = await getDocs(q);
          
          if (!querySnapshot.empty) {
            const userDoc = querySnapshot.docs[0];
            const userFirestoreData = userDoc.data();
            const userDocId = userDoc.id;
            
            // Verificar si hay una contraseÃ±a guardada en Firestore (fallback)
            if (userFirestoreData.tempPassword) {
              try {
                const storedPassword = atob(userFirestoreData.tempPassword);
                
                if (password === storedPassword) {
                  console.log('âœ… Login exitoso con contraseÃ±a de Firestore (fallback)');
                  console.log('ðŸ” Necesita sincronizaciÃ³n con Auth?:', userFirestoreData.needsAuthSync);
                  console.log('ðŸ” Requiere cambio de contraseÃ±a?:', userFirestoreData.requirePasswordChange);
                  
                  // Si needsAuthSync es true, intentar sincronizar
                  if (userFirestoreData.needsAuthSync === true) {
                    // Intentar sincronizar con Firebase Auth
                    try {
                      console.log('ðŸ”„ Intentando sincronizar con Firebase Auth...');
                      await createUserWithEmailAndPassword(auth, normalizedEmail, password);
                      console.log('âœ… Usuario sincronizado con Firebase Auth');
                      
                      const firebaseUser = auth.currentUser;
                      if (firebaseUser && firebaseUser.uid !== userDocId) {
                        // Migrar datos al nuevo UID de Firebase
                        console.log('ðŸ”„ Migrando datos al UID de Firebase Auth...');
                        
                        // Crear nuevo documento con el UID de Firebase
                        await setDoc(doc(firestore, 'users', firebaseUser.uid), {
                          ...userFirestoreData,
                          tempPassword: null, // Limpiar contraseÃ±a temporal
                          needsAuthSync: false,
                          authPasswordSynced: true,
                          requirePasswordChange: false,
                          lastLogin: new Date().toISOString()
                        });
                        
                        // Cerrar sesiÃ³n y usar el nuevo UID para el login
                        await signOut(auth);
                        
                        onLogin({
                          uid: firebaseUser.uid,
                          email: userFirestoreData.email,
                          role: userFirestoreData.role,
                          name: userFirestoreData.name,
                          ...userFirestoreData,
                          requirePasswordChange: false
                        });
                      } else {
                        // Limpiar flags de sincronizaciÃ³n
                        await updateDoc(doc(firestore, 'users', userDocId), {
                          tempPassword: null, // Limpiar contraseÃ±a temporal ya sincronizada
                          needsAuthSync: false,
                          authPasswordSynced: true,
                          requirePasswordChange: false,
                          lastLogin: new Date().toISOString()
                        });
                        
                        // Cerrar sesiÃ³n
                        if (auth.currentUser) {
                          await signOut(auth);
                        }
                        
                        // Login exitoso
                        onLogin({
                          uid: userDocId,
                          email: userFirestoreData.email,
                          role: userFirestoreData.role,
                          name: userFirestoreData.name,
                          ...userFirestoreData,
                          requirePasswordChange: false
                        });
                      }
                    } catch (syncError) {
                      console.log('âš ï¸ No se pudo sincronizar con Auth:', syncError.code);
                      
                      if (syncError.code === 'auth/email-already-in-use') {
                        // El usuario ya existe en Auth, continuar sin sincronizar
                        console.log('â„¹ï¸ Usuario ya existe en Auth, continuando con Firestore...');
                      }
                      
                      // Actualizar Ãºltima conexiÃ³n
                      await updateDoc(doc(firestore, 'users', userDocId), {
                        lastLogin: new Date().toISOString()
                      });
                      
                      // Login exitoso con Firestore
                      onLogin({
                        uid: userDocId,
                        email: userFirestoreData.email,
                        role: userFirestoreData.role,
                        name: userFirestoreData.name,
                        ...userFirestoreData,
                        requirePasswordChange: false
                      });
                    }
                  } else {
                    // No necesita sincronizaciÃ³n, login directo
                    await updateDoc(doc(firestore, 'users', userDocId), {
                      lastLogin: new Date().toISOString()
                    });
                    
                    onLogin({
                      uid: userDocId,
                      email: userFirestoreData.email,
                      role: userFirestoreData.role,
                      name: userFirestoreData.name,
                      ...userFirestoreData,
                      requirePasswordChange: false
                    });
                  }
                  
                  setLoading(false);
                  return;
                }
              } catch (e) {
                console.log('Error verificando contraseÃ±a de fallback:', e);
              }
            }
          }
        }
        
        // Si llegamos aquÃ­, el login fallÃ³ completamente
        throw authError;
      }
    } catch (error) {
      console.error('âŒ Error al iniciar sesiÃ³n:', error);
      console.error('CÃ³digo de error:', error.code);
      console.error('Mensaje:', error.message);

      // Mensajes de error en espaÃ±ol
      switch (error.code) {
        case 'auth/user-not-found':
          setError('Usuario no encontrado. Verifica tu email');
          break;
        case 'auth/wrong-password':
          setError('ContraseÃ±a incorrecta');
          break;
        case 'auth/invalid-email':
          setError('Email invÃ¡lido');
          break;
        case 'auth/too-many-requests':
          setError('Demasiados intentos. Intenta mÃ¡s tarde');
          break;
        case 'auth/invalid-credential':
          setError('Credenciales invÃ¡lidas. Verifica tu email y contraseÃ±a');
          break;
        case 'auth/network-request-failed':
          setError('Error de conexiÃ³n. Verifica tu internet');
          break;
        default:
          setError('Error al iniciar sesiÃ³n. Intenta nuevamente');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleChangePassword = async (e) => {
    e.preventDefault();

    // Validaciones
    if (newPassword.length < 6) {
      setError('La contraseÃ±a debe tener al menos 6 caracteres');
      return;
    }

    if (newPassword !== confirmPassword) {
      setError('Las contraseÃ±as no coinciden');
      return;
    }

    if (newPassword === oldPassword) {
      setError('La nueva contraseÃ±a debe ser diferente a la anterior');
      return;
    }

    // ValidaciÃ³n adicional de seguridad
    const hasNumber = /\d/.test(newPassword);
    const hasLetter = /[a-zA-Z]/.test(newPassword);
    
    if (!hasNumber || !hasLetter) {
      setError('La contraseÃ±a debe contener al menos una letra y un nÃºmero');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Verificar si es un login con contraseÃ±a temporal
      if (currentUser.isTempPasswordLogin) {
        console.log('ðŸ”„ Actualizando desde contraseÃ±a temporal...');
        
        let authUpdated = false;
        
        // ESTRATEGIA MEJORADA: Intentar mÃºltiples mÃ©todos para actualizar en Firebase Auth
        
        // MÃ©todo 1: Intentar crear el usuario nuevo
        try {
          await createUserWithEmailAndPassword(auth, currentUser.email, newPassword);
          console.log('âœ… Usuario creado en Firebase Auth con nueva contraseÃ±a');
          authUpdated = true;
          
          // Si se creÃ³ exitosamente, obtener el UID real de Firebase Auth
          const firebaseUser = auth.currentUser;
          if (firebaseUser) {
            // Actualizar el UID en Firestore si es diferente
            if (currentUser.uid !== firebaseUser.uid) {
              console.log('ðŸ”„ Migrando datos al nuevo UID en Firestore...');
              // Copiar datos del documento antiguo al nuevo
              const oldUserDoc = await getDoc(doc(firestore, 'users', currentUser.uid));
              const oldUserData = oldUserDoc.exists() ? oldUserDoc.data() : {};
              
              // Crear o actualizar el documento con el nuevo UID
              await setDoc(doc(firestore, 'users', firebaseUser.uid), {
                ...oldUserData,
                email: currentUser.email,
                requirePasswordChange: false,
                firstLogin: false,
                passwordChangedAt: new Date().toISOString(),
                tempPassword: null,
                lastTempPassword: null,
                resetMethod: null,
                authPasswordSynced: true
              });
              
              // Actualizar el currentUser con el nuevo UID
              currentUser.uid = firebaseUser.uid;
            } else {
              // Si el UID es el mismo, solo actualizar
              await updateDoc(doc(firestore, 'users', currentUser.uid), {
                requirePasswordChange: false,
                firstLogin: false,
                passwordChangedAt: new Date().toISOString(),
                tempPassword: null,
                lastTempPassword: null,
                resetMethod: null,
                authPasswordSynced: true
              });
            }
            
            // Cerrar sesiÃ³n para hacer un login limpio despuÃ©s
            await signOut(auth);
          }
        } catch (createError) {
          console.log('âŒ No se pudo crear usuario:', createError.code);
          
          if (createError.code === 'auth/email-already-in-use') {
            // MÃ©todo 2: El usuario existe, intentar mÃºltiples formas de actualizar
            
            // MÃ©todo 2a: Intentar con la Ãºltima contraseÃ±a temporal conocida
            if (currentUser.lastTempPassword) {
              try {
                const lastTemp = atob(currentUser.lastTempPassword);
                await signInWithEmailAndPassword(auth, currentUser.email, lastTemp);
                await updatePassword(auth.currentUser, newPassword);
                console.log('âœ… ContraseÃ±a actualizada usando Ãºltima temporal');
                authUpdated = true;
                // Cerrar sesiÃ³n para hacer un login limpio despuÃ©s
                await signOut(auth);
              } catch (e) {
                console.log('âŒ No funcionÃ³ con Ãºltima temporal');
              }
            }
            
            // MÃ©todo 2b: Intentar con la contraseÃ±a temporal actual
            if (!authUpdated) {
              try {
                await signInWithEmailAndPassword(auth, currentUser.email, oldPassword);
                await updatePassword(auth.currentUser, newPassword);
                console.log('âœ… ContraseÃ±a actualizada con temporal actual');
                authUpdated = true;
                // Cerrar sesiÃ³n para hacer un login limpio despuÃ©s
                await signOut(auth);
              } catch (e) {
                console.log('âŒ No funcionÃ³ con temporal actual');
              }
            }
            
            // MÃ©todo 2c: Si nada funcionÃ³, guardar la nueva contraseÃ±a como temporal en Firestore
            if (!authUpdated) {
              console.log('âš ï¸ Guardando nueva contraseÃ±a en Firestore como fallback');
              
              // IMPORTANTE: Marcar requirePasswordChange como FALSE para evitar el ciclo
              await updateDoc(doc(firestore, 'users', currentUser.uid), {
                tempPassword: btoa(newPassword),
                requirePasswordChange: false, // IMPORTANTE: false para evitar ciclo
                firstLogin: false,
                passwordChangedAt: new Date().toISOString(),
                lastTempPassword: btoa(oldPassword),
                resetMethod: 'FALLBACK_FIRESTORE',
                authPasswordSynced: false,
                needsAuthSync: true
              });
              
              console.log('âœ… ContraseÃ±a guardada en Firestore');
              setSuccessMessage('âœ… ContraseÃ±a actualizada correctamente. La pÃ¡gina se recargarÃ¡ en 3 segundos...');
              
              // Esperar y recargar la pÃ¡gina
              setTimeout(() => {
                console.log('ðŸ”„ Recargando pÃ¡gina para aplicar cambios...');
                window.location.reload();
              }, 3000);
              
              return; // Salir aquÃ­ para usar el flujo especial
            }
          } else {
            throw createError;
          }
        }

        // Si llegamos aquÃ­, la contraseÃ±a se actualizÃ³ en Auth exitosamente
        if (authUpdated) {
          // Verificar si el documento existe en Firestore antes de actualizar
          const userDocRef = doc(firestore, 'users', currentUser.uid);
          const userDocSnap = await getDoc(userDocRef);
          
          if (userDocSnap.exists()) {
            // Actualizar en Firestore (limpiar campos temporales)
            await updateDoc(userDocRef, {
              requirePasswordChange: false,
              firstLogin: false,
              passwordChangedAt: new Date().toISOString(),
              tempPassword: null,
              lastTempPassword: null,
              resetMethod: null,
              authPasswordSynced: true,
              needsAuthSync: false
            });
          } else {
            // Si no existe, crear el documento
            await setDoc(userDocRef, {
              email: currentUser.email,
              role: currentUser.role || 'worker',
              name: currentUser.name || '',
              requirePasswordChange: false,
              firstLogin: false,
              passwordChangedAt: new Date().toISOString(),
              tempPassword: null,
              lastTempPassword: null,
              resetMethod: null,
              authPasswordSynced: true,
              needsAuthSync: false
            });
          }

          setSuccessMessage('âœ… ContraseÃ±a actualizada exitosamente');
          
          // Hacer login automÃ¡tico con la nueva contraseÃ±a
          setTimeout(async () => {
            try {
              // Asegurar que no hay sesiÃ³n activa
              if (auth.currentUser) {
                console.log('âš ï¸ Cerrando sesiÃ³n activa antes de auto-login...');
                await signOut(auth);
                // Esperar un momento para asegurar que se cerrÃ³ la sesiÃ³n
                await new Promise(resolve => setTimeout(resolve, 500));
              }
              
              console.log('ðŸ” Intentando auto-login con nueva contraseÃ±a...');
              
              // Iniciar sesiÃ³n con la nueva contraseÃ±a
              const userCredential = await signInWithEmailAndPassword(auth, currentUser.email, newPassword);
              const user = userCredential.user;
              
              console.log('âœ… Auto-login exitoso, UID:', user.uid);
              
              // Obtener datos actualizados de Firestore con el UID correcto
              const userDocRef = doc(firestore, 'users', user.uid);
              const userDocSnap = await getDoc(userDocRef);
              
              if (userDocSnap.exists()) {
                const userData = userDocSnap.data();
                onLogin({
                  uid: user.uid,
                  email: user.email,
                  role: userData.role,
                  name: userData.name,
                  ...userData,
                  requirePasswordChange: false,
                  firstLogin: false,
                  isTempPasswordLogin: false
                });
              } else {
                // Si no existe con el UID de Auth, usar los datos que tenemos
                onLogin({
                  ...currentUser,
                  uid: user.uid,
                  requirePasswordChange: false,
                  firstLogin: false,
                  isTempPasswordLogin: false
                });
              }
            } catch (error) {
              console.log('âŒ Error en auto-login:', error.code, error.message);
              // Si falla el auto-login, continuar de todos modos
              onLogin({
                ...currentUser,
                requirePasswordChange: false,
                firstLogin: false,
                isTempPasswordLogin: false
              });
            }
          }, 2000); // Aumentar el tiempo de espera
        }
        
      } else {
        // Usuario autenticado normalmente con Firebase Auth
        const user = auth.currentUser;

        if (user) {
          // Actualizar contraseÃ±a en Firebase Auth
          await updatePassword(user, newPassword);

          // Actualizar en Firestore
          await updateDoc(doc(firestore, 'users', user.uid), {
            requirePasswordChange: false,
            firstLogin: false,
            passwordChangedAt: new Date().toISOString(),
            tempPassword: null,
            lastTempPassword: null,
            resetMethod: null
          });

          setSuccessMessage('âœ… ContraseÃ±a actualizada exitosamente');
          
          // Esperar un momento antes de continuar
          setTimeout(() => {
            onLogin({
              ...currentUser,
              requirePasswordChange: false,
              firstLogin: false
            });
          }, 2000);
        } else {
          setError('Error de autenticaciÃ³n. Por favor, vuelve a iniciar sesiÃ³n');
        }
      }

    } catch (error) {
      console.error('Error al cambiar contraseÃ±a:', error);

      switch (error.code) {
        case 'auth/weak-password':
          setError('La contraseÃ±a es muy dÃ©bil. Usa al menos 6 caracteres con letras y nÃºmeros');
          break;
        case 'auth/requires-recent-login':
          setError('Por seguridad, vuelve a iniciar sesiÃ³n');
          break;
        case 'auth/network-request-failed':
          setError('Error de conexiÃ³n. Verifica tu internet');
          break;
        default:
          setError('Error al cambiar la contraseÃ±a. Intenta nuevamente');
      }
    } finally {
      setLoading(false);
    }
  };

  // Formulario de cambio de contraseÃ±a obligatorio
  if (showChangePassword) {
    return (
      <div style={{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        fontFamily: 'Arial, sans-serif'
      }}>
        <div style={{
          background: 'white',
          padding: '40px',
          borderRadius: '20px',
          boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
          maxWidth: '400px',
          width: '90%'
        }}>
          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <div style={{ fontSize: '48px', marginBottom: '10px' }}>ðŸ”</div>
            <h2 style={{
              color: '#1f2937',
              margin: '0 0 10px 0',
              fontSize: '24px',
              fontWeight: '700'
            }}>
              Cambio de ContraseÃ±a Obligatorio
            </h2>
            <p style={{
              color: '#6b7280',
              margin: 0,
              fontSize: '14px'
            }}>
              Por seguridad, debes cambiar tu contraseÃ±a temporal
            </p>
          </div>

          <form onSubmit={handleChangePassword}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Nueva ContraseÃ±a
              </label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
                placeholder="MÃ­nimo 6 caracteres con letras y nÃºmeros"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '14px',
                  outline: 'none',
                  transition: 'border-color 0.2s'
                }}
                onFocus={(e) => e.target.style.borderColor = '#667eea'}
                onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
              />
            </div>

            <div style={{ marginBottom: '24px' }}>
              <label style={{
                display: 'block',
