// src/App.js - ACTUALIZADO con soporte para Dashboard Trabajador y persistencia de sesión

import React, { useState, useEffect } from 'react';
import { auth, firestore } from './config/firebase';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import Login from './components/Login';
import SuperAdmin from './components/SuperAdmin';
import MapaQuillonFirebase from './components/MapaQuillonFirebase';
import SistemaTrabajadoresFirebase from './components/SistemaTrabajadoresFirebase';
import DashboardTrabajador from './components/DashboardTrabajador'; // NUEVA IMPORTACIÓN
import ZonesManagement from './components/ZonesManagement';
import FleetPanel from './components/FleetPanel';
import JuntaVecinosPanel from './components/JuntaVecinosPanel';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
const [currentView, setCurrentView] = useState(() => {
  const savedView = localStorage.getItem('currentView');
  return savedView || 'admin';
});

useEffect(() => {
  localStorage.setItem('currentView', currentView);
}, [currentView]);
  const [trabajadorView, setTrabajadorView] = useState('gps'); // NUEVO ESTADO para trabajador

  useEffect(() => {
    // Primero verificar localStorage
    const checkLocalStorage = async () => {
      const isAuthenticated = localStorage.getItem('isAuthenticated');
      const storedUserData = localStorage.getItem('userData');
      
      if (isAuthenticated === 'true' && storedUserData) {
        try {
          const userData = JSON.parse(storedUserData);
          setUser(userData);
          
          // Establecer vista inicial según el rol
          if (userData.role === 'superadmin') {
            setCurrentView('admin');
          } else if (userData.role === 'admin') {
            setCurrentView('dashboard');
          } else if (userData.role === 'junta_vecinos') {
            setCurrentView('junta_vecinos');
          } else if (userData.role === 'trabajador') {
            setTrabajadorView('gps');
          }
          
          // Verificar si los datos siguen siendo válidos en Firestore
          try {
            const userDoc = await getDoc(doc(firestore, 'users', userData.uid));
            if (userDoc.exists()) {
              const freshData = userDoc.data();
              const updatedUser = {
                uid: userData.uid,
                email: userData.email,
                role: freshData.role,
                name: freshData.name,
                localidad: freshData.localidad,
                ...freshData
              };
              setUser(updatedUser);
              localStorage.setItem('userData', JSON.stringify(updatedUser));
            }
          } catch (error) {
            console.error('Error verificando usuario en Firestore:', error);
          }
        } catch (e) {
          console.error('Error parseando datos de localStorage:', e);
          localStorage.removeItem('isAuthenticated');
          localStorage.removeItem('userData');
        }
      }
    };

    checkLocalStorage();

    // Luego configurar listener de Firebase Auth
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      if (firebaseUser) {
        try {
          const userDoc = await getDoc(doc(firestore, 'users', firebaseUser.uid));
          
          if (userDoc.exists()) {
            const userData = userDoc.data();
            const userInfo = {
              uid: firebaseUser.uid,
              email: firebaseUser.email,
              role: userData.role,
              name: userData.name,
              localidad: userData.localidad,
              ...userData
            };
            
            setUser(userInfo);
            
            // Guardar en localStorage
            localStorage.setItem('userData', JSON.stringify(userInfo));
            localStorage.setItem('isAuthenticated', 'true');
            
            // Establecer vista inicial según el rol
            if (userData.role === 'superadmin') {
              setCurrentView('admin');
            } else if (userData.role === 'admin') {
              setCurrentView('dashboard');
            } else if (userData.role === 'junta_vecinos') {
              setCurrentView('junta_vecinos');
            } else if (userData.role === 'trabajador') {
              setTrabajadorView('gps');
            }
          } else {
            // Si el usuario existe en Auth pero no en Firestore
            const userInfo = {
              uid: firebaseUser.uid,
              email: firebaseUser.email,
              role: null
            };
            setUser(userInfo);
            localStorage.setItem('userData', JSON.stringify(userInfo));
            localStorage.setItem('isAuthenticated', 'true');
          }
        } catch (error) {
          console.error('Error al obtener datos del usuario:', error);
          const userInfo = {
            uid: firebaseUser.uid,
            email: firebaseUser.email,
            role: null
          };
          setUser(userInfo);
          localStorage.setItem('userData', JSON.stringify(userInfo));
          localStorage.setItem('isAuthenticated', 'true');
        }
      } else {
        // Si no hay usuario en Firebase Auth, verificar si hay en localStorage (fallback)
        const isAuthenticated = localStorage.getItem('isAuthenticated');
        const storedUserData = localStorage.getItem('userData');
        
        if (isAuthenticated === 'true' && storedUserData) {
          // Ya se manejó arriba en checkLocalStorage
        } else {
          // No hay sesión en ningún lado
          setUser(null);
          localStorage.removeItem('isAuthenticated');
          localStorage.removeItem('userData');
        }
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    
    // Guardar en localStorage
    localStorage.setItem('userData', JSON.stringify(userData));
    localStorage.setItem('isAuthenticated', 'true');
    
    // Establecer vista inicial según el rol al hacer login
    if (userData.role === 'superadmin') {
      setCurrentView('admin');
    } else if (userData.role === 'admin') {
      setCurrentView('dashboard');
    } else if (userData.role === 'junta_vecinos') {
      setCurrentView('junta_vecinos');
    } else if (userData.role === 'trabajador') {
      setTrabajadorView('gps');
    }
  };

  const handleLogout = async () => {
    try {
      // Limpiar localStorage primero
      localStorage.removeItem('isAuthenticated');
      localStorage.removeItem('userData');
      
      // Luego cerrar sesión en Firebase si hay usuario
      if (auth.currentUser) {
        await signOut(auth);
      }
      
      setUser(null);
      setCurrentView('dashboard');
      setTrabajadorView('gps');
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
      // Aunque falle Firebase, limpiar la sesión local
      localStorage.removeItem('isAuthenticated');
      localStorage.removeItem('userData');
      setUser(null);
    }
  };

  // Función para manejar cambio de vista del trabajador
  const handleTrabajadorViewChange = (view) => {
    if (view === 'dashboard-trabajador') {
      setTrabajadorView('dashboard');
    } else if (view === 'gps') {
      setTrabajadorView('gps');
    }
  };

  // Pantalla de carga
  if (loading) {
    return (
      <div style={{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
      }}>
        <div style={{ textAlign: 'center', color: 'white' }}>
          <div style={{
            width: '60px',
            height: '60px',
            border: '4px solid rgba(255,255,255,0.3)',
            borderTop: '4px solid white',
            borderRadius: '50%',
            margin: '0 auto 20px auto',
            animation: 'spin 1s linear infinite'
          }}></div>
          <h2>Cargando MapaQuillón...</h2>
        </div>
      </div>
    );
  }

  // Si no hay usuario, mostrar login
  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  // Renderizar según el rol del usuario
  switch (user.role) {
    case 'superadmin':
      // SuperAdmin puede acceder a todas las vistas
      if (currentView === 'admin') {
        return (
          <SuperAdmin 
            currentUser={user} 
            onLogout={handleLogout} 
            onViewChange={setCurrentView} 
            currentView={currentView} 
          />
        );
      } else if (currentView === 'dashboard') {
        return (
          <MapaQuillonFirebase 
            currentUser={user} 
            onLogout={handleLogout} 
            onViewChange={setCurrentView} 
            currentView={currentView} 
          />
        );
      } else if (currentView === 'zones') {
        return (
          <ZonesManagement 
            currentUser={user} 
            onLogout={handleLogout} 
            onViewChange={setCurrentView} 
            currentView={currentView} 
          />
        );
      } else if (currentView === 'fleet') {
        return (
          <FleetPanel 
            currentUser={user} 
            onLogout={handleLogout} 
            onViewChange={setCurrentView} 
            currentView={currentView} 
          />
        );
      }
      // Por defecto mostrar panel de admin
      return (
        <SuperAdmin 
          currentUser={user} 
          onLogout={handleLogout} 
          onViewChange={setCurrentView} 
          currentView={currentView} 
        />
      );
    
    case 'admin':
      // Admin puede acceder a Dashboard, Zones y Fleet Panel
      if (currentView === 'dashboard') {
        return (
          <MapaQuillonFirebase 
            currentUser={user} 
            onLogout={handleLogout} 
            onViewChange={setCurrentView} 
            currentView={currentView} 
          />
        );
      } else if (currentView === 'zones') {
        return (
          <ZonesManagement 
            currentUser={user} 
            onLogout={handleLogout} 
            onViewChange={setCurrentView} 
            currentView={currentView} 
          />
        );
      } else if (currentView === 'fleet') {
        return (
          <FleetPanel 
            currentUser={user} 
            onLogout={handleLogout} 
            onViewChange={setCurrentView} 
            currentView={currentView} 
          />
        );
      }
      // Por defecto mostrar dashboard
      return (
        <MapaQuillonFirebase 
          currentUser={user} 
          onLogout={handleLogout} 
          onViewChange={setCurrentView} 
          currentView={currentView} 
        />
      );
    
    case 'trabajador':
      // MODIFICADO: Trabajador ahora puede cambiar entre Dashboard y GPS
      if (trabajadorView === 'dashboard') {
        return (
          <DashboardTrabajador 
            currentUser={user} 
            onLogout={handleLogout}
            onNavigateToGPS={() => setTrabajadorView('gps')}
          />
        );
      } else {
        return (
          <SistemaTrabajadoresFirebase 
            currentUser={user} 
            onLogout={handleLogout}
            onViewChange={handleTrabajadorViewChange}
          />
        );
      }
    
    case 'junta_vecinos':
      // Junta de Vecinos solo tiene acceso a su panel
      return <JuntaVecinosPanel currentUser={user} onLogout={handleLogout} />;
    
    default:
      // Si no tiene rol definido, mostrar mensaje
      return (
        <div style={{
          width: '100%',
          height: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          background: '#f3f4f6',
          padding: '20px'
        }}>
          <div style={{
            background: 'white',
            padding: '40px',
            borderRadius: '12px',
            boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
            textAlign: 'center',
            maxWidth: '400px'
          }}>
            <div style={{ fontSize: '48px', marginBottom: '20px' }}>⚠️</div>
            <h2 style={{ color: '#1f2937', marginBottom: '10px' }}>
              Rol no asignado
            </h2>
            <p style={{ color: '#6b7280', marginBottom: '20px' }}>
              Tu cuenta no tiene un rol asignado. Contacta al administrador del sistema.
            </p>
            <p style={{ 
              background: '#f3f4f6', 
              padding: '10px', 
              borderRadius: '6px',
              fontSize: '14px',
              color: '#374151',
              marginBottom: '20px'
            }}>
              Usuario: {user.email}
            </p>
            
            <button
              onClick={handleLogout}
              style={{
                width: '100%',
                padding: '10px 20px',
                background: '#ef4444',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '14px'
              }}
            >
              Cerrar Sesión
            </button>
          </div>
        </div>
      );
  }
}

export default App;
