                      </div>
                    </div>
                    <div style={{ display: 'flex', gap: '6px', marginLeft: '10px' }}>
                      <a 
                        href={cert.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        style={{
                          padding: '4px 8px',
                          background: '#3b82f6',
                          color: 'white',
                          borderRadius: '4px',
                          textDecoration: 'none',
                          fontSize: '11px'
                        }}
                      >
                        Ver
                      </a>
                      <button
                        type="button"
                        onClick={() => handleDeleteDocument('certificados', index)}
                        style={{
                          padding: '4px 8px',
                          background: '#ef4444',
