    },
    headerActions: {
      display: 'flex',
      gap: '8px',
      alignItems: 'center'
    },
    togglePanelBtn: {
      padding: '8px',
      background: 'rgba(255,255,255,0.2)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      border: '1px solid rgba(255,255,255,0.3)',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '20px',
      transition: 'all 0.3s ease',
      transform: showPanel ? 'rotate(0deg)' : 'rotate(180deg)'
    },
    dashboardBtn: {
      padding: '8px 14px',
      background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '600',
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      transition: 'all 0.3s ease'
    },
    vehicleBtn: {
      padding: '8px 14px',
      background: 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '600',
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      transition: 'all 0.3s ease'
    },
    documentsBtn: {
      padding: '8px 14px',
      background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '600',
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      transition: 'all 0.3s ease'
    },
    notificationBtn: {
      padding: '8px',
      background: 'rgba(255,255,255,0.2)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      border: '1px solid rgba(255,255,255,0.3)',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '16px',
      position: 'relative',
      transition: 'all 0.3s ease'
    },
    notificationBadge: {
      position: 'absolute',
      top: '-4px',
      right: '-4px',
      background: '#ef4444',
      color: 'white',
      borderRadius: '50%',
      width: '18px',
      height: '18px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '10px',
      fontWeight: 'bold',
      animation: 'pulse 2s infinite'
    },
    logoutBtn: {
      padding: '8px 14px',
      background: 'rgba(255,255,255,0.2)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      border: '1px solid rgba(255,255,255,0.3)',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '600'
    },
    gpsIndicator: {
      display: 'inline-flex',
      alignItems: 'center',
      gap: '6px',
      marginTop: '4px',
      padding: '2px 8px',
      background: 'rgba(255,255,255,0.2)',
      borderRadius: '12px',
      fontSize: '12px'
    },
    gpsActiveDot: {
      width: '6px',
      height: '6px',
      background: '#4ade80',
      borderRadius: '50%',
      animation: 'pulse 2s infinite'
    },
    mainContent: {
      position: 'relative',
      width: '100%',
      height: '100vh',
      paddingTop: isMobile ? '64px' : '72px'
    },
    panel: {
      position: 'fixed',
      top: isMobile ? '64px' : '72px',
      left: showPanel ? '0' : '-100%',
      width: isMobile ? '100%' : '420px',
      maxWidth: '100%',
      height: `calc(100vh - ${isMobile ? '64px' : '72px'})`,
      background: 'linear-gradient(180deg, #ffffff 0%, #f8fafc 100%)',
      boxShadow: '4px 0 20px rgba(0,0,0,0.1)',
      overflowY: 'auto',
      overflowX: 'hidden',
      transition: 'left 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
      zIndex: 1001,
      borderRight: '1px solid rgba(0,0,0,0.05)'
    },
    panelContent: {
      padding: isMobile ? '16px' : '20px'
    },
    gpsButton: {
      width: '100%',
      padding: isMobile ? '14px' : '13px',
      fontSize: isMobile ? '15px' : '14px',
      border: 'none',
      borderRadius: '10px',
      fontWeight: '600',
      cursor: 'pointer',
      marginBottom: '12px',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      position: 'relative',
      overflow: 'hidden',
      background: isTracking 
        ? 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)' 
        : 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
      color: 'white'
    },
    map: {
      width: '100%',
      height: '100%',
      position: 'absolute',
      top: 0,
      left: 0
    },
    card: {
      background: 'white',
      borderRadius: '12px',
      padding: isMobile ? '14px' : '16px',
      marginBottom: '16px',
      boxShadow: '0 2px 12px rgba(0,0,0,0.06)',
      border: '1px solid rgba(0,0,0,0.04)',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)'
    },
    toast: {
      position: 'fixed',
      top: '80px',
      right: '20px',
      padding: '12px 20px',
      borderRadius: '10px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.15)',
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      zIndex: 2000,
      animation: 'slideInRight 0.3s ease',
      maxWidth: '350px',
      backdropFilter: 'blur(10px)'
    },
    successModal: {
      position: 'fixed',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      background: 'white',
      borderRadius: '20px',
      padding: '30px',
      boxShadow: '0 20px 60px rgba(0,0,0,0.3)',
      zIndex: 3000,
      textAlign: 'center',
      animation: 'zoomIn 0.3s ease',
      minWidth: '300px'
    }
  };

  return (
    <div style={styles.container}>
      {/* Header Mejorado */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <div style={styles.headerLeft}>
            <button
              onClick={() => setShowPanel(!showPanel)}
              style={styles.togglePanelBtn}
            >
              {showPanel ? '◀' : '▶'}
            </button>
            <div style={styles.headerInfo}>
              <h1 style={styles.headerTitle}>
                👷‍♂️ {currentUser.name || 'Trabajador'}
              </h1>
              {gpsStatus === 'active' && (
                <div style={styles.gpsIndicator}>
                  <span style={styles.gpsActiveDot}></span>
                  GPS Activo
                </div>
              )}
            </div>
          </div>
          
          <div style={styles.headerActions}>
            {/* NUEVO BOTÓN DE DOCUMENTOS */}
            <button
              onClick={() => setShowDocumentsModal(true)}
              style={styles.documentsBtn}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.05)';
                e.currentTarget.style.boxShadow = '0 4px 12px rgba(245, 158, 11, 0.4)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
                e.currentTarget.style.boxShadow = 'none';
              }}
            >
              📄 Mis Documentos
            </button>
            
            {/* Botón Mi Vehículo */}
            <button
              onClick={() => setShowVehiclePanel(true)}
              style={styles.vehicleBtn}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.05)';
                e.currentTarget.style.boxShadow = '0 4px 12px rgba(139, 92, 246, 0.4)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
                e.currentTarget.style.boxShadow = 'none';
              }}
            >
              🚛 Mi Vehículo
            </button>
            
            {/* Botón Dashboard */}
            <button
              onClick={navigateToDashboard}
              style={styles.dashboardBtn}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.05)';
                e.currentTarget.style.boxShadow = '0 4px 12px rgba(59, 130, 246, 0.4)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
                e.currentTarget.style.boxShadow = 'none';
              }}
            >
              📊 Dashboard
            </button>
            
            {/* Notificaciones */}
            <button
              onClick={() => setShowNotifications(!showNotifications)}
              style={styles.notificationBtn}
            >
              🔔
              {notificaciones.length > 0 && (
                <span style={styles.notificationBadge}>
                  {notificaciones.length}
                </span>
              )}
            </button>
            
            {/* Logout */}
            <button onClick={onLogout} style={styles.logoutBtn}>
              Salir
            </button>
          </div>
        </div>
      </div>

      {/* Contenido principal con mapa */}
      <div style={styles.mainContent}>
        {/* Mapa de fondo */}
        <div ref={mapRef} style={styles.map} />
        
        {/* Panel lateral mejorado */}
        <div style={styles.panel}>
          <div style={styles.panelContent}>
            {/* Panel de notificaciones desplegable */}
            {showNotifications && (
              <div style={{
                ...styles.card,
                background: notificaciones.length > 0 
                  ? 'linear-gradient(135deg, #fef3c7 0%, #fed7aa 100%)'
                  : 'linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)',
                border: notificaciones.length > 0 
                  ? '2px solid #fbbf24'
                  : '2px solid #d1d5db',
                marginBottom: '20px',
                animation: 'slideIn 0.3s ease'
              }}>
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  marginBottom: notificaciones.length > 0 ? '12px' : '0'
                }}>
                  <h4 style={{ 
                    margin: 0, 
                    fontSize: '15px', 
                    color: notificaciones.length > 0 ? '#92400e' : '#6b7280', 
                    fontWeight: '700' 
                  }}>
                    🔔 Notificaciones
                  </h4>
                  {notificaciones.length > 0 && (
                    <button
                      onClick={clearAllNotifications}
                      style={{
                        padding: '4px 8px',
                        background: '#dc2626',
                        color: 'white',
                        border: 'none',
                        borderRadius: '6px',
                        fontSize: '12px',
                        cursor: 'pointer',
                        fontWeight: '600',
                        transition: 'all 0.2s ease'
                      }}
                    >
                      Limpiar todas
                    </button>
                  )}
                </div>
                
                {notificaciones.length > 0 ? (
                  <div style={{ maxHeight: '200px', overflowY: 'auto' }}>
                    {notificaciones.map(notif => (
                      <div 
                        key={notif.id} 
                        style={{
                          padding: '10px',
                          background: 'rgba(255,255,255,0.9)',
                          borderRadius: '8px',
                          marginBottom: '8px',
                          fontSize: '13px',
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'start',
                          gap: '8px',
                          cursor: 'pointer',
                          transition: 'all 0.2s ease',
                          border: '1px solid rgba(0,0,0,0.05)'
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.transform = 'translateX(-2px)';
                          e.currentTarget.style.boxShadow = '0 2px 8px rgba(0,0,0,0.1)';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.transform = 'translateX(0)';
                          e.currentTarget.style.boxShadow = 'none';
                        }}
                      >
                        <div style={{ flex: 1 }}>
                          <div style={{ fontWeight: '600', marginBottom: '4px', color: '#1f2937' }}>
                            {notif.mensaje}
                          </div>
                          <div style={{ fontSize: '11px', color: '#6b7280' }}>
                            {new Date(notif.timestamp).toLocaleTimeString('es-CL', {
                              hour: '2-digit',
                              minute: '2-digit'
                            })}
                          </div>
                        </div>
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            markNotificationAsRead(notif.id);
                          }}
                          style={{
                            padding: '4px',
                            background: 'transparent',
                            border: 'none',
                            color: '#dc2626',
                            cursor: 'pointer',
                            fontSize: '16px',
                            transition: 'all 0.2s ease',
                            borderRadius: '4px'
                          }}
                          onMouseEnter={(e) => {
                            e.target.style.background = 'rgba(220, 38, 38, 0.1)';
                          }}
                          onMouseLeave={(e) => {
                            e.target.style.background = 'transparent';
                          }}
                        >
                          ✕
                        </button>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div style={{
                    padding: '20px',
                    textAlign: 'center',
                    color: '#6b7280',
                    fontSize: '14px'
                  }}>
                    <div style={{ fontSize: '32px', marginBottom: '8px', opacity: 0.5 }}>
                      📭
                    </div>
                    No tienes notificaciones nuevas
                  </div>
                )}
              </div>
            )}

            {/* Activación GPS */}
            <button onClick={toggleGPS} style={styles.gpsButton}>
              {isTracking ? '📍 Desactivar GPS' : '📍 Activar GPS'}
            </button>

            {/* Selector de zona */}
            {isTracking && workStatus === 'idle' && (
              <div style={styles.card}>
                <label style={{ 
                  display: 'block', 
                  marginBottom: '10px', 
                  fontSize: '14px', 
                  fontWeight: '600',
                  color: '#374151'
                }}>
                  🏗️ Selecciona tu zona de trabajo
                </label>
                {zonasAsignadas.length > 0 ? (
                  <select
                    value={selectedZona?.id || ''}
                    onChange={(e) => {
                      const zona = zonasAsignadas.find(z => z.id === e.target.value);
                      if (zona) selectZona(zona);
                    }}
                    style={{
                      width: '100%',
                      padding: '12px',
                      borderRadius: '8px',
                      border: '2px solid #e5e7eb',
                      fontSize: '14px',
                      background: 'white'
                    }}
                  >
                    <option value="">-- Seleccionar zona --</option>
                    {zonasAsignadas.map(zona => (
                      <option key={zona.id} value={zona.id}>
                        {zona.nombre}
                      </option>
                    ))}
                  </select>
                ) : (
                  <div style={{
                    padding: '16px',
                    background: '#f3f4f6',
                    borderRadius: '8px',
                    textAlign: 'center',
                    fontSize: '14px',
                    color: '#6b7280'
                  }}>
                    No tienes zonas asignadas
                  </div>
                )}
              </div>
            )}

            {/* Información de zona seleccionada */}
            {selectedZona && workStatus === 'idle' && (
              <div style={{
                ...styles.card,
                background: 'linear-gradient(135deg, #dbeafe 0%, #bfdbfe 100%)',
                border: '2px solid #3b82f6'
              }}>
                <h4 style={{ margin: '0 0 10px 0', color: '#1e40af', fontSize: '15px' }}>
                  📍 {selectedZona.nombre}
                </h4>
                <p style={{ margin: 0, fontSize: '13px', color: '#1e3a8a' }}>
                  {selectedZona.direccion}
                </p>
              </div>
            )}

            {/* Botón iniciar recorrido */}
            {workStatus === 'idle' && selectedZona && userPosition && (
              <button
                onClick={startNavigation}
                style={{
                  ...styles.gpsButton,
                  background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                  color: 'white'
                }}
              >
                🚗 Iniciar Recorrido
              </button>
            )}

            {/* Estado: En camino */}
            {workStatus === 'traveling' && (
              <div style={styles.card}>
                <div style={{
                  background: 'linear-gradient(135deg, #fef3c7 0%, #fde68a 100%)',
                  margin: '-16px -16px 16px -16px',
                  padding: '16px',
                  borderRadius: '12px 12px 0 0'
                }}>
                  <div style={{ fontSize: '17px', fontWeight: '700', color: '#92400e' }}>
                    🚗 En camino a la zona
                  </div>
                  <div style={{ fontSize: '14px', marginTop: '6px', color: '#78350f' }}>
                    📍 {selectedZona?.nombre}
                  </div>
                  {distance && duration && (
                    <div style={{ 
                      marginTop: '12px', 
                      padding: '8px',
                      background: 'rgba(255,255,255,0.8)',
                      borderRadius: '6px',
                      fontSize: '14px',
                      display: 'flex',
                      gap: '16px'
                    }}>
                      <span>📏 {distance}</span>
                      <span>⏱️ {duration}</span>
                    </div>
                  )}
                </div>
                
                <button
                  onClick={handleArrival}
                  style={{
                    ...styles.gpsButton,
                    background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                    color: 'white',
                    marginBottom: '12px'
                  }}
                >
                  📍 Ya llegué a la zona
                </button>
                
                <button
                  onClick={() => {
                    if (window.confirm('¿Cancelar el recorrido?')) {
                      cancelNavigation();
                    }
                  }}
                  style={{
                    ...styles.gpsButton,
                    background: 'white',
                    color: '#ef4444',
                    border: '2px solid #ef4444',
                    marginBottom: 0
                  }}
                >
                  ❌ Cancelar
                </button>
              </div>
            )}

            {/* Estado: Trabajando */}
            {workStatus === 'working' && (
              <div style={styles.card}>
                <div style={{
                  background: 'linear-gradient(135deg, #dcfce7 0%, #bbf7d0 100%)',
                  margin: '-16px -16px 16px -16px',
                  padding: '20px',
                  borderRadius: '12px 12px 0 0',
                  border: '2px solid #22c55e'
                }}>
                  <div style={{ 
                    fontSize: '17px', 
                    fontWeight: '700', 
                    color: '#14532d',
                    animation: 'pulse 2s infinite'
                  }}>
                    🔧 TRABAJANDO EN ZONA
                  </div>
                  <div style={{ fontSize: '15px', color: '#166534', marginTop: '8px' }}>
                    📍 {selectedZona?.nombre}
                  </div>
                  <div style={{ 
                    fontSize: '13px', 
                    marginTop: '10px', 
                    color: '#166534'
                  }}>
                    ⏰ Llegada: {formatTime(workLog.arrivalTime)}
                  </div>
                </div>
                
                <button
                  onClick={() => {
                    if (window.confirm('¿Confirmas que has completado la tarea?')) {
                      finishWork();
                    }
                  }}
                  style={{
                    ...styles.gpsButton,
                    background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                    color: 'white',
                    fontSize: '16px',
                    fontWeight: '700',
                    marginBottom: '12px'
                  }}
                >
                  ✅ COMPLETAR TAREA
                </button>
                
                <button
                  onClick={() => {
                    if (window.confirm('¿Cancelar el trabajo?')) {
                      cancelNavigation();
                    }
                  }}
                  style={{
                    ...styles.gpsButton,
                    background: 'white',
                    color: '#ef4444',
                    border: '2px solid #ef4444',
                    marginBottom: 0
                  }}
                >
                  ❌ Cancelar
                </button>
              </div>
            )}

            {/* Estado: Completado */}
            {workStatus === 'completed' && (
              <div style={{
                ...styles.card,
                background: 'linear-gradient(135deg, #e0e7ff 0%, #c7d2fe 100%)',
                border: '2px solid #6366f1'
              }}>
                <div style={{ 
                  fontSize: '17px', 
                  fontWeight: '700', 
                  color: '#312e81',
                  textAlign: 'center',
                  marginBottom: '16px'
                }}>
                  ✅ Trabajo Completado
                </div>
                <button
                  onClick={resetWork}
                  style={{
                    ...styles.gpsButton,
                    background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                    color: 'white',
                    marginBottom: 0
                  }}
                >
                  🔄 Nuevo Trabajo
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Notificación Toast */}
      {toastNotification && (
        <div style={{
          ...styles.toast,
          background: toastNotification.type === 'success' ? 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)' :
                     toastNotification.type === 'error' ? 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)' :
                     toastNotification.type === 'warning' ? 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)' :
                     'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
          color: 'white'
        }}>
          <span style={{ fontSize: '18px' }}>
            {toastNotification.type === 'success' ? '✅' :
             toastNotification.type === 'error' ? '❌' :
             toastNotification.type === 'warning' ? '⚠️' : 'ℹ️'}
          </span>
          <span style={{ flex: 1, fontSize: '14px', fontWeight: '500' }}>
            {toastNotification.message}
          </span>
        </div>
      )}

      {/* Modal del panel de vehículo */}
      {showVehiclePanel && (
        <TrabajadorVehiclePanel 
          currentUser={currentUser}
          onClose={() => setShowVehiclePanel(false)}
        />
      )}

      {/* Modal de Documentos */}
      <DocumentosModal />

      {/* Modal de éxito */}
      {successMessage && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.5)',
          zIndex: 2999,
          animation: 'fadeIn 0.3s ease'
        }}>
          <div style={styles.successModal}>
            <div style={{
              fontSize: '60px',
              marginBottom: '20px',
              animation: 'bounce 0.5s ease'
            }}>
              ✅
            </div>
            <h2 style={{
              margin: '0 0 10px 0',
              fontSize: '24px',
              color: '#16a34a'
            }}>
              {successMessage.title}
            </h2>
            <p style={{
              margin: 0,
              fontSize: '16px',
              color: '#6b7280'
            }}>
              {successMessage.message}
            </p>
          </div>
        </div>
      )}

      <style>{`
        @keyframes slideInRight {
          from {
            transform: translateX(100%);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }
        
        @keyframes slideIn {
          from {
            transform: translateY(-10px);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }
        
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        
        @keyframes zoomIn {
          from {
            transform: translate(-50%, -50%) scale(0.8);
            opacity: 0;
          }
          to {
            transform: translate(-50%, -50%) scale(1);
            opacity: 1;
          }
        }
        
        @keyframes bounce {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.2); }
        }
        
        @keyframes pulse {
          0% {
            box-shadow: 0 0 0 0 rgba(34, 197, 94, 0.7);
          }
          50% {
            box-shadow: 0 0 0 10px rgba(34, 197, 94, 0);
          }
          100% {
            box-shadow: 0 0 0 0 rgba(34, 197, 94, 0);
          }
        }
      `}</style>
    </div>
  );
};

export default SistemaTrabajadoresFirebase;
