// src/components/SistemaTrabajadoresFirebase.js
import React, { useEffect, useRef, useState } from 'react';
import { trabajadoresService } from '../services/firebaseservices';
import { database } from '../config/firebase';
import { ref, onValue, update, set } from 'firebase/database';

const SistemaTrabajadoresFirebase = ({ currentUser, onLogout }) => {
  // Estados principales
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [userPosition, setUserPosition] = useState(null);
  const [selectedZona, setSelectedZona] = useState(null);
  const [isTracking, setIsTracking] = useState(false);
  const [isNavigating, setIsNavigating] = useState(false);
  const [workStatus, setWorkStatus] = useState('idle');
  const [workLog, setWorkLog] = useState({
    startTime: null,
    arrivalTime: null,
    endTime: null,
    zona: null
  });
  
  // Estados para zonas y notificaciones
  const [zonasAsignadas, setZonasAsignadas] = useState([]);
  const [notificaciones, setNotificaciones] = useState([]);
  const [showPanel, setShowPanel] = useState(true);
  const [isMobile, setIsMobile] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [toastNotification, setToastNotification] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  
  // Referencias para marcadores y servicios
  const userMarkerRef = useRef(null);
  const zonaMarkerRef = useRef(null);
  const directionsServiceRef = useRef(null);
  const directionsRendererRef = useRef(null);
  const watchIdRef = useRef(null);
  const lastUpdateRef = useRef(null);
  const toastTimeoutRef = useRef(null);
  
  // Estados de UI
  const [mapLoaded, setMapLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [distance, setDistance] = useState(null);
  const [duration, setDuration] = useState(null);
  const [showWorkLog, setShowWorkLog] = useState(false);
  const [gpsStatus, setGpsStatus] = useState('inactive');

  const API_KEY = 'AIzaSyA7DrdL36n5cx-RNzuXGAQggFeGCheHDbY';

  // Función para mostrar notificaciones tipo toast
  const showToast = (message, type = 'info', duration = 4000) => {
    if (toastTimeoutRef.current) {
      clearTimeout(toastTimeoutRef.current);
    }
    
    setToastNotification({ message, type });
    
    toastTimeoutRef.current = setTimeout(() => {
      setToastNotification(null);
    }, duration);
  };

  // Función para mostrar mensaje de éxito
  const showSuccess = (title, message, duration = 3000) => {
    setSuccessMessage({ title, message });
    setTimeout(() => {
      setSuccessMessage(null);
    }, duration);
  };

  // Detectar si es móvil
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Cargar zonas asignadas desde Firebase
  useEffect(() => {
    if (currentUser && currentUser.uid) {
      console.log('🔄 Cargando zonas asignadas para trabajador:', currentUser.uid);
      
      // Escuchar zonas asignadas al trabajador
      const zonasRef = ref(database, 'zonas_asignadas');
      const unsubZonas = onValue(zonasRef, (snapshot) => {
        const data = snapshot.val();
        console.log('📦 Datos de zonas recibidos:', data);
        
        if (data) {
          const zonasArray = Object.entries(data)
            .filter(([id, zona]) => {
              console.log(`Zona ${id}: asignada a ${zona.trabajadorAsignado}, yo soy ${currentUser.uid}`);
              return zona.trabajadorAsignado === currentUser.uid && zona.estado !== 'completado';
            })
            .map(([id, zona]) => ({
              id,
              ...zona
            }));
          
          console.log('✅ Zonas asignadas filtradas:', zonasArray);
          
          // Mostrar notificación si hay nuevas zonas
          if (zonasArray.length > zonasAsignadas.length) {
            showToast('📍 Nueva zona asignada', 'success');
          }
          
          setZonasAsignadas(zonasArray);
        } else {
          console.log('⚠️ No hay zonas en Firebase');
          setZonasAsignadas([]);
        }
      });

      // Escuchar notificaciones
      const notifRef = ref(database, 'notificaciones');
      const unsubNotif = onValue(notifRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
          const notifArray = Object.entries(data)
            .filter(([id, notif]) => notif.trabajadorId === currentUser.uid && !notif.leido)
            .map(([id, notif]) => ({
              id,
              ...notif
            }))
            .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
          
          // Mostrar toast para nuevas notificaciones
          if (notifArray.length > notificaciones.length) {
            const newNotif = notifArray[0];
            showToast(newNotif.mensaje, 'info');
          }
          
          setNotificaciones(notifArray);
        }
      });

      return () => {
        unsubZonas();
        unsubNotif();
      };
    }
  }, [currentUser, zonasAsignadas.length, notificaciones.length]);

  // Registrar trabajador al montar el componente
  useEffect(() => {
    if (currentUser && currentUser.uid) {
      console.log('🔄 Registrando trabajador al iniciar...');
      
      trabajadoresService.createOrUpdateTrabajador(currentUser.uid, {
        nombre: currentUser.name || currentUser.email || 'Trabajador',
        email: currentUser.email,
        telefono: currentUser.phone || '',
        vehicleId: currentUser.vehicleId || null,
        estado: 'inactivo',
        rol: 'trabajador',
        ubicacion: null,
        createdAt: new Date().toISOString(),
        ultimaActualizacion: new Date().toISOString()
      }).then(() => {
        console.log('✅ Trabajador registrado en Realtime Database al iniciar');
      }).catch(error => {
        console.error('❌ Error al registrar trabajador al iniciar:', error);
      });
    }
  }, [currentUser]);

  // Función para cargar Google Maps
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve, reject) => {
        if (window.google && window.google.maps) {
          resolve(window.google.maps);
          return;
        }

        const existingScript = document.querySelector('script[src*="maps.googleapis.com"]');
        if (existingScript) {
          existingScript.addEventListener('load', () => {
            if (window.google && window.google.maps) {
              resolve(window.google.maps);
            } else {
              reject(new Error('Google Maps no disponible'));
            }
          });
          return;
        }

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${API_KEY}&libraries=places,geometry&v=weekly`;
        script.async = true;
        script.defer = true;

        script.onload = () => {
          if (window.google && window.google.maps) {
            resolve(window.google.maps);
          } else {
            reject(new Error('Google Maps no disponible'));
          }
        };

        script.onerror = () => {
          reject(new Error('Error al cargar Google Maps'));
        };

        document.head.appendChild(script);
      });
    };

    const initMap = async () => {
      try {
        const maps = await loadGoogleMaps();
        
        if (!mapRef.current) return;

        const mapInstance = new maps.Map(mapRef.current, {
          center: { lat: -36.7333, lng: -72.4667 },
          zoom: 13,
          mapTypeId: 'roadmap',
          zoomControl: !isMobile,
          mapTypeControl: false,
          scaleControl: false,
          streetViewControl: false,
          rotateControl: false,
          fullscreenControl: true,
          gestureHandling: 'greedy'
        });

        setMap(mapInstance);
        
        // Inicializar servicios de direcciones
        directionsServiceRef.current = new maps.DirectionsService();
        directionsRendererRef.current = new maps.DirectionsRenderer({
          map: mapInstance,
          suppressMarkers: false,
          polylineOptions: {
            strokeColor: '#4285F4',
            strokeWeight: 6,
            strokeOpacity: 0.8
          },
          preserveViewport: false
        });

        setMapLoaded(true);
      } catch (err) {
        setError('Error al cargar el mapa: ' + err.message);
      }
    };

    initMap();
  }, [isMobile]);

  // Actualizar posición del usuario
  const updateUserPosition = (position) => {
    console.log('🔄 Actualizando posición:', position);
    setUserPosition(position);
    
    const ahora = Date.now();
    if (!lastUpdateRef.current || (ahora - lastUpdateRef.current) > 5000) {
      console.log('📍 Enviando ubicación a Firebase:', position);
      
      if (currentUser && currentUser.uid) {
        trabajadoresService.updateTrabajadorLocation(currentUser.uid, {
          lat: position.lat,
          lng: position.lng,
          timestamp: new Date().toISOString(),
          accuracy: position.accuracy || null
        }).then(() => {
          console.log('✅ Ubicación enviada a Firebase exitosamente');
        }).catch(error => {
          console.error('❌ Error al enviar ubicación a Firebase:', error);
        });
        
        const estadoActual = workStatus === 'traveling' ? 'en_camino' : 
                            workStatus === 'working' ? 'trabajando' : 
                            'disponible';
        
        trabajadoresService.updateTrabajadorData(currentUser.uid, {
          nombre: currentUser.name || currentUser.email,
          email: currentUser.email,
          estado: estadoActual,
          zonaDestino: selectedZona ? selectedZona.nombre : null,
          ultimaActualizacion: new Date().toISOString()
        });
        
        lastUpdateRef.current = ahora;
      }
    }
    
    if (!map || !window.google) return;

    if (!userMarkerRef.current) {
      userMarkerRef.current = new window.google.maps.Marker({
        position: position,
        map: map,
        title: 'Tu ubicación',
        icon: {
          path: window.google.maps.SymbolPath.CIRCLE,
          scale: 10,
          fillColor: '#4285F4',
          fillOpacity: 1,
          strokeColor: '#ffffff',
          strokeWeight: 3
        },
        zIndex: 1000
      });
      
      map.setCenter(position);
      map.setZoom(15);
    } else {
      userMarkerRef.current.setPosition(position);
    }

    if (isNavigating && selectedZona) {
      const distance = window.google.maps.geometry.spherical.computeDistanceBetween(
        new window.google.maps.LatLng(position.lat, position.lng),
        new window.google.maps.LatLng(selectedZona.lat, selectedZona.lng)
      );
      
      console.log('📏 Distancia a la zona:', Math.round(distance), 'metros');
    }
  };

  // Función para activar/desactivar GPS
  const toggleGPS = () => {
    if (isTracking) {
      if (watchIdRef.current) {
        navigator.geolocation.clearWatch(watchIdRef.current);
        watchIdRef.current = null;
      }
      setIsTracking(false);
      setGpsStatus('inactive');
      
      if (userMarkerRef.current) {
        userMarkerRef.current.setMap(null);
        userMarkerRef.current = null;
      }
      
      showToast('📍 GPS desactivado', 'warning');
      
      if (currentUser && currentUser.uid) {
        trabajadoresService.updateTrabajadorData(currentUser.uid, {
          estado: 'inactivo',
          ultimaActualizacion: new Date().toISOString()
        });
      }
    } else {
      if (!navigator.geolocation) {
        showToast('❌ Tu navegador no soporta geolocalización', 'error');
        setGpsStatus('error');
        return;
      }

      console.log('🔄 Iniciando GPS...');
      setIsTracking(true);
      setGpsStatus('activating');
      showToast('🔄 Activando GPS...', 'info');
      
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const pos = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            accuracy: position.coords.accuracy
          };
          updateUserPosition(pos);
          setGpsStatus('active');
          showToast('✅ GPS activado correctamente', 'success');
        },
        (error) => {
          console.warn('⚠️ Error al obtener ubicación:', error.message);
          
          if (error.code === error.PERMISSION_DENIED) {
            showToast('❌ Permisos de ubicación denegados', 'error');
            setIsTracking(false);
            setGpsStatus('error');
            return;
          }
        },
        {
          enableHighAccuracy: true,
          timeout: 15000,
          maximumAge: 0
        }
      );

      const gpsWatchId = navigator.geolocation.watchPosition(
        (position) => {
          if (position.coords.accuracy <= 150) {
            const pos = {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
              accuracy: position.coords.accuracy
            };
            updateUserPosition(pos);
            setGpsStatus('active');
          }
        },
        (error) => {
          console.log('⏱️ Esperando señal GPS...', error.message);
        },
        {
          enableHighAccuracy: true,
          timeout: 30000,
          maximumAge: 0,
          distanceFilter: 5
        }
      );
      
      watchIdRef.current = gpsWatchId;
    }
  };

  // Seleccionar zona de trabajo
  const selectZona = (zona) => {
    setSelectedZona(zona);
    showToast(`📍 Zona seleccionada: ${zona.nombre}`, 'info');
    
    if (!map || !window.google) return;

    if (zonaMarkerRef.current) {
      zonaMarkerRef.current.setMap(null);
    }

    zonaMarkerRef.current = new window.google.maps.Marker({
      position: { lat: zona.lat, lng: zona.lng },
      map: map,
      title: zona.nombre,
      icon: {
        path: window.google.maps.SymbolPath.BACKWARD_CLOSED_ARROW,
        scale: 8,
        fillColor: '#22c55e',
        fillOpacity: 1,
        strokeColor: '#ffffff',
        strokeWeight: 2
      }
    });

    if (userPosition) {
      const bounds = new window.google.maps.LatLngBounds();
      bounds.extend(userPosition);
      bounds.extend({ lat: zona.lat, lng: zona.lng });
      map.fitBounds(bounds, 100);
    } else {
      map.setCenter({ lat: zona.lat, lng: zona.lng });
      map.setZoom(15);
    }
  };

  // Iniciar navegación
  const startNavigation = async () => {
    if (!selectedZona || !userPosition) {
      showToast('⚠️ Activa el GPS y selecciona una zona primero', 'warning');
      return;
    }

    setIsNavigating(true);
    setWorkStatus('traveling');
    setWorkLog({
      ...workLog,
      startTime: new Date().toISOString(),
      zona: selectedZona
    });

    showToast('🚗 Iniciando recorrido...', 'success');

    try {
      await trabajadoresService.updateTrabajadorData(currentUser.uid, {
        estado: 'en_camino',
        zonaDestino: selectedZona.nombre,
        recorridoInicio: new Date().toISOString()
      });
      
      if (selectedZona.id) {
        await update(ref(database, `zonas_asignadas/${selectedZona.id}`), {
          estado: 'en_progreso',
          ultimaActualizacion: new Date().toISOString()
        });
      }
      
      console.log('✅ Estado actualizado a "en_camino" en Firebase');
    } catch (error) {
      console.error('Error al actualizar estado en Firebase:', error);
    }

    const request = {
      origin: new window.google.maps.LatLng(userPosition.lat, userPosition.lng),
      destination: new window.google.maps.LatLng(selectedZona.lat, selectedZona.lng),
      travelMode: window.google.maps.TravelMode.DRIVING,
      unitSystem: window.google.maps.UnitSystem.METRIC,
      region: 'CL'
    };

    directionsServiceRef.current.route(request, (result, status) => {
      if (status === 'OK') {
        directionsRendererRef.current.setDirections(result);
        const route = result.routes[0];
        const leg = route.legs[0];
        setDistance(leg.distance.text);
        setDuration(leg.duration.text);
      } else {
        mostrarLineaDirecta();
      }
    });
  };

  // Función auxiliar para mostrar línea directa
  const mostrarLineaDirecta = () => {
    if (!window.google || !map || !userPosition || !selectedZona) return;
    
    const distanceInMeters = window.google.maps.geometry.spherical.computeDistanceBetween(
      new window.google.maps.LatLng(userPosition.lat, userPosition.lng),
      new window.google.maps.LatLng(selectedZona.lat, selectedZona.lng)
    );
    
    const distanceInKm = (distanceInMeters / 1000).toFixed(2);
    setDistance(`~${distanceInKm} km`);
    setDuration('Calculando...');
    
    showToast('📍 Mostrando ruta directa', 'info');
  };

  // Manejar llegada a la zona
  const handleArrival = async () => {
    setWorkStatus('working');
    const arrivalTime = new Date().toISOString();
    setWorkLog({
      ...workLog,
      arrivalTime: arrivalTime
    });
    
    showToast('✅ Has llegado a la zona de trabajo', 'success');
    
    try {
      await trabajadoresService.updateTrabajadorData(currentUser.uid, {
        estado: 'trabajando',
        horaLlegada: arrivalTime
      });
    } catch (error) {
      console.error('Error al registrar llegada:', error);
    }
    
    if (directionsRendererRef.current) {
      directionsRendererRef.current.setDirections({ routes: [] });
    }
  };

  // Finalizar trabajo
  const finishWork = async () => {
    const endTime = new Date().toISOString();
    const tiempoTrabajado = workLog.arrivalTime ? 
      Math.floor((new Date(endTime) - new Date(workLog.arrivalTime)) / 60000) : 0;
    
    const finalLog = {
      ...workLog,
      endTime: endTime,
      tiempoTrabajadoMinutos: tiempoTrabajado
    };
    
    setWorkLog(finalLog);
    setWorkStatus('completed');
    setIsNavigating(false);
    
    if (directionsRendererRef.current) {
      directionsRendererRef.current.setDirections({ routes: [] });
    }
    
    try {
      await trabajadoresService.updateTrabajadorData(currentUser.uid, {
        estado: 'disponible',
        zonaDestino: null
      });
      
      if (selectedZona.id) {
        await update(ref(database, `zonas_asignadas/${selectedZona.id}`), {
          estado: 'completado',
          fechaCompletado: endTime,
          tiempoTrabajadoMinutos: tiempoTrabajado,
          completadoPor: currentUser.uid,
          ultimaActualizacion: new Date().toISOString()
        });
      }
      
      // Mostrar mensaje de éxito personalizado
      showSuccess(
        '¡Excelente trabajo!',
        'La tarea ha sido marcada como completada.'
      );
      
      // Mostrar resumen después de un delay
      setTimeout(() => {
        setShowWorkLog(true);
      }, 500);
      
    } catch (error) {
      console.error('Error al registrar finalización:', error);
      showToast('⚠️ Error al guardar. Intenta nuevamente.', 'error');
    }
  };

  // Cancelar navegación
  const cancelNavigation = async () => {
    setIsNavigating(false);
    setWorkStatus('idle');
    setDistance(null);
    setDuration(null);
    
    if (directionsRendererRef.current) {
      directionsRendererRef.current.setDirections({ routes: [] });
    }
    
    showToast('❌ Recorrido cancelado', 'warning');
    
    try {
      await trabajadoresService.updateTrabajadorData(currentUser.uid, {
        estado: 'disponible',
        zonaDestino: null
      });
      
      if (selectedZona && selectedZona.id && workStatus !== 'idle') {
        await update(ref(database, `zonas_asignadas/${selectedZona.id}`), {
          estado: 'pendiente',
          ultimaActualizacion: new Date().toISOString()
        });
      }
    } catch (error) {
      console.error('Error al cancelar recorrido:', error);
    }
  };

  // Resetear para nuevo trabajo
  const resetWork = () => {
    setSelectedZona(null);
    setWorkStatus('idle');
    setWorkLog({
      startTime: null,
      arrivalTime: null,
      endTime: null,
      zona: null
    });
    setIsNavigating(false);
    setDistance(null);
    setDuration(null);
    setShowWorkLog(false);
    
    if (zonaMarkerRef.current) {
      zonaMarkerRef.current.setMap(null);
      zonaMarkerRef.current = null;
    }
    
    if (directionsRendererRef.current) {
      directionsRendererRef.current.setDirections({ routes: [] });
    }
    
    showToast('🔄 Listo para un nuevo trabajo', 'info');
  };

  // Marcar notificación como leída
  const markNotificationAsRead = async (notifId) => {
    try {
      const notifRef = ref(database, `notificaciones/${notifId}`);
      await update(notifRef, {
        leido: true,
        fechaLeido: new Date().toISOString()
      });
    } catch (error) {
      console.error('Error al marcar notificación:', error);
    }
  };

  // Limpiar todas las notificaciones
  const clearAllNotifications = async () => {
    try {
      const updates = {};
      notificaciones.forEach(notif => {
        updates[`notificaciones/${notif.id}/leido`] = true;
        updates[`notificaciones/${notif.id}/fechaLeido`] = new Date().toISOString();
      });
      
      await update(ref(database), updates);
      setShowNotifications(false);
      showToast('✅ Notificaciones limpiadas', 'success');
    } catch (error) {
      console.error('Error al limpiar notificaciones:', error);
    }
  };

  // Formatear tiempo
  const formatTime = (isoString) => {
    if (!isoString) return '--:--';
    const date = new Date(isoString);
    return date.toLocaleTimeString('es-CL', { 
      hour: '2-digit', 
      minute: '2-digit',
      hour12: false 
    });
  };

  // Calcular duración
  const calculateDuration = (start, end) => {
    if (!start || !end) return '--';
    const diff = new Date(end) - new Date(start);
    const hours = Math.floor(diff / 3600000);
    const minutes = Math.floor((diff % 3600000) / 60000);
    return `${hours}h ${minutes}m`;
  };

  // Limpiar al desmontar
  useEffect(() => {
    return () => {
      if (watchIdRef.current) {
        navigator.geolocation.clearWatch(watchIdRef.current);
      }
      if (toastTimeoutRef.current) {
        clearTimeout(toastTimeoutRef.current);
      }
    };
  }, []);

  // Estilos mejorados
  const styles = {
    container: {
      width: '100%',
      height: '100vh',
      position: 'relative',
      overflow: 'hidden',
      background: '#f8fafc'
    },
    header: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
      color: 'white',
      padding: isMobile ? '12px 16px' : '16px 24px',
      zIndex: 1002,
      boxShadow: '0 4px 20px rgba(0,0,0,0.15)',
      backdropFilter: 'blur(10px)'
    },
    headerContent: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      width: '100%'
    },
    headerLeft: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      flex: 1
    },
    headerInfo: {
      display: 'flex',
      flexDirection: 'column'
    },
    headerTitle: {
      margin: 0,
      fontSize: isMobile ? '18px' : '20px',
      fontWeight: '700',
      letterSpacing: '-0.5px'
    },
    headerActions: {
      display: 'flex',
      gap: '8px',
      alignItems: 'center'
    },
    togglePanelBtn: {
      padding: '8px',
      background: 'rgba(255,255,255,0.2)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      border: '1px solid rgba(255,255,255,0.3)',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '20px',
      transition: 'all 0.3s ease',
      transform: showPanel ? 'rotate(0deg)' : 'rotate(180deg)'
    },
    notificationBtn: {
      padding: '8px',
      background: 'rgba(255,255,255,0.2)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      border: '1px solid rgba(255,255,255,0.3)',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '16px',
      position: 'relative',
      transition: 'all 0.3s ease'
    },
    notificationBadge: {
      position: 'absolute',
      top: '-4px',
      right: '-4px',
      background: '#ef4444',
      color: 'white',
      borderRadius: '50%',
      width: '18px',
      height: '18px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '10px',
      fontWeight: 'bold',
      animation: 'pulse 2s infinite'
    },
    logoutBtn: {
      padding: '8px 14px',
      background: 'rgba(255,255,255,0.2)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      border: '1px solid rgba(255,255,255,0.3)',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '600'
    },
    gpsIndicator: {
      display: 'inline-flex',
      alignItems: 'center',
      gap: '6px',
      marginTop: '4px',
      padding: '2px 8px',
      background: 'rgba(255,255,255,0.2)',
      borderRadius: '12px',
      fontSize: '12px'
    },
    gpsActiveDot: {
      width: '6px',
      height: '6px',
      background: '#4ade80',
      borderRadius: '50%',
      animation: 'pulse 2s infinite'
    },
    mainContent: {
      position: 'relative',
      width: '100%',
      height: '100vh',
      paddingTop: isMobile ? '64px' : '72px' // Espacio para el header fijo
    },
    panel: {
      position: 'fixed',
      top: isMobile ? '64px' : '72px',
      left: showPanel ? '0' : '-100%',
      width: isMobile ? '100%' : '420px',
      maxWidth: '100%',
      height: `calc(100vh - ${isMobile ? '64px' : '72px'})`,
      background: 'linear-gradient(180deg, #ffffff 0%, #f8fafc 100%)',
      boxShadow: '4px 0 20px rgba(0,0,0,0.1)',
      overflowY: 'auto',
      overflowX: 'hidden',
      transition: 'left 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
      zIndex: 1001,
      borderRight: '1px solid rgba(0,0,0,0.05)'
    },
    panelContent: {
      padding: isMobile ? '16px' : '20px'
    },
    gpsButton: {
      width: '100%',
      padding: isMobile ? '14px' : '13px',
      fontSize: isMobile ? '15px' : '14px',
      border: 'none',
      borderRadius: '10px',
      fontWeight: '600',
      cursor: 'pointer',
      marginBottom: '12px',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      position: 'relative',
      overflow: 'hidden',
      background: isTracking 
        ? 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)' 
        : 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
      color: 'white'
    },
    map: {
      width: '100%',
      height: '100%',
      position: 'absolute',
      top: 0,
      left: 0
    },
    card: {
      background: 'white',
      borderRadius: '12px',
      padding: isMobile ? '14px' : '16px',
      marginBottom: '16px',
      boxShadow: '0 2px 12px rgba(0,0,0,0.06)',
      border: '1px solid rgba(0,0,0,0.04)',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)'
    },
    toast: {
      position: 'fixed',
      top: '80px',
      right: '20px',
      padding: '12px 20px',
      borderRadius: '10px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.15)',
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      zIndex: 2000,
      animation: 'slideInRight 0.3s ease',
      maxWidth: '350px',
      backdropFilter: 'blur(10px)'
    },
    successModal: {
      position: 'fixed',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      background: 'white',
      borderRadius: '20px',
      padding: '30px',
      boxShadow: '0 20px 60px rgba(0,0,0,0.3)',
      zIndex: 3000,
      textAlign: 'center',
      animation: 'zoomIn 0.3s ease',
      minWidth: '300px'
    }
  };

  return (
    <div style={styles.container}>
      {/* Header Mejorado */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <div style={styles.headerLeft}>
            <button
              onClick={() => setShowPanel(!showPanel)}
              style={styles.togglePanelBtn}
            >
              {showPanel ? '◀' : '▶'}
            </button>
            <div style={styles.headerInfo}>
              <h1 style={styles.headerTitle}>
                👷‍♂️ {currentUser.name || 'Trabajador'}
              </h1>
              {gpsStatus === 'active' && (
                <div style={styles.gpsIndicator}>
                  <span style={styles.gpsActiveDot}></span>
                  GPS Activo
                </div>
              )}
            </div>
          </div>
          
          <div style={styles.headerActions}>
            <button
              onClick={() => setShowNotifications(!showNotifications)}
              style={styles.notificationBtn}
            >
              🔔
              {notificaciones.length > 0 && (
                <span style={styles.notificationBadge}>
                  {notificaciones.length}
                </span>
              )}
            </button>
            
            <button onClick={onLogout} style={styles.logoutBtn}>
              Salir
            </button>
          </div>
        </div>
      </div>

      {/* Contenido principal con mapa */}
      <div style={styles.mainContent}>
        {/* Mapa de fondo */}
        <div ref={mapRef} style={styles.map} />
        
        {/* Panel lateral mejorado */}
        <div style={styles.panel}>
          <div style={styles.panelContent}>
            {/* Panel de notificaciones desplegable */}
            {showNotifications && (
              <div style={{
                ...styles.card,
                background: notificaciones.length > 0 
                  ? 'linear-gradient(135deg, #fef3c7 0%, #fed7aa 100%)'
                  : 'linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)',
                border: notificaciones.length > 0 
                  ? '2px solid #fbbf24'
                  : '2px solid #d1d5db',
                marginBottom: '20px',
                animation: 'slideIn 0.3s ease'
              }}>
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  marginBottom: notificaciones.length > 0 ? '12px' : '0'
                }}>
                  <h4 style={{ 
                    margin: 0, 
                    fontSize: '15px', 
                    color: notificaciones.length > 0 ? '#92400e' : '#6b7280', 
                    fontWeight: '700' 
                  }}>
                    🔔 Notificaciones
                  </h4>
                  {notificaciones.length > 0 && (
                    <button
                      onClick={clearAllNotifications}
                      style={{
                        padding: '4px 8px',
                        background: '#dc2626',
                        color: 'white',
                        border: 'none',
                        borderRadius: '6px',
                        fontSize: '12px',
                        cursor: 'pointer',
                        fontWeight: '600',
                        transition: 'all 0.2s ease'
                      }}
                    >
                      Limpiar todas
                    </button>
                  )}
                </div>
                
                {notificaciones.length > 0 ? (
                  <div style={{ maxHeight: '200px', overflowY: 'auto' }}>
                    {notificaciones.map(notif => (
                      <div 
                        key={notif.id} 
                        style={{
                          padding: '10px',
                          background: 'rgba(255,255,255,0.9)',
                          borderRadius: '8px',
                          marginBottom: '8px',
                          fontSize: '13px',
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'start',
                          gap: '8px',
                          cursor: 'pointer',
                          transition: 'all 0.2s ease',
                          border: '1px solid rgba(0,0,0,0.05)'
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.transform = 'translateX(-2px)';
                          e.currentTarget.style.boxShadow = '0 2px 8px rgba(0,0,0,0.1)';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.transform = 'translateX(0)';
                          e.currentTarget.style.boxShadow = 'none';
                        }}
                      >
                        <div style={{ flex: 1 }}>
                          <div style={{ fontWeight: '600', marginBottom: '4px', color: '#1f2937' }}>
                            {notif.mensaje}
                          </div>
                          <div style={{ fontSize: '11px', color: '#6b7280' }}>
                            {new Date(notif.timestamp).toLocaleTimeString('es-CL', {
                              hour: '2-digit',
                              minute: '2-digit'
                            })}
                          </div>
                        </div>
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            markNotificationAsRead(notif.id);
                          }}
                          style={{
                            padding: '4px',
                            background: 'transparent',
                            border: 'none',
                            color: '#dc2626',
                            cursor: 'pointer',
                            fontSize: '16px',
                            transition: 'all 0.2s ease',
                            borderRadius: '4px'
                          }}
                          onMouseEnter={(e) => {
                            e.target.style.background = 'rgba(220, 38, 38, 0.1)';
                          }}
                          onMouseLeave={(e) => {
                            e.target.style.background = 'transparent';
                          }}
                        >
                          ✕
                        </button>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div style={{
                    padding: '20px',
                    textAlign: 'center',
                    color: '#6b7280',
                    fontSize: '14px'
                  }}>
                    <div style={{ fontSize: '32px', marginBottom: '8px', opacity: 0.5 }}>
                      📭
                    </div>
                    No tienes notificaciones nuevas
                  </div>
                )}
              </div>
            )}

            {/* Activación GPS */}
            <button onClick={toggleGPS} style={styles.gpsButton}>
              {isTracking ? '📍 Desactivar GPS' : '📍 Activar GPS'}
            </button>

            {/* Selector de zona */}
            {isTracking && workStatus === 'idle' && (
              <div style={styles.card}>
                <label style={{ 
                  display: 'block', 
                  marginBottom: '10px', 
                  fontSize: '14px', 
                  fontWeight: '600',
                  color: '#374151'
                }}>
                  🏗️ Selecciona tu zona de trabajo
                </label>
                {zonasAsignadas.length > 0 ? (
                  <select
                    value={selectedZona?.id || ''}
                    onChange={(e) => {
                      const zona = zonasAsignadas.find(z => z.id === e.target.value);
                      if (zona) selectZona(zona);
                    }}
                    style={{
                      width: '100%',
                      padding: '12px',
                      borderRadius: '8px',
                      border: '2px solid #e5e7eb',
                      fontSize: '14px',
                      background: 'white'
                    }}
                  >
                    <option value="">-- Seleccionar zona --</option>
                    {zonasAsignadas.map(zona => (
                      <option key={zona.id} value={zona.id}>
                        {zona.nombre}
                      </option>
                    ))}
                  </select>
                ) : (
                  <div style={{
                    padding: '16px',
                    background: '#f3f4f6',
                    borderRadius: '8px',
                    textAlign: 'center',
                    fontSize: '14px',
                    color: '#6b7280'
                  }}>
                    No tienes zonas asignadas
                  </div>
                )}
              </div>
            )}

            {/* Información de zona seleccionada */}
            {selectedZona && workStatus === 'idle' && (
              <div style={{
                ...styles.card,
                background: 'linear-gradient(135deg, #dbeafe 0%, #bfdbfe 100%)',
                border: '2px solid #3b82f6'
              }}>
                <h4 style={{ margin: '0 0 10px 0', color: '#1e40af', fontSize: '15px' }}>
                  📍 {selectedZona.nombre}
                </h4>
                <p style={{ margin: 0, fontSize: '13px', color: '#1e3a8a' }}>
                  {selectedZona.direccion}
                </p>
              </div>
            )}

            {/* Botón iniciar recorrido */}
            {workStatus === 'idle' && selectedZona && userPosition && (
              <button
                onClick={startNavigation}
                style={{
                  ...styles.gpsButton,
                  background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                  color: 'white'
                }}
              >
                🚗 Iniciar Recorrido
              </button>
            )}

            {/* Estado: En camino */}
            {workStatus === 'traveling' && (
              <div style={styles.card}>
                <div style={{
                  background: 'linear-gradient(135deg, #fef3c7 0%, #fde68a 100%)',
                  margin: '-16px -16px 16px -16px',
                  padding: '16px',
                  borderRadius: '12px 12px 0 0'
                }}>
                  <div style={{ fontSize: '17px', fontWeight: '700', color: '#92400e' }}>
                    🚗 En camino a la zona
                  </div>
                  <div style={{ fontSize: '14px', marginTop: '6px', color: '#78350f' }}>
                    📍 {selectedZona?.nombre}
                  </div>
                  {distance && duration && (
                    <div style={{ 
                      marginTop: '12px', 
                      padding: '8px',
                      background: 'rgba(255,255,255,0.8)',
                      borderRadius: '6px',
                      fontSize: '14px',
                      display: 'flex',
                      gap: '16px'
                    }}>
                      <span>📏 {distance}</span>
                      <span>⏱️ {duration}</span>
                    </div>
                  )}
                </div>
                
                <button
                  onClick={handleArrival}
                  style={{
                    ...styles.gpsButton,
                    background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                    color: 'white',
                    marginBottom: '12px'
                  }}
                >
                  📍 Ya llegué a la zona
                </button>
                
                <button
                  onClick={() => {
                    if (window.confirm('¿Cancelar el recorrido?')) {
                      cancelNavigation();
                    }
                  }}
                  style={{
                    ...styles.gpsButton,
                    background: 'white',
                    color: '#ef4444',
                    border: '2px solid #ef4444',
                    marginBottom: 0
                  }}
                >
                  ❌ Cancelar
                </button>
              </div>
            )}

            {/* Estado: Trabajando */}
            {workStatus === 'working' && (
              <div style={styles.card}>
                <div style={{
                  background: 'linear-gradient(135deg, #dcfce7 0%, #bbf7d0 100%)',
                  margin: '-16px -16px 16px -16px',
                  padding: '20px',
                  borderRadius: '12px 12px 0 0',
                  border: '2px solid #22c55e'
                }}>
                  <div style={{ 
                    fontSize: '17px', 
                    fontWeight: '700', 
                    color: '#14532d',
                    animation: 'pulse 2s infinite'
                  }}>
                    🔧 TRABAJANDO EN ZONA
                  </div>
                  <div style={{ fontSize: '15px', color: '#166534', marginTop: '8px' }}>
                    📍 {selectedZona?.nombre}
                  </div>
                  <div style={{ 
                    fontSize: '13px', 
                    marginTop: '10px', 
                    color: '#166534'
                  }}>
                    ⏰ Llegada: {formatTime(workLog.arrivalTime)}
                  </div>
                </div>
                
                <button
                  onClick={() => {
                    if (window.confirm('¿Confirmas que has completado la tarea?')) {
                      finishWork();
                    }
                  }}
                  style={{
                    ...styles.gpsButton,
                    background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                    color: 'white',
                    fontSize: '16px',
                    fontWeight: '700',
                    marginBottom: '12px'
                  }}
                >
                  ✅ COMPLETAR TAREA
                </button>
                
                <button
                  onClick={() => {
                    if (window.confirm('¿Cancelar el trabajo?')) {
                      cancelNavigation();
                    }
                  }}
                  style={{
                    ...styles.gpsButton,
                    background: 'white',
                    color: '#ef4444',
                    border: '2px solid #ef4444',
                    marginBottom: 0
                  }}
                >
                  ❌ Cancelar
                </button>
              </div>
            )}

            {/* Estado: Completado */}
            {workStatus === 'completed' && (
              <div style={{
                ...styles.card,
                background: 'linear-gradient(135deg, #e0e7ff 0%, #c7d2fe 100%)',
                border: '2px solid #6366f1'
              }}>
                <div style={{ 
                  fontSize: '17px', 
                  fontWeight: '700', 
                  color: '#312e81',
                  textAlign: 'center',
                  marginBottom: '16px'
                }}>
                  ✅ Trabajo Completado
                </div>
                <button
                  onClick={resetWork}
                  style={{
                    ...styles.gpsButton,
                    background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                    color: 'white',
                    marginBottom: 0
                  }}
                >
                  🔄 Nuevo Trabajo
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Notificación Toast */}
      {toastNotification && (
        <div style={{
          ...styles.toast,
          background: toastNotification.type === 'success' ? 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)' :
                     toastNotification.type === 'error' ? 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)' :
                     toastNotification.type === 'warning' ? 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)' :
                     'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
          color: 'white'
        }}>
          <span style={{ fontSize: '18px' }}>
            {toastNotification.type === 'success' ? '✅' :
             toastNotification.type === 'error' ? '❌' :
             toastNotification.type === 'warning' ? '⚠️' : 'ℹ️'}
          </span>
          <span style={{ flex: 1, fontSize: '14px', fontWeight: '500' }}>
            {toastNotification.message}
          </span>
        </div>
      )}

      {/* Modal de éxito (mantener como está) */}
      {successMessage && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.5)',
          zIndex: 2999,
          animation: 'fadeIn 0.3s ease'
        }}>
          <div style={styles.successModal}>
            <div style={{
              fontSize: '60px',
              marginBottom: '20px',
              animation: 'bounce 0.5s ease'
            }}>
              ✅
            </div>
            <h2 style={{
              margin: '0 0 10px 0',
              fontSize: '24px',
              color: '#16a34a'
            }}>
              {successMessage.title}
            </h2>
            <p style={{
              margin: 0,
              fontSize: '16px',
              color: '#6b7280'
            }}>
              {successMessage.message}
            </p>
          </div>
        </div>
      )}

      <style>{`
        @keyframes slideInRight {
          from {
            transform: translateX(100%);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }
        
        @keyframes slideIn {
          from {
            transform: translateY(-10px);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }
        
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        
        @keyframes zoomIn {
          from {
            transform: translate(-50%, -50%) scale(0.8);
            opacity: 0;
          }
          to {
            transform: translate(-50%, -50%) scale(1);
            opacity: 1;
          }
        }
        
        @keyframes bounce {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.2); }
        }
        
        @keyframes pulse {
          0% {
            box-shadow: 0 0 0 0 rgba(34, 197, 94, 0.7);
          }
          50% {
            box-shadow: 0 0 0 10px rgba(34, 197, 94, 0);
          }
          100% {
            box-shadow: 0 0 0 0 rgba(34, 197, 94, 0);
          }
        }
      `}</style>
    </div>
  );
};

export default SistemaTrabajadoresFirebase;
