// src/components/Login.js
import React, { useState } from 'react';
import PasswordRecovery from './PasswordRecovery';
import {
  signInWithEmailAndPassword,
  updatePassword,
  EmailAuthProvider,
  reauthenticateWithCredential
} from 'firebase/auth';
import { auth, firestore } from '../config/firebase';
import { doc, getDoc, updateDoc, query, where, collection, getDocs } from 'firebase/firestore';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Estados para cambio de contrase√±a obligatorio
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [oldPassword, setOldPassword] = useState('');
  const [showPasswordRecovery, setShowPasswordRecovery] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccessMessage('');

    try {
      // Intentar login con Firebase Auth directamente
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Obtener datos del usuario desde Firestore
      const userDoc = await getDoc(doc(firestore, 'users', user.uid));

      if (userDoc.exists()) {
        const userData = userDoc.data();

        // Verificar si debe cambiar contrase√±a
        if (userData.requirePasswordChange || userData.firstLogin !== false) {
          // Limpiar flags de contrase√±a temporal si existen
          if (userData.tempPassword) {
            await updateDoc(doc(firestore, 'users', user.uid), {
              tempPassword: null,
              lastTempPassword: userData.tempPassword // Guardar para referencia
            });
          }

          setCurrentUser({
            uid: user.uid,
            email: user.email,
            role: userData.role,
            name: userData.name,
            ...userData
          });
          setOldPassword(password);
          setShowChangePassword(true);
        } else {
          // Login exitoso - actualizar √∫ltima conexi√≥n
          await updateDoc(doc(firestore, 'users', user.uid), {
            lastLogin: new Date().toISOString()
          });

          // Login normal
          onLogin({
            uid: user.uid,
            email: user.email,
            role: userData.role,
            name: userData.name,
            ...userData
          });
        }
      } else {
        setError('No se encontraron datos del usuario');
      }
    } catch (error) {
      console.error('Error al iniciar sesi√≥n:', error);

      // Mensajes de error en espa√±ol
      switch (error.code) {
        case 'auth/user-not-found':
          setError('Usuario no encontrado. Verifica tu email');
          break;
        case 'auth/wrong-password':
          setError('Contrase√±a incorrecta');
          break;
        case 'auth/invalid-email':
          setError('Email inv√°lido');
          break;
        case 'auth/too-many-requests':
          setError('Demasiados intentos. Intenta m√°s tarde');
          break;
        case 'auth/invalid-credential':
          setError('Credenciales inv√°lidas. Verifica tu email y contrase√±a');
          break;
        case 'auth/network-request-failed':
          setError('Error de conexi√≥n. Verifica tu internet');
          break;
        default:
          setError('Error al iniciar sesi√≥n. Intenta nuevamente');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleChangePassword = async (e) => {
    e.preventDefault();

    // Validaciones
    if (newPassword.length < 6) {
      setError('La contrase√±a debe tener al menos 6 caracteres');
      return;
    }

    if (newPassword !== confirmPassword) {
      setError('Las contrase√±as no coinciden');
      return;
    }

    if (newPassword === oldPassword) {
      setError('La nueva contrase√±a debe ser diferente a la anterior');
      return;
    }

    // Validaci√≥n adicional de seguridad
    const hasNumber = /\d/.test(newPassword);
    const hasLetter = /[a-zA-Z]/.test(newPassword);
    
    if (!hasNumber || !hasLetter) {
      setError('La contrase√±a debe contener al menos una letra y un n√∫mero');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const user = auth.currentUser;

      if (user) {
        // Actualizar contrase√±a en Firebase Auth
        await updatePassword(user, newPassword);

        // Actualizar en Firestore
        await updateDoc(doc(firestore, 'users', user.uid), {
          requirePasswordChange: false,
          firstLogin: false,
          passwordChangedAt: new Date().toISOString(),
          tempPassword: null,
          lastTempPassword: null,
          resetMethod: null
        });

        setSuccessMessage('‚úÖ Contrase√±a actualizada exitosamente');
        
        // Esperar un momento antes de continuar
        setTimeout(() => {
          onLogin({
            ...currentUser,
            requirePasswordChange: false,
            firstLogin: false
          });
        }, 2000);
      } else {
        setError('Error de autenticaci√≥n. Por favor, vuelve a iniciar sesi√≥n');
      }

    } catch (error) {
      console.error('Error al cambiar contrase√±a:', error);

      switch (error.code) {
        case 'auth/weak-password':
          setError('La contrase√±a es muy d√©bil. Usa al menos 6 caracteres con letras y n√∫meros');
          break;
        case 'auth/requires-recent-login':
          setError('Por seguridad, vuelve a iniciar sesi√≥n');
          break;
        case 'auth/network-request-failed':
          setError('Error de conexi√≥n. Verifica tu internet');
          break;
        default:
          setError('Error al cambiar la contrase√±a. Intenta nuevamente');
      }
    } finally {
      setLoading(false);
    }
  };

  // Formulario de cambio de contrase√±a obligatorio
  if (showChangePassword) {
    return (
      <div style={{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        fontFamily: 'Arial, sans-serif'
      }}>
        <div style={{
          background: 'white',
          padding: '40px',
          borderRadius: '20px',
          boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
          maxWidth: '400px',
          width: '90%'
        }}>
          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <div style={{ fontSize: '48px', marginBottom: '10px' }}>üîê</div>
            <h2 style={{
              color: '#1f2937',
              margin: '0 0 10px 0',
              fontSize: '24px',
              fontWeight: '700'
            }}>
              Cambio de Contrase√±a Obligatorio
            </h2>
            <p style={{
              color: '#6b7280',
              margin: 0,
              fontSize: '14px'
            }}>
              Por seguridad, debes cambiar tu contrase√±a temporal
            </p>
          </div>

          <form onSubmit={handleChangePassword}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Nueva Contrase√±a
              </label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
                placeholder="M√≠nimo 6 caracteres con letras y n√∫meros"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '14px',
                  outline: 'none',
                  transition: 'border-color 0.2s'
                }}
                onFocus={(e) => e.target.style.borderColor = '#667eea'}
                onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
              />
            </div>

            <div style={{ marginBottom: '24px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Confirmar Nueva Contrase√±a
              </label>
              <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
                placeholder="Repite la contrase√±a"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '14px',
                  outline: 'none',
                  transition: 'border-color 0.2s'
                }}
                onFocus={(e) => e.target.style.borderColor = '#667eea'}
                onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
              />
            </div>

            {/* Requisitos de contrase√±a */}
            <div style={{
              marginBottom: '20px',
              padding: '12px',
              background: '#f3f4f6',
              borderRadius: '6px',
              fontSize: '12px',
              color: '#6b7280'
            }}>
              <strong>Requisitos de la contrase√±a:</strong>
              <ul style={{ margin: '5px 0 0 0', paddingLeft: '20px' }}>
                <li>M√≠nimo 6 caracteres</li>
                <li>Al menos una letra</li>
                <li>Al menos un n√∫mero</li>
                <li>Diferente a la contrase√±a anterior</li>
              </ul>
            </div>

            {/* Mensajes */}
            {error && (
              <div style={{
                background: '#fef2f2',
                border: '1px solid #fecaca',
                color: '#dc2626',
                padding: '10px',
                borderRadius: '6px',
                marginBottom: '20px',
                fontSize: '14px',
                textAlign: 'center'
              }}>
                {error}
              </div>
            )}

            {successMessage && (
              <div style={{
                background: '#dcfce7',
                border: '1px solid #86efac',
                color: '#166534',
                padding: '10px',
                borderRadius: '6px',
                marginBottom: '20px',
                fontSize: '14px',
                textAlign: 'center'
              }}>
                {successMessage}
              </div>
            )}

            <button
              type="submit"
              disabled={loading}
              style={{
                width: '100%',
                padding: '14px',
                background: loading ? '#9ca3af' : 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: loading ? 'not-allowed' : 'pointer',
                transition: 'transform 0.2s',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px'
              }}
            >
              {loading ? (
                <>
                  <span className="spinner"></span>
                  Actualizando...
                </>
              ) : (
                '‚úÖ Cambiar Contrase√±a'
              )}
            </button>
          </form>
        </div>

        <style>{`
          .spinner {
            width: 16px;
            height: 16px;
            border: 2px solid #ffffff;
            border-top-color: transparent;
            border-radius: 50%;
            animation: spin 0.6s linear infinite;
          }
          
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  // Formulario de login principal
  return (
    <div style={{
      width: '100%',
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        background: 'white',
        padding: '40px',
        borderRadius: '20px',
        boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
        maxWidth: '400px',
        width: '90%'
      }}>
        {/* Logo y t√≠tulo */}
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <div style={{ fontSize: '48px', marginBottom: '10px' }}>üó∫Ô∏è</div>
          <h1 style={{
            color: '#1f2937',
            margin: '0 0 10px 0',
            fontSize: '28px',
            fontWeight: '700'
          }}>
            MapaQuill√≥n
          </h1>
          <p style={{
            color: '#6b7280',
            margin: 0,
            fontSize: '14px'
          }}>
            Sistema de Control de Flota<br />
            Municipalidad de Quill√≥n
          </p>
        </div>

        {/* Formulario */}
        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{
              display: 'block',
              marginBottom: '8px',
              color: '#374151',
              fontSize: '14px',
              fontWeight: '500'
            }}>
              Correo Electr√≥nico
            </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="ejemplo@gmail.com"
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #d1d5db',
                borderRadius: '8px',
                fontSize: '14px',
                outline: 'none',
                transition: 'border-color 0.2s'
              }}
              onFocus={(e) => e.target.style.borderColor = '#667eea'}
              onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
            />
          </div>

          <div style={{ marginBottom: '12px' }}>
            <label style={{
              display: 'block',
              marginBottom: '8px',
              color: '#374151',
              fontSize: '14px',
              fontWeight: '500'
            }}>
              Contrase√±a
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #d1d5db',
                borderRadius: '8px',
                fontSize: '14px',
                outline: 'none',
                transition: 'border-color 0.2s'
              }}
              onFocus={(e) => e.target.style.borderColor = '#667eea'}
              onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
            />
          </div>

          {/* Mensajes */}
          {error && (
            <div style={{
              background: '#fef2f2',
              border: '1px solid #fecaca',
              color: '#dc2626',
              padding: '10px',
              borderRadius: '6px',
              marginBottom: '20px',
              fontSize: '14px',
              textAlign: 'center'
            }}>
              {error}
            </div>
          )}

          {successMessage && (
            <div style={{
              background: '#dcfce7',
              border: '1px solid #86efac',
              color: '#166534',
              padding: '10px',
              borderRadius: '6px',
              marginBottom: '20px',
              fontSize: '14px',
              textAlign: 'center'
            }}>
              {successMessage}
            </div>
          )}

          {/* Bot√≥n de login */}
          <button
            type="submit"
            disabled={loading}
            style={{
              width: '100%',
              padding: '14px',
              background: loading ? '#9ca3af' : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: '600',
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'transform 0.2s',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px'
            }}
            onMouseOver={(e) => !loading && (e.currentTarget.style.transform = 'translateY(-2px)')}
            onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0px)'}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                Iniciando sesi√≥n...
              </>
            ) : (
              'Iniciar Sesi√≥n'
            )}
          </button>

          <button
            type="button"
            onClick={() => setShowPasswordRecovery(true)}
            style={{
              width: '100%',
              padding: '12px',
              background: 'transparent',
              color: '#3b82f6',
              border: '1px solid #3b82f6',
              borderRadius: '8px',
              fontSize: '14px',
              fontWeight: '500',
              cursor: 'pointer',
              marginTop: '10px'
            }}
          >
            üîì ¬øOlvidaste tu contrase√±a?
          </button>

          {/* Modal de recuperaci√≥n de contrase√±a */}
          {showPasswordRecovery && (
            <PasswordRecovery
              onClose={() => setShowPasswordRecovery(false)}
              onSuccess={() => {
                setShowPasswordRecovery(false);
                setSuccessMessage('‚úÖ Ya puedes iniciar sesi√≥n con tu contrase√±a temporal');
                setTimeout(() => setSuccessMessage(''), 5000);
              }}
            />
          )}
        </form>

        {/* Informaci√≥n de contacto */}
        <div style={{
          marginTop: '30px',
          padding: '15px',
          background: '#f9fafb',
          borderRadius: '8px',
          fontSize: '12px',
          color: '#6b7280',
          textAlign: 'center'
        }}>
          <strong>¬øNecesitas ayuda?</strong><br />
          Contacta al administrador del sistema<br />
          üìû +56 9 1234 5678
        </div>
      </div>

      <style>{`
        .spinner {
          width: 16px;
          height: 16px;
          border: 2px solid #ffffff;
          border-top-color: transparent;
          border-radius: 50%;
          animation: spin 0.6s linear infinite;
          display: inline-block;
        }
        
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default Login;
