// src/services/pdfExportService.js
import { jsPDF } from 'jspdf'; // ‚ö†Ô∏è IMPORTANTE: Usar destructuring { jsPDF }
import autoTable from 'jspdf-autotable'; // ‚ö†Ô∏è IMPORTANTE: Importar autoTable

// ========== CONFIGURACI√ìN DE ESTILOS ==========
const COLORS = {
  primary: '#667eea',
  secondary: '#764ba2',
  success: '#22c55e',
  warning: '#f59e0b',
  danger: '#ef4444',
  dark: '#1f2937',
  gray: '#6b7280'
};

const FONTS = {
  title: 20,
  subtitle: 16,
  heading: 14,
  normal: 11,
  small: 9
};

// ========== SERVICIO DE EXPORTACI√ìN PDF ==========
export const pdfExportService = {
  
  // ========== EXPORTAR USUARIOS A PDF ==========
  exportUsersToPDF: (users, currentUser) => {
    const doc = new jsPDF(); // Crear nueva instancia
    const pageWidth = doc.internal.pageSize.width;
    const pageHeight = doc.internal.pageSize.height;
    const margins = { top: 20, left: 15, right: 15, bottom: 20 };
    
    // Fecha y hora actual
    const now = new Date();
    const fecha = now.toLocaleDateString('es-CL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
    const hora = now.toLocaleTimeString('es-CL', {
      hour: '2-digit',
      minute: '2-digit'
    });

    // ========== HEADER ==========
    // Logo o t√≠tulo del sistema
    doc.setFillColor(102, 126, 234);
    doc.rect(0, 0, pageWidth, 40, 'F');
    
    // T√≠tulo principal
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(FONTS.title);
    doc.setFont(undefined, 'bold');
    doc.text('SISTEMA MAPAQUILL√ìN', pageWidth / 2, 20, { align: 'center' });
    
    // Subt√≠tulo
    doc.setFontSize(FONTS.subtitle);
    doc.setFont(undefined, 'normal');
    doc.text('Reporte de Usuarios', pageWidth / 2, 30, { align: 'center' });
    
    // ========== INFORMACI√ìN DEL REPORTE ==========
    doc.setTextColor(COLORS.dark);
    doc.setFontSize(FONTS.small);
    doc.setFont(undefined, 'normal');
    
    // Cuadro de informaci√≥n
    doc.setFillColor(245, 247, 250);
    doc.rect(margins.left, 50, pageWidth - margins.left - margins.right, 25, 'F');
    
    doc.text(`Fecha de generaci√≥n: ${fecha}`, margins.left + 5, 60);
    doc.text(`Hora: ${hora}`, margins.left + 5, 67);
    doc.text(`Generado por: ${currentUser.name || currentUser.email}`, pageWidth / 2, 60);
    doc.text(`Total de usuarios: ${users.length}`, pageWidth / 2, 67);
    
    // ========== ESTAD√çSTICAS DE USUARIOS ==========
    const stats = {
      superadmins: users.filter(u => u.role === 'superadmin').length,
      admins: users.filter(u => u.role === 'admin').length,
      trabajadores: users.filter(u => u.role === 'trabajador').length,
      juntasVecinos: users.filter(u => u.role === 'junta_vecinos').length,
      activos: users.filter(u => u.active !== false).length,
      inactivos: users.filter(u => u.active === false).length
    };
    
    // Cuadro de estad√≠sticas
    let yPosition = 85;
    doc.setFontSize(FONTS.heading);
    doc.setFont(undefined, 'bold');
    doc.text('Estad√≠sticas de Usuarios', margins.left, yPosition);
    
    yPosition += 10;
    doc.setFontSize(FONTS.normal);
    doc.setFont(undefined, 'normal');
    
    // Crear grid de estad√≠sticas usando autoTable correctamente
    const statsData = [
      ['Super Admins', stats.superadmins.toString(), 'Administradores', stats.admins.toString()],
      ['Trabajadores', stats.trabajadores.toString(), 'Juntas de Vecinos', stats.juntasVecinos.toString()],
      ['Usuarios Activos', stats.activos.toString(), 'Usuarios Inactivos', stats.inactivos.toString()]
    ];
    
    // ‚ö†Ô∏è FORMA CORRECTA DE USAR autoTable
    autoTable(doc, {
      startY: yPosition,
      head: [],
      body: statsData,
      theme: 'grid',
      styles: {
        fontSize: 10,
        cellPadding: 3
      },
      columnStyles: {
        0: { fontStyle: 'bold', fillColor: [240, 242, 245] },
        2: { fontStyle: 'bold', fillColor: [240, 242, 245] }
      }
    });
    
    // ========== TABLA DE USUARIOS ==========
    yPosition = doc.lastAutoTable.finalY + 15;
    
    doc.setFontSize(FONTS.heading);
    doc.setFont(undefined, 'bold');
    doc.text('Lista Detallada de Usuarios', margins.left, yPosition);
    
    // Preparar datos para la tabla
    const tableHeaders = [
      ['#', 'Nombre', 'Email', 'Rol', 'Tel√©fono', 'Estado']
    ];
    
    const tableData = users.map((user, index) => {
      const rol = {
        'superadmin': 'Super Admin',
        'admin': 'Administrador',
        'trabajador': 'Trabajador',
        'junta_vecinos': 'Junta Vecinos'
      }[user.role] || user.role || 'Sin rol';
      
      return [
        (index + 1).toString(),
        user.name || 'Sin nombre',
        user.email || '',
        rol,
        user.phone || '-',
        user.active === false ? 'Inactivo' : 'Activo'
      ];
    });
    
    // ‚ö†Ô∏è FORMA CORRECTA DE USAR autoTable
    autoTable(doc, {
      startY: yPosition + 7,
      head: tableHeaders,
      body: tableData,
      theme: 'striped',
      headStyles: {
        fillColor: [102, 126, 234],
        textColor: 255,
        fontSize: 10,
        fontStyle: 'bold'
      },
      bodyStyles: {
        fontSize: 9
      },
      alternateRowStyles: {
        fillColor: [245, 247, 250]
      },
      columnStyles: {
        0: { cellWidth: 15, halign: 'center' },
        1: { cellWidth: 40 },
        2: { cellWidth: 55 },
        3: { cellWidth: 35 },
        4: { cellWidth: 30 },
        5: { cellWidth: 25, halign: 'center' }
      },
      didDrawPage: function(data) {
        // Footer en cada p√°gina
        doc.setFontSize(FONTS.small);
        doc.setTextColor(COLORS.gray);
        doc.text(
          `P√°gina ${data.pageNumber}`,
          pageWidth / 2,
          pageHeight - 10,
          { align: 'center' }
        );
      }
    });
    
    // ========== PIE DE P√ÅGINA FINAL ==========
    const finalY = doc.lastAutoTable.finalY;
    if (finalY < pageHeight - 40) {
      doc.setDrawColor(200, 200, 200);
      doc.line(margins.left, finalY + 10, pageWidth - margins.right, finalY + 10);
      
      doc.setFontSize(FONTS.small);
      doc.setTextColor(COLORS.gray);
      doc.setFont(undefined, 'italic');
      doc.text(
        'Este documento fue generado autom√°ticamente por el Sistema MapaQuill√≥n',
        pageWidth / 2,
        finalY + 20,
        { align: 'center' }
      );
      doc.text(
        '¬© 2024 Municipalidad de Quill√≥n - Todos los derechos reservados',
        pageWidth / 2,
        finalY + 27,
        { align: 'center' }
      );
    }
    
    // Guardar el PDF
    doc.save(`usuarios_mapaquillon_${now.getTime()}.pdf`);
  },

  // ========== EXPORTAR VEH√çCULOS A PDF ==========
  exportVehiclesToPDF: (vehiculos, tiposVehiculo, currentUser) => {
    const doc = new jsPDF('landscape'); // Orientaci√≥n horizontal
    const pageWidth = doc.internal.pageSize.width;
    const pageHeight = doc.internal.pageSize.height;
    const margins = { top: 20, left: 15, right: 15, bottom: 20 };
    
    // Fecha y hora actual
    const now = new Date();
    const fecha = now.toLocaleDateString('es-CL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
    const hora = now.toLocaleTimeString('es-CL', {
      hour: '2-digit',
      minute: '2-digit'
    });

    // ========== HEADER ==========
    doc.setFillColor(59, 130, 246);
    doc.rect(0, 0, pageWidth, 40, 'F');
    
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(FONTS.title);
    doc.setFont(undefined, 'bold');
    doc.text('SISTEMA MAPAQUILL√ìN', pageWidth / 2, 20, { align: 'center' });
    
    doc.setFontSize(FONTS.subtitle);
    doc.setFont(undefined, 'normal');
    doc.text('Reporte de Flota Vehicular', pageWidth / 2, 30, { align: 'center' });
    
    // ========== INFORMACI√ìN DEL REPORTE ==========
    doc.setTextColor(COLORS.dark);
    doc.setFontSize(FONTS.small);
    doc.setFont(undefined, 'normal');
    
    doc.setFillColor(245, 247, 250);
    doc.rect(margins.left, 50, pageWidth - margins.left - margins.right, 25, 'F');
    
    doc.text(`Fecha: ${fecha}`, margins.left + 5, 60);
    doc.text(`Hora: ${hora}`, margins.left + 5, 67);
    doc.text(`Generado por: ${currentUser.name || currentUser.email}`, pageWidth / 2, 60);
    doc.text(`Total de veh√≠culos: ${vehiculos.length}`, pageWidth / 2, 67);
    
    // ========== LISTA DE VEH√çCULOS ==========
    let yPosition = 85;
    doc.setFontSize(FONTS.heading);
    doc.setFont(undefined, 'bold');
    doc.text('Inventario de Veh√≠culos', margins.left, yPosition);
    
    // Preparar datos para la tabla
    const tableHeaders = [
      ['#', 'Nombre', 'Tipo', 'Marca', 'Modelo', 'A√±o', 'Patente', 'Estado']
    ];
    
    const tableData = vehiculos.map((vehiculo, index) => {
      const tipo = tiposVehiculo?.find(t => t.id === vehiculo.tipo);
      const tipoDisplay = tipo ? tipo.nombre : vehiculo.tipo || 'Sin tipo';
      
      const estadoDisplay = {
        'disponible': 'Disponible',
        'en_uso': 'En Uso',
        'mantenimiento': 'Mantenimiento',
        'fuera_servicio': 'Fuera Servicio'
      }[vehiculo.estado] || vehiculo.estado || 'Desconocido';
      
      return [
        (index + 1).toString(),
        vehiculo.nombre || 'Sin nombre',
        tipoDisplay,
        vehiculo.marca || '-',
        vehiculo.modelo || '-',
        vehiculo.a√±o?.toString() || '-',
        vehiculo.patente || '-',
        estadoDisplay
      ];
    });
    
    // ‚ö†Ô∏è FORMA CORRECTA DE USAR autoTable
    autoTable(doc, {
      startY: yPosition + 7,
      head: tableHeaders,
      body: tableData,
      theme: 'striped',
      headStyles: {
        fillColor: [59, 130, 246],
        textColor: 255,
        fontSize: 9,
        fontStyle: 'bold'
      },
      bodyStyles: {
        fontSize: 8
      },
      alternateRowStyles: {
        fillColor: [245, 247, 250]
      },
      columnStyles: {
        0: { cellWidth: 10, halign: 'center' },
        1: { cellWidth: 40 },
        2: { cellWidth: 35 },
        3: { cellWidth: 30 },
        4: { cellWidth: 30 },
        5: { cellWidth: 20, halign: 'center' },
        6: { cellWidth: 30 },
        7: { cellWidth: 30 }
      },
      didDrawPage: function(data) {
        // Footer en cada p√°gina
        doc.setFontSize(FONTS.small);
        doc.setTextColor(COLORS.gray);
        doc.text(
          `P√°gina ${data.pageNumber}`,
          pageWidth / 2,
          pageHeight - 10,
          { align: 'center' }
        );
      }
    });
    // Agregar esta funci√≥n dentro del objeto pdfExportService

// ========== EXPORTAR USUARIO INDIVIDUAL A PDF ==========
exportSingleUserToPDF: (user, currentUser) => {
  const doc = new jsPDF();
  const pageWidth = doc.internal.pageSize.width;
  const pageHeight = doc.internal.pageSize.height;
  
  // Fecha y hora actual
  const now = new Date();
  const fecha = now.toLocaleDateString('es-CL', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });
  const hora = now.toLocaleTimeString('es-CL', {
    hour: '2-digit',
    minute: '2-digit'
  });

  // ========== HEADER CON GRADIENTE ==========
  doc.setFillColor(102, 126, 234);
  doc.rect(0, 0, pageWidth, 50, 'F');
  
  // T√≠tulo
  doc.setTextColor(255, 255, 255);
  doc.setFontSize(24);
  doc.setFont(undefined, 'bold');
  doc.text('SISTEMA MAPAQUILL√ìN', pageWidth / 2, 25, { align: 'center' });
  
  doc.setFontSize(16);
  doc.setFont(undefined, 'normal');
  doc.text('Ficha de Usuario', pageWidth / 2, 38, { align: 'center' });

  // ========== FOTO/AVATAR PLACEHOLDER ==========
  // C√≠rculo para avatar
  doc.setFillColor(240, 242, 245);
  doc.circle(pageWidth / 2, 75, 20, 'F');
  
  // Inicial del usuario
  doc.setTextColor(102, 126, 234);
  doc.setFontSize(24);
  doc.setFont(undefined, 'bold');
  const inicial = (user.name || user.email || '?').charAt(0).toUpperCase();
  doc.text(inicial, pageWidth / 2, 82, { align: 'center' });

  // ========== NOMBRE DEL USUARIO ==========
  doc.setTextColor(31, 41, 55);
  doc.setFontSize(20);
  doc.setFont(undefined, 'bold');
  doc.text(user.name || 'Sin nombre', pageWidth / 2, 105, { align: 'center' });
  
  // Rol con badge
  const roleColors = {
    'superadmin': [109, 33, 168],    // P√∫rpura
    'admin': [30, 64, 175],           // Azul
    'trabajador': [22, 163, 74],      // Verde
    'junta_vecinos': [217, 119, 6]    // Naranja
  };
  
  const roleNames = {
    'superadmin': 'üîê SUPER ADMINISTRADOR',
    'admin': 'üë®‚Äçüíº ADMINISTRADOR',
    'trabajador': 'üë∑ TRABAJADOR',
    'junta_vecinos': 'üèòÔ∏è JUNTA DE VECINOS'
  };
  
  const roleColor = roleColors[user.role] || [107, 114, 128];
  const roleName = roleNames[user.role] || user.role?.toUpperCase() || 'SIN ROL';
  
  // Badge del rol
  doc.setFillColor(...roleColor);
  doc.roundedRect(pageWidth/2 - 40, 110, 80, 10, 2, 2, 'F');
  doc.setTextColor(255, 255, 255);
  doc.setFontSize(10);
  doc.setFont(undefined, 'bold');
  doc.text(roleName, pageWidth / 2, 117, { align: 'center' });

  // ========== INFORMACI√ìN DETALLADA ==========
  let yPos = 140;
  
  // T√≠tulo de secci√≥n
  doc.setTextColor(31, 41, 55);
  doc.setFontSize(14);
  doc.setFont(undefined, 'bold');
  doc.text('INFORMACI√ìN PERSONAL', 20, yPos);
  
  // L√≠nea decorativa
  doc.setDrawColor(229, 231, 235);
  doc.setLineWidth(0.5);
  doc.line(20, yPos + 2, pageWidth - 20, yPos + 2);
  
  yPos += 15;
  
  // Funci√≥n helper para agregar campo
  const addField = (label, value, yPosition) => {
    // Label
    doc.setTextColor(107, 114, 128);
    doc.setFontSize(10);
    doc.setFont(undefined, 'normal');
    doc.text(label, 30, yPosition);
    
    // Value
    doc.setTextColor(31, 41, 55);
    doc.setFontSize(12);
    doc.setFont(undefined, 'bold');
    doc.text(value || 'No especificado', 30, yPosition + 6);
    
    return yPosition + 18;
  };
  
  // Campos de informaci√≥n
  yPos = addField('Correo Electr√≥nico:', user.email, yPos);
  yPos = addField('Tel√©fono:', user.phone, yPos);
  yPos = addField('Localidad/Sector:', user.localidad, yPos);
  yPos = addField('Estado de la Cuenta:', user.active === false ? '‚ùå Inactiva' : '‚úÖ Activa', yPos);
  
  // Fecha de registro
  if (user.createdAt) {
    const fechaRegistro = new Date(user.createdAt).toLocaleDateString('es-CL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
    yPos = addField('Fecha de Registro:', fechaRegistro, yPos);
  }
  
  // ID del usuario
  yPos = addField('ID de Usuario:', user.id || 'No disponible', yPos);

  // ========== INFORMACI√ìN ADICIONAL ==========
  yPos += 10;
  doc.setTextColor(31, 41, 55);
  doc.setFontSize(14);
  doc.setFont(undefined, 'bold');
  doc.text('INFORMACI√ìN ADICIONAL', 20, yPos);
  
  doc.setDrawColor(229, 231, 235);
  doc.line(20, yPos + 2, pageWidth - 20, yPos + 2);
  
  yPos += 15;
  
  // Permisos seg√∫n el rol
  doc.setTextColor(107, 114, 128);
  doc.setFontSize(10);
  doc.setFont(undefined, 'normal');
  doc.text('Permisos del Usuario:', 30, yPos);
  
  yPos += 8;
  
  const permisos = {
    'superadmin': [
      '‚úÖ Acceso total al sistema',
      '‚úÖ Gesti√≥n de usuarios',
      '‚úÖ Gesti√≥n de veh√≠culos',
      '‚úÖ Configuraci√≥n del sistema',
      '‚úÖ Acceso a todos los m√≥dulos'
    ],
    'admin': [
      '‚úÖ Dashboard GPS',
      '‚úÖ Gesti√≥n de zonas',
      '‚úÖ Monitoreo de flota',
      '‚úÖ Asignaci√≥n de trabajos',
      '‚ùå No puede crear usuarios'
    ],
    'trabajador': [
      '‚úÖ Sistema GPS personal',
      '‚úÖ Recepci√≥n de zonas asignadas',
      '‚úÖ Registro de recorridos',
      '‚úÖ Dashboard personal',
      '‚ùå No puede asignar trabajos'
    ],
    'junta_vecinos': [
      '‚úÖ Crear solicitudes',
      '‚úÖ Ver estado de solicitudes',
      '‚úÖ Comunicaci√≥n con municipio',
      '‚ùå Acceso limitado a su localidad'
    ]
  };
  
  const userPermisos = permisos[user.role] || ['Sin permisos definidos'];
  
  doc.setFontSize(9);
  userPermisos.forEach(permiso => {
    doc.text(`  ${permiso}`, 30, yPos);
    yPos += 6;
  });

  // ========== C√ìDIGO QR SIMULADO (Opcional) ==========
  yPos += 10;
  doc.setFillColor(240, 242, 245);
  doc.rect(pageWidth/2 - 25, yPos, 50, 50, 'F');
  
  doc.setTextColor(107, 114, 128);
  doc.setFontSize(8);
  doc.text('C√≥digo QR', pageWidth/2, yPos + 60, { align: 'center' });
  doc.text(user.id || 'ID', pageWidth/2, yPos + 25, { align: 'center' });

  // ========== PIE DE P√ÅGINA ==========
  doc.setDrawColor(229, 231, 235);
  doc.line(20, pageHeight - 35, pageWidth - 20, pageHeight - 35);
  
  doc.setTextColor(107, 114, 128);
  doc.setFontSize(9);
  doc.setFont(undefined, 'italic');
  doc.text(`Generado el ${fecha} a las ${hora}`, 20, pageHeight - 25);
  doc.text(`Por: ${currentUser.name || currentUser.email}`, 20, pageHeight - 20);
  doc.text('¬© 2024 Sistema MapaQuill√≥n', pageWidth - 20, pageHeight - 20, { align: 'right' });
  
  // Marca de agua diagonal (opcional)
  doc.setTextColor(229, 231, 235);
  doc.setFontSize(60);
  doc.setFont(undefined, 'bold');
  doc.text('CONFIDENCIAL', pageWidth/2, pageHeight/2, { 
    align: 'center',
    angle: 45
  });
  
  // Guardar el PDF con el nombre del usuario
  const fileName = `usuario_${user.name?.replace(/\s+/g, '_') || user.email?.split('@')[0] || 'sin_nombre'}_${now.getTime()}.pdf`;
  doc.save(fileName);
},
    
    // Guardar el PDF
    doc.save(`vehiculos_mapaquillon_${now.getTime()}.pdf`);
  }
};

export default pdfExportService;
