// src/components/FleetPanel.js
import React, { useState, useEffect } from 'react';
import { database, firestore } from '../config/firebase';
import { ref, onValue, update, get } from 'firebase/database';
import { collection, getDocs, updateDoc, doc } from 'firebase/firestore';

const FleetPanel = ({ currentUser, onLogout, onViewChange, currentView = 'fleet' }) => {
  const [vehiculos, setVehiculos] = useState([]);
  const [trabajadores, setTrabajadores] = useState([]);
  const [users, setUsers] = useState([]);
  const [activeTab, setActiveTab] = useState('vehicles');
  const [editingItem, setEditingItem] = useState(null);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('todos');
  const [isMobile, setIsMobile] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editFormData, setEditFormData] = useState({});
  const [expandedCards, setExpandedCards] = useState({});
  const [quickEditMode, setQuickEditMode] = useState({}); // Para ediciÃ³n rÃ¡pida de operador
  
  // NUEVO: Estados para documentos
  const [showDocumentsModal, setShowDocumentsModal] = useState(false);
  const [selectedDocuments, setSelectedDocuments] = useState(null);
  const [documentOwner, setDocumentOwner] = useState(null);
  const [loadingDocuments, setLoadingDocuments] = useState(false);

  // Detectar si es mÃ³vil - MEJORADO
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768 || /iPhone|iPad|iPod|Android/i.test(navigator.userAgent));
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Cargar datos
  useEffect(() => {
    loadVehiculos();
    loadTrabajadores();
    loadUsers();
  }, []);

  const loadVehiculos = () => {
    const vehiculosRef = ref(database, 'vehiculos');
    const unsubscribe = onValue(vehiculosRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const vehiculosArray = Object.entries(data).map(([id, vehiculo]) => ({
          id,
          ...vehiculo
        }));
        setVehiculos(vehiculosArray);
      } else {
        setVehiculos([]);
      }
    });
    return unsubscribe;
  };

  const loadTrabajadores = () => {
    const trabajadoresRef = ref(database, 'trabajadores');
    const unsubscribe = onValue(trabajadoresRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const trabajadoresArray = Object.entries(data).map(([id, trabajador]) => ({
          id,
          ...trabajador
        }));
        setTrabajadores(trabajadoresArray);
      } else {
        setTrabajadores([]);
      }
    });
    return unsubscribe;
  };

  const loadUsers = async () => {
    try {
      const usersSnapshot = await getDocs(collection(firestore, 'users'));
      const usersList = [];
      usersSnapshot.forEach((doc) => {
        usersList.push({ id: doc.id, ...doc.data() });
      });
      setUsers(usersList);
    } catch (error) {
      console.error('Error al cargar usuarios:', error);
    }
  };

  // NUEVA FUNCIÃ“N: Cargar documentos de vehÃ­culo
  const loadVehicleDocuments = async (vehiculoId, vehicleData) => {
    setLoadingDocuments(true);
    try {
      // Buscar documentos en diferentes rutas posibles
      const rutas = [
        `vehiculos/${vehiculoId}/documentos`,
        `documentos/vehiculos/${vehiculoId}`,
        `documentos_vehiculos/${vehiculoId}`
      ];
      
      let documentosEncontrados = null;
      
      for (const ruta of rutas) {
        const docsRef = ref(database, ruta);
        const snapshot = await get(docsRef);
        const data = snapshot.val();
        
        if (data && Object.keys(data).length > 0) {
          documentosEncontrados = data;
          console.log(`ðŸ“„ Documentos de vehÃ­culo encontrados en: ${ruta}`);
          break;
        }
      }
      
      // Verificar si hay documentos dentro del objeto del vehÃ­culo
      if (!documentosEncontrados && vehicleData?.documentos) {
        documentosEncontrados = vehicleData.documentos;
        console.log('ðŸ“„ Documentos encontrados dentro del objeto vehÃ­culo');
      }
      
      // Pasar todos los documentos encontrados tal como estÃ¡n
      setSelectedDocuments(documentosEncontrados || {});
      
      setDocumentOwner({
        type: 'vehicle',
        id: vehiculoId,
        name: vehicleData.nombre || 'VehÃ­culo'
      });
      
      setShowDocumentsModal(true);
    } catch (error) {
      console.error('Error al cargar documentos del vehÃ­culo:', error);
      setMessage({ type: 'error', text: 'âŒ Error al cargar documentos' });
    } finally {
      setLoadingDocuments(false);
    }
  };

  // NUEVA FUNCIÃ“N: Cargar documentos de trabajador
  const loadWorkerDocuments = async (trabajadorId, workerData) => {
    setLoadingDocuments(true);
    try {
      // Buscar documentos en diferentes rutas posibles
      const rutas = [
        `trabajadores/${trabajadorId}/documentos`,
        `documentos/${trabajadorId}`,
        `usuarios/${trabajadorId}/documentos`
      ];
      
      let documentosEncontrados = null;
      
      for (const ruta of rutas) {
        const docsRef = ref(database, ruta);
        const snapshot = await get(docsRef);
        const data = snapshot.val();
        
        if (data && Object.keys(data).length > 0) {
          documentosEncontrados = data;
          console.log(`ðŸ“„ Documentos de trabajador encontrados en: ${ruta}`);
          break;
        }
      }
      
      // Verificar si hay documentos dentro del objeto del trabajador
      if (!documentosEncontrados && workerData?.documentos) {
        documentosEncontrados = workerData.documentos;
        console.log('ðŸ“„ Documentos encontrados dentro del objeto trabajador');
      }
      
      if (documentosEncontrados) {
        // Procesar certificados si existen
        let certificadosProcesados = [];
        if (documentosEncontrados.certificados && typeof documentosEncontrados.certificados === 'object') {
          certificadosProcesados = Object.entries(documentosEncontrados.certificados).map(([key, cert]) => ({
            firebaseKey: key,
            ...cert
          }));
        }
        
        setSelectedDocuments({
          licenciaConducir: documentosEncontrados.licenciaConducir || null,
          certificadoOperador: documentosEncontrados.certificadoOperador || null,
          certificados: certificadosProcesados
        });
      } else {
        setSelectedDocuments({
          licenciaConducir: null,
          certificadoOperador: null,
          certificados: []
        });
      }
      
      setDocumentOwner({
        type: 'worker',
        id: trabajadorId,
        name: workerData.nombre || 'Trabajador'
      });
      
      setShowDocumentsModal(true);
    } catch (error) {
      console.error('Error al cargar documentos del trabajador:', error);
      setMessage({ type: 'error', text: 'âŒ Error al cargar documentos' });
    } finally {
      setLoadingDocuments(false);
    }
  };

  // FunciÃ³n para formatear tamaÃ±o de archivo
  const formatFileSize = (bytes) => {
    if (!bytes || bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
  };

  // FunciÃ³n para actualizaciÃ³n rÃ¡pida del operador
  const quickUpdateOperator = async (vehiculoId, nuevoOperadorId) => {
    setLoading(true);
    try {
      // Actualizar en Realtime Database
      const vehiculoRef = ref(database, `vehiculos/${vehiculoId}`);
      await update(vehiculoRef, {
        operadorAsignado: nuevoOperadorId || null,
        ultimaActualizacion: new Date().toISOString(),
        actualizadoPor: currentUser.uid
      });

      // Si se asignÃ³ un trabajador, actualizar su informaciÃ³n tambiÃ©n
      if (nuevoOperadorId) {
        const userRef = doc(firestore, 'users', nuevoOperadorId);
        await updateDoc(userRef, {
          vehicleId: vehiculoId,
          updatedAt: new Date().toISOString()
        });

        // Actualizar en trabajadores
        const trabajadorRef = ref(database, `trabajadores/${nuevoOperadorId}`);
        await update(trabajadorRef, {
          vehiculoAsignado: vehiculoId,
          ultimaActualizacion: new Date().toISOString()
        });
      }

      // Limpiar vehÃ­culo anterior si otro trabajador lo tenÃ­a
      if (nuevoOperadorId) {
        vehiculos.forEach(async (v) => {
          if (v.id !== vehiculoId && v.operadorAsignado === nuevoOperadorId) {
            const otroVehiculoRef = ref(database, `vehiculos/${v.id}`);
            await update(otroVehiculoRef, {
              operadorAsignado: null,
              ultimaActualizacion: new Date().toISOString()
            });
          }
        });
      }

      setMessage({ type: 'success', text: 'âœ… Operador actualizado exitosamente' });
      setQuickEditMode({ ...quickEditMode, [vehiculoId]: false });
      setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    } catch (error) {
      setMessage({ type: 'error', text: 'âŒ Error al actualizar operador' });
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  // Abrir modal de ediciÃ³n
  const openEditModal = (item, type) => {
    if (type === 'vehicle') {
      setEditFormData({
        nombre: item.nombre || '',
        patente: item.patente || '',
        kilometraje: item.kilometraje || 0,
        operadorAsignado: item.operadorAsignado || '',
        observaciones: item.observaciones || '',
        estado: item.estado || 'disponible'
      });
    } else if (type === 'worker') {
      const userData = users.find(u => u.id === item.id);
      setEditFormData({
        nombre: item.nombre || userData?.name || '',
        email: userData?.email || '',
        telefono: userData?.phone || item.telefono || '',
        vehicleId: userData?.vehicleId || '',
        estado: item.estado || 'disponible'
      });
    }
    setEditingItem({ ...item, type });
    setShowEditModal(true);
  };

  // Guardar cambios de vehÃ­culo
  const saveVehicleChanges = async () => {
    if (!editingItem) return;
    
    setLoading(true);
    try {
      const vehicleRef = ref(database, `vehiculos/${editingItem.id}`);
      await update(vehicleRef, {
        ...editFormData,
        ultimaActualizacion: new Date().toISOString(),
        actualizadoPor: currentUser.uid
      });
      
      setMessage({ type: 'success', text: 'âœ… VehÃ­culo actualizado exitosamente' });
      setShowEditModal(false);
      setEditingItem(null);
      setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    } catch (error) {
      setMessage({ type: 'error', text: 'âŒ Error al actualizar vehÃ­culo' });
    } finally {
      setLoading(false);
    }
  };

  // Guardar cambios de trabajador
  const saveWorkerChanges = async () => {
    if (!editingItem) return;
    
    setLoading(true);
    try {
      // Actualizar en Realtime Database
      const trabajadorRef = ref(database, `trabajadores/${editingItem.id}`);
      await update(trabajadorRef, {
        nombre: editFormData.nombre,
        telefono: editFormData.telefono,
        estado: editFormData.estado,
        ultimaActualizacion: new Date().toISOString()
      });
      
      // Actualizar en Firestore
      const userRef = doc(firestore, 'users', editingItem.id);
      await updateDoc(userRef, {
        name: editFormData.nombre,
        phone: editFormData.telefono,
        vehicleId: editFormData.vehicleId || null,
        updatedAt: new Date().toISOString(),
        updatedBy: currentUser.uid
      });
      
      setMessage({ type: 'success', text: 'âœ… Trabajador actualizado exitosamente' });
      setShowEditModal(false);
      setEditingItem(null);
      setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    } catch (error) {
      setMessage({ type: 'error', text: 'âŒ Error al actualizar trabajador' });
    } finally {
      setLoading(false);
    }
  };

  // Filtrar vehÃ­culos
  const filteredVehiculos = vehiculos.filter(v => {
    const matchesSearch = v.nombre?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          v.patente?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          v.marca?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filterStatus === 'todos' || v.estado === filterStatus;
    return matchesSearch && matchesFilter;
  });

  // Filtrar trabajadores
  const filteredTrabajadores = trabajadores.filter(t => {
    const matchesSearch = t.nombre?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          t.email?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filterStatus === 'todos' || t.estado === filterStatus;
    return matchesSearch && matchesFilter;
  });

  // Obtener estadÃ­sticas
  const getStats = () => {
    const vehicleStats = {
      total: vehiculos.length,
      disponibles: vehiculos.filter(v => v.estado === 'disponible').length,
      enUso: vehiculos.filter(v => v.estado === 'en_uso').length,
      mantenimiento: vehiculos.filter(v => v.estado === 'mantenimiento').length
    };
    
    const workerStats = {
      total: trabajadores.length,
      activos: trabajadores.filter(t => t.ubicacion && 
        (new Date() - new Date(t.ubicacion.timestamp)) < 300000).length,
      enRuta: trabajadores.filter(t => t.estado === 'en_camino').length,
      trabajando: trabajadores.filter(t => t.estado === 'trabajando').length,
      disponibles: trabajadores.filter(t => t.estado === 'disponible' || !t.estado).length
    };
    
    return { vehicleStats, workerStats };
  };

  const { vehicleStats, workerStats } = getStats();

  // Toggle expandir/contraer tarjeta
  const toggleCardExpansion = (id) => {
    setExpandedCards(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  // NUEVO: Modal de Documentos
  const DocumentsModal = () => {
    if (!showDocumentsModal || !selectedDocuments || !documentOwner) return null;

    const isVehicle = documentOwner.type === 'vehicle';
    
    return (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(0,0,0,0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: isMobile ? 'flex-start' : 'center',
        zIndex: 3000,
        padding: isMobile ? '10px' : '20px',
        overflowY: 'auto'
      }}>
        <div style={{
          background: 'white',
          borderRadius: '12px',
          maxWidth: isMobile ? '100%' : '900px',
          width: '100%',
          maxHeight: isMobile ? 'none' : '90vh',
          height: isMobile ? 'auto' : 'auto',
          overflow: 'auto',
          padding: isMobile ? '20px' : '30px',
          marginTop: isMobile ? '20px' : '0',
          marginBottom: isMobile ? '20px' : '0'
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '25px',
            flexWrap: 'wrap',
            gap: '10px'
          }}>
            <h2 style={{ 
              margin: 0, 
              color: '#1f2937', 
              fontSize: isMobile ? '20px' : '24px',
              flex: 1
            }}>
              ðŸ“„ Documentos de {documentOwner.name}
            </h2>
            <button
              onClick={() => {
                setShowDocumentsModal(false);
                setSelectedDocuments(null);
                setDocumentOwner(null);
              }}
              style={{
                padding: '8px',
                background: '#6b7280',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '18px',
                minWidth: '40px',
                height: '40px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
            >
              âœ–
            </button>
          </div>

          {loadingDocuments ? (
            <div style={{
              padding: '40px',
              textAlign: 'center',
              color: '#6b7280'
            }}>
              <div style={{ fontSize: '24px', marginBottom: '10px' }}>â³</div>
              <div>Cargando documentos...</div>
            </div>
          ) : (
            <>
              {isVehicle ? (
                // Documentos de VehÃ­culo - MOSTRAR TODOS LOS DOCUMENTOS
                <div>
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fit, minmax(280px, 1fr))',
                    gap: '20px'
                  }}>
                    {/* Mapear todos los documentos del vehÃ­culo */}
                    {Object.entries(selectedDocuments).map(([key, documento]) => {
                      // Saltar el array de "otros" ya que lo procesaremos despuÃ©s
                      if (key === 'otros' || !documento || typeof documento !== 'object') return null;
                      
                      // Determinar el icono y nombre del documento
                      const getDocumentInfo = (docKey) => {
                        const docTypes = {
                          permisoCirculacion: { icon: 'ðŸ“‹', name: 'Permiso de CirculaciÃ³n' },
                          revisionTecnica: { icon: 'ðŸ”§', name: 'RevisiÃ³n TÃ©cnica' },
                          seguroObligatorio: { icon: 'ðŸ›¡ï¸', name: 'Seguro Obligatorio' },
                          soap: { icon: 'ðŸ›¡ï¸', name: 'SOAP' },
                          padronMunicipal: { icon: 'ðŸ›ï¸', name: 'PadrÃ³n Municipal' },
                          certificadoGases: { icon: 'ðŸ’¨', name: 'Certificado de Gases' },
                          seguroComplementario: { icon: 'ðŸ”’', name: 'Seguro Complementario' },
                          certificadoRevision: { icon: 'ðŸ“', name: 'Certificado de RevisiÃ³n' },
                          permisoTransporte: { icon: 'ðŸš›', name: 'Permiso de Transporte' },
                          certificadoOperatividad: { icon: 'âœ…', name: 'Certificado de Operatividad' }
                        };
                        
                        return docTypes[docKey] || { 
                          icon: 'ðŸ“„', 
                          name: docKey.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase()) 
                        };
                      };
                      
                      const docInfo = getDocumentInfo(key);
                      
                      return (
                        <div key={key} style={{
                          border: '1px solid #e5e7eb',
                          borderRadius: '8px',
                          padding: '15px',
                          background: documento?.url ? '#f0fdf4' : 'white'
                        }}>
                          <h5 style={{ 
                            margin: '0 0 10px 0', 
                            fontSize: '14px', 
                            fontWeight: '600', 
                            color: '#374151' 
                          }}>
                            {docInfo.icon} {docInfo.name}
                          </h5>
                          {documento?.url ? (
                            <div>
                              <div style={{
                                padding: '8px',
                                background: '#f9fafb',
                                borderRadius: '6px',
                                marginBottom: '8px',
                                fontSize: '12px'
                              }}>
                                <div style={{ 
                                  fontWeight: '500', 
                                  color: '#1f2937',
                                  overflow: 'hidden',
                                  textOverflow: 'ellipsis',
                                  whiteSpace: 'nowrap' 
                                }}>
                                  {documento.nombre || 'Documento'}
                                </div>
                                {documento.fecha && (
                                  <div style={{ color: '#6b7280', fontSize: '11px', marginTop: '2px' }}>
                                    {new Date(documento.fecha).toLocaleDateString('es-CL')}
                                  </div>
                                )}
                                {documento.tamaÃ±o > 0 && (
                                  <div style={{ color: '#9ca3af', fontSize: '10px', marginTop: '2px' }}>
                                    {formatFileSize(documento.tamaÃ±o)}
                                  </div>
                                )}
                              </div>
                              <a 
                                href={documento.url}
                                target="_blank"
                                rel="noopener noreferrer"
                                style={{
                                  display: 'block',
                                  padding: '6px',
                                  background: '#3b82f6',
                                  color: 'white',
                                  textAlign: 'center',
                                  borderRadius: '6px',
                                  textDecoration: 'none',
                                  fontSize: '12px'
                                }}
                              >
                                ðŸ‘ï¸ Ver Documento
                              </a>
                            </div>
                          ) : (
                            <div style={{
                              padding: '20px',
                              background: '#f9fafb',
                              borderRadius: '6px',
                              textAlign: 'center',
                              color: '#6b7280',
                              fontSize: '12px'
                            }}>
                              Sin documento
                            </div>
                          )}
                        </div>
                      );
                    })}
                  </div>
                  
                  {/* Documentos adicionales si existen */}
                  {selectedDocuments.otros && selectedDocuments.otros.length > 0 && (
                    <div style={{
                      marginTop: '30px',
                      padding: '20px',
                      background: '#f9fafb',
                      borderRadius: '8px'
                    }}>
                      <h5 style={{ 
                        margin: '0 0 15px 0', 
                        fontSize: '16px', 
                        fontWeight: '600', 
                        color: '#374151' 
                      }}>
                        ðŸ“š Otros Documentos
                      </h5>
                      <div>
                        {selectedDocuments.otros.map((doc, index) => (
                          <div key={index} style={{
                            padding: '10px',
                            background: 'white',
                            borderRadius: '6px',
                            marginBottom: '8px',
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center',
                            border: '1px solid #e5e7eb'
                          }}>
                            <div style={{ flex: 1, minWidth: 0 }}>
                              <div style={{ 
                                fontWeight: '500', 
                                fontSize: '13px',
                                color: '#1f2937'
                              }}>
                                ðŸ“œ {doc.nombre || 'Documento adicional'}
                              </div>
                              <div style={{ 
                                color: '#6b7280', 
                                fontSize: '11px',
                                marginTop: '2px' 
                              }}>
                                {doc.tamaÃ±o && formatFileSize(doc.tamaÃ±o)} 
                                {doc.fecha && ` â€¢ ${new Date(doc.fecha).toLocaleDateString('es-CL')}`}
                              </div>
                            </div>
                            <a 
                              href={doc.url}
                              target="_blank"
                              rel="noopener noreferrer"
                              style={{
                                padding: '4px 8px',
                                background: '#3b82f6',
                                color: 'white',
                                borderRadius: '4px',
                                textDecoration: 'none',
                                fontSize: '11px',
                                marginLeft: '10px'
                              }}
                            >
                              Ver
                            </a>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              ) : (
                // Documentos de Trabajador
                <div>
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fit, minmax(280px, 1fr))',
                    gap: '20px',
                    marginBottom: '30px'
                  }}>
                    {/* Licencia de Conducir */}
                    <div style={{
                      border: '1px solid #e5e7eb',
                      borderRadius: '8px',
                      padding: '15px',
                      background: selectedDocuments.licenciaConducir?.url ? '#f0fdf4' : 'white'
                    }}>
                      <h5 style={{ 
                        margin: '0 0 10px 0', 
                        fontSize: '14px', 
                        fontWeight: '600', 
                        color: '#374151' 
                      }}>
                        ðŸš— Licencia de Conducir
                      </h5>
                      {selectedDocuments.licenciaConducir?.url ? (
                        <div>
                          <div style={{
                            padding: '8px',
                            background: '#f9fafb',
                            borderRadius: '6px',
                            marginBottom: '8px',
                            fontSize: '12px'
                          }}>
                            <div style={{ fontWeight: '500', color: '#1f2937' }}>
                              {selectedDocuments.licenciaConducir.nombre || 'Documento'}
                            </div>
                            {selectedDocuments.licenciaConducir.fecha && (
