                              <div style={{ marginTop: '10px', padding: '10px', background: '#f9fafb', borderRadius: '6px' }}>
                                <strong style={{ color: '#6b7280', fontSize: '12px' }}>ðŸ“ Observaciones:</strong>
                                <p style={{ margin: '5px 0 0 0', fontSize: '13px' }}>{vehiculo.observaciones}</p>
                              </div>
                            )}
                          </div>
                        </>
                      )}
                    </div>

                    {/* Botones de acciÃ³n */}
                    <div style={{ 
                      display: 'grid',
                      gridTemplateColumns: isMobile ? 'repeat(3, 1fr)' : 'repeat(3, 1fr)',
                      gap: '8px',
                      marginTop: '15px'
                    }}>
                      <button
                        onClick={() => toggleCardExpansion(vehiculo.id)}
                        style={{
                          padding: isMobile ? '6px' : '8px',
                          background: '#f3f4f6',
                          color: '#374151',
                          border: 'none',
                          borderRadius: '6px',
                          fontSize: isMobile ? '11px' : '13px',
                          cursor: 'pointer',
                          fontWeight: '500',
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis'
                        }}
                      >
                        {isExpanded ? 'âž–' : 'âž•'} {!isMobile && (isExpanded ? 'Menos' : 'MÃ¡s')}
                      </button>
                      <button
                        onClick={() => loadVehicleDocuments(vehiculo.id, vehiculo)}
                        style={{
                          padding: isMobile ? '6px' : '8px',
                          background: '#10b981',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          fontSize: isMobile ? '11px' : '13px',
                          cursor: 'pointer',
                          fontWeight: '500',
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis'
                        }}
                      >
                        ðŸ“„ {!isMobile && 'Docs'}
                      </button>
                      <button
                        onClick={() => openEditModal(vehiculo, 'vehicle')}
                        style={{
                          padding: isMobile ? '6px' : '8px',
                          background: '#3b82f6',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          fontSize: isMobile ? '11px' : '13px',
                          cursor: 'pointer',
                          fontWeight: '500',
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis'
                        }}
                      >
                        âœï¸ {!isMobile && 'Editar'}
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {/* Lista de Trabajadores */}
        {activeTab === 'workers' && (
          <div style={{
            display: 'grid',
            gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fill, minmax(350px, 1fr))',
            gap: '20px'
          }}>
            {filteredTrabajadores.map(trabajador => {
              const userData = users.find(u => u.id === trabajador.id);
              const vehiculo = vehiculos.find(v => v.operadorAsignado === trabajador.id);
              const isExpanded = expandedCards[trabajador.id];
              const isGPSActive = trabajador.ubicacion && 
                (new Date() - new Date(trabajador.ubicacion.timestamp)) < 60000;
              
              return (
                <div key={trabajador.id} style={{
                  background: 'white',
                  borderRadius: '12px',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
                  overflow: 'hidden',
                  transition: 'all 0.3s ease',
                  border: isGPSActive ? '2px solid #10b981' : '1px solid #e5e7eb'
                }}>
                  {/* Header de la tarjeta */}
                  <div style={{
                    padding: '15px 20px',
                    background: trabajador.estado === 'en_camino' ? '#ede9fe' :
                              trabajador.estado === 'trabajando' ? '#dcfce7' :
                              trabajador.estado === 'disponible' ? '#e0f2fe' : '#f3f4f6',
                    borderBottom: '1px solid #e5e7eb'
                  }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <h3 style={{ margin: 0, fontSize: '18px', fontWeight: '600', color: '#1f2937' }}>
                        ðŸ‘· {trabajador.nombre || userData?.name || 'Trabajador'}
                      </h3>
                      <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
                        {isGPSActive && (
                          <span style={{
                            width: '10px',
                            height: '10px',
                            borderRadius: '50%',
                            background: '#10b981',
                            animation: 'pulse 2s infinite'
                          }} title="GPS Activo" />
                        )}
                        <span style={{
                          padding: '4px 10px',
                          borderRadius: '20px',
                          fontSize: '12px',
                          fontWeight: '500',
                          background: 'white',
                          color: trabajador.estado === 'en_camino' ? '#6b21a8' :
                                 trabajador.estado === 'trabajando' ? '#15803d' :
                                 trabajador.estado === 'disponible' ? '#0891b2' : '#6b7280'
                        }}>
                          {trabajador.estado === 'en_camino' ? 'ðŸš— En Camino' :
                           trabajador.estado === 'trabajando' ? 'ðŸ”§ Trabajando' :
                           trabajador.estado === 'disponible' ? 'âœ… Disponible' : 'â¸ï¸ Inactivo'}
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Contenido de la tarjeta */}
                  <div style={{ padding: '20px' }}>
                    <div style={{ display: 'grid', gap: '10px', fontSize: '14px' }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ color: '#6b7280' }}>ðŸ“§ Email:</span>
                        <span style={{ fontWeight: '500', fontSize: '13px' }}>{userData?.email || 'N/A'}</span>
                      </div>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ color: '#6b7280' }}>ðŸ“± TelÃ©fono:</span>
                        <span style={{ fontWeight: '500' }}>{userData?.phone || trabajador.telefono || 'N/A'}</span>
                      </div>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ color: '#6b7280' }}>ðŸš› VehÃ­culo:</span>
                        <span style={{ fontWeight: '500' }}>{vehiculo?.nombre || 'Sin asignar'}</span>
                      </div>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ color: '#6b7280' }}>ðŸ“ GPS:</span>
                        <span style={{ 
                          fontWeight: '500',
                          color: isGPSActive ? '#10b981' : '#ef4444'
                        }}>
                          {isGPSActive ? 'ðŸŸ¢ Activo' : 'ðŸ”´ Inactivo'}
                        </span>
                      </div>

                      {/* InformaciÃ³n expandida */}
                      {isExpanded && (
                        <>
                          <div style={{ borderTop: '1px solid #e5e7eb', paddingTop: '10px', marginTop: '5px' }}>
                            {trabajador.zonaDestino && (
                              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                                <span style={{ color: '#6b7280' }}>ðŸŽ¯ Zona Destino:</span>
                                <span style={{ fontWeight: '500' }}>{trabajador.zonaDestino}</span>
                              </div>
                            )}
                            {trabajador.ubicacion && (
                              <>
                                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                                  <span style={{ color: '#6b7280' }}>ðŸ“ Ãšltima ubicaciÃ³n:</span>
                                  <span style={{ fontWeight: '500', fontSize: '12px' }}>
                                    {new Date(trabajador.ubicacion.timestamp).toLocaleTimeString('es-CL')}
                                  </span>
                                </div>
                                <div style={{
                                  marginTop: '10px',
                                  padding: '10px',
                                  background: '#f9fafb',
                                  borderRadius: '6px',
                                  fontSize: '12px',
                                  fontFamily: 'monospace'
                                }}>
                                  <div>Lat: {trabajador.ubicacion.lat.toFixed(6)}</div>
                                  <div>Lng: {trabajador.ubicacion.lng.toFixed(6)}</div>
                                </div>
                              </>
                            )}
                            {trabajador.ultimoTrabajo && (
                              <div style={{ marginTop: '10px', padding: '10px', background: '#f0f9ff', borderRadius: '6px' }}>
                                <strong style={{ color: '#0369a1', fontSize: '12px' }}>ðŸ“‹ Ãšltimo trabajo:</strong>
                                <p style={{ margin: '5px 0 0 0', fontSize: '13px' }}>
                                  {trabajador.ultimoTrabajo.zona} - {
                                    trabajador.ultimoTrabajo.completado ? 'âœ… Completado' : 'â³ En proceso'
                                  }
                                </p>
                              </div>
                            )}
                          </div>
                        </>
                      )}
                    </div>

                    {/* Botones de acciÃ³n */}
                    <div style={{ 
                      display: 'grid',
                      gridTemplateColumns: isMobile ? 'repeat(3, 1fr)' : 'repeat(3, 1fr)',
                      gap: '8px',
                      marginTop: '15px'
                    }}>
                      <button
                        onClick={() => toggleCardExpansion(trabajador.id)}
                        style={{
                          padding: isMobile ? '6px' : '8px',
                          background: '#f3f4f6',
                          color: '#374151',
                          border: 'none',
                          borderRadius: '6px',
                          fontSize: isMobile ? '11px' : '13px',
                          cursor: 'pointer',
                          fontWeight: '500',
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis'
                        }}
                      >
                        {isExpanded ? 'âž–' : 'âž•'} {!isMobile && (isExpanded ? 'Menos' : 'MÃ¡s')}
                      </button>
                      <button
                        onClick={() => loadWorkerDocuments(trabajador.id, trabajador)}
                        style={{
                          padding: isMobile ? '6px' : '8px',
                          background: '#10b981',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          fontSize: isMobile ? '11px' : '13px',
                          cursor: 'pointer',
                          fontWeight: '500',
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis'
                        }}
                      >
                        ðŸ“„ {!isMobile && 'Docs'}
                      </button>
                      <button
                        onClick={() => openEditModal(trabajador, 'worker')}
                        style={{
                          padding: isMobile ? '6px' : '8px',
                          background: '#3b82f6',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          fontSize: isMobile ? '11px' : '13px',
                          cursor: 'pointer',
                          fontWeight: '500',
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis'
                        }}
                      >
                        âœï¸ {!isMobile && 'Editar'}
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {/* No hay resultados */}
        {((activeTab === 'vehicles' && filteredVehiculos.length === 0) ||
          (activeTab === 'workers' && filteredTrabajadores.length === 0)) && (
          <div style={{
            background: 'white',
            borderRadius: '12px',
            padding: '60px 20px',
            textAlign: 'center',
            color: '#6b7280'
          }}>
            <div style={{ fontSize: '48px', marginBottom: '20px' }}>
              {activeTab === 'vehicles' ? 'ðŸš›' : 'ðŸ‘·'}
            </div>
            <p style={{ fontSize: '16px' }}>
              No se encontraron {activeTab === 'vehicles' ? 'vehÃ­culos' : 'trabajadores'} 
              {searchTerm && ` para "${searchTerm}"`}
              {filterStatus !== 'todos' && ` con estado "${filterStatus}"`}
            </p>
          </div>
        )}
      </div>

      {/* Modal de ediciÃ³n */}
      {showEditModal && editingItem && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.5)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 2000,
          padding: '20px'
        }}>
          <div style={{
            background: 'white',
            borderRadius: '12px',
            padding: isMobile ? '20px' : '30px',
            maxWidth: '500px',
            width: '100%',
            maxHeight: '80vh',
            overflow: 'auto'
          }}>
            <h2 style={{ margin: '0 0 20px 0', color: '#1f2937' }}>
              âœï¸ Editar {editingItem.type === 'vehicle' ? 'VehÃ­culo' : 'Trabajador'}
            </h2>

            {editingItem.type === 'vehicle' ? (
              <>
                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    Nombre
                  </label>
                  <input
                    type="text"
                    value={editFormData.nombre}
                    onChange={(e) => setEditFormData({ ...editFormData, nombre: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    Patente
                  </label>
                  <input
                    type="text"
                    value={editFormData.patente}
                    onChange={(e) => setEditFormData({ ...editFormData, patente: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    Kilometraje
                  </label>
                  <input
                    type="number"
                    value={editFormData.kilometraje}
                    onChange={(e) => setEditFormData({ ...editFormData, kilometraje: parseInt(e.target.value) })}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    Operador Asignado
                  </label>
                  <select
                    value={editFormData.operadorAsignado}
                    onChange={(e) => setEditFormData({ ...editFormData, operadorAsignado: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  >
                    <option value="">-- Sin asignar --</option>
                    {users.filter(u => u.role === 'trabajador').map(user => (
                      <option key={user.id} value={user.id}>
                        {user.name || user.email}
                      </option>
                    ))}
                  </select>
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    Observaciones
                  </label>
                  <textarea
                    value={editFormData.observaciones}
                    onChange={(e) => setEditFormData({ ...editFormData, observaciones: e.target.value })}
                    rows={3}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px',
                      resize: 'vertical'
                    }}
                  />
                </div>
              </>
            ) : (
              <>
                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    Nombre
                  </label>
                  <input
                    type="text"
                    value={editFormData.nombre}
                    onChange={(e) => setEditFormData({ ...editFormData, nombre: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    Email
                  </label>
                  <input
                    type="email"
                    value={editFormData.email}
                    disabled
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px',
                      background: '#f3f4f6',
                      cursor: 'not-allowed'
                    }}
                  />
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    TelÃ©fono
                  </label>
                  <input
                    type="tel"
                    value={editFormData.telefono}
                    onChange={(e) => setEditFormData({ ...editFormData, telefono: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', fontWeight: '500' }}>
                    VehÃ­culo Asignado
                  </label>
                  <select
                    value={editFormData.vehicleId}
                    onChange={(e) => setEditFormData({ ...editFormData, vehicleId: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  >
                    <option value="">-- Sin asignar --</option>
                    {vehiculos.filter(v => !v.operadorAsignado || v.operadorAsignado === editingItem.id).map(vehiculo => (
                      <option key={vehiculo.id} value={vehiculo.id}>
                        {vehiculo.nombre}
                      </option>
                    ))}
                  </select>
                </div>
              </>
            )}

            <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
              <button
                onClick={() => editingItem.type === 'vehicle' ? saveVehicleChanges() : saveWorkerChanges()}
                disabled={loading}
                style={{
                  flex: 1,
                  padding: '12px',
                  background: loading ? '#9ca3af' : '#22c55e',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  fontWeight: '500',
                  cursor: loading ? 'not-allowed' : 'pointer'
                }}
              >
                {loading ? 'Guardando...' : 'âœ… Guardar Cambios'}
              </button>
              <button
                onClick={() => {
                  setShowEditModal(false);
                  setEditingItem(null);
                }}
                style={{
                  flex: 1,
                  padding: '12px',
                  background: '#6b7280',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  fontWeight: '500',
                  cursor: 'pointer'
                }}
              >
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal de Documentos */}
      <DocumentsModal />

      <style jsx>{`
        @keyframes pulse {
          0% {
            box-shadow: 0 0 0 0 rgba(16, 185, 129, 0.7);
          }
          70% {
            box-shadow: 0 0 0 10px rgba(16, 185, 129, 0);
          }
          100% {
            box-shadow: 0 0 0 0 rgba(16, 185, 129, 0);
          }
        }
      `}</style>
    </div>
  );
};

export default FleetPanel;
