// src/components/PasswordRecovery.js
import React, { useState } from 'react';
import { auth, firestore } from '../config/firebase';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { signInWithEmailAndPassword, updatePassword } from 'firebase/auth';

const PasswordRecovery = ({ onClose, onSuccess }) => {
  const [step, setStep] = useState(1); // 1: Email/PIN, 2: Nueva contraseña
  const [formData, setFormData] = useState({
    email: '',
    pin: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [userData, setUserData] = useState(null);

  // Verificar email y PIN
  const handleVerifyPin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Buscar usuario por email
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', formData.email));
      const querySnapshot = await getDocs(q);

      if (querySnapshot.empty) {
        setMessage({ 
          type: 'error', 
          text: '❌ No existe un usuario con ese correo electrónico' 
        });
        setLoading(false);
        return;
      }

      const userDoc = querySnapshot.docs[0];
      const user = { id: userDoc.id, ...userDoc.data() };

      // Verificar PIN (decodificar de base64 y comparar)
      const storedPin = user.recoveryPin ? atob(user.recoveryPin) : null;
      
      if (!storedPin) {
        setMessage({ 
          type: 'error', 
          text: '❌ Este usuario no tiene PIN de recuperación configurado. Contacta al administrador.' 
        });
        setLoading(false);
        return;
      }

      if (formData.pin !== storedPin) {
        setMessage({ 
          type: 'error', 
          text: '❌ PIN incorrecto. Intenta nuevamente.' 
        });
        setLoading(false);
        return;
      }

      // PIN correcto, pasar al siguiente paso
      setUserData(user);
      setStep(2);
      setMessage({ 
        type: 'success', 
        text: '✅ PIN verificado correctamente. Ahora crea tu nueva contraseña.' 
      });

    } catch (error) {
      console.error('Error al verificar PIN:', error);
      setMessage({ 
        type: 'error', 
        text: '❌ Error al verificar los datos. Intenta nuevamente.' 
      });
    } finally {
      setLoading(false);
    }
  };

  // Actualizar contraseña
  const handleUpdatePassword = async (e) => {
    e.preventDefault();
    
    // Validar contraseñas
    if (formData.newPassword !== formData.confirmPassword) {
      setMessage({ 
        type: 'error', 
        text: '❌ Las contraseñas no coinciden' 
      });
      return;
    }

    if (formData.newPassword.length < 6) {
      setMessage({ 
        type: 'error', 
        text: '❌ La contraseña debe tener al menos 6 caracteres' 
      });
      return;
    }

    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Crear una contraseña temporal para hacer login
      // Esto es necesario porque Firebase requiere autenticación para cambiar la contraseña
      const tempPassword = Math.random().toString(36).slice(-12) + 'Temp123!';
      
      // Primero, el admin debe haber configurado una forma de resetear la contraseña
      // O usar un método alternativo con Firebase Admin SDK en el backend
      
      // OPCIÓN SIMPLIFICADA: Notificar al administrador
      setMessage({ 
        type: 'success', 
        text: '✅ Verificación exitosa. Tu nueva contraseña ha sido registrada. Un administrador la activará en breve.' 
      });

      // Guardar solicitud de cambio de contraseña en Firebase
      const passwordResetRef = collection(firestore, 'password_resets');
      await setDoc(doc(passwordResetRef), {
        userId: userData.id,
        email: userData.email,
        newPasswordHash: btoa(formData.newPassword), // Codificar temporalmente
        requestedAt: new Date().toISOString(),
        verified: true,
        pinVerified: true,
        status: 'pending'
      });

      setTimeout(() => {
        onSuccess();
      }, 2000);

    } catch (error) {
      console.error('Error al actualizar contraseña:', error);
      setMessage({ 
        type: 'error', 
        text: '❌ Error al actualizar la contraseña. Contacta al administrador.' 
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000,
      padding: '20px'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        padding: '30px',
        maxWidth: '400px',
        width: '100%',
        boxShadow: '0 10px 40px rgba(0,0,0,0.2)'
      }}>
        {/* Header */}
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '25px'
        }}>
          <h2 style={{ 
            margin: 0, 
            fontSize: '24px', 
            color: '#1f2937' 
          }}>
            🔐 Recuperar Contraseña
          </h2>
          <button
            onClick={onClose}
            style={{
              background: 'transparent',
              border: 'none',
              fontSize: '24px',
              cursor: 'pointer',
              color: '#6b7280'
            }}
          >
            ✖
          </button>
        </div>

        {/* Indicador de pasos */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          marginBottom: '25px',
          gap: '10px'
        }}>
          <div style={{
            width: '100px',
            height: '4px',
            background: step >= 1 ? '#3b82f6' : '#e5e7eb',
            borderRadius: '2px'
          }} />
          <div style={{
            width: '100px',
            height: '4px',
            background: step >= 2 ? '#3b82f6' : '#e5e7eb',
            borderRadius: '2px'
          }} />
        </div>

        {/* Mensajes */}
        {message.text && (
          <div style={{
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            background: message.type === 'success' ? '#dcfce7' : '#fee2e2',
            border: `1px solid ${message.type === 'success' ? '#86efac' : '#fecaca'}`,
            color: message.type === 'success' ? '#166534' : '#991b1b',
            fontSize: '14px'
          }}>
            {message.text}
          </div>
        )}

        {/* PASO 1: Verificar Email y PIN */}
        {step === 1 && (
          <form onSubmit={handleVerifyPin}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Correo Electrónico
              </label>
              <input
                type="email"
                required
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                placeholder="tu@correo.com"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px'
                }}
              />
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                PIN de Recuperación (4 dígitos)
              </label>
              <input
                type="text"
                required
                maxLength="4"
                pattern="[0-9]{4}"
                value={formData.pin}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, '');
                  setFormData({...formData, pin: value});
                }}
                placeholder="0000"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '20px',
                  textAlign: 'center',
                  letterSpacing: '10px'
                }}
              />
              <small style={{ 
                color: '#6b7280', 
                fontSize: '12px',
                display: 'block',
                marginTop: '5px'
              }}>
                Ingresa el PIN de 4 dígitos que configuraste al crear tu cuenta
              </small>
            </div>

            <button
              type="submit"
              disabled={loading || formData.pin.length !== 4}
              style={{
                width: '100%',
                padding: '12px',
                background: loading || formData.pin.length !== 4 ? '#9ca3af' : '#3b82f6',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: loading || formData.pin.length !== 4 ? 'not-allowed' : 'pointer'
              }}
            >
              {loading ? 'Verificando...' : 'Verificar PIN →'}
            </button>
          </form>
        )}

        {/* PASO 2: Nueva Contraseña */}
        {step === 2 && (
          <form onSubmit={handleUpdatePassword}>
            <div style={{
              marginBottom: '20px',
              padding: '15px',
              background: '#eff6ff',
              borderRadius: '8px',
              border: '1px solid #3b82f6'
            }}>
              <p style={{ 
                margin: 0, 
                fontSize: '14px', 
                color: '#1e40af' 
              }}>
                👤 Usuario verificado: <strong>{userData?.name}</strong>
              </p>
              <p style={{ 
                margin: '5px 0 0 0', 
                fontSize: '13px', 
                color: '#1e40af' 
              }}>
                📧 {userData?.email}
              </p>
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Nueva Contraseña
              </label>
              <input
                type="password"
                required
                minLength="6"
                value={formData.newPassword}
                onChange={(e) => setFormData({...formData, newPassword: e.target.value})}
                placeholder="Mínimo 6 caracteres"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px'
                }}
              />
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Confirmar Nueva Contraseña
              </label>
              <input
                type="password"
                required
                minLength="6"
                value={formData.confirmPassword}
                onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                placeholder="Repite la contraseña"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px'
                }}
              />
            </div>

            <div style={{ display: 'flex', gap: '10px' }}>
              <button
                type="button"
                onClick={() => {
                  setStep(1);
                  setFormData({...formData, newPassword: '', confirmPassword: ''});
                }}
                style={{
                  flex: 1,
                  padding: '12px',
                  background: '#6b7280',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '16px',
                  fontWeight: '600',
                  cursor: 'pointer'
                }}
              >
                ← Volver
              </button>
              <button
                type="submit"
                disabled={loading}
                style={{
                  flex: 1,
                  padding: '12px',
                  background: loading ? '#9ca3af' : '#22c55e',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '16px',
                  fontWeight: '600',
                  cursor: loading ? 'not-allowed' : 'pointer'
                }}
              >
                {loading ? 'Actualizando...' : '✅ Cambiar Contraseña'}
              </button>
            </div>
          </form>
        )}

        <div style={{
          marginTop: '20px',
          paddingTop: '20px',
          borderTop: '1px solid #e5e7eb',
          textAlign: 'center'
        }}>
          <p style={{ 
            margin: 0, 
            fontSize: '13px', 
            color: '#6b7280' 
          }}>
            ¿No recuerdas tu PIN? <br />
            <strong>Contacta al administrador</strong>
          </p>
        </div>
      </div>
    </div>
  );
};

export default PasswordRecovery;
