// src/components/MapaQuillonFirebase.js
import React, { useEffect, useRef, useState } from 'react';
import { vehiculosService, zonasService, trabajadoresService, reportesService } from '../services/firebaseservices';

const MapaQuillonFirebase = ({ currentUser, onLogout, onViewChange, currentView = 'dashboard' }) => {
  // Estados principales
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [vehiculos, setVehiculos] = useState([]);
  const [zonas, setZonas] = useState([]);
  const [trabajadores, setTrabajadores] = useState([]);
  const [stats, setStats] = useState({
    vehiculos: { total: 0, activos: 0, enMantenimiento: 0, enUso: 0 },
    zonas: { total: 0, activas: 0, completadas: 0, pendientes: 0 },
    trabajadores: { total: 0, enRuta: 0, trabajando: 0, disponibles: 0 }
  });
  
  // Estados UI
  const [selectedItem, setSelectedItem] = useState(null);
  const [filterType, setFilterType] = useState('all');
  const [showSidebar, setShowSidebar] = useState(true);
  const [showLegend, setShowLegend] = useState(true);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [isMobile, setIsMobile] = useState(false);
  const [activeTab, setActiveTab] = useState('vehiculos');
  const [showMobilePanel, setShowMobilePanel] = useState(false);
  const [centerOnUser, setCenterOnUser] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [hoveredItem, setHoveredItem] = useState(null);
  
  // Referencias para marcadores
  const markersRef = useRef({
    vehiculos: {},
    zonas: {},
    trabajadores: {}
  });
  
  const infoWindowRef = useRef(null);
  const API_KEY = 'AIzaSyA7DrdL36n5cx-RNzuXGAQggFeGCheHDbY';

  // Detectar si es m√≥vil
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
      if (window.innerWidth <= 768) {
        setShowSidebar(false);
      }
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Cargar Google Maps (mantener igual)
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve, reject) => {
        if (window.google && window.google.maps) {
          resolve(window.google.maps);
          return;
        }

        const existingScript = document.querySelector('script[src*="maps.googleapis.com"]');
        if (existingScript) {
          existingScript.addEventListener('load', () => {
            resolve(window.google.maps);
          });
          return;
        }

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${API_KEY}&libraries=places,geometry&v=weekly`;
        script.async = true;
        script.defer = true;
        script.onload = () => resolve(window.google.maps);
        script.onerror = reject;
        document.head.appendChild(script);
      });
    };

    const initMap = async () => {
      try {
        const maps = await loadGoogleMaps();
        
        if (!mapRef.current) return;

        const mapInstance = new maps.Map(mapRef.current, {
          center: { lat: -36.7333, lng: -72.4667 },
          zoom: 13,
          mapTypeId: 'roadmap',
          zoomControl: !isMobile,
          mapTypeControl: false,
          scaleControl: false,
          streetViewControl: false,
          rotateControl: false,
          fullscreenControl: true,
          gestureHandling: isMobile ? 'greedy' : 'auto',
          styles: [
            {
              featureType: "poi.business",
              stylers: [{ visibility: "off" }]
            },
            {
              featureType: "transit",
              elementType: "labels.icon",
              stylers: [{ visibility: "off" }]
            }
          ]
        });

        setMap(mapInstance);
        
        // Crear InfoWindow compartida
        infoWindowRef.current = new maps.InfoWindow();
        
        // Agregar bot√≥n de centrar en ubicaci√≥n actual
        const locationButton = document.createElement('button');
        locationButton.innerHTML = `
          <div style="
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            border-radius: 50%;
            width: 44px;
            height: 44px;
            display: flex;
            align-items: center;
            justify-content: center;
            box-shadow: 0 4px 12px rgba(102, 126, 234, 0.4);
            cursor: pointer;
            transition: all 0.3s ease;
          ">
            <span style="font-size: 22px;">üìç</span>
          </div>
        `;
        locationButton.style.cssText = 'border: none; background: none; padding: 0; margin: 10px;';
        locationButton.title = 'Mi ubicaci√≥n';
        
        mapInstance.controls[maps.ControlPosition.RIGHT_BOTTOM].push(locationButton);
        
        locationButton.addEventListener('click', () => {
          if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
              (position) => {
                const pos = {
                  lat: position.coords.latitude,
                  lng: position.coords.longitude
                };
                mapInstance.setCenter(pos);
                mapInstance.setZoom(15);
              },
              () => {
                console.log('Error al obtener ubicaci√≥n');
              }
            );
          }
        });
        
        setMapLoaded(true);
      } catch (err) {
        console.error('Error al cargar el mapa:', err);
      }
    };

    initMap();
  }, [isMobile]);

  // Cargar datos desde Firebase (mantener igual)
  useEffect(() => {
    const unsubVehiculos = vehiculosService.subscribeToVehiculos((data) => {
      setVehiculos(data);
      updateMarkers('vehiculos', data);
    });

    const unsubZonas = zonasService.subscribeToZonasAsignadas((data) => {
      setZonas(data);
      updateMarkers('zonas', data);
    });

    const unsubTrabajadores = trabajadoresService.subscribeToTrabajadores((data) => {
      setTrabajadores(data);
      updateMarkers('trabajadores', data);
    });

    reportesService.getRealtimeStats((statsData) => {
      setStats(statsData);
    });

    return () => {
      unsubVehiculos();
      unsubZonas();
      unsubTrabajadores();
    };
  }, []);

  // Actualizar marcadores (mantener igual)
  const updateMarkers = (type, data) => {
    if (!map || !window.google) return;

    Object.values(markersRef.current[type]).forEach(marker => marker.setMap(null));
    markersRef.current[type] = {};

    if (filterType !== 'all' && filterType !== type) return;

    data.forEach(item => {
      let position, icon, title, content;

      switch (type) {
        case 'vehiculos':
          if (!item.lat || !item.lng) return;
          position = { lat: item.lat, lng: item.lng };
          icon = {
            url: item.tipo === 'maquinaria' ? 
              'https://maps.google.com/mapfiles/kml/shapes/mechanic.png' :
              'https://maps.google.com/mapfiles/kml/shapes/cabs.png',
            scaledSize: new window.google.maps.Size(36, 36)
          };
          title = item.nombre;
          content = `
            <div style="padding: 12px; min-width: 250px;">
              <h3 style="margin: 0 0 10px 0; color: #1e293b;">
                ${item.tipo === 'maquinaria' ? 'üèóÔ∏è' : 'üöó'} ${item.nombre}
              </h3>
              <p style="margin: 5px 0;"><strong>Estado:</strong> ${item.estado}</p>
              <p style="margin: 5px 0;"><strong>Patente:</strong> ${item.patente || 'N/A'}</p>
              <p style="margin: 5px 0;"><strong>Operador:</strong> ${item.operadorAsignado || 'Sin asignar'}</p>
            </div>
          `;
          break;

        case 'zonas':
          if (!item.lat || !item.lng) return;
          position = { lat: item.lat, lng: item.lng };
          icon = {
            path: window.google.maps.SymbolPath.CIRCLE,
            scale: 14,
            fillColor: item.estado === 'completado' ? '#22c55e' : 
                     item.estado === 'en_progreso' ? '#3b82f6' : '#f59e0b',
            fillOpacity: 0.9,
            strokeColor: '#ffffff',
            strokeWeight: 3
          };
          title = item.nombre;
          content = `
            <div style="padding: 12px; min-width: 250px;">
              <h3 style="margin: 0 0 10px 0; color: #1e293b;">üèóÔ∏è ${item.nombre}</h3>
              <p style="margin: 5px 0;"><strong>Estado:</strong> ${item.estado}</p>
              <p style="margin: 5px 0;"><strong>Direcci√≥n:</strong> ${item.direccion}</p>
              <p style="margin: 5px 0;"><strong>Trabajador:</strong> ${item.trabajadorAsignado || 'Sin asignar'}</p>
            </div>
          `;
          break;

        case 'trabajadores':
          if (!item.ubicacion || !item.ubicacion.lat || !item.ubicacion.lng) return;
          position = { lat: item.ubicacion.lat, lng: item.ubicacion.lng };
          
          const lastUpdate = new Date(item.ubicacion.timestamp);
          const now = new Date();
          const diffMinutes = (now - lastUpdate) / 60000;
          if (diffMinutes > 5) return;
          
          icon = {
            path: window.google.maps.SymbolPath.FORWARD_CLOSED_ARROW,
            scale: 10,
            fillColor: item.estado === 'trabajando' ? '#22c55e' :
                       item.estado === 'en_camino' ? '#f59e0b' : '#6b7280',
            fillOpacity: 1,
            strokeColor: '#ffffff',
            strokeWeight: 3,
            rotation: 0
          };
          title = item.nombre;
          content = `
            <div style="padding: 12px; min-width: 250px;">
              <h3 style="margin: 0 0 10px 0; color: #1e293b;">üë∑ ${item.nombre}</h3>
              <p style="margin: 5px 0;"><strong>Estado:</strong> ${item.estado}</p>
              <p style="margin: 5px 0;"><strong>Zona:</strong> ${item.zonaDestino || 'Sin asignar'}</p>
              <p style="margin: 5px 0;"><strong>√öltima actualizaci√≥n:</strong> ${new Date(item.ubicacion.timestamp).toLocaleTimeString('es-CL')}</p>
            </div>
          `;
          break;

        default:
          return;
      }

      const marker = new window.google.maps.Marker({
        position,
        map,
        title,
        icon,
        animation: window.google.maps.Animation.DROP
      });

      marker.addListener('click', () => {
        infoWindowRef.current.setContent(content);
        infoWindowRef.current.open(map, marker);
        setSelectedItem(item);
      });

      markersRef.current[type][item.id] = marker;
    });
  };

  // Centrar mapa en item seleccionado
  const centerOnItem = (item, type) => {
    if (!map || !item) return;

    let position;
    switch (type) {
      case 'vehiculos':
      case 'zonas':
        if (item.lat && item.lng) {
          position = { lat: item.lat, lng: item.lng };
        }
        break;
      case 'trabajadores':
        if (item.ubicacion && item.ubicacion.lat && item.ubicacion.lng) {
          position = { lat: item.ubicacion.lat, lng: item.ubicacion.lng };
        }
        break;
    }

    if (position) {
      map.panTo(position);
      map.setZoom(16);
      
      const marker = markersRef.current[type][item.id];
      if (marker) {
        window.google.maps.event.trigger(marker, 'click');
      }
    }
  };

  // Cambiar filtro
  const handleFilterChange = (newFilter) => {
    setFilterType(newFilter);
    
    ['vehiculos', 'zonas', 'trabajadores'].forEach(type => {
      Object.values(markersRef.current[type]).forEach(marker => marker.setMap(null));
      markersRef.current[type] = {};
    });

    if (newFilter === 'all' || newFilter === 'vehiculos') {
      updateMarkers('vehiculos', vehiculos);
    }
    if (newFilter === 'all' || newFilter === 'zonas') {
      updateMarkers('zonas', zonas);
    }
    if (newFilter === 'all' || newFilter === 'trabajadores') {
      updateMarkers('trabajadores', trabajadores);
    }
  };

  // Filtrar items por b√∫squeda
  const filterBySearch = (items) => {
    if (!searchTerm) return items;
    return items.filter(item => {
      const searchLower = searchTerm.toLowerCase();
      return (
        item.nombre?.toLowerCase().includes(searchLower) ||
        item.patente?.toLowerCase().includes(searchLower) ||
        item.direccion?.toLowerCase().includes(searchLower) ||
        item.zonaDestino?.toLowerCase().includes(searchLower)
      );
    });
  };

  const styles = {
    container: {
      width: '100%',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      background: '#f8f9fa'
    },
    header: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      padding: isMobile ? '10px' : '16px 24px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.15)',
      zIndex: 1000,
      backdropFilter: 'blur(10px)'
    },
    headerContent: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      flexWrap: isMobile ? 'wrap' : 'nowrap',
      gap: '10px'
    },
    headerLeft: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    },
    title: {
      margin: 0,
      fontSize: isMobile ? '18px' : '24px',
      fontWeight: '700',
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      letterSpacing: '-0.5px'
    },
    navigationButtons: {
      display: 'flex',
      gap: '10px',
      alignItems: 'center',
      flexWrap: 'wrap'
    },
    navButton: {
      padding: isMobile ? '8px 12px' : '10px 18px',
      background: 'rgba(255,255,255,0.15)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      border: '1px solid rgba(255,255,255,0.25)',
      borderRadius: '10px',
      cursor: 'pointer',
      fontSize: isMobile ? '12px' : '14px',
      fontWeight: '500',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      display: 'flex',
      alignItems: 'center',
      gap: '6px'
    },
    logoutButton: {
      padding: isMobile ? '8px 12px' : '10px 18px',
      background: 'rgba(239, 68, 68, 0.9)',
      backdropFilter: 'blur(10px)',
      color: 'white',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer',
      fontSize: isMobile ? '12px' : '14px',
      fontWeight: '600',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)'
    },
    mainContent: {
      flex: 1,
      display: 'flex',
      position: 'relative',
      overflow: 'hidden'
    },
    sidebar: {
      width: showSidebar ? (isMobile ? '100%' : '380px') : '0', // CORRECCI√ìN: Ancho 0 cuando est√° oculto
      background: 'linear-gradient(180deg, #ffffff 0%, #f8f9fa 100%)',
      borderRight: showSidebar ? '1px solid rgba(0,0,0,0.08)' : 'none',
      overflowY: 'auto',
      overflowX: 'hidden', // CORRECCI√ìN: Ocultar overflow horizontal
      display: 'flex',
      flexDirection: 'column',
      position: isMobile ? 'absolute' : 'relative',
      height: '100%',
      zIndex: isMobile ? 999 : 1,
      boxShadow: showSidebar ? '4px 0 20px rgba(0,0,0,0.08)' : 'none',
      transition: 'width 0.4s cubic-bezier(0.4, 0, 0.2, 1), border 0.4s ease'
    },
    sidebarContent: { // NUEVO: Contenedor para el contenido del sidebar
      width: '380px', // Ancho fijo del contenido
      opacity: showSidebar ? 1 : 0,
      transition: 'opacity 0.3s ease',
      pointerEvents: showSidebar ? 'auto' : 'none'
    },
    sidebarHeader: {
      padding: isMobile ? '16px' : '20px',
      borderBottom: '1px solid rgba(0,0,0,0.06)',
      background: 'linear-gradient(135deg, #f8f9fa 0%, #ffffff 100%)'
    },
    searchBar: {
      width: '100%',
      padding: '12px 16px',
      border: '2px solid #e2e8f0',
      borderRadius: '12px',
      fontSize: '14px',
      outline: 'none',
      transition: 'all 0.3s ease',
      marginBottom: '16px',
      background: 'white'
    },
    filterButtons: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '10px',
      marginBottom: '16px'
    },
    filterButton: {
      padding: isMobile ? '10px' : '12px',
      borderRadius: '12px',
      border: '2px solid #e2e8f0',
      background: 'white',
      cursor: 'pointer',
      fontSize: isMobile ? '13px' : '14px',
      fontWeight: '500',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      textAlign: 'center',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '6px',
      color: '#64748b'
    },
    filterButtonActive: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      borderColor: 'transparent',
      transform: 'scale(1.02)',
      boxShadow: '0 4px 12px rgba(102, 126, 234, 0.3)'
    },
    statsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)', // CORRECCI√ìN: Siempre 2 columnas
      gap: '12px',
      marginTop: '16px'
    },
    statCard: {
      padding: '12px', // CORRECCI√ìN: Padding reducido
      background: 'white',
      borderRadius: '14px',
      border: '1px solid rgba(0,0,0,0.06)',
      textAlign: 'center',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      cursor: 'pointer'
    },
    statNumber: {
      fontSize: '20px', // CORRECCI√ìN: Tama√±o reducido
      fontWeight: '700',
      color: '#1e293b',
      marginBottom: '2px'
    },
    statLabel: {
      fontSize: '10px', // CORRECCI√ìN: Tama√±o reducido
      color: '#64748b',
      textTransform: 'uppercase',
      letterSpacing: '0.3px',
      fontWeight: '600'
    },
    tabs: {
      display: 'flex',
      borderBottom: '2px solid #f1f5f9',
      background: 'white',
      padding: '0 20px',
      gap: '8px',
      position: 'sticky',
      top: 0,
      zIndex: 10
    },
    tab: {
      flex: 1,
      padding: '14px 8px',
      background: 'transparent',
      border: 'none',
      borderBottom: '3px solid transparent',
      cursor: 'pointer',
      fontSize: isMobile ? '13px' : '14px',
      color: '#64748b',
      fontWeight: '500',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '6px',
      position: 'relative'
    },
    tabActive: {
      color: '#667eea',
      borderBottomColor: '#667eea',
      fontWeight: '600',
      background: 'linear-gradient(180deg, rgba(102, 126, 234, 0.05) 0%, transparent 100%)'
    },
    listContainer: {
      padding: '16px',
      flex: 1,
      overflowY: 'auto',
      overflowX: 'hidden' // CORRECCI√ìN: Evitar scroll horizontal
    },
    listItem: {
      padding: '14px', // CORRECCI√ìN: Padding reducido
      marginBottom: '10px',
      background: 'white',
      border: '1px solid rgba(0,0,0,0.06)',
      borderRadius: '12px',
      cursor: 'pointer',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      position: 'relative',
      overflow: 'hidden'
    },
    listItemHover: {
      transform: 'translateY(-2px) scale(1.01)',
      boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
      borderColor: '#667eea'
    },
    listItemHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '8px',
      gap: '8px'
    },
    listItemTitle: {
      fontSize: '14px', // CORRECCI√ìN: Tama√±o reducido
      fontWeight: '600',
      color: '#1e293b',
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      flex: 1,
      minWidth: 0,
      overflow: 'hidden',
      textOverflow: 'ellipsis',
      whiteSpace: 'nowrap'
    },
    listItemBadge: {
      padding: '3px 8px', // CORRECCI√ìN: Padding reducido
      borderRadius: '6px',
      fontSize: '10px', // CORRECCI√ìN: Tama√±o reducido
      fontWeight: '600',
      textTransform: 'uppercase',
      letterSpacing: '0.3px',
      flexShrink: 0
    },
    listItemDetails: {
      fontSize: '12px', // CORRECCI√ìN: Tama√±o reducido
      color: '#64748b',
      lineHeight: '1.5',
      display: 'flex',
      flexDirection: 'column',
      gap: '2px'
    },
    map: {
      flex: 1,
      position: 'relative'
    },
    toggleSidebarButton: {
      position: 'fixed',
      left: showSidebar ? '390px' : '10px', // CORRECCI√ìN: Posici√≥n correcta
      top: '50%',
      transform: 'translateY(-50%)',
      background: 'white',
      border: 'none',
      borderRadius: showSidebar ? '0 12px 12px 0' : '12px',
      padding: '16px 8px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      cursor: 'pointer',
      zIndex: 1002,
      transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    legend: {
      position: 'absolute',
      bottom: isMobile ? '60px' : '20px',
      left: '20px',
      background: 'white',
      borderRadius: '14px',
      padding: '16px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
      zIndex: 900,
      fontSize: isMobile ? '12px' : '13px',
      minWidth: '180px',
      backdropFilter: 'blur(10px)',
      background: 'rgba(255,255,255,0.95)',
      border: '1px solid rgba(0,0,0,0.06)',
      transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
    },
    legendToggleButton: { // NUEVO: Bot√≥n para mostrar/ocultar leyenda
      position: 'absolute',
      bottom: '20px',
      left: '20px',
      background: 'white',
      borderRadius: '12px',
      padding: '10px 14px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      cursor: 'pointer',
      zIndex: 901,
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      fontSize: '13px',
      fontWeight: '500',
      color: '#64748b',
      border: '1px solid rgba(0,0,0,0.06)',
      transition: 'all 0.3s ease'
    },
    legendTitle: {
      fontWeight: '700',
      marginBottom: '12px',
      color: '#1e293b',
      fontSize: '14px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    legendItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      marginBottom: '8px',
      padding: '4px 0'
    },
    legendIcon: {
      width: '14px',
      height: '14px',
      borderRadius: '50%',
      flexShrink: 0
    },
    mobileBottomNav: {
      position: 'fixed',
      bottom: 0,
      left: 0,
      right: 0,
      background: 'white',
      borderTop: '1px solid rgba(0,0,0,0.08)',
      display: 'flex',
      padding: '8px',
      gap: '8px',
      zIndex: 1000,
