                      onClick={() => centerOnItem(zona, 'zonas')}
                      onMouseEnter={() => setHoveredItem(zona.id)}
                      onMouseLeave={() => setHoveredItem(null)}
                    >
                      <div style={styles.listItemHeader}>
                        <span style={styles.listItemTitle}>
                          üèóÔ∏è {zona.nombre}
