          <button
            onClick={() => onViewChange('zones')}
            style={styles.mobileNavButton}
          >
            <span style={{ fontSize: '18px' }}>üó∫Ô∏è</span>
            <span>Zonas</span>
          </button>
          <button
            onClick={() => onViewChange('fleet')}
