// src/utils/pdfToImage.js
import * as pdfjsLib from 'pdfjs-dist/build/pdf';
import pdfjsWorker from 'pdfjs-dist/build/pdf.worker.entry';

pdfjsLib.GlobalWorkerOptions.workerSrc = pdfjsWorker;

export const convertPdfToImage = async (file) => {
  try {
    // Leer el PDF
    const arrayBuffer = await file.arrayBuffer();
    const pdf = await pdfjsLib.getDocument({ data: arrayBuffer }).promise;
    
    // Obtener la primera p치gina
    const page = await pdf.getPage(1);
    
    // Configurar el canvas
    const viewport = page.getViewport({ scale: 2.0 }); // Escala para mejor calidad
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    
    canvas.height = viewport.height;
    canvas.width = viewport.width;
    
    // Renderizar la p치gina en el canvas
    await page.render({
      canvasContext: context,
      viewport: viewport
    }).promise;
    
    // Convertir canvas a blob (imagen)
    return new Promise((resolve) => {
      canvas.toBlob((blob) => {
        // Crear un File desde el blob
        const imageFile = new File(
          [blob], 
          file.name.replace('.pdf', '.jpg'),
          { type: 'image/jpeg' }
        );
        resolve(imageFile);
      }, 'image/jpeg', 0.95); // 95% de calidad
    });
    
  } catch (error) {
    console.error('Error convirtiendo PDF:', error);
    throw error;
  }
};
