// src/components/MapaQuillonFirebase.js
import React, { useEffect, useRef, useState } from 'react';
import { vehiculosService, zonasService, trabajadoresService, reportesService } from '../services/firebaseservices';

const MapaQuillonFirebase = ({ currentUser, onLogout, onViewChange, currentView = 'dashboard' }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [vehiculos, setVehiculos] = useState([]);
  const [zonas, setZonas] = useState([]);
  const [trabajadores, setTrabajadores] = useState([]);
  const [selectedItemId, setSelectedItemId] = useState(null);
  const [selectedItemType, setSelectedItemType] = useState(null);
  const [filtroActivo, setFiltroActivo] = useState('todos');
  const [mapLoaded, setMapLoaded] = useState(false);
  const [showStats, setShowStats] = useState(true);
  const [isMobile, setIsMobile] = useState(false);
  const [realtimeStats, setRealtimeStats] = useState({
    vehiculos: { total: 0, activos: 0, enMantenimiento: 0 },
    zonas: { total: 0, activas: 0, completadas: 0, pendientes: 0 },
    trabajadores: { total: 0, enRuta: 0, trabajando: 0, disponibles: 0 }
  });
  const [statsInitialized, setStatsInitialized] = useState(false);
  
  const markersRef = useRef({});
  const infoWindowRef = useRef(null);
  const unsubscribesRef = useRef([]);
  const dataRef = useRef({ vehiculos: [], zonas: [], trabajadores: [] });
  
  const API_KEY = 'AIzaSyA7DrdL36n5cx-RNzuXGAQggFeGCheHDbY';

  // Detectar si es mÃ³vil
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Cargar Google Maps
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve, reject) => {
        if (window.google && window.google.maps) {
          resolve(window.google.maps);
          return;
        }

        const existingScript = document.querySelector('script[src*="maps.googleapis.com"]');
        if (existingScript) {
          existingScript.addEventListener('load', () => {
            if (window.google && window.google.maps) {
              resolve(window.google.maps);
            } else {
              reject(new Error('Google Maps no disponible'));
            }
          });
          return;
        }

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${API_KEY}&libraries=places,geometry&v=weekly`;
        script.async = true;
        script.defer = true;

        script.onload = () => {
          if (window.google && window.google.maps) {
            resolve(window.google.maps);
          }
        };

        script.onerror = () => reject(new Error('Error al cargar Google Maps'));
        document.head.appendChild(script);
      });
    };

    const initMap = async () => {
      try {
        const maps = await loadGoogleMaps();
        
        if (!mapRef.current) return;

        const mapInstance = new maps.Map(mapRef.current, {
          center: { lat: -36.7333, lng: -72.4667 },
          zoom: 13,
          mapTypeId: 'roadmap',
          styles: [
            {
              featureType: 'poi.business',
              stylers: [{ visibility: 'off' }]
            }
          ]
        });

        setMap(mapInstance);
        infoWindowRef.current = new maps.InfoWindow();
        
        mapInstance.addListener('click', () => {
          if (infoWindowRef.current) {
            infoWindowRef.current.close();
            setSelectedItemId(null);
            setSelectedItemType(null);
          }
        });
        
        setMapLoaded(true);
      } catch (err) {
        console.error('Error al cargar mapa:', err);
      }
    };

    initMap();
  }, []);

  // Calcular estadÃ­sticas locales de vehÃ­culos cuando cambien
  useEffect(() => {
    console.log('ğŸ”„ Recalculando estadÃ­sticas locales. VehÃ­culos:', vehiculos.length);
    
    if (vehiculos.length >= 0) { // Cambiar > a >= para incluir cuando es 0
      const disponibles = vehiculos.filter(v => v.estado === 'disponible').length;
      const enUso = vehiculos.filter(v => v.estado === 'en_uso').length;
      const enMantenimiento = vehiculos.filter(v => v.estado === 'mantenimiento').length;
      const activos = disponibles + enUso;
      
      console.log('ğŸ“Š CÃ¡lculo local de estadÃ­sticas:', {
        total: vehiculos.length,
        disponibles,
        enUso,
        activos,
        enMantenimiento,
        detalleVehiculos: vehiculos.map(v => ({
          id: v.id,
          nombre: v.nombre,
          estado: v.estado,
          esDisponible: v.estado === 'disponible',
          esEnUso: v.estado === 'en_uso'
        }))
      });
      
      // Actualizar estadÃ­sticas localmente
      setRealtimeStats(prev => {
        const nuevoEstado = {
          ...prev,
          vehiculos: {
            total: vehiculos.length,
            activos: activos,
            enMantenimiento: enMantenimiento
          }
        };
        console.log('ğŸ“Š Nuevo estado de estadÃ­sticas:', nuevoEstado);
        return nuevoEstado;
      });
      
      setStatsInitialized(true);
    }
  }, [vehiculos]);

  // Suscribirse a datos de Firebase
  useEffect(() => {
    console.log('ğŸ”„ Iniciando suscripciones a Firebase...');
    
    const unsubVehiculos = vehiculosService.subscribeToVehiculos((vehiculosData) => {
      console.log('ğŸš› VehÃ­culos recibidos desde Firebase:', vehiculosData);
      console.log('ğŸš› Tipos de datos:', vehiculosData.map(v => ({
        id: v.id,
        nombre: v.nombre,
        estado: v.estado,
        tipoEstado: typeof v.estado,
        estadoTrimmed: v.estado?.trim ? v.estado.trim() : v.estado
      })));
      
      // Limpiar y normalizar estados
      const vehiculosNormalizados = vehiculosData.map(v => ({
        ...v,
        estado: v.estado?.toLowerCase?.()?.trim?.() || 'disponible'
      }));
      
      dataRef.current.vehiculos = vehiculosNormalizados;
      setVehiculos(vehiculosNormalizados);
      console.log('ğŸš› VehÃ­culos normalizados y guardados:', vehiculosNormalizados.length);
    });
    unsubscribesRef.current.push(unsubVehiculos);

    const unsubZonas = zonasService.subscribeToZonasAsignadas ? 
      zonasService.subscribeToZonasAsignadas((zonasData) => {
        dataRef.current.zonas = zonasData;
        setZonas(zonasData);
        console.log('ğŸ—ï¸ Zonas asignadas actualizadas:', zonasData.length);
      }) :
      zonasService.subscribeToZonas((zonasData) => {
        dataRef.current.zonas = zonasData;
        setZonas(zonasData);
        console.log('ğŸ—ï¸ Zonas actualizadas:', zonasData.length);
      });
    unsubscribesRef.current.push(unsubZonas);

