      if (markersRef.current[key]) {
        const marker = markersRef.current[key];
        marker.setPosition(position);
        marker.setIcon(icon);
        marker.setTitle(title);
        
        if (marker.getAnimation() && !icon.fillColor.includes('#22c55e')) {
          marker.setAnimation(null);
        } else if (!marker.getAnimation() && icon.fillColor.includes('#22c55e')) {
          marker.setAnimation(window.google.maps.Animation.BOUNCE);
        }
      } else {
        const marker = new window.google.maps.Marker({
          position: position,
          map: map,
          title: title,
          icon: icon,
          animation: icon.fillColor && icon.fillColor.includes('#22c55e') ? 
                    window.google.maps.Animation.BOUNCE : null
