              <button
                onClick={() => {
                  console.log('ğŸ” DEPURACIÃ“N - Estado actual:');
                  console.log('ğŸ“Š realtimeStats:', realtimeStats);
                  console.log('ğŸš› vehiculos array:', vehiculos);
                  console.log('ğŸš› Estados de vehÃ­culos:', vehiculos.map(v => ({
                    nombre: v.nombre,
                    estado: v.estado,
                    tipo_estado: typeof v.estado
                  })));
                  const disponibles = vehiculos.filter(v => v.estado === 'disponible');
                  const enUso = vehiculos.filter(v => v.estado === 'en_uso');
                  const enMant = vehiculos.filter(v => v.estado === 'mantenimiento');
