                  console.log('ğŸ”§ En mantenimiento:', enMant.length, enMant);
                  console.log('âš¡ Total activos (calculado):', disponibles.length + enUso.length);
                  alert(`DepuraciÃ³n enviada a la consola. 
VehÃ­culos totales: ${vehiculos.length}
Disponibles: ${disponibles.length}
En uso: ${enUso.length}
Activos (Disp+Uso): ${disponibles.length + enUso.length}
En mantenimiento: ${enMant.length}

Revisa la consola (F12) para mÃ¡s detalles.`);
                }}
                style={{
                  background: '#3b82f6',
                  border: 'none',
                  color: 'white',
                  padding: '4px 8px',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '11px'
