// src/components/Login.js - DISE√ëO MODERNO Y LIMPIO
import React, { useState } from 'react';
import PasswordRecovery from './PasswordRecovery';
import {
  signInWithEmailAndPassword,
  updatePassword,
  EmailAuthProvider,
  reauthenticateWithCredential,
  createUserWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import { auth, firestore } from '../config/firebase';
import { doc, getDoc, updateDoc, query, where, collection, getDocs, setDoc } from 'firebase/firestore';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  // Estados para cambio de contrase√±a obligatorio
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [oldPassword, setOldPassword] = useState('');
  const [showPasswordRecovery, setShowPasswordRecovery] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // Paleta de colores moderna
  const colors = {
    primary: '#4F46E5',      // Indigo vibrante
    primaryHover: '#4338CA',  // Indigo m√°s oscuro
    secondary: '#F3F4F6',     // Gris claro para fondos
    text: '#111827',          // Casi negro para texto principal
    textSecondary: '#6B7280', // Gris para texto secundario
    border: '#E5E7EB',        // Gris para bordes
    error: '#EF4444',         // Rojo para errores
    success: '#10B981',       // Verde para √©xito
    white: '#FFFFFF',
    background: '#F9FAFB'     // Fondo muy claro
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccessMessage('');

    const normalizedEmail = email.toLowerCase().trim();

    try {
      // PRIMERO: Verificar si existe una contrase√±a temporal en Firestore
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', normalizedEmail));
      const querySnapshot = await getDocs(q);

      if (!querySnapshot.empty) {
        const userDoc = querySnapshot.docs[0];
        const userFirestoreData = userDoc.data();
        const userDocId = userDoc.id;

        // Verificar si hay contrase√±a temporal
        if (userFirestoreData.tempPassword) {
          try {
            const tempPasswordDecoded = atob(userFirestoreData.tempPassword);
            
            if (password === tempPasswordDecoded) {
              // Login exitoso con contrase√±a temporal
              await updateDoc(doc(firestore, 'users', userDocId), {
                lastLogin: new Date().toISOString()
              });
              
              if (userFirestoreData.requirePasswordChange === true) {
                setCurrentUser({
                  uid: userDocId,
                  email: userFirestoreData.email,
                  role: userFirestoreData.role,
                  name: userFirestoreData.name,
                  ...userFirestoreData,
                  isTempPasswordLogin: true
                });
                setOldPassword(password);
                setShowChangePassword(true);
                setLoading(false);
                return;
              } else {
                onLogin({
                  uid: userDocId,
                  email: userFirestoreData.email,
                  role: userFirestoreData.role,
                  name: userFirestoreData.name,
                  ...userFirestoreData
                });
                setLoading(false);
                return;
              }
            }
          } catch (e) {
            // Error decodificando contrase√±a temporal
          }
        }
      }

      // SEGUNDO: Intentar login normal con Firebase Auth
      try {
        const userCredential = await signInWithEmailAndPassword(auth, normalizedEmail, password);
        const user = userCredential.user;

        const userDoc = await getDoc(doc(firestore, 'users', user.uid));

        if (userDoc.exists()) {
          const userData = userDoc.data();

          if (userData.requirePasswordChange || userData.firstLogin !== false) {
            if (userData.tempPassword) {
              await updateDoc(doc(firestore, 'users', user.uid), {
                tempPassword: null,
                lastTempPassword: userData.tempPassword
              });
            }

            setCurrentUser({
              uid: user.uid,
              email: user.email,
              role: userData.role,
              name: userData.name,
              ...userData
            });
            setOldPassword(password);
            setShowChangePassword(true);
          } else {
            await updateDoc(doc(firestore, 'users', user.uid), {
              lastLogin: new Date().toISOString()
            });

            onLogin({
              uid: user.uid,
              email: user.email,
              role: userData.role,
              name: userData.name,
              ...userData
            });
          }
        } else {
          setError('No se encontraron datos del usuario');
        }
      } catch (authError) {
        // Manejo de errores de autenticaci√≥n con fallback
        if (authError.code === 'auth/invalid-credential' || authError.code === 'auth/wrong-password') {
          const usersRef = collection(firestore, 'users');
          const q = query(usersRef, where('email', '==', normalizedEmail));
          const querySnapshot = await getDocs(q);
          
          if (!querySnapshot.empty) {
            const userDoc = querySnapshot.docs[0];
            const userFirestoreData = userDoc.data();
            const userDocId = userDoc.id;
            
            if (userFirestoreData.tempPassword) {
              try {
                const storedPassword = atob(userFirestoreData.tempPassword);
                
                if (password === storedPassword) {
                  if (userFirestoreData.needsAuthSync === true) {
                    try {
                      await createUserWithEmailAndPassword(auth, normalizedEmail, password);
                      const firebaseUser = auth.currentUser;
                      
                      if (firebaseUser && firebaseUser.uid !== userDocId) {
                        await setDoc(doc(firestore, 'users', firebaseUser.uid), {
                          ...userFirestoreData,
                          tempPassword: null,
                          needsAuthSync: false,
                          authPasswordSynced: true,
                          requirePasswordChange: false,
                          lastLogin: new Date().toISOString()
                        });
                        
                        await signOut(auth);
                        
                        onLogin({
                          uid: firebaseUser.uid,
                          email: userFirestoreData.email,
                          role: userFirestoreData.role,
                          name: userFirestoreData.name,
                          ...userFirestoreData,
                          requirePasswordChange: false
                        });
                      } else {
                        await updateDoc(doc(firestore, 'users', userDocId), {
                          tempPassword: null,
                          needsAuthSync: false,
                          authPasswordSynced: true,
                          requirePasswordChange: false,
                          lastLogin: new Date().toISOString()
                        });
                        
                        if (auth.currentUser) {
                          await signOut(auth);
                        }
                        
                        onLogin({
                          uid: userDocId,
                          email: userFirestoreData.email,
                          role: userFirestoreData.role,
                          name: userFirestoreData.name,
                          ...userFirestoreData,
                          requirePasswordChange: false
                        });
                      }
                    } catch (syncError) {
                      await updateDoc(doc(firestore, 'users', userDocId), {
                        lastLogin: new Date().toISOString()
                      });
                      
                      onLogin({
                        uid: userDocId,
                        email: userFirestoreData.email,
                        role: userFirestoreData.role,
                        name: userFirestoreData.name,
                        ...userFirestoreData,
                        requirePasswordChange: false
                      });
                    }
                  } else {
                    await updateDoc(doc(firestore, 'users', userDocId), {
                      lastLogin: new Date().toISOString()
                    });
                    
                    onLogin({
                      uid: userDocId,
                      email: userFirestoreData.email,
                      role: userFirestoreData.role,
                      name: userFirestoreData.name,
                      ...userFirestoreData,
                      requirePasswordChange: false
                    });
                  }
                  
                  setLoading(false);
                  return;
                }
              } catch (e) {
                // Error verificando contrase√±a de fallback
              }
            }
          }
        }
        
        throw authError;
      }
    } catch (error) {
      switch (error.code) {
        case 'auth/user-not-found':
          setError('Usuario no encontrado. Verifica tu email');
          break;
        case 'auth/wrong-password':
          setError('Contrase√±a incorrecta');
          break;
        case 'auth/invalid-email':
          setError('Email inv√°lido');
          break;
        case 'auth/too-many-requests':
          setError('Demasiados intentos. Intenta m√°s tarde');
          break;
        case 'auth/invalid-credential':
          setError('Credenciales inv√°lidas. Verifica tu email y contrase√±a');
          break;
        case 'auth/network-request-failed':
          setError('Error de conexi√≥n. Verifica tu internet');
          break;
        default:
          setError('Error al iniciar sesi√≥n. Intenta nuevamente');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleChangePassword = async (e) => {
    e.preventDefault();

    if (newPassword.length < 6) {
      setError('La contrase√±a debe tener al menos 6 caracteres');
      return;
    }

    if (newPassword !== confirmPassword) {
      setError('Las contrase√±as no coinciden');
      return;
    }

    if (newPassword === oldPassword) {
      setError('La nueva contrase√±a debe ser diferente a la anterior');
      return;
    }

    const hasNumber = /\d/.test(newPassword);
    const hasLetter = /[a-zA-Z]/.test(newPassword);
    
    if (!hasNumber || !hasLetter) {
      setError('La contrase√±a debe contener al menos una letra y un n√∫mero');
      return;
    }

    setLoading(true);
    setError('');

    try {
      if (currentUser.isTempPasswordLogin) {
        let authUpdated = false;
        
        try {
          await createUserWithEmailAndPassword(auth, currentUser.email, newPassword);
          authUpdated = true;
          
          const firebaseUser = auth.currentUser;
          if (firebaseUser) {
            if (currentUser.uid !== firebaseUser.uid) {
              const oldUserDoc = await getDoc(doc(firestore, 'users', currentUser.uid));
              const oldUserData = oldUserDoc.exists() ? oldUserDoc.data() : {};
              
              await setDoc(doc(firestore, 'users', firebaseUser.uid), {
                ...oldUserData,
                email: currentUser.email,
                requirePasswordChange: false,
                firstLogin: false,
                passwordChangedAt: new Date().toISOString(),
                tempPassword: null,
                lastTempPassword: null,
                resetMethod: null,
                authPasswordSynced: true
              });
              
              currentUser.uid = firebaseUser.uid;
            } else {
              await updateDoc(doc(firestore, 'users', currentUser.uid), {
                requirePasswordChange: false,
                firstLogin: false,
                passwordChangedAt: new Date().toISOString(),
                tempPassword: null,
                lastTempPassword: null,
                resetMethod: null,
                authPasswordSynced: true
              });
            }
            
            await signOut(auth);
          }
        } catch (createError) {
          if (createError.code === 'auth/email-already-in-use') {
            if (currentUser.lastTempPassword) {
              try {
                const lastTemp = atob(currentUser.lastTempPassword);
                await signInWithEmailAndPassword(auth, currentUser.email, lastTemp);
                await updatePassword(auth.currentUser, newPassword);
                authUpdated = true;
                await signOut(auth);
              } catch (e) {
                // No funcion√≥ con √∫ltima temporal
              }
            }
            
            if (!authUpdated) {
              try {
                await signInWithEmailAndPassword(auth, currentUser.email, oldPassword);
                await updatePassword(auth.currentUser, newPassword);
                authUpdated = true;
                await signOut(auth);
              } catch (e) {
                // No funcion√≥ con temporal actual
              }
            }
            
            if (!authUpdated) {
              await updateDoc(doc(firestore, 'users', currentUser.uid), {
                tempPassword: btoa(newPassword),
                requirePasswordChange: false,
                firstLogin: false,
                passwordChangedAt: new Date().toISOString(),
                lastTempPassword: btoa(oldPassword),
                resetMethod: 'FALLBACK_FIRESTORE',
                authPasswordSynced: false,
                needsAuthSync: true
              });
              
              setSuccessMessage('‚úÖ Contrase√±a actualizada correctamente. Redirigiendo...');
              
              setTimeout(() => {
                window.location.reload();
              }, 3000);
              
              return;
            }
          } else {
            throw createError;
          }
        }

        if (authUpdated) {
          const userDocRef = doc(firestore, 'users', currentUser.uid);
          const userDocSnap = await getDoc(userDocRef);
          
          if (userDocSnap.exists()) {
            await updateDoc(userDocRef, {
              requirePasswordChange: false,
              firstLogin: false,
              passwordChangedAt: new Date().toISOString(),
              tempPassword: null,
              lastTempPassword: null,
              resetMethod: null,
              authPasswordSynced: true,
              needsAuthSync: false
            });
          } else {
            await setDoc(userDocRef, {
              email: currentUser.email,
              role: currentUser.role || 'worker',
              name: currentUser.name || '',
              requirePasswordChange: false,
              firstLogin: false,
              passwordChangedAt: new Date().toISOString(),
              tempPassword: null,
              lastTempPassword: null,
              resetMethod: null,
              authPasswordSynced: true,
              needsAuthSync: false
            });
          }

          setSuccessMessage('‚úÖ Contrase√±a actualizada exitosamente');
          
          setTimeout(async () => {
            try {
              if (auth.currentUser) {
                await signOut(auth);
                await new Promise(resolve => setTimeout(resolve, 500));
              }
              
              const userCredential = await signInWithEmailAndPassword(auth, currentUser.email, newPassword);
              const user = userCredential.user;
              
              const userDocRef = doc(firestore, 'users', user.uid);
              const userDocSnap = await getDoc(userDocRef);
              
              if (userDocSnap.exists()) {
                const userData = userDocSnap.data();
                onLogin({
                  uid: user.uid,
                  email: user.email,
                  role: userData.role,
                  name: userData.name,
                  ...userData,
                  requirePasswordChange: false,
                  firstLogin: false,
                  isTempPasswordLogin: false
                });
              } else {
                onLogin({
                  ...currentUser,
                  uid: user.uid,
                  requirePasswordChange: false,
                  firstLogin: false,
                  isTempPasswordLogin: false
                });
              }
            } catch (error) {
              onLogin({
                ...currentUser,
                requirePasswordChange: false,
                firstLogin: false,
                isTempPasswordLogin: false
              });
            }
          }, 2000);
        }
      } else {
        const user = auth.currentUser;

        if (user) {
          await updatePassword(user, newPassword);

          await updateDoc(doc(firestore, 'users', user.uid), {
            requirePasswordChange: false,
            firstLogin: false,
            passwordChangedAt: new Date().toISOString(),
            tempPassword: null,
            lastTempPassword: null,
            resetMethod: null
          });

          setSuccessMessage('‚úÖ Contrase√±a actualizada exitosamente');
          
          setTimeout(() => {
            onLogin({
              ...currentUser,
              requirePasswordChange: false,
              firstLogin: false
            });
          }, 2000);
        } else {
          setError('Error de autenticaci√≥n. Por favor, vuelve a iniciar sesi√≥n');
        }
      }
    } catch (error) {
      switch (error.code) {
        case 'auth/weak-password':
          setError('La contrase√±a es muy d√©bil. Usa al menos 6 caracteres con letras y n√∫meros');
          break;
        case 'auth/requires-recent-login':
          setError('Por seguridad, vuelve a iniciar sesi√≥n');
          break;
        case 'auth/network-request-failed':
          setError('Error de conexi√≥n. Verifica tu internet');
          break;
        default:
          setError('Error al cambiar la contrase√±a. Intenta nuevamente');
      }
    } finally {
      setLoading(false);
    }
  };

  // Formulario de cambio de contrase√±a obligatorio
  if (showChangePassword) {
    return (
      <div style={{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: colors.background,
        fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif'
      }}>
        <div style={{
          backgroundColor: colors.white,
          padding: '48px',
          borderRadius: '16px',
          boxShadow: '0 10px 25px rgba(0,0,0,0.08)',
          maxWidth: '420px',
          width: '90%'
        }}>
          <div style={{ textAlign: 'center', marginBottom: '32px' }}>
            <div style={{
              width: '64px',
              height: '64px',
              backgroundColor: colors.primary,
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              margin: '0 auto 20px',
              fontSize: '28px'
            }}>
              üîê
            </div>
            <h2 style={{
              color: colors.text,
              margin: '0 0 8px 0',
              fontSize: '24px',
              fontWeight: '600'
            }}>
              Actualiza tu contrase√±a
            </h2>
            <p style={{
              color: colors.textSecondary,
              margin: 0,
              fontSize: '14px'
            }}>
              Por tu seguridad, crea una nueva contrase√±a
            </p>
          </div>

          <form onSubmit={handleChangePassword}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: colors.text,
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Nueva Contrase√±a
              </label>
              <div style={{ position: 'relative' }}>
                <input
                  type={showNewPassword ? 'text' : 'password'}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  required
                  placeholder="M√≠nimo 6 caracteres"
                  style={{
                    width: '100%',
                    padding: '12px',
                    paddingRight: '40px',
                    border: `1px solid ${colors.border}`,
                    borderRadius: '10px',
                    fontSize: '14px',
                    outline: 'none',
                    transition: 'all 0.2s',
                    backgroundColor: colors.white
                  }}
                  onFocus={(e) => e.target.style.borderColor = colors.primary}
                  onBlur={(e) => e.target.style.borderColor = colors.border}
                />
                <button
                  type="button"
                  onClick={() => setShowNewPassword(!showNewPassword)}
                  style={{
                    position: 'absolute',
                    right: '12px',
                    top: '50%',
                    transform: 'translateY(-50%)',
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer',
                    color: colors.textSecondary,
                    fontSize: '18px'
                  }}
                >
                  {showNewPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
            </div>

            <div style={{ marginBottom: '24px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: colors.text,
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Confirmar Contrase√±a
