// src/components/Login.js
import React, { useState } from 'react';
import PasswordRecovery from './PasswordRecovery';
import {
  signInWithEmailAndPassword,
  sendPasswordResetEmail,
  updatePassword,
  reauthenticateWithCredential,
  EmailAuthProvider,
  createUserWithEmailAndPassword  // ← IMPORT AGREGADO
} from 'firebase/auth';
import { auth, firestore } from '../config/firebase';
import { doc, getDoc, updateDoc, query, where, collection, getDocs } from 'firebase/firestore';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Estados para cambio de contraseña obligatorio
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [oldPassword, setOldPassword] = useState('');
  const [showPasswordRecovery, setShowPasswordRecovery] = useState(false);

  // Estados para recuperación con PIN
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [resetEmail, setResetEmail] = useState('');
  const [recoveryPin, setRecoveryPin] = useState('');
  const [resetNewPassword, setResetNewPassword] = useState('');
  const [resetConfirmPassword, setResetConfirmPassword] = useState('');
  const [userIdForReset, setUserIdForReset] = useState(null);
  const [step, setStep] = useState(1); // 1: email y pin, 2: nueva contraseña

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccessMessage('');

    try {
      // Primero verificar si el usuario tiene una contraseña temporal
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', email));
      const querySnapshot = await getDocs(q);

      if (!querySnapshot.empty) {
        const userDoc = querySnapshot.docs[0];
        const userData = userDoc.data();

        // Verificar si hay contraseña temporal (recuperada con PIN)
        if (userData.tempPassword) {
          const tempPasswordDecoded = atob(userData.tempPassword);

          if (password === tempPasswordDecoded) {
            // Login exitoso con contraseña temporal
            // Crear una sesión temporal
            const userInfo = {
              uid: userDoc.id,
              email: userData.email,
              role: userData.role,
              name: userData.name,
              ...userData
            };

            // Limpiar la contraseña temporal y marcar para cambio obligatorio
            await updateDoc(doc(firestore, 'users', userDoc.id), {
              tempPassword: null,
              requirePasswordChange: true,
              lastLogin: new Date().toISOString()
            });

            // Mostrar formulario de cambio de contraseña obligatorio
            setCurrentUser(userInfo);
            setOldPassword(password);
            setShowChangePassword(true);
            setLoading(false);
            return;
          }
        }
      }

      // Si no hay contraseña temporal, intentar login normal con Firebase Auth
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Obtener datos del usuario desde Firestore
      const userDoc = await getDoc(doc(firestore, 'users', user.uid));

      if (userDoc.exists()) {
        const userData = userDoc.data();

        // Verificar si es el primer login o si debe cambiar contraseña
        if (userData.requirePasswordChange || userData.firstLogin !== false) {
          setCurrentUser({
            uid: user.uid,
            email: user.email,
            role: userData.role,
            name: userData.name,
            ...userData
          });
          setOldPassword(password);
          setShowChangePassword(true);
          setLoading(false);
          return;
        }

        // Login normal
        onLogin({
          uid: user.uid,
          email: user.email,
          role: userData.role,
          name: userData.name,
          ...userData
        });
      } else {
        setError('No se encontraron datos del usuario');
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error);

      // Mensajes de error en español
      switch (error.code) {
        case 'auth/user-not-found':
          setError('Usuario no encontrado');
          break;
        case 'auth/wrong-password':
          setError('Contraseña incorrecta');
          break;
        case 'auth/invalid-email':
          setError('Email inválido');
          break;
        case 'auth/too-many-requests':
          setError('Demasiados intentos. Intenta más tarde');
          break;
        case 'auth/invalid-credential':
          setError('Credenciales inválidas. Verifica tu email y contraseña');
          break;
        default:
          setError('Error al iniciar sesión: ' + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleChangePassword = async (e) => {
  e.preventDefault();
  
  // Validaciones existentes...
  if (newPassword.length < 6) {
    setError('La contraseña debe tener al menos 6 caracteres');
    return;
  }
  
  if (newPassword !== confirmPassword) {
    setError('Las contraseñas no coinciden');
    return;
  }
  
  if (newPassword === oldPassword) {
    setError('La nueva contraseña debe ser diferente a la anterior');
    return;
  }
  
  setLoading(true);
  setError('');
  
  try {
    const user = auth.currentUser;
    
    if (user) {
      // Usuario autenticado con Firebase Auth
      const credential = EmailAuthProvider.credential(user.email, oldPassword);
      await reauthenticateWithCredential(user, credential);
      await updatePassword(user, newPassword);
      
      // Actualizar en Firestore
      await updateDoc(doc(firestore, 'users', user.uid), {
        requirePasswordChange: false,
        firstLogin: false,
        passwordChangedAt: new Date().toISOString(),
        tempPassword: null,
        resetMethod: null
      });
      
      setSuccessMessage('✅ Contraseña actualizada exitosamente');
    } else {
      // Usuario con contraseña temporal - necesita crear cuenta en Auth
      setError('Por favor, usa el enlace de recuperación enviado a tu correo para establecer tu contraseña definitiva');
      return;
    }
    
    setTimeout(() => {
      onLogin(currentUser);
    }, 2000);
    
  } catch (error) {
    console.error('Error al cambiar contraseña:', error);
    
    switch (error.code) {
      case 'auth/weak-password':
        setError('La contraseña es muy débil');
        break;
      case 'auth/requires-recent-login':
        setError('Por seguridad, vuelve a iniciar sesión');
        break;
      case 'auth/wrong-password':
        setError('La contraseña actual es incorrecta');
        break;
      default:
        setError('Error al cambiar la contraseña');
    }
  } finally {
    setLoading(false);
  }
};

  // Función para verificar email y PIN
  const handleVerifyPinAndEmail = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Buscar usuario por email
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', resetEmail));
      const querySnapshot = await getDocs(q);

      if (querySnapshot.empty) {
        setError('No existe una cuenta con este correo');
        setLoading(false);
        return;
      }

      // Verificar PIN
      const userData = querySnapshot.docs[0].data();
      const userId = querySnapshot.docs[0].id;
      const storedPin = userData.recoveryPin;

      // Verificar si el usuario tiene PIN configurado
      if (!storedPin) {
        setError('Este usuario no tiene PIN configurado. Contacta al administrador.');
        setLoading(false);
        return;
      }

      // Comparar PIN (simple comparación, en producción deberías usar hash)
      if (recoveryPin !== storedPin && btoa(recoveryPin) !== storedPin) {
        setError('PIN incorrecto. Intenta nuevamente.');
        setLoading(false);
        return;
      }

      // PIN correcto, pasar al siguiente paso
      setUserIdForReset(userId);
      setStep(2);
      setSuccessMessage('✅ PIN verificado. Ahora crea tu nueva contraseña.');
      setError('');

    } catch (error) {
      console.error('Error:', error);
      setError('Error al verificar los datos');
    } finally {
      setLoading(false);
    }
  };

  // Función para actualizar contraseña con PIN
  const handleResetPasswordWithPin = async (e) => {
    e.preventDefault();

    if (resetNewPassword.length < 6) {
      setError('La contraseña debe tener al menos 6 caracteres');
      return;
    }

    if (resetNewPassword !== resetConfirmPassword) {
      setError('Las contraseñas no coinciden');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // NOTA: Esta es una solución temporal. 
      // En producción, deberías usar una Cloud Function para cambiar la contraseña

      // Guardar la nueva contraseña en Firestore (temporalmente)
      await updateDoc(doc(firestore, 'users', userIdForReset), {
        tempPassword: btoa(resetNewPassword), // Contraseña temporal codificada
        requirePasswordReset: true,
        passwordResetWithPin: true,
        passwordResetAt: new Date().toISOString(),
        resetRequestedBy: 'PIN_RECOVERY'
      });

      setSuccessMessage('✅ Solicitud de cambio procesada. Un administrador completará el cambio pronto.');

      // Mensaje informativo
      setTimeout(() => {
        alert(
          '📢 IMPORTANTE:\n\n' +
          'Tu solicitud de cambio de contraseña ha sido registrada.\n\n' +
          'Un administrador del sistema completará el proceso dentro de las próximas 24 horas.\n\n' +
          'Recibirás una confirmación cuando puedas usar tu nueva contraseña.\n\n' +
          'Si es urgente, contacta al administrador:\n' +
          '📞 WhatsApp: +56 9 1234 5678\n' +
          '📧 Email: admin@municipalidadquillon.cl'
        );
      }, 1000);

      // Limpiar y volver al login
      setTimeout(() => {
        setShowForgotPassword(false);
        setStep(1);
        setResetEmail('');
        setRecoveryPin('');
        setResetNewPassword('');
        setResetConfirmPassword('');
        setUserIdForReset(null);
        setSuccessMessage('');
        setError('');
      }, 5000);

    } catch (error) {
      console.error('Error:', error);
      setError('Error al procesar la solicitud');
    } finally {
      setLoading(false);
    }
  };

  // Formulario de cambio de contraseña obligatorio (primer login)
  if (showChangePassword) {
    return (
      <div style={{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        fontFamily: 'Arial, sans-serif'
      }}>
        <div style={{
          background: 'white',
          padding: '40px',
          borderRadius: '20px',
          boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
          maxWidth: '400px',
          width: '90%'
        }}>
          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <div style={{ fontSize: '48px', marginBottom: '10px' }}>🔐</div>
            <h2 style={{
              color: '#1f2937',
              margin: '0 0 10px 0',
              fontSize: '24px',
              fontWeight: '700'
            }}>
              Cambio de Contraseña Obligatorio
            </h2>
            <p style={{
              color: '#6b7280',
              margin: 0,
              fontSize: '14px'
            }}>
              Por seguridad, debes cambiar tu contraseña en el primer inicio de sesión
            </p>
          </div>

          <form onSubmit={handleChangePassword}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Nueva Contraseña
              </label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
                placeholder="Mínimo 6 caracteres"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '14px',
                  outline: 'none',
                  transition: 'border-color 0.2s'
                }}
                onFocus={(e) => e.target.style.borderColor = '#667eea'}
                onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
              />
            </div>

            <div style={{ marginBottom: '24px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Confirmar Nueva Contraseña
              </label>
              <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
                placeholder="Repite la contraseña"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '14px',
                  outline: 'none',
                  transition: 'border-color 0.2s'
                }}
                onFocus={(e) => e.target.style.borderColor = '#667eea'}
                onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
              />
            </div>

            {/* Mensajes */}
            {error && (
              <div style={{
                background: '#fef2f2',
                border: '1px solid #fecaca',
                color: '#dc2626',
                padding: '10px',
                borderRadius: '6px',
                marginBottom: '20px',
                fontSize: '14px',
                textAlign: 'center'
              }}>
                {error}
              </div>
            )}

            {successMessage && (
              <div style={{
                background: '#dcfce7',
                border: '1px solid #86efac',
                color: '#166534',
                padding: '10px',
                borderRadius: '6px',
                marginBottom: '20px',
                fontSize: '14px',
                textAlign: 'center'
              }}>
                {successMessage}
              </div>
            )}

            <button
              type="submit"
              disabled={loading}
              style={{
                width: '100%',
                padding: '14px',
                background: loading ? '#9ca3af' : 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: loading ? 'not-allowed' : 'pointer',
                transition: 'transform 0.2s',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px'
              }}
            >
              {loading ? (
                <>
                  <span className="spinner"></span>
                  Actualizando...
                </>
              ) : (
                '✅ Cambiar Contraseña'
              )}
            </button>
          </form>
        </div>

        <style>{`
          .spinner {
            width: 16px;
            height: 16px;
            border: 2px solid #ffffff;
            border-top-color: transparent;
            border-radius: 50%;
            animation: spin 0.6s linear infinite;
          }
          
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  // Formulario de recuperación con PIN
  if (showForgotPassword) {
    return (
      <div style={{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        fontFamily: 'Arial, sans-serif'
      }}>
        <div style={{
          background: 'white',
          padding: '40px',
          borderRadius: '20px',
          boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
          maxWidth: '400px',
          width: '90%'
        }}>
          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <div style={{ fontSize: '48px', marginBottom: '10px' }}>
              {step === 1 ? '🔐' : '🔑'}
            </div>
            <h2 style={{
              color: '#1f2937',
              margin: '0 0 10px 0',
              fontSize: '24px',
              fontWeight: '700'
            }}>
              {step === 1 ? 'Recuperar Contraseña' : 'Nueva Contraseña'}
            </h2>
            <p style={{
              color: '#6b7280',
              margin: 0,
              fontSize: '14px'
            }}>
              {step === 1
                ? 'Ingresa tu email y PIN de seguridad'
                : 'Crea tu nueva contraseña'}
            </p>
          </div>

          {/* PASO 1: Email y PIN */}
          {step === 1 && (
            <form onSubmit={handleVerifyPinAndEmail}>
              <div style={{ marginBottom: '20px' }}>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  color: '#374151',
                  fontSize: '14px',
                  fontWeight: '500'
                }}>
                  Correo Electrónico
                </label>
                <input
                  type="email"
                  value={resetEmail}
                  onChange={(e) => setResetEmail(e.target.value)}
                  required
                  placeholder="ejemplo@gmail.com"
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: '1px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '14px',
                    outline: 'none',
                    transition: 'border-color 0.2s'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#667eea'}
                  onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                />
              </div>

              <div style={{ marginBottom: '24px' }}>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  color: '#374151',
                  fontSize: '14px',
                  fontWeight: '500'
                }}>
                  PIN de Seguridad (4 dígitos)
                </label>
                <input
                  type="text"
                  maxLength="4"
                  pattern="[0-9]{4}"
                  value={recoveryPin}
                  onChange={(e) => {
                    const value = e.target.value.replace(/\D/g, '');
                    setRecoveryPin(value);
                  }}
                  required
                  placeholder="••••"
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: '1px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '20px',
                    letterSpacing: '10px',
                    textAlign: 'center',
                    outline: 'none',
                    transition: 'border-color 0.2s'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#667eea'}
                  onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                />
                <small style={{
                  color: '#6b7280',
                  fontSize: '12px',
                  display: 'block',
                  marginTop: '5px'
                }}>
                  Ingresa el PIN que se te proporcionó al crear tu cuenta
                </small>
              </div>

              {error && (
                <div style={{
                  background: '#fef2f2',
                  border: '1px solid #fecaca',
                  color: '#dc2626',
                  padding: '10px',
                  borderRadius: '6px',
                  marginBottom: '20px',
                  fontSize: '14px',
                  textAlign: 'center'
                }}>
                  {error}
                </div>
              )}

              {successMessage && (
                <div style={{
                  background: '#dcfce7',
                  border: '1px solid #86efac',
                  color: '#166534',
                  padding: '10px',
                  borderRadius: '6px',
                  marginBottom: '20px',
                  fontSize: '14px',
                  textAlign: 'center'
                }}>
                  {successMessage}
                </div>
              )}

              <button
                type="submit"
                disabled={loading || recoveryPin.length !== 4}
                style={{
                  width: '100%',
                  padding: '14px',
                  background: loading || recoveryPin.length !== 4
                    ? '#9ca3af'
                    : 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '16px',
                  fontWeight: '600',
                  cursor: loading || recoveryPin.length !== 4 ? 'not-allowed' : 'pointer',
                  marginBottom: '12px'
                }}
              >
                {loading ? (
                  <>
                    <span className="spinner" style={{
                      display: 'inline-block',
                      width: '16px',
                      height: '16px',
                      marginRight: '8px'
                    }}></span>
                    Verificando...
                  </>
                ) : (
                  'Verificar PIN'
                )}
              </button>
            </form>
          )}

          {/* PASO 2: Nueva Contraseña */}
          {step === 2 && (
            <form onSubmit={handleResetPasswordWithPin}>
              <div style={{ marginBottom: '20px' }}>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  color: '#374151',
                  fontSize: '14px',
                  fontWeight: '500'
                }}>
                  Nueva Contraseña
                </label>
                <input
                  type="password"
                  value={resetNewPassword}
                  onChange={(e) => setResetNewPassword(e.target.value)}
                  required
                  placeholder="Mínimo 6 caracteres"
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: '1px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '14px',
                    outline: 'none',
                    transition: 'border-color 0.2s'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#667eea'}
                  onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                />
              </div>

              <div style={{ marginBottom: '24px' }}>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  color: '#374151',
                  fontSize: '14px',
                  fontWeight: '500'
                }}>
                  Confirmar Nueva Contraseña
                </label>
                <input
                  type="password"
                  value={resetConfirmPassword}
                  onChange={(e) => setResetConfirmPassword(e.target.value)}
                  required
                  placeholder="Repite la contraseña"
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: '1px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '14px',
                    outline: 'none',
                    transition: 'border-color 0.2s'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#667eea'}
                  onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                />
              </div>

              {error && (
                <div style={{
                  background: '#fef2f2',
                  border: '1px solid #fecaca',
                  color: '#dc2626',
                  padding: '10px',
                  borderRadius: '6px',
                  marginBottom: '20px',
                  fontSize: '14px',
                  textAlign: 'center'
                }}>
                  {error}
                </div>
              )}

              {successMessage && (
                <div style={{
                  background: '#dcfce7',
                  border: '1px solid #86efac',
                  color: '#166534',
                  padding: '10px',
                  borderRadius: '6px',
                  marginBottom: '20px',
                  fontSize: '14px',
                  textAlign: 'center'
                }}>
                  {successMessage}
                </div>
              )}

              <button
                type="submit"
                disabled={loading}
                style={{
                  width: '100%',
                  padding: '14px',
                  background: loading ? '#9ca3af' : 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '16px',
                  fontWeight: '600',
                  cursor: loading ? 'not-allowed' : 'pointer',
                  marginBottom: '12px'
                }}
              >
                {loading ? (
                  <>
                    <span className="spinner" style={{
                      display: 'inline-block',
                      width: '16px',
                      height: '16px',
                      marginRight: '8px'
                    }}></span>
                    Procesando...
                  </>
                ) : (
                  '✅ Actualizar Contraseña'
                )}
              </button>
            </form>
          )}

          <button
            type="button"
            onClick={() => {
              setShowForgotPassword(false);
              setError('');
              setSuccessMessage('');
              setResetEmail('');
              setRecoveryPin('');
              setResetNewPassword('');
              setResetConfirmPassword('');
              setStep(1);
              setUserIdForReset(null);
            }}
            style={{
              width: '100%',
              padding: '14px',
              background: '#6b7280',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: '600',
              cursor: 'pointer'
            }}
          >
            ← Volver al Login
          </button>

          {step === 1 && (
            <div style={{
              marginTop: '20px',
              padding: '15px',
              background: '#f9fafb',
              borderRadius: '8px',
              fontSize: '12px',
              color: '#6b7280',
              textAlign: 'center'
            }}>
              <strong>¿No recuerdas tu PIN?</strong><br />
              Contacta al administrador del sistema<br />
              📞 +56 9 1234 5678<br />
              📧 admin@municipalidadquillon.cl
            </div>
          )}
        </div>

        <style>{`
          .spinner {
            width: 16px;
            height: 16px;
            border: 2px solid #ffffff;
            border-top-color: transparent;
            border-radius: 50%;
            animation: spin 0.6s linear infinite;
          }
          
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  // Formulario de login principal
  return (
    <div style={{
      width: '100%',
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        background: 'white',
        padding: '40px',
        borderRadius: '20px',
        boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
        maxWidth: '400px',
        width: '90%'
      }}>
        {/* Logo y título */}
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <div style={{ fontSize: '48px', marginBottom: '10px' }}>🗺️</div>
          <h1 style={{
            color: '#1f2937',
            margin: '0 0 10px 0',
            fontSize: '28px',
            fontWeight: '700'
          }}>
            MapaQuillón
          </h1>
          <p style={{
            color: '#6b7280',
            margin: 0,
            fontSize: '14px'
          }}>
            Sistema de Control de Flota<br />
            Municipalidad de Quillón
          </p>
        </div>

        {/* Formulario */}
        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{
              display: 'block',
              marginBottom: '8px',
              color: '#374151',
              fontSize: '14px',
              fontWeight: '500'
            }}>
              Correo Electrónico
            </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="ejemplo@gmail.com"
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #d1d5db',
                borderRadius: '8px',
                fontSize: '14px',
                outline: 'none',
                transition: 'border-color 0.2s'
              }}
              onFocus={(e) => e.target.style.borderColor = '#667eea'}
              onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
            />
          </div>

          <div style={{ marginBottom: '12px' }}>
            <label style={{
              display: 'block',
              marginBottom: '8px',
              color: '#374151',
              fontSize: '14px',
              fontWeight: '500'
            }}>
              Contraseña
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="••••••••"
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #d1d5db',
                borderRadius: '8px',
                fontSize: '14px',
                outline: 'none',
                transition: 'border-color 0.2s'
              }}
              onFocus={(e) => e.target.style.borderColor = '#667eea'}
              onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
            />
          </div>



          {/* Mensaje de error */}
          {error && (
            <div style={{
              background: '#fef2f2',
              border: '1px solid #fecaca',
              color: '#dc2626',
              padding: '10px',
              borderRadius: '6px',
              marginBottom: '20px',
              fontSize: '14px',
              textAlign: 'center'
            }}>
              {error}
            </div>
          )}

          {/* Botón de login */}
          <button
            type="submit"
            disabled={loading}
            style={{
              width: '100%',
              padding: '14px',
              background: loading ? '#9ca3af' : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: '600',
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'transform 0.2s',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px'
            }}
            onMouseOver={(e) => !loading && (e.currentTarget.style.transform = 'translateY(-2px)')}
            onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0px)'}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                Iniciando sesión...
              </>
            ) : (
              'Iniciar Sesión'
            )}
            
          </button>
          <button
            type="button"
            onClick={() => setShowPasswordRecovery(true)}
            style={{
              width: '100%',
              padding: '12px',
              background: 'transparent',
              color: '#3b82f6',
              border: '1px solid #3b82f6',
              borderRadius: '8px',
              fontSize: '14px',
              fontWeight: '500',
              cursor: 'pointer',
              marginTop: '10px'
            }}
          >
            🔓 ¿Olvidaste tu contraseña?
          </button>

          {/* Modal de recuperación de contraseña */}
          {showPasswordRecovery && (
            <PasswordRecovery
              onClose={() => setShowPasswordRecovery(false)}
              onSuccess={() => {
                setShowPasswordRecovery(false);
                setSuccessMessage('✅ Proceso completado. Intenta iniciar sesión con tu nueva contraseña.');


              }}
            />
          )}
        </form>


        {/* Información de contacto */}
        <div style={{
          marginTop: '30px',
          padding: '15px',
          background: '#f9fafb',
          borderRadius: '8px',
          fontSize: '12px',
          color: '#6b7280',
          textAlign: 'center'
        }}>
          <strong>¿Necesitas ayuda?</strong><br />
          Contacta al administrador del sistema<br />
          📞 +56 9 1234 5678
        </div>
      </div>

      <style>{`
        .spinner {
          width: 16px;
          height: 16px;
          border: 2px solid #ffffff;
          border-top-color: transparent;
          border-radius: 50%;
          animation: spin 0.6s linear infinite;
          display: inline-block;
        }
        
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default Login;
