// src/components/MonitorPanel.js

import React, { useState, useEffect, useCallback } from 'react';
import { database, firestore } from '../config/firebase';
import { 
  ref as databaseRef, 
  onValue, 
  off 
} from 'firebase/database';
import { collection, getDocs } from 'firebase/firestore';

const MonitorPanel = ({ currentUser, onLogout }) => {
  // Estados principales
  const [vehiculos, setVehiculos] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [tiposVehiculo, setTiposVehiculo] = useState([]);
  const [solicitudes, setSolicitudes] = useState([]);
  const [zonas, setZonas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [selectedUser, setSelectedUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('todos');
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [refreshTime, setRefreshTime] = useState(new Date());

  // Detectar m√≥vil
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Auto-refresh cada 30 segundos
  useEffect(() => {
    const interval = setInterval(() => {
      setRefreshTime(new Date());
      loadAllData();
    }, 30000);

    return () => clearInterval(interval);
  }, []);

  // Cargar todos los datos
  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    setLoading(true);
    
    // Cargar veh√≠culos
    const unsubscribeVehiculos = loadVehiculos();
    
    // Cargar tipos de veh√≠culo
    const unsubscribeTipos = loadTiposVehiculo();
    
    // Cargar solicitudes
    const unsubscribeSolicitudes = loadSolicitudes();
    
    // Cargar zonas
    const unsubscribeZonas = loadZonas();
    
    // Cargar usuarios
    await loadUsuarios();
    
    setLoading(false);

    // Cleanup
    return () => {
      if (unsubscribeVehiculos) unsubscribeVehiculos();
      if (unsubscribeTipos) unsubscribeTipos();
      if (unsubscribeSolicitudes) unsubscribeSolicitudes();
      if (unsubscribeZonas) unsubscribeZonas();
    };
  };

  const loadVehiculos = useCallback(() => {
    const vehiculosRef = databaseRef(database, 'vehiculos');
    const unsubscribe = onValue(vehiculosRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const vehiculosArray = Object.entries(data).map(([id, vehiculo]) => ({
          id,
          ...vehiculo
        }));
        setVehiculos(vehiculosArray);
      } else {
        setVehiculos([]);
      }
    });
    return () => off(vehiculosRef);
  }, []);

  const loadTiposVehiculo = useCallback(() => {
    const tiposRef = databaseRef(database, 'tipos_vehiculo');
    const unsubscribe = onValue(tiposRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const tiposArray = Object.entries(data).map(([id, tipo]) => ({
          id,
          ...tipo
        }));
        setTiposVehiculo(tiposArray);
      }
    });
    return () => off(tiposRef);
  }, []);

  const loadSolicitudes = useCallback(() => {
    const solicitudesRef = databaseRef(database, 'solicitudes_maquinaria');
    const unsubscribe = onValue(solicitudesRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const solicitudesArray = Object.entries(data)
          .map(([id, sol]) => ({ id, ...sol }))
          .sort((a, b) => new Date(b.fechaSolicitud) - new Date(a.fechaSolicitud));
        setSolicitudes(solicitudesArray);
      }
    });
    return () => off(solicitudesRef);
  }, []);

  const loadZonas = useCallback(() => {
    const zonasRef = databaseRef(database, 'zones');
    const unsubscribe = onValue(zonasRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const zonasArray = Object.entries(data).map(([id, zona]) => ({
          id,
          ...zona
        }));
        setZonas(zonasArray);
      }
    });
    return () => off(zonasRef);
  }, []);

  const loadUsuarios = async () => {
    try {
      const usersSnapshot = await getDocs(collection(firestore, 'users'));
      const usersList = [];
      usersSnapshot.forEach((doc) => {
        usersList.push({ id: doc.id, ...doc.data() });
      });
      setUsuarios(usersList);
    } catch (error) {
      console.error('Error al cargar usuarios:', error);
    }
  };

  // Funciones de estad√≠sticas
  const getVehicleStats = () => {
    return {
      total: vehiculos.length,
      disponibles: vehiculos.filter(v => v.estado === 'disponible').length,
      enUso: vehiculos.filter(v => v.estado === 'en_uso').length,
      mantenimiento: vehiculos.filter(v => v.estado === 'mantenimiento').length,
      fueraServicio: vehiculos.filter(v => v.estado === 'fuera_servicio').length
    };
  };

  const getUserStats = () => {
    return {
      total: usuarios.length,
      superadmins: usuarios.filter(u => u.role === 'superadmin').length,
      admins: usuarios.filter(u => u.role === 'admin').length,
      trabajadores: usuarios.filter(u => u.role === 'trabajador').length,
      monitores: usuarios.filter(u => u.role === 'monitor').length,
      juntaVecinos: usuarios.filter(u => u.role === 'junta_vecinos').length,
      activos: usuarios.filter(u => u.active !== false).length
    };
  };

  const getSolicitudesStats = () => {
    return {
      total: solicitudes.length,
      pendientes: solicitudes.filter(s => s.estado === 'pendiente').length,
      aprobadas: solicitudes.filter(s => s.estado === 'aprobada').length,
      rechazadas: solicitudes.filter(s => s.estado === 'rechazada').length,
      completadas: solicitudes.filter(s => s.estado === 'completada').length
    };
  };

  // Filtrar veh√≠culos
  const filteredVehiculos = vehiculos.filter(vehiculo => {
    const matchesSearch = vehiculo.nombre?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          vehiculo.patente?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          vehiculo.marca?.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesFilter = filterStatus === 'todos' || vehiculo.estado === filterStatus;
    
    return matchesSearch && matchesFilter;
  });

  // Verificar veh√≠culos que necesitan mantenimiento
  const getVehiculosNecesitanMantenimiento = () => {
    const hoy = new Date();
    return vehiculos.filter(v => {
      if (v.proximoMantenimiento) {
        const fechaMantenimiento = new Date(v.proximoMantenimiento);
        const diasRestantes = Math.ceil((fechaMantenimiento - hoy) / (1000 * 60 * 60 * 24));
        return diasRestantes <= 7 && diasRestantes >= 0;
      }
      return false;
    });
  };

  // Verificar documentos pr√≥ximos a vencer
  const getDocumentosProximosVencer = () => {
    const hoy = new Date();
    const alertas = [];
    
    vehiculos.forEach(vehiculo => {
      // Revisar revisi√≥n t√©cnica
      if (vehiculo.fechaRevisionTecnica) {
        const fecha = new Date(vehiculo.fechaRevisionTecnica);
        const diasRestantes = Math.ceil((fecha - hoy) / (1000 * 60 * 60 * 24));
        if (diasRestantes <= 30 && diasRestantes >= 0) {
          alertas.push({
            vehiculo: vehiculo.nombre,
            documento: 'Revisi√≥n T√©cnica',
            vencimiento: vehiculo.fechaRevisionTecnica,
            dias: diasRestantes
          });
        }
      }
      
      // Revisar permiso de circulaci√≥n
      if (vehiculo.fechaPermisoCirculacion) {
        const fecha = new Date(vehiculo.fechaPermisoCirculacion);
        const diasRestantes = Math.ceil((fecha - hoy) / (1000 * 60 * 60 * 24));
        if (diasRestantes <= 30 && diasRestantes >= 0) {
          alertas.push({
            vehiculo: vehiculo.nombre,
            documento: 'Permiso de Circulaci√≥n',
            vencimiento: vehiculo.fechaPermisoCirculacion,
            dias: diasRestantes
          });
        }
      }
      
      // Revisar seguro
      if (vehiculo.fechaSeguro) {
        const fecha = new Date(vehiculo.fechaSeguro);
        const diasRestantes = Math.ceil((fecha - hoy) / (1000 * 60 * 60 * 24));
        if (diasRestantes <= 30 && diasRestantes >= 0) {
          alertas.push({
            vehiculo: vehiculo.nombre,
            documento: 'Seguro',
            vencimiento: vehiculo.fechaSeguro,
            dias: diasRestantes
          });
        }
      }
    });
    
    return alertas.sort((a, b) => a.dias - b.dias);
  };

  // Modal de detalles de veh√≠culo
  const VehicleDetailsModal = ({ vehicle, onClose }) => {
    if (!vehicle) return null;

    const tipo = tiposVehiculo.find(t => t.id === vehicle.tipo);
    const operador = usuarios.find(u => u.id === vehicle.operadorAsignado);

    return (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(0,0,0,0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1000,
        padding: '20px'
      }}>
        <div style={{
          background: 'white',
          borderRadius: '12px',
          maxWidth: '800px',
          width: '100%',
          maxHeight: '90vh',
          overflow: 'auto',
          padding: '30px'
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '25px'
          }}>
            <h2 style={{ margin: 0, color: '#1f2937', fontSize: '24px' }}>
              üöõ Detalles del Veh√≠culo
            </h2>
            <button
              onClick={onClose}
              style={{
                padding: '8px',
                background: '#6b7280',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '18px'
              }}
            >
              ‚úñ
            </button>
          </div>

          <div style={{
            display: 'grid',
            gridTemplateColumns: isMobile ? '1fr' : 'repeat(2, 1fr)',
            gap: '20px'
          }}>
            {/* Informaci√≥n General */}
            <div style={{
              padding: '20px',
              background: '#f9fafb',
              borderRadius: '8px'
            }}>
              <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                üìã Informaci√≥n General
              </h3>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <div>
                  <strong>Nombre:</strong> {vehicle.nombre}
                </div>
                <div>
                  <strong>Tipo:</strong> {tipo ? `${tipo.icon} ${tipo.nombre}` : vehicle.tipo}
                </div>
                <div>
                  <strong>Marca/Modelo:</strong> {vehicle.marca} {vehicle.modelo}
                </div>
                <div>
                  <strong>A√±o:</strong> {vehicle.a√±o}
                </div>
                <div>
                  <strong>Patente:</strong> {vehicle.patente}
                </div>
                <div>
                  <strong>Estado:</strong>{' '}
                  <span style={{
                    padding: '2px 8px',
                    borderRadius: '4px',
                    fontSize: '12px',
                    background: vehicle.estado === 'disponible' ? '#dcfce7' :
                               vehicle.estado === 'en_uso' ? '#dbeafe' :
                               vehicle.estado === 'mantenimiento' ? '#fef3c7' : '#fee2e2',
                    color: vehicle.estado === 'disponible' ? '#15803d' :
                           vehicle.estado === 'en_uso' ? '#1e40af' :
                           vehicle.estado === 'mantenimiento' ? '#92400e' : '#991b1b'
                  }}>
                    {vehicle.estado === 'disponible' ? '‚úÖ Disponible' :
                     vehicle.estado === 'en_uso' ? 'üîß En Uso' :
                     vehicle.estado === 'mantenimiento' ? 'üîß Mantenimiento' : '‚ùå Fuera de Servicio'}
                  </span>
                </div>
                {operador && (
                  <div>
                    <strong>Operador Asignado:</strong> {operador.name}
                  </div>
                )}
              </div>
            </div>

            {/* Informaci√≥n T√©cnica */}
            <div style={{
              padding: '20px',
              background: '#f9fafb',
              borderRadius: '8px'
            }}>
              <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                ‚öôÔ∏è Informaci√≥n T√©cnica
              </h3>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <div>
                  <strong>Kilometraje:</strong> {vehicle.kilometraje?.toLocaleString()} km
                </div>
                <div>
                  <strong>Combustible:</strong> {vehicle.tipoCombustible}
                </div>
                <div>
                  <strong>N¬∞ Chasis:</strong> {vehicle.numeroChasis || 'No registrado'}
                </div>
                <div>
                  <strong>N¬∞ Motor:</strong> {vehicle.numeroMotor || 'No registrado'}
                </div>
                {vehicle.horasUso > 0 && (
                  <div>
                    <strong>Horas de Uso:</strong> {vehicle.horasUso} hrs
                  </div>
                )}
              </div>
            </div>

            {/* Mantenimiento */}
            <div style={{
              padding: '20px',
              background: '#f9fafb',
              borderRadius: '8px'
            }}>
              <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                üîß Mantenimiento
              </h3>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <div>
                  <strong>√öltimo Mantenimiento:</strong>{' '}
                  {vehicle.ultimoMantenimiento ? 
                    new Date(vehicle.ultimoMantenimiento).toLocaleDateString('es-CL') : 
                    'No registrado'}
                </div>
                <div>
                  <strong>Pr√≥ximo Mantenimiento:</strong>{' '}
                  {vehicle.proximoMantenimiento ? 
                    new Date(vehicle.proximoMantenimiento).toLocaleDateString('es-CL') : 
                    'No programado'}
                </div>
                {vehicle.kilometrajeProximoMantenimiento > 0 && (
                  <div>
                    <strong>Pr√≥ximo por Km:</strong> {vehicle.kilometrajeProximoMantenimiento?.toLocaleString()} km
                  </div>
                )}
              </div>
            </div>

            {/* Documentaci√≥n */}
            <div style={{
              padding: '20px',
              background: '#f9fafb',
              borderRadius: '8px'
            }}>
              <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                üìÑ Documentaci√≥n
              </h3>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <div>
                  <strong>Revisi√≥n T√©cnica:</strong>{' '}
                  {vehicle.fechaRevisionTecnica ? 
                    new Date(vehicle.fechaRevisionTecnica).toLocaleDateString('es-CL') : 
                    'No registrada'}
                </div>
                <div>
                  <strong>Permiso Circulaci√≥n:</strong>{' '}
                  {vehicle.fechaPermisoCirculacion ? 
                    new Date(vehicle.fechaPermisoCirculacion).toLocaleDateString('es-CL') : 
                    'No registrado'}
                </div>
                <div>
                  <strong>Seguro:</strong>{' '}
                  {vehicle.fechaSeguro ? 
                    new Date(vehicle.fechaSeguro).toLocaleDateString('es-CL') : 
                    'No registrado'}
                </div>
              </div>
            </div>
          </div>

          {/* Observaciones */}
          {vehicle.observaciones && (
            <div style={{
              marginTop: '20px',
              padding: '20px',
              background: '#f9fafb',
              borderRadius: '8px'
            }}>
              <h3 style={{ margin: '0 0 10px 0', color: '#374151', fontSize: '16px' }}>
                üìù Observaciones
              </h3>
              <p style={{ margin: 0, color: '#6b7280' }}>
                {vehicle.observaciones}
              </p>
            </div>
          )}
        </div>
      </div>
    );
  };

  // Modal de detalles de usuario
  const UserDetailsModal = ({ user, onClose }) => {
    if (!user) return null;

    const vehiculoAsignado = vehiculos.find(v => v.operadorAsignado === user.id);

    return (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(0,0,0,0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1000,
        padding: '20px'
      }}>
        <div style={{
          background: 'white',
          borderRadius: '12px',
          maxWidth: '600px',
          width: '100%',
          maxHeight: '90vh',
          overflow: 'auto',
          padding: '30px'
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '25px'
          }}>
            <h2 style={{ margin: 0, color: '#1f2937', fontSize: '24px' }}>
              üë§ Detalles del Usuario
            </h2>
            <button
              onClick={onClose}
              style={{
                padding: '8px',
                background: '#6b7280',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '18px'
              }}
            >
              ‚úñ
            </button>
          </div>

          <div style={{
            display: 'flex',
            flexDirection: 'column',
            gap: '20px'
          }}>
            <div style={{
              padding: '20px',
              background: '#f9fafb',
              borderRadius: '8px'
            }}>
              <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                Informaci√≥n Personal
              </h3>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <div>
                  <strong>Nombre:</strong> {user.name}
                </div>
                <div>
                  <strong>Email:</strong> {user.email}
                </div>
                <div>
                  <strong>RUT:</strong> {user.rut || 'No registrado'}
                </div>
                <div>
                  <strong>Tel√©fono:</strong> {user.phone || 'No registrado'}
                </div>
                <div>
                  <strong>Rol:</strong>{' '}
                  <span style={{
                    padding: '2px 8px',
                    borderRadius: '4px',
                    fontSize: '12px',
                    background: user.role === 'superadmin' ? '#ede9fe' :
                               user.role === 'admin' ? '#dbeafe' :
                               user.role === 'monitor' ? '#f3f4f6' :
                               user.role === 'trabajador' ? '#dcfce7' : '#fef3c7',
                    color: user.role === 'superadmin' ? '#6b21a8' :
                           user.role === 'admin' ? '#1e40af' :
                           user.role === 'monitor' ? '#374151' :
                           user.role === 'trabajador' ? '#15803d' : '#92400e'
                  }}>
                    {user.role === 'superadmin' ? 'üîê Super Admin' :
                     user.role === 'admin' ? 'üë®‚Äçüíº Administrador' :
                     user.role === 'monitor' ? 'üìä Monitor' :
                     user.role === 'trabajador' ? 'üë∑ Trabajador' : 'üèòÔ∏è Junta Vecinos'}
                  </span>
                </div>
              </div>
            </div>

            {user.role === 'trabajador' && (
              <div style={{
                padding: '20px',
                background: '#f9fafb',
                borderRadius: '8px'
              }}>
                <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                  Informaci√≥n Laboral
                </h3>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                  <div>
                    <strong>Licencias:</strong>{' '}
                    {user.licenciasConducir?.length > 0 ? 
                      user.licenciasConducir.join(', ') : 
                      user.licenciaConducir || 'No registrada'}
                  </div>
                  {user.fechaVencimientoLicencia && (
                    <div>
                      <strong>Vencimiento Licencia:</strong>{' '}
                      {new Date(user.fechaVencimientoLicencia).toLocaleDateString('es-CL')}
                    </div>
                  )}
                  {vehiculoAsignado && (
                    <div>
                      <strong>Veh√≠culo Asignado:</strong> {vehiculoAsignado.nombre}
                    </div>
                  )}
                </div>
              </div>
            )}

            {user.localidad && (
              <div style={{
                padding: '20px',
                background: '#f9fafb',
                borderRadius: '8px'
              }}>
                <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                  Localidad
                </h3>
                <p style={{ margin: 0 }}>{user.localidad}</p>
              </div>
            )}

            <div style={{
              padding: '20px',
              background: '#f9fafb',
              borderRadius: '8px'
            }}>
              <h3 style={{ margin: '0 0 15px 0', color: '#374151', fontSize: '16px' }}>
                Estado de Cuenta
              </h3>
              <div>
                <strong>Estado:</strong>{' '}
                <span style={{
                  padding: '2px 8px',
                  borderRadius: '4px',
                  fontSize: '12px',
                  background: user.active !== false ? '#dcfce7' : '#fee2e2',
                  color: user.active !== false ? '#15803d' : '#991b1b'
                }}>
                  {user.active !== false ? '‚úÖ Activo' : '‚ùå Inactivo'}
                </span>
              </div>
              {user.createdAt && (
                <div style={{ marginTop: '10px' }}>
                  <strong>Fecha de Creaci√≥n:</strong>{' '}
                  {new Date(user.createdAt).toLocaleDateString('es-CL')}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div style={{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: '#f3f4f6'
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{
            width: '60px',
            height: '60px',
            border: '4px solid #e5e7eb',
            borderTop: '4px solid #3b82f6',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite'
          }}></div>
          <p style={{ marginTop: '20px', color: '#6b7280' }}>Cargando datos...</p>
        </div>
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', background: '#f3f4f6' }}>
      {/* Header */}
      <div style={{
        background: 'white',
        borderBottom: '1px solid #e5e7eb',
        padding: '20px'
      }}>
        <div style={{
          maxWidth: '1400px',
          margin: '0 auto',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          flexWrap: 'wrap',
          gap: '20px'
        }}>
          <div>
            <h1 style={{
              margin: 0,
              fontSize: '28px',
              fontWeight: '600',
              color: '#1f2937'
            }}>
