              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr style={{ background: '#f9fafb', borderBottom: '1px solid #e5e7eb' }}>
                    <th style={{ padding: '12px 20px', textAlign: 'left', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                      Usuario
                    </th>
                    <th style={{ padding: '12px 20px', textAlign: 'left', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                      Rol
                    </th>
                    <th style={{ padding: '12px 20px', textAlign: 'left', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                      Contacto
                    </th>
                    <th style={{ padding: '12px 20px', textAlign: 'left', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                      Estado
                    </th>
                    <th style={{ padding: '12px 20px', textAlign: 'center', fontSize: '14px', fontWeight: '600', color: '#374151' }}>
                      Acciones
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {usuarios.map((usuario) => (
                    <tr key={usuario.id} style={{ borderBottom: '1px solid #e5e7eb' }}>
                      <td style={{ padding: '16px 20px' }}>
                        <div>
                          <div style={{ fontSize: '14px', fontWeight: '500', color: '#1f2937' }}>
                            {usuario.name || 'Sin nombre'}
                          </div>
                          <div style={{ fontSize: '12px', color: '#6b7280' }}>
                            {usuario.email}
                          </div>
                        </div>
                      </td>
                      <td style={{ padding: '16px 20px' }}>
                        <span style={{
                          padding: '4px 8px',
                          borderRadius: '4px',
                          fontSize: '12px',
                          fontWeight: '500',
                          background: usuario.role === 'superadmin' ? '#ede9fe' :
                                     usuario.role === 'admin' ? '#dbeafe' :
                                     usuario.role === 'monitor' ? '#f3f4f6' :
                                     usuario.role === 'trabajador' ? '#dcfce7' : '#fef3c7',
                          color: usuario.role === 'superadmin' ? '#6b21a8' :
                                 usuario.role === 'admin' ? '#1e40af' :
                                 usuario.role === 'monitor' ? '#374151' :
                                 usuario.role === 'trabajador' ? '#15803d' : '#92400e'
                        }}>
                          {usuario.role === 'superadmin' ? 'üîê Super Admin' :
                           usuario.role === 'admin' ? 'üë®‚Äçüíº Administrador' :
                           usuario.role === 'monitor' ? 'üìä Monitor' :
                           usuario.role === 'trabajador' ? 'üë∑ Trabajador' : 'üèòÔ∏è Junta Vecinos'}
                        </span>
                      </td>
                      <td style={{ padding: '16px 20px' }}>
                        <div style={{ fontSize: '13px', color: '#374151' }}>
                          {usuario.phone || 'Sin tel√©fono'}
                        </div>
                        {usuario.localidad && (
                          <div style={{ fontSize: '12px', color: '#6b7280' }}>
                            üìç {usuario.localidad}
                          </div>
                        )}
                      </td>
                      <td style={{ padding: '16px 20px' }}>
                        <span style={{
                          padding: '4px 8px',
                          borderRadius: '4px',
                          fontSize: '12px',
                          background: usuario.active !== false ? '#dcfce7' : '#fee2e2',
                          color: usuario.active !== false ? '#15803d' : '#991b1b'
                        }}>
                          {usuario.active !== false ? '‚úÖ Activo' : '‚ùå Inactivo'}
                        </span>
                      </td>
                      <td style={{ padding: '16px 20px', textAlign: 'center' }}>
                        <button
                          onClick={() => setSelectedUser(usuario)}
                          style={{
                            padding: '6px 12px',
                            background: '#3b82f6',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            fontSize: '12px',
                            cursor: 'pointer'
                          }}
                        >
                          üëÅÔ∏è Ver Detalles
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Alertas */}
        {activeTab === 'alertas' && (
          <div>
            {/* Veh√≠culos que necesitan mantenimiento */}
            <div style={{
              background: 'white',
              padding: '20px',
              borderRadius: '12px',
              marginBottom: '20px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.05)'
            }}>
              <h3 style={{
                margin: '0 0 20px 0',
                fontSize: '18px',
                color: '#1f2937',
                display: 'flex',
                alignItems: 'center',
                gap: '10px'
              }}>
                üîß Mantenimientos Pr√≥ximos
                <span style={{
                  padding: '2px 8px',
                  background: '#fef3c7',
                  color: '#92400e',
                  borderRadius: '4px',
                  fontSize: '14px'
                }}>
                  {getVehiculosNecesitanMantenimiento().length}
                </span>
              </h3>

              {getVehiculosNecesitanMantenimiento().length > 0 ? (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                  {getVehiculosNecesitanMantenimiento().map(vehiculo => {
                    const diasRestantes = Math.ceil(
                      (new Date(vehiculo.proximoMantenimiento) - new Date()) / (1000 * 60 * 60 * 24)
                    );
                    
                    return (
                      <div key={vehiculo.id} style={{
                        padding: '15px',
                        background: '#fef3c7',
                        borderRadius: '8px',
                        borderLeft: '4px solid #f59e0b'
                      }}>
                        <div style={{
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'center'
                        }}>
                          <div>
                            <div style={{
                              fontSize: '14px',
                              fontWeight: '500',
                              color: '#92400e'
                            }}>
                              {vehiculo.nombre} - {vehiculo.patente}
                            </div>
                            <div style={{
                              fontSize: '12px',
                              color: '#78350f',
                              marginTop: '5px'
                            }}>
                              Pr√≥ximo mantenimiento: {new Date(vehiculo.proximoMantenimiento).toLocaleDateString('es-CL')}
                            </div>
                          </div>
                          <div style={{
                            padding: '8px 12px',
                            background: '#f59e0b',
                            color: 'white',
                            borderRadius: '6px',
                            fontSize: '14px',
                            fontWeight: '600'
                          }}>
                            {diasRestantes} d√≠as
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              ) : (
                <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
                  No hay veh√≠culos con mantenimiento pr√≥ximo
                </p>
              )}
            </div>

            {/* Documentos pr√≥ximos a vencer */}
            <div style={{
              background: 'white',
              padding: '20px',
              borderRadius: '12px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.05)'
            }}>
              <h3 style={{
                margin: '0 0 20px 0',
                fontSize: '18px',
                color: '#1f2937',
                display: 'flex',
                alignItems: 'center',
                gap: '10px'
              }}>
                üìÑ Documentos Pr√≥ximos a Vencer
                <span style={{
                  padding: '2px 8px',
                  background: '#fee2e2',
                  color: '#991b1b',
                  borderRadius: '4px',
                  fontSize: '14px'
                }}>
                  {getDocumentosProximosVencer().length}
                </span>
              </h3>

              {getDocumentosProximosVencer().length > 0 ? (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                  {getDocumentosProximosVencer().map((alerta, index) => (
                    <div key={index} style={{
                      padding: '15px',
                      background: alerta.dias <= 7 ? '#fee2e2' : '#fef3c7',
                      borderRadius: '8px',
                      borderLeft: `4px solid ${alerta.dias <= 7 ? '#ef4444' : '#f59e0b'}`
                    }}>
                      <div style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center'
                      }}>
                        <div>
                          <div style={{
                            fontSize: '14px',
                            fontWeight: '500',
                            color: alerta.dias <= 7 ? '#991b1b' : '#92400e'
                          }}>
                            {alerta.vehiculo} - {alerta.documento}
                          </div>
                          <div style={{
                            fontSize: '12px',
                            color: alerta.dias <= 7 ? '#7f1d1d' : '#78350f',
                            marginTop: '5px'
                          }}>
                            Vence: {new Date(alerta.vencimiento).toLocaleDateString('es-CL')}
                          </div>
                        </div>
                        <div style={{
                          padding: '8px 12px',
                          background: alerta.dias <= 7 ? '#ef4444' : '#f59e0b',
                          color: 'white',
                          borderRadius: '6px',
                          fontSize: '14px',
                          fontWeight: '600'
                        }}>
                          {alerta.dias} d√≠as
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
                  No hay documentos pr√≥ximos a vencer
                </p>
              )}
            </div>

            {/* Solicitudes pendientes */}
            <div style={{
              background: 'white',
              padding: '20px',
              borderRadius: '12px',
              marginTop: '20px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.05)'
            }}>
              <h3 style={{
                margin: '0 0 20px 0',
                fontSize: '18px',
                color: '#1f2937',
                display: 'flex',
                alignItems: 'center',
                gap: '10px'
              }}>
                üìã Solicitudes Pendientes
                <span style={{
                  padding: '2px 8px',
                  background: '#fef3c7',
                  color: '#92400e',
                  borderRadius: '4px',
                  fontSize: '14px'
                }}>
                  {getSolicitudesStats().pendientes}
                </span>
              </h3>

              {getSolicitudesStats().pendientes > 0 ? (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                  {solicitudes.filter(s => s.estado === 'pendiente').slice(0, 5).map(solicitud => (
                    <div key={solicitud.id} style={{
                      padding: '15px',
                      background: '#f9fafb',
                      borderRadius: '8px',
                      borderLeft: '4px solid #f59e0b'
                    }}>
                      <div style={{
                        fontSize: '14px',
                        fontWeight: '500',
                        color: '#1f2937',
                        marginBottom: '5px'
                      }}>
                        {solicitud.asunto}
                      </div>
                      <div style={{
                        fontSize: '12px',
                        color: '#6b7280'
                      }}>
                        Solicitante: {solicitud.nombreSolicitante} ‚Ä¢ {new Date(solicitud.fechaSolicitud).toLocaleDateString('es-CL')}
                      </div>
                      {solicitud.prioridad === 'alta' && (
                        <span style={{
                          display: 'inline-block',
                          marginTop: '5px',
                          padding: '2px 6px',
                          background: '#fee2e2',
                          color: '#991b1b',
                          borderRadius: '4px',
                          fontSize: '11px',
                          fontWeight: '600'
                        }}>
                          ‚ö†Ô∏è PRIORIDAD ALTA
                        </span>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
                  No hay solicitudes pendientes
                </p>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Modales */}
      {selectedVehicle && (
        <VehicleDetailsModal
          vehicle={selectedVehicle}
          onClose={() => setSelectedVehicle(null)}
        />
      )}

      {selectedUser && (
        <UserDetailsModal
          user={selectedUser}
          onClose={() => setSelectedUser(null)}
        />
      )}
    </div>
  );
};

export default MonitorPanel;
