// src/components/SuperAdmin.js
import React, { useState, useEffect } from 'react';
import { auth, firestore, database } from '../config/firebase';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { doc, setDoc, collection, getDocs, deleteDoc, updateDoc } from 'firebase/firestore';
import { ref, set, push, update, remove, onValue } from 'firebase/database';

const SuperAdmin = ({ currentUser, onLogout, onViewChange, currentView = 'admin' }) => {
  // ========== ESTADOS PARA USUARIOS ==========
  const [solicitudParaZona, setSolicitudParaZona] = useState(null);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    role: 'trabajador',
    phone: '',
    vehicleId: '',
    localidad: ''
  });
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [activeTab, setActiveTab] = useState('users');
  const [isMobile, setIsMobile] = useState(false);
  const [showEditUserForm, setShowEditUserForm] = useState(false);
  
  // ========== ESTADOS PARA VEHÍCULOS ==========
  const [vehiculos, setVehiculos] = useState([]);
  const [showVehicleForm, setShowVehicleForm] = useState(false);
  const [editingVehicle, setEditingVehicle] = useState(null);
  const [vehicleFormData, setVehicleFormData] = useState({
    nombre: '',
    tipo: '',
    marca: '',
    modelo: '',
    año: new Date().getFullYear(),
    patente: '',
    color: '',
    numeroChasis: '',
    numeroMotor: '',
    kilometraje: 0,
    capacidadCombustible: 0,
    tipoCombustible: 'gasolina',
    ultimoMantenimiento: '',
    proximoMantenimiento: '',
    kilometrajeUltimoMantenimiento: 0,
    kilometrajeProximoMantenimiento: 0,
    fechaRevisionTecnica: '',
    fechaSeguro: '',
    fechaPermisoCirculacion: '',
    operadorAsignado: '',
    estado: 'disponible',
    lat: null,
    lng: null,
    observaciones: ''
  });

  // ========== ESTADOS PARA TIPOS DE VEHÍCULOS DINÁMICOS ==========
  const [tiposVehiculo, setTiposVehiculo] = useState([]);
  const [showTipoModal, setShowTipoModal] = useState(false);
  const [editingTipo, setEditingTipo] = useState(null);
  const [tipoFormData, setTipoFormData] = useState({
    nombre: '',
    icon: '🚛',
    descripcion: '',
    categoria: 'general'
  });

  // ========== ESTADOS PARA SOLICITUDES ==========
  const [solicitudes, setSolicitudes] = useState([]);
  const [showSolicitudDetails, setShowSolicitudDetails] = useState(null);

  // ========== ESTADOS PARA SIDEBAR ==========
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [expandedMenus, setExpandedMenus] = useState(['gestion']);

  // ========== DETECTAR SI ES MÓVIL ==========
  useEffect(() => {
    const checkMobile = () => {
      const mobile = window.innerWidth <= 768;
      setIsMobile(mobile);
      if (mobile) {
        setIsSidebarOpen(false);
      }
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // ========== CARGAR DATOS INICIALES ==========
  useEffect(() => {
    loadUsers();
    loadVehiculos();
    loadSolicitudes();
    loadTiposVehiculo();
  }, []);

  // ========== FUNCIONES DE CARGA DE DATOS ==========
  const loadUsers = async () => {
    try {
      const usersSnapshot = await getDocs(collection(firestore, 'users'));
      const usersList = [];
      usersSnapshot.forEach((doc) => {
        usersList.push({ id: doc.id, ...doc.data() });
      });
      setUsers(usersList);
    } catch (error) {
      console.error('Error al cargar usuarios:', error);
    }
  };

  const loadVehiculos = () => {
    const vehiculosRef = ref(database, 'vehiculos');
    const unsubscribe = onValue(vehiculosRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const vehiculosArray = Object.entries(data).map(([id, vehiculo]) => ({
          id,
          ...vehiculo
        }));
        setVehiculos(vehiculosArray);
      } else {
        setVehiculos([]);
      }
    });
    return unsubscribe;
  };

  const loadSolicitudes = () => {
    const solicitudesRef = ref(database, 'solicitudes_maquinaria');
    const unsubscribe = onValue(solicitudesRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const solicitudesArray = Object.entries(data)
          .map(([id, sol]) => ({ id, ...sol }))
          .sort((a, b) => {
            if (a.estado === 'pendiente' && b.estado !== 'pendiente') return -1;
            if (a.estado !== 'pendiente' && b.estado === 'pendiente') return 1;
            return new Date(b.fechaSolicitud) - new Date(a.fechaSolicitud);
          });
        setSolicitudes(solicitudesArray);
      } else {
        setSolicitudes([]);
      }
    });
    return unsubscribe;
  };

  const loadTiposVehiculo = () => {
    const tiposRef = ref(database, 'tipos_vehiculo');
    const unsubscribe = onValue(tiposRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const tiposArray = Object.entries(data).map(([id, tipo]) => ({
          id,
          ...tipo
        }));
        setTiposVehiculo(tiposArray);
      } else {
        setTiposVehiculo([]);
      }
    });
    return unsubscribe;
  };

  // ========== FUNCIONES PARA TIPOS DE VEHÍCULOS ==========
  const handleSaveTipoVehiculo = async (e) => {
    e.preventDefault();
    
    if (!tipoFormData.nombre.trim()) {
      setMessage({ type: 'error', text: 'El nombre del tipo es requerido' });
      return;
    }

    setLoading(true);
    
    try {
      if (editingTipo) {
        const tipoRef = ref(database, `tipos_vehiculo/${editingTipo.id}`);
        await update(tipoRef, {
          nombre: tipoFormData.nombre,
          icon: tipoFormData.icon,
          descripcion: tipoFormData.descripcion || '',
          categoria: tipoFormData.categoria || 'general',
          updatedAt: new Date().toISOString(),
          updatedBy: currentUser.uid
        });
        
        setMessage({ 
          type: 'success', 
          text: `✅ Tipo "${tipoFormData.nombre}" actualizado exitosamente` 
        });
      } else {
        const tipoId = tipoFormData.nombre.toLowerCase()
          .normalize("NFD")
          .replace(/[\u0300-\u036f]/g, "")
          .replace(/\s+/g, '_')
          .replace(/[^a-z0-9_]/g, '');


        const tipoRef = ref(database, `tipos_vehiculo/${tipoId}`);
        await set(tipoRef, {
          nombre: tipoFormData.nombre,
          icon: tipoFormData.icon,
          descripcion: tipoFormData.descripcion || '',
          categoria: tipoFormData.categoria || 'general',
          createdAt: new Date().toISOString(),
          createdBy: currentUser.uid
        });
        
        setMessage({ 
          type: 'success', 
          text: `✅ Tipo "${tipoFormData.nombre}" creado exitosamente` 
        });
      }
      
      setTipoFormData({
        nombre: '',
        icon: '🚛',
        descripcion: '',
        categoria: 'general'
      });
      setEditingTipo(null);
      setShowTipoModal(false);
      
    } catch (error) {
      console.error('Error al guardar tipo:', error);
      setMessage({ 
        type: 'error', 
        text: 'Error al guardar el tipo de vehículo' 
      });
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteTipoVehiculo = async (tipo) => {
    const vehiculosConTipo = vehiculos.filter(v => v.tipo === tipo.id);
    
    if (vehiculosConTipo.length > 0) {
      setMessage({ 
        type: 'error', 
        text: `❌ No se puede eliminar "${tipo.nombre}". Hay ${vehiculosConTipo.length} vehículo(s) usando este tipo` 
      });
      return;
    }

    if (window.confirm(`¿Estás seguro de eliminar el tipo "${tipo.nombre}"?`)) {
      try {
        const tipoRef = ref(database, `tipos_vehiculo/${tipo.id}`);
        await remove(tipoRef);
        
        setMessage({ 
          type: 'success', 
          text: `✅ Tipo "${tipo.nombre}" eliminado exitosamente` 
        });
      } catch (error) {
        console.error('Error al eliminar tipo:', error);
        setMessage({ 
          type: 'error', 
          text: 'Error al eliminar el tipo de vehículo' 
        });
      }
    }
  };

  const handleEditTipo = (tipo) => {
    setEditingTipo(tipo);
    setTipoFormData({
      nombre: tipo.nombre,
      icon: tipo.icon || '🚛',
      descripcion: tipo.descripcion || '',
      categoria: tipo.categoria || 'general'
    });
    setShowTipoModal(true);
  };

  // ========== FUNCIONES PARA SOLICITUDES ==========
  const handleResponseSolicitud = async (solicitudId, estado, respuesta) => {
    try {
      const solicitudRef = ref(database, `solicitudes_maquinaria/${solicitudId}`);
      await update(solicitudRef, {
        estado,
        respuesta,
        fechaRespuesta: new Date().toISOString(),
        respondidoPor: currentUser.uid,
        visto: true
      });

      const solicitud = solicitudes.find(s => s.id === solicitudId);
      if (solicitud) {
        const notificacionRef = ref(database, 'notificaciones');
        await push(notificacionRef, {
          tipo: 'respuesta_solicitud',
          mensaje: `Tu solicitud "${solicitud.asunto}" ha sido ${estado}`,
          destinatario: solicitud.solicitanteId,
          timestamp: new Date().toISOString(),
          leido: false
        });
        
        if (estado === 'aprobada') {
          setSolicitudParaZona(solicitud);
        }
      }

      setMessage({ 
        type: 'success', 
        text: `✅ Solicitud ${estado} exitosamente` 
      });
      setShowSolicitudDetails(null);
      
      if (estado === 'rechazada') {
        setSolicitudParaZona(null);
      }
    } catch (error) {
      console.error('Error al responder solicitud:', error);
      setMessage({ 
        type: 'error', 
        text: '❌ Error al procesar la solicitud' 
      });
    }
  };

  const crearZonaDesdeSolicitud = () => {
    if (!solicitudParaZona) return;
    
    localStorage.setItem('solicitudParaZona', JSON.stringify({
      nombre: `Trabajo: ${solicitudParaZona.asunto}`,
      descripcion: `${solicitudParaZona.descripcion}\n\nSolicitante: ${solicitudParaZona.nombreSolicitante}\nTeléfono: ${solicitudParaZona.telefonoContacto}\nLocalidad: ${solicitudParaZona.localidad || 'No especificada'}`,
      direccion: `Solicitado por ${solicitudParaZona.localidad || 'Junta de Vecinos'}`,
      lat: solicitudParaZona.ubicacion.lat,
      lng: solicitudParaZona.ubicacion.lng,
      prioridad: solicitudParaZona.prioridad,
      fechaLimite: solicitudParaZona.fechaNecesaria,
      tipoMaquinaria: solicitudParaZona.tipoMaquinaria,
      duracionEstimada: solicitudParaZona.duracionEstimada,
      solicitudId: solicitudParaZona.id
    }));
    
    setSolicitudParaZona(null);
    onViewChange('zones');
  };

  // ========== FUNCIONES PARA USUARIOS ==========
  const handleCreateUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        formData.email,
        formData.password
      );

      await setDoc(doc(firestore, 'users', userCredential.user.uid), {
        email: formData.email,
        name: formData.name,
        role: formData.role,
        phone: formData.phone,
        vehicleId: formData.vehicleId || null,
        localidad: formData.localidad || null,
        createdAt: new Date().toISOString(),
        createdBy: currentUser.uid,
        active: true
      });

      setMessage({ 
        type: 'success', 
        text: `✅ Usuario ${formData.name} creado exitosamente` 
      });

      setFormData({
        email: '',
        password: '',
        name: '',
        role: 'trabajador',
        phone: '',
        vehicleId: '',
        localidad: ''
      });

      loadUsers();
      setShowCreateForm(false);

    } catch (error) {
      console.error('Error al crear usuario:', error);
      
      let errorMessage = 'Error al crear usuario';
      switch (error.code) {
        case 'auth/email-already-in-use':
          errorMessage = 'Este correo ya está registrado';
          break;
        case 'auth/invalid-email':
          errorMessage = 'Correo electrónico inválido';
          break;
        case 'auth/weak-password':
          errorMessage = 'La contraseña debe tener al menos 6 caracteres';
          break;
        default:
          errorMessage = error.message;
      }
      
      setMessage({ type: 'error', text: errorMessage });
    } finally {
      setLoading(false);
    }
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setFormData({
      email: user.email,
      password: '',
      name: user.name || '',
      role: user.role || 'trabajador',
      phone: user.phone || '',
      vehicleId: user.vehicleId || '',
      localidad: user.localidad || ''
    });
    setShowEditUserForm(true);
    setShowCreateForm(false);
  };

  const handleUpdateUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      await updateDoc(doc(firestore, 'users', editingUser.id), {
        name: formData.name,
        role: formData.role,
        phone: formData.phone,
        vehicleId: formData.vehicleId || null,
        localidad: formData.localidad || null,
        updatedAt: new Date().toISOString(),
        updatedBy: currentUser.uid
      });

      setMessage({ 
        type: 'success', 
        text: `✅ Usuario ${formData.name} actualizado exitosamente` 
      });

      setFormData({
        email: '',
        password: '',
        name: '',
        role: 'trabajador',
        phone: '',
        vehicleId: '',
        localidad: ''
      });

      loadUsers();
      setShowEditUserForm(false);
      setEditingUser(null);

    } catch (error) {
      console.error('Error al actualizar usuario:', error);
      setMessage({ type: 'error', text: 'Error al actualizar usuario: ' + error.message });
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('¿Estás seguro de eliminar este usuario?')) {
      try {
        await deleteDoc(doc(firestore, 'users', userId));
        setMessage({ type: 'success', text: '✅ Usuario eliminado' });
        loadUsers();
      } catch (error) {
        setMessage({ type: 'error', text: 'Error al eliminar usuario' });
      }
    }
  };

  // ========== FUNCIONES PARA VEHÍCULOS ==========
  const handleCreateOrUpdateVehicle = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const estadoValido = ['disponible', 'en_uso', 'mantenimiento', 'fuera_servicio'].includes(vehicleFormData.estado) 
        ? vehicleFormData.estado 
        : 'disponible';
      
      const vehicleData = {
        ...vehicleFormData,
        estado: estadoValido,
        ultimaActualizacion: new Date().toISOString(),
        actualizadoPor: currentUser.uid
      };

      if (editingVehicle) {
        const vehicleRef = ref(database, `vehiculos/${editingVehicle.id}`);
        await update(vehicleRef, vehicleData);
        setMessage({ 
          type: 'success', 
          text: `✅ Vehículo ${vehicleFormData.nombre} actualizado exitosamente` 
        });
      } else {
        vehicleData.createdAt = new Date().toISOString();
        vehicleData.createdBy = currentUser.uid;
        
        const vehiculosRef = ref(database, 'vehiculos');
        const newVehicleRef = push(vehiculosRef);
        await set(newVehicleRef, vehicleData);
        
        setMessage({ 
          type: 'success', 
          text: `✅ Vehículo ${vehicleFormData.nombre} creado exitosamente` 
        });
      }

      setVehicleFormData({
        nombre: '',
        tipo: '',
        marca: '',
        modelo: '',
        año: new Date().getFullYear(),
        patente: '',
        color: '',
        numeroChasis: '',
        numeroMotor: '',
        kilometraje: 0,
        capacidadCombustible: 0,
        tipoCombustible: 'gasolina',
        ultimoMantenimiento: '',
        proximoMantenimiento: '',
        kilometrajeUltimoMantenimiento: 0,
        kilometrajeProximoMantenimiento: 0,
        fechaRevisionTecnica: '',
        fechaSeguro: '',
        fechaPermisoCirculacion: '',
        operadorAsignado: '',
        estado: 'disponible',
        lat: null,
        lng: null,
        observaciones: ''
      });
      
      setShowVehicleForm(false);
      setEditingVehicle(null);
      
      setTimeout(() => {
        loadVehiculos();
      }, 500);

    } catch (error) {
      console.error('Error al guardar vehículo:', error);
      setMessage({ type: 'error', text: 'Error al guardar el vehículo' });
    } finally {
      setLoading(false);
    }
  };

  const handleEditVehicle = (vehicle) => {
    setEditingVehicle(vehicle);
    setVehicleFormData({
      nombre: vehicle.nombre || '',
      tipo: vehicle.tipo || '',
      marca: vehicle.marca || '',
      modelo: vehicle.modelo || '',
      año: vehicle.año || new Date().getFullYear(),
      patente: vehicle.patente || '',
      color: vehicle.color || '',
      numeroChasis: vehicle.numeroChasis || '',
      numeroMotor: vehicle.numeroMotor || '',
      kilometraje: vehicle.kilometraje || 0,
      capacidadCombustible: vehicle.capacidadCombustible || 0,
      tipoCombustible: vehicle.tipoCombustible || 'gasolina',
      ultimoMantenimiento: vehicle.ultimoMantenimiento || '',
      proximoMantenimiento: vehicle.proximoMantenimiento || '',
      kilometrajeUltimoMantenimiento: vehicle.kilometrajeUltimoMantenimiento || 0,
      kilometrajeProximoMantenimiento: vehicle.kilometrajeProximoMantenimiento || 0,
      fechaRevisionTecnica: vehicle.fechaRevisionTecnica || '',
      fechaSeguro: vehicle.fechaSeguro || '',
      fechaPermisoCirculacion: vehicle.fechaPermisoCirculacion || '',
      operadorAsignado: vehicle.operadorAsignado || '',
      estado: vehicle.estado || 'disponible',
      lat: vehicle.lat || null,
      lng: vehicle.lng || null,
      observaciones: vehicle.observaciones || ''
    });
    setShowVehicleForm(true);
  };

  const handleQuickMaintenanceToggle = async (vehiculo) => {
    try {
      const nuevoEstado = vehiculo.estado === 'mantenimiento' ? 'disponible' : 'mantenimiento';
      const vehicleRef = ref(database, `vehiculos/${vehiculo.id}`);
      
      const updateData = {
        estado: nuevoEstado,
        ultimaActualizacion: new Date().toISOString(),
        fechaMantenimiento: nuevoEstado === 'mantenimiento' ? new Date().toISOString() : null
      };
      
      await update(vehicleRef, updateData);
      
      const vehiculoActualizado = { ...vehiculo, ...updateData };
      setVehiculos(prev => prev.map(v => v.id === vehiculo.id ? vehiculoActualizado : v));
      
      setMessage({ 
        type: 'success', 
        text: `✅ Vehículo ${vehiculo.nombre} marcado como ${nuevoEstado === 'mantenimiento' ? 'En Mantenimiento 🔧' : 'Disponible ✅'}` 
      });
      
      setTimeout(() => {
        loadVehiculos();
      }, 500);
    } catch (error) {
      console.error('Error al cambiar estado:', error);
      setMessage({ type: 'error', text: 'Error al cambiar estado del vehículo' });
    }
  };

  const handleCompleteMaintenance = async (vehiculo) => {
    try {
      const hoy = new Date();
      const hoyFormateado = hoy.toISOString().split('T')[0];
      const kilometrajeActual = vehiculo.kilometraje || 0;
      
      const proximaFecha = new Date();
      proximaFecha.setMonth(proximaFecha.getMonth() + 3);
      const proximoKm = kilometrajeActual + 5000;
      
      const vehicleRef = ref(database, `vehiculos/${vehiculo.id}`);
      const updateData = {
        estado: 'disponible',
        ultimoMantenimiento: hoyFormateado,
        kilometrajeUltimoMantenimiento: kilometrajeActual,
        proximoMantenimiento: proximaFecha.toISOString().split('T')[0],
        kilometrajeProximoMantenimiento: proximoKm,
        ultimaActualizacion: new Date().toISOString(),
        fechaMantenimiento: null
      };
      
      await update(vehicleRef, updateData);
      
      setMessage({ 
        type: 'success', 
        text: `✅ Mantenimiento completado para ${vehiculo.nombre}. Próximo mantenimiento: ${proximaFecha.toLocaleDateString('es-CL')} o ${proximoKm.toLocaleString()} km` 
      });
      
      setTimeout(() => {
        loadVehiculos();
      }, 500);
    } catch (error) {
      console.error('Error al completar mantenimiento:', error);
      setMessage({ type: 'error', text: 'Error al completar el mantenimiento' });
    }
  };

  const handleDeleteVehicle = async (vehicleId) => {
    if (window.confirm('¿Estás seguro de eliminar este vehículo? Esta acción no se puede deshacer.')) {
      try {
        const vehicleRef = ref(database, `vehiculos/${vehicleId}`);
        await remove(vehicleRef);
        setMessage({ type: 'success', text: '✅ Vehículo eliminado exitosamente' });
      } catch (error) {
        setMessage({ type: 'error', text: 'Error al eliminar el vehículo' });
      }
    }
  };

  // ========== FUNCIONES DE ESTADÍSTICAS ==========
  const getUserStats = () => {
    const stats = {
      total: users.length,
      admins: users.filter(u => u.role === 'admin').length,
      trabajadores: users.filter(u => u.role === 'trabajador').length,
      superadmins: users.filter(u => u.role === 'superadmin').length,
      juntasVecinos: users.filter(u => u.role === 'junta_vecinos').length
    };
    return stats;
  };

  const getVehicleStats = () => {
    const stats = {
      total: vehiculos.length,
      disponibles: vehiculos.filter(v => v.estado === 'disponible').length,
      enUso: vehiculos.filter(v => v.estado === 'en_uso').length,
      mantenimiento: vehiculos.filter(v => v.estado === 'mantenimiento').length,
      activos: vehiculos.filter(v => v.estado === 'disponible' || v.estado === 'en_uso').length,
      fueraServicio: vehiculos.filter(v => v.estado === 'fuera_servicio').length
    };
    return stats;
  };

  // ========== FUNCIONES DEL SIDEBAR ==========
  const toggleMenu = (menuId) => {
    setExpandedMenus(prev => 
      prev.includes(menuId) 
        ? prev.filter(id => id !== menuId)
        : [...prev, menuId]
    );
  };

  const menuItems = [
    {
      id: 'navegacion',
      icon: '🚀',
      title: 'Navegación',
      submenu: [
        { id: 'dashboard', icon: '📊', title: 'Dashboard GPS', view: 'dashboard' },
        { id: 'zones', icon: '🗺️', title: 'Gestión Zonas', view: 'zones' },
        { id: 'fleet', icon: '🚛', title: 'Panel Flota', view: 'fleet' }
      ]
    },
    {
      id: 'gestion',
      icon: '⚙️',
      title: 'Gestión Sistema',
      submenu: [
        { 
          id: 'users', 
          icon: '👥', 
          title: 'Usuarios', 
          tab: 'users',
          badge: getUserStats().total 
        },
        { 
          id: 'vehicles', 
          icon: '🚛', 
          title: 'Vehículos', 
          tab: 'vehicles',
          badge: getVehicleStats().total 
        },
        { 
          id: 'solicitudes', 
          icon: '📋', 
          title: 'Solicitudes', 
          tab: 'solicitudes',
          badge: solicitudes.filter(s => s.estado === 'pendiente').length,
          badgeType: 'alert'
        }
      ]
    }
  ];

  // ========== COMPONENTE MODAL DE TIPOS DE VEHÍCULOS ==========
  {showTipoModal && (
  <div style={{
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'rgba(0,0,0,0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
    padding: '20px'
  }}>
        <div style={{
      background: 'white',
      borderRadius: '12px',
      maxWidth: '900px',
      width: '100%',
      maxHeight: '90vh',
      overflow: 'auto',
      padding: '30px'
    }}>
          <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '25px'
      }}>
            <h2 style={{ margin: 0, color: '#1f2937', fontSize: '24px' }}>
              ⚙️ Gestión de Tipos de Vehículos
            </h2>
            <button
              onClick={() => {
                setShowTipoModal(false);
                setEditingTipo(null);
                setTipoFormData({
                  nombre: '',
                  icon: '🚛',
                  descripcion: '',
                  categoria: 'general'
                });
              }}
              style={{
                padding: '8px',
                background: '#6b7280',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '18px'
              }}
            >
              ✖
            </button>
          </div>

          <div style={{
            background: '#f9fafb',
            padding: '20px',
            borderRadius: '8px',
            marginBottom: '25px'
          }}>
            <h3 style={{ 
              margin: '0 0 15px 0', 
              color: '#374151', 
              fontSize: '18px' 
            }}>
              {editingTipo ? '✏️ Editar Tipo' : '➕ Crear Nuevo Tipo'}
            </h3>
            
            <form onSubmit={handleSaveTipoVehiculo}>
              <div style={{
                display: 'grid',
                gridTemplateColumns: isMobile ? '1fr' : '1fr 1fr',
                gap: '15px'
              }}>
                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '6px',
                    color: '#374151',
                    fontSize: '14px',
                    fontWeight: '500'
                  }}>
                    Nombre del Tipo *
                  </label>
                  <input
                    type="text"
                    required
                    value={tipoFormData.nombre}
                    onChange={(e) => setTipoFormData({...tipoFormData, nombre: e.target.value})}
                    placeholder="Ej: Retroexcavadora, Camión Tolva, etc."
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>

                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '6px',
                    color: '#374151',
                    fontSize: '14px',
                    fontWeight: '500'
                  }}>
                    Ícono
                  </label>
                  <div style={{ display: 'flex', gap: '10px' }}>
                    <select
                      value={tipoFormData.icon}
                      onChange={(e) => setTipoFormData({...tipoFormData, icon: e.target.value})}
                      style={{
                        flex: 1,
                        padding: '10px',
                        border: '1px solid #d1d5db',
                        borderRadius: '6px',
                        fontSize: '16px'
                      }}
                    >
                      <optgroup label="Vehículos">
                        <option value="🚗">🚗 Auto</option>
                        <option value="🛻">🛻 Pickup</option>
                        <option value="🚚">🚚 Camión</option>
                        <option value="🚛">🚛 Camión Grande</option>
                      </optgroup>
                      <optgroup label="Maquinaria">
                        <option value="🚜">🚜 MaquinariaPesada</option>
                      </optgroup>
                      
                    </select>
                    <div style={{
                      width: '50px',
                      height: '42px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      background: '#f3f4f6',
                      borderRadius: '6px',
                      fontSize: '24px'
                    }}>
                      {tipoFormData.icon}
                    </div>
                  </div>
                </div>

                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '6px',
                    color: '#374151',
                    fontSize: '14px',
                    fontWeight: '500'
                  }}>
                    Categoría
                  </label>
                  <select
                    value={tipoFormData.categoria}
                    onChange={(e) => setTipoFormData({...tipoFormData, categoria: e.target.value})}
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  >
                    <option value="general">General</option>
                    <option value="liviano">Vehículo Liviano</option>
                    <option value="pesado">Vehículo Pesado</option>
                    <option value="maquinaria">Maquinaria</option>
                    <option value="especial">Especial</option>
                    <option value="emergencia">Emergencia</option>
                  </select>
                </div>

                <div>
                  <label style={{
                    display: 'block',
                    marginBottom: '6px',
                    color: '#374151',
                    fontSize: '14px',
                    fontWeight: '500'
                  }}>
                    Descripción (Opcional)
                  </label>
                  <input
                    type="text"
                    value={tipoFormData.descripcion}
                    onChange={(e) => setTipoFormData({...tipoFormData, descripcion: e.target.value})}
                    placeholder="Descripción breve del tipo"
                    style={{
                      width: '100%',
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>
              </div>

              <div style={{ marginTop: '20px', display: 'flex', gap: '10px' }}>
                <button
                  type="submit"
                  disabled={loading}
                  style={{
                    padding: '10px 20px',
                    background: loading ? '#9ca3af' : '#22c55e',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: loading ? 'not-allowed' : 'pointer'
                  }}
                >
                  {loading ? 'Guardando...' : editingTipo ? '✅ Actualizar' : '✅ Crear Tipo'}
                </button>
                
                {editingTipo && (
                  <button
                    type="button"
                    onClick={() => {
                      setEditingTipo(null);
                      setTipoFormData({
                        nombre: '',
                        icon: '🚛',
                        descripcion: '',
                        categoria: 'general'
                      });
                    }}
                    style={{
                      padding: '10px 20px',
                      background: '#6b7280',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      fontSize: '14px',
                      fontWeight: '500',
                      cursor: 'pointer'
                    }}
                  >
                    Cancelar Edición
                  </button>
                )}
              </div>
            </form>
          </div>

          <div>
            <h3 style={{ 
              margin: '0 0 15px 0', 
              color: '#374151', 
              fontSize: '18px' 
            }}>
              📋 Tipos Existentes ({tiposVehiculo.length})
            </h3>

            {tiposVehiculo.length === 0 ? (
              <div style={{
                padding: '40px',
                textAlign: 'center',
                background: '#f9fafb',
                borderRadius: '8px',
                color: '#6b7280'
              }}>
                <p style={{ fontSize: '16px', marginBottom: '10px' }}>
                  No hay tipos de vehículos creados
                </p>
                <p style={{ fontSize: '14px' }}>
                  Crea tu primer tipo usando el formulario de arriba
                </p>
              </div>
            ) : (
              <div style={{
                display: 'grid',
                gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fill, minmax(250px, 1fr))',
                gap: '15px'
              }}>
                {tiposVehiculo.map(tipo => {
                  const vehiculosConTipo = vehiculos.filter(v => v.tipo === tipo.id).length;
                  
                  return (
                    <div key={tipo.id} style={{
                      background: 'white',
                      border: '1px solid #e5e7eb',
                      borderRadius: '8px',
                      padding: '15px',
                      position: 'relative'
                    }}>
                      <div style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '10px',
                        marginBottom: '10px'
                      }}>
                        <span style={{ fontSize: '24px' }}>{tipo.icon}</span>
                        <div style={{ flex: 1 }}>
                          <h4 style={{ 
                            margin: 0, 
                            fontSize: '16px', 
                            color: '#1f2937' 
                          }}>
                            {tipo.nombre}
                          </h4>
                          {tipo.categoria && (
                            <span style={{
                              fontSize: '12px',
                              color: '#6b7280',
                              background: '#f3f4f6',
                              padding: '2px 6px',
                              borderRadius: '4px',
                              display: 'inline-block',
                              marginTop: '4px'
                            }}>
                              {tipo.categoria}
                            </span>
                          )}
                        </div>
                      </div>
                      
                      {tipo.descripcion && (
                        <p style={{ 
                          margin: '0 0 10px 0', 
                          fontSize: '13px', 
                          color: '#6b7280' 
                        }}>
                          {tipo.descripcion}
                        </p>
                      )}
                      
                      <div style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        paddingTop: '10px',
                        borderTop: '1px solid #f3f4f6'
                      }}>
                        <span style={{
                          fontSize: '12px',
                          color: '#6b7280'
                        }}>
                          {vehiculosConTipo} vehículo(s)
                        </span>
                        
                        <div style={{ display: 'flex', gap: '5px' }}>
                          <button
                            onClick={() => handleEditTipo(tipo)}
                            style={{
                              padding: '6px 10px',
                              background: '#3b82f6',
                              color: 'white',
                              border: 'none',
                              borderRadius: '4px',
                              fontSize: '12px',
                              cursor: 'pointer'
                            }}
                          >
                            ✏️
                          </button>
                          <button
                            onClick={() => handleDeleteTipoVehiculo(tipo)}
                            disabled={vehiculosConTipo > 0}
                            style={{
                              padding: '6px 10px',
                              background: vehiculosConTipo > 0 ? '#d1d5db' : '#ef4444',
                              color: 'white',
                              border: 'none',
                              borderRadius: '4px',
                              fontSize: '12px',
                              cursor: vehiculosConTipo > 0 ? 'not-allowed' : 'pointer',
                              opacity: vehiculosConTipo > 0 ? 0.5 : 1
                            }}
                            title={vehiculosConTipo > 0 ? 'No se puede eliminar, hay vehículos usando este tipo' : 'Eliminar tipo'}
                          >
                            🗑️
                          </button>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };

  // ========== RENDER PRINCIPAL ==========
  return (
    <div style={{ display: 'flex', height: '100vh', background: '#f3f4f6' }}>
      {/* Modal de Tipos de Vehículos */}
      
      <TiposVehiculoModal />

      {/* Overlay para móvil */}
      {isMobile && isSidebarOpen && (
        <div 
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0,0,0,0.5)',
            zIndex: 998
          }}
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      {/* Sidebar */}
      <div style={{
        width: isSidebarOpen ? (isMobile ? '280px' : '260px') : '60px',
        background: 'linear-gradient(135deg, #1e293b 0%, #334155 100%)',
        transition: 'width 0.3s ease',
        position: isMobile ? 'fixed' : 'relative',
        height: '100vh',
        zIndex: 999,
        left: isMobile && !isSidebarOpen ? '-280px' : '0',
        boxShadow: '2px 0 10px rgba(0,0,0,0.1)',
        display: 'flex',
        flexDirection: 'column'
      }}>
        {/* Header del Sidebar */}
        <div style={{
          padding: isSidebarOpen ? '20px' : '20px 10px',
          borderBottom: '1px solid rgba(255,255,255,0.1)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}>
          <div style={{ 
            display: 'flex', 
            alignItems: 'center',
            gap: '10px'
          }}>
            {isSidebarOpen ? (
              <>
                <span style={{ fontSize: '24px' }}>🔐</span>
                <div>
                  <h2 style={{ 
                    margin: 0, 
                    color: 'white', 
                    fontSize: '16px',
                    fontWeight: '600'
                  }}>
                    Super Admin
                  </h2>
                  <p style={{ 
                    margin: 0, 
                    color: 'rgba(255,255,255,0.7)', 
                    fontSize: '12px' 
                  }}>
                    Panel de Control
                  </p>
                </div>
              </>
            ) : (
              <span style={{ fontSize: '24px' }}>🔐</span>
            )}
          </div>
          
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            style={{
              background: 'rgba(255,255,255,0.1)',
              border: 'none',
              color: 'white',
              padding: '8px',
              borderRadius: '6px',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            {isSidebarOpen ? '◀' : '▶'}
          </button>
        </div>

        {/* Menú del Sidebar */}
        <div style={{ 
          flex: 1, 
          overflowY: 'auto',
          padding: '10px 0'
        }}>
          {menuItems.map(menu => (
            <div key={menu.id} style={{ marginBottom: '5px' }}>
              <button
                onClick={() => toggleMenu(menu.id)}
                style={{
                  width: '100%',
                  padding: isSidebarOpen ? '12px 20px' : '12px',
                  background: 'transparent',
                  border: 'none',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: isSidebarOpen ? 'space-between' : 'center',
                  cursor: 'pointer',
                  transition: 'background 0.2s',
                  fontSize: '14px'
                }}
                onMouseEnter={(e) => e.currentTarget.style.background = 'rgba(255,255,255,0.1)'}
                onMouseLeave={(e) => e.currentTarget.style.background = 'transparent'}
              >
                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                  <span style={{ fontSize: '18px' }}>{menu.icon}</span>
                  {isSidebarOpen && (
                    <span style={{ fontWeight: '500' }}>{menu.title}</span>
                  )}
                </div>
                {isSidebarOpen && (
                  <span style={{ fontSize: '12px' }}>
                    {expandedMenus.includes(menu.id) ? '▼' : '▶'}
                  </span>
                )}
              </button>

              {/* Submenú */}
              {isSidebarOpen && expandedMenus.includes(menu.id) && (
                <div style={{ 
                  background: 'rgba(0,0,0,0.2)',
                  marginTop: '2px'
                }}>
                  {menu.submenu.map(item => (
                    <button
                      key={item.id}
                      onClick={() => {
                        if (item.view) {
                          onViewChange(item.view);
                        } else if (item.tab) {
                          setActiveTab(item.tab);
                        }
                      }}
                      style={{
                        width: '100%',
                        padding: '10px 20px 10px 50px',
                        background: (item.tab && activeTab === item.tab) || 
                                   (item.view && currentView === item.view) 
                                   ? 'rgba(59, 130, 246, 0.3)' : 'transparent',
                        border: 'none',
                        borderLeft: (item.tab && activeTab === item.tab) || 
                                    (item.view && currentView === item.view)
                                    ? '3px solid #3b82f6' : '3px solid transparent',
                        color: 'white',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                        cursor: 'pointer',
                        transition: 'all 0.2s',
                        fontSize: '13px',
                        textAlign: 'left'
                      }}
                      onMouseEnter={(e) => {
                        if (!((item.tab && activeTab === item.tab) || 
                              (item.view && currentView === item.view))) {
                          e.currentTarget.style.background = 'rgba(255,255,255,0.05)';
                        }
                      }}
                      onMouseLeave={(e) => {
                        if (!((item.tab && activeTab === item.tab) || 
                              (item.view && currentView === item.view))) {
                          e.currentTarget.style.background = 'transparent';
                        }
                      }}
                    >
                      <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                        <span>{item.icon}</span>
                        <span>{item.title}</span>
                      </div>
                      {item.badge > 0 && (
                        <span style={{
                          background: item.badgeType === 'alert' ? '#ef4444' : '#3b82f6',
                          color: 'white',
                          padding: '2px 6px',
                          borderRadius: '10px',
                          fontSize: '11px',
                          fontWeight: 'bold',
                          minWidth: '20px',
                          textAlign: 'center'
                        }}>
                          {item.badge}
                        </span>
                      )}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Footer del Sidebar */}
        <div style={{
          padding: isSidebarOpen ? '20px' : '10px',
          borderTop: '1px solid rgba(255,255,255,0.1)'
        }}>
          {isSidebarOpen ? (
            <>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '10px',
                marginBottom: '15px',
                color: 'white'
              }}>
                <div style={{
                  width: '40px',
                  height: '40px',
                  background: 'rgba(255,255,255,0.2)',
                  borderRadius: '50%',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '18px'
                }}>
                  👤
                </div>
                <div style={{ flex: 1 }}>
                  <div style={{ fontSize: '14px', fontWeight: '500' }}>
                    {currentUser.name || 'Usuario'}
                  </div>
                  <div style={{ fontSize: '12px', opacity: 0.7 }}>
                    {currentUser.email}
                  </div>
                </div>
              </div>
              <button
                onClick={onLogout}
                style={{
                  width: '100%',
                  padding: '10px',
                  background: '#ef4444',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: '500',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '8px'
                }}
              >
                🚪 Cerrar Sesión
              </button>
            </>
          ) : (
            <button
              onClick={onLogout}
              style={{
                width: '40px',
                height: '40px',
                background: '#ef4444',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '18px',
                margin: '0 auto',
                display: 'block'
              }}
              title="Cerrar Sesión"
            >
              🚪
            </button>
          )}
        </div>
      </div>

      {/* Contenido Principal */}
      <div style={{ 
        flex: 1, 
        overflow: 'auto',
        position: 'relative'
      }}>
        {/* Header móvil */}
        {isMobile && (
          <div style={{
            background: 'white',
            padding: '15px',
            borderBottom: '1px solid #e5e7eb',
            display: 'flex',
            alignItems: 'center',
            gap: '15px'
          }}>
            <button
              onClick={() => setIsSidebarOpen(true)}
              style={{
                background: '#1e293b',
                color: 'white',
                border: 'none',
                padding: '8px',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '20px'
              }}
            >
              ☰
            </button>
            <h1 style={{ 
              margin: 0, 
              fontSize: '18px', 
              color: '#1e293b' 
            }}>
              Panel de Control
            </h1>
          </div>
        )}

        <div style={{ padding: isMobile ? '15px' : '30px' }}>
          {/* Mensajes */}
          {message.text && (
            <div style={{
              padding: '12px',
              borderRadius: '8px',
              marginBottom: '20px',
              background: message.type === 'success' ? '#dcfce7' : '#fee2e2',
              border: `1px solid ${message.type === 'success' ? '#86efac' : '#fecaca'}`,
              color: message.type === 'success' ? '#166534' : '#991b1b'
            }}>
              {message.text}
            </div>
          )}

          {/* Mensaje para crear zona desde solicitud */}
          {solicitudParaZona && (
            <div style={{
              padding: '15px',
              borderRadius: '8px',
              marginBottom: '20px',
              background: '#dbeafe',
              border: '2px solid #3b82f6',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              flexWrap: 'wrap',
              gap: '10px'
            }}>
              <div>
                <strong style={{ color: '#1e40af' }}>
                  ✅ Solicitud aprobada: {solicitudParaZona.asunto}
                </strong>
                <p style={{ margin: '5px 0 0 0', fontSize: '14px', color: '#1e3a8a' }}>
                  Ahora puedes crear una zona de trabajo basada en esta solicitud
                </p>
              </div>
              <div style={{ display: 'flex', gap: '10px' }}>
                <button
                  onClick={crearZonaDesdeSolicitud}
                  style={{
                    padding: '10px 20px',
                    background: '#22c55e',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontSize: '14px',
                    fontWeight: '600'
                  }}
                >
                  🗺️ Crear Zona
                </button>
                <button
                  onClick={() => setSolicitudParaZona(null)}
                  style={{
                    padding: '10px 20px',
                    background: '#6b7280',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontSize: '14px'
                  }}
                >
                  Cancelar
                </button>
              </div>
            </div>
          )}

          {/* CONTENIDO DE USUARIOS */}
          {activeTab === 'users' && (
            <>
              <h1 style={{ 
                margin: '0 0 30px 0', 
                color: '#1e293b',
                fontSize: '28px',
                fontWeight: '600'
              }}>
                👥 Gestión de Usuarios
              </h1>

              {/* Estadísticas de usuarios */}
              <div style={{
                display: 'grid',
                gridTemplateColumns: `repeat(auto-fit, minmax(${isMobile ? '150px' : '200px'}, 1fr))`,
                gap: '20px',
                marginBottom: '30px'
              }}>
                <div style={{
                  background: 'white',
                  padding: '20px',
                  borderRadius: '12px',
