// src/components/NotificationSystem.js
import React, { useState, useEffect } from 'react';
import { database } from '../config/firebase';
import { ref, onValue, push, update, remove, set } from 'firebase/database';

const NotificationSystem = ({ currentUser, isMobile }) => {
  const [notifications, setNotifications] = useState([]);
  const [showNotifications, setShowNotifications] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [loading, setLoading] = useState(false);

  // Cargar notificaciones
  useEffect(() => {
    if (!currentUser?.uid) return;

    const notificationsRef = ref(database, `notificaciones/${currentUser.uid}`);
    const unsubscribe = onValue(notificationsRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const notificationsArray = Object.entries(data)
          .map(([id, notif]) => ({
            id,
            ...notif
          }))
          .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
        
        setNotifications(notificationsArray);
        
        // Contar no le√≠das
        const unread = notificationsArray.filter(n => !n.leida).length;
        setUnreadCount(unread);
      } else {
        setNotifications([]);
        setUnreadCount(0);
      }
    });

    return () => unsubscribe();
  }, [currentUser]);

  // Marcar como le√≠da
  const markAsRead = async (notificationId) => {
    try {
      const notifRef = ref(database, `notificaciones/${currentUser.uid}/${notificationId}`);
      await update(notifRef, {
        leida: true,
        fechaLectura: new Date().toISOString()
      });
    } catch (error) {
      console.error('Error al marcar como le√≠da:', error);
    }
  };

  // Marcar todas como le√≠das
  const markAllAsRead = async () => {
    setLoading(true);
    try {
      const updates = {};
      notifications.forEach(notif => {
        if (!notif.leida) {
          updates[`notificaciones/${currentUser.uid}/${notif.id}/leida`] = true;
          updates[`notificaciones/${currentUser.uid}/${notif.id}/fechaLectura`] = new Date().toISOString();
        }
      });
      
      if (Object.keys(updates).length > 0) {
        await update(ref(database), updates);
      }
    } catch (error) {
      console.error('Error al marcar todas como le√≠das:', error);
    } finally {
      setLoading(false);
    }
  };

  // Eliminar notificaci√≥n
  const deleteNotification = async (notificationId) => {
    try {
      const notifRef = ref(database, `notificaciones/${currentUser.uid}/${notificationId}`);
      await remove(notifRef);
    } catch (error) {
      console.error('Error al eliminar notificaci√≥n:', error);
    }
  };

  // Eliminar todas las notificaciones
  const deleteAllNotifications = async () => {
    if (!window.confirm('¬øEst√°s seguro de eliminar todas las notificaciones?')) return;
    
    setLoading(true);
    try {
      const notificationsRef = ref(database, `notificaciones/${currentUser.uid}`);
      await remove(notificationsRef);
      setShowNotifications(false);
    } catch (error) {
      console.error('Error al eliminar todas las notificaciones:', error);
    } finally {
      setLoading(false);
    }
  };

  // Obtener icono seg√∫n tipo de notificaci√≥n
  const getNotificationIcon = (tipo) => {
    const icons = {
      vehiculo_asignado: 'üöõ',
      vehiculo_actualizado: 'üîß',
      vehiculo_mantenimiento: 'üîß',
      documento_subido: 'üìÑ',
      documento_vencido: '‚ö†Ô∏è',
      licencia_vencida: 'üö´',
      licencia_proxima: '‚è∞',
      trabajador_creado: 'üë∑',
      trabajador_actualizado: '‚úèÔ∏è',
      sistema: '‚öôÔ∏è',
      alerta: '‚ö†Ô∏è',
      exito: '‚úÖ',
      error: '‚ùå'
    };
    return icons[tipo] || 'üì¢';
  };

  // Obtener color seg√∫n tipo
  const getNotificationColor = (tipo) => {
    const colors = {
      error: '#ef4444',
      alerta: '#f59e0b',
      exito: '#22c55e',
      vehiculo_mantenimiento: '#f59e0b',
      documento_vencido: '#ef4444',
      licencia_vencida: '#ef4444',
      licencia_proxima: '#f59e0b'
    };
    return colors[tipo] || '#3b82f6';
  };

  // Formatear fecha relativa
  const formatRelativeTime = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now - date;
    const seconds = Math.floor(diff / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (seconds < 60) return 'Hace un momento';
    if (minutes < 60) return `Hace ${minutes} minuto${minutes > 1 ? 's' : ''}`;
    if (hours < 24) return `Hace ${hours} hora${hours > 1 ? 's' : ''}`;
    if (days < 7) return `Hace ${days} d√≠a${days > 1 ? 's' : ''}`;
    
    return date.toLocaleDateString('es-CL');
  };

  return (
    <>
      {/* Bot√≥n de Notificaciones */}
      <button
        onClick={() => setShowNotifications(!showNotifications)}
        style={{
          position: 'relative',
          padding: '10px',
          background: 'white',
          border: '1px solid #e5e7eb',
          borderRadius: '8px',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          gap: '8px',
          transition: 'all 0.2s'
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.boxShadow = '0 2px 8px rgba(0,0,0,0.1)';
          e.currentTarget.style.transform = 'translateY(-1px)';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.boxShadow = 'none';
          e.currentTarget.style.transform = 'translateY(0)';
        }}
      >
        <span style={{ fontSize: '20px' }}>üîî</span>
        
        {/* Contador de no le√≠das */}
        {unreadCount > 0 && (
          <span style={{
            position: 'absolute',
            top: '-5px',
            right: '-5px',
            background: '#ef4444',
            color: 'white',
            borderRadius: '50%',
            width: '22px',
            height: '22px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '11px',
            fontWeight: '600',
            border: '2px solid white',
            animation: unreadCount > 0 ? 'pulse 2s infinite' : 'none'
          }}>
            {unreadCount > 99 ? '99+' : unreadCount}
          </span>
        )}
        
        {!isMobile && (
          <span style={{ fontSize: '14px', color: '#374151' }}>
            Notificaciones
          </span>
        )}
      </button>

      {/* Panel de Notificaciones */}
      {showNotifications && (
        <>
          {/* Overlay para cerrar al hacer clic fuera */}
          <div
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'rgba(0,0,0,0.3)',
              zIndex: 998
            }}
            onClick={() => setShowNotifications(false)}
          />

          {/* Panel de Notificaciones */}
          <div style={{
            position: 'fixed',
            top: isMobile ? '0' : '70px',
            right: isMobile ? '0' : '20px',
            width: isMobile ? '100%' : '400px',
            maxHeight: isMobile ? '100vh' : '80vh',
            background: 'white',
            borderRadius: isMobile ? '0' : '12px',
            boxShadow: '0 10px 40px rgba(0,0,0,0.15)',
            zIndex: 999,
            display: 'flex',
            flexDirection: 'column',
            animation: 'slideIn 0.3s ease'
          }}>
            {/* Header */}
            <div style={{
              padding: '20px',
              borderBottom: '1px solid #e5e7eb',
              background: 'linear-gradient(135deg, #f9fafb 0%, #f3f4f6 100%)'
            }}>
              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                marginBottom: '10px'
              }}>
                <h3 style={{
                  margin: 0,
                  fontSize: '18px',
                  color: '#1f2937',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}>
                  üîî Notificaciones
                  {unreadCount > 0 && (
                    <span style={{
                      padding: '2px 8px',
                      background: '#ef4444',
                      color: 'white',
                      borderRadius: '12px',
                      fontSize: '12px'
                    }}>
                      {unreadCount} nueva{unreadCount > 1 ? 's' : ''}
                    </span>
                  )}
                </h3>
                <button
                  onClick={() => setShowNotifications(false)}
                  style={{
                    background: 'transparent',
                    border: 'none',
                    fontSize: '20px',
                    cursor: 'pointer',
                    color: '#6b7280'
                  }}
                >
                  ‚úñ
                </button>
              </div>

              {/* Acciones r√°pidas */}
              {notifications.length > 0 && (
                <div style={{
                  display: 'flex',
                  gap: '10px',
                  fontSize: '13px'
                }}>
                  <button
                    onClick={markAllAsRead}
                    disabled={loading || unreadCount === 0}
                    style={{
                      flex: 1,
                      padding: '6px 12px',
                      background: '#3b82f6',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      cursor: loading || unreadCount === 0 ? 'not-allowed' : 'pointer',
                      opacity: loading || unreadCount === 0 ? 0.5 : 1,
                      fontSize: '12px',
                      fontWeight: '500'
                    }}
                  >
                    ‚úì Marcar todas como le√≠das
                  </button>
                  <button
                    onClick={deleteAllNotifications}
                    disabled={loading}
                    style={{
                      flex: 1,
                      padding: '6px 12px',
                      background: '#ef4444',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      cursor: loading ? 'not-allowed' : 'pointer',
                      opacity: loading ? 0.5 : 1,
                      fontSize: '12px',
                      fontWeight: '500'
                    }}
                  >
                    üóëÔ∏è Eliminar todas
                  </button>
                </div>
              )}
            </div>

            {/* Lista de Notificaciones */}
            <div style={{
              flex: 1,
              overflowY: 'auto',
              maxHeight: isMobile ? 'calc(100vh - 140px)' : '500px'
            }}>
              {notifications.length === 0 ? (
                <div style={{
                  padding: '60px 20px',
                  textAlign: 'center',
                  color: '#9ca3af'
                }}>
                  <div style={{ fontSize: '48px', marginBottom: '10px' }}>üì≠</div>
                  <p style={{ fontSize: '14px' }}>No tienes notificaciones</p>
                </div>
              ) : (
                notifications.map((notif) => (
                  <div
                    key={notif.id}
                    style={{
                      padding: '15px 20px',
                      borderBottom: '1px solid #f3f4f6',
                      background: notif.leida ? 'white' : '#f0f9ff',
                      cursor: 'pointer',
                      transition: 'background 0.2s',
                      position: 'relative'
                    }}
                    onClick={() => !notif.leida && markAsRead(notif.id)}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.background = '#f9fafb';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.background = notif.leida ? 'white' : '#f0f9ff';
                    }}
                  >
                    <div style={{
                      display: 'flex',
                      gap: '12px'
                    }}>
                      {/* Icono */}
                      <div style={{
                        width: '40px',
                        height: '40px',
                        background: `${getNotificationColor(notif.tipo)}20`,
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        fontSize: '20px',
                        flexShrink: 0
                      }}>
                        {getNotificationIcon(notif.tipo)}
                      </div>

                      {/* Contenido */}
                      <div style={{ flex: 1, minWidth: 0 }}>
                        <div style={{
                          fontSize: '14px',
                          color: '#1f2937',
                          fontWeight: notif.leida ? '400' : '600',
                          marginBottom: '4px',
                          lineHeight: '1.4'
                        }}>
                          {notif.titulo}
                        </div>
                        {notif.mensaje && (
                          <div style={{
                            fontSize: '13px',
                            color: '#6b7280',
                            lineHeight: '1.4'
                          }}>
                            {notif.mensaje}
                          </div>
                        )}
                        <div style={{
                          fontSize: '11px',
                          color: '#9ca3af',
                          marginTop: '6px',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '8px'
                        }}>
                          <span>‚è∞ {formatRelativeTime(notif.timestamp)}</span>
                          {!notif.leida && (
                            <span style={{
                              padding: '2px 6px',
                              background: '#3b82f6',
                              color: 'white',
                              borderRadius: '4px',
                              fontSize: '10px',
                              fontWeight: '600'
                            }}>
                              NUEVA
                            </span>
                          )}
                        </div>
                      </div>

                      {/* Bot√≥n eliminar */}
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          deleteNotification(notif.id);
                        }}
                        style={{
                          background: 'transparent',
                          border: 'none',
                          color: '#ef4444',
                          cursor: 'pointer',
                          padding: '4px',
                          fontSize: '16px',
                          opacity: 0.6,
                          transition: 'opacity 0.2s'
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.opacity = '1';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.opacity = '0.6';
                        }}
                      >
                        üóëÔ∏è
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </>
      )}

      <style jsx>{`
        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateY(-20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes pulse {
          0% {
            transform: scale(1);
          }
          50% {
            transform: scale(1.1);
          }
          100% {
            transform: scale(1);
          }
        }
      `}</style>
    </>
  );
};

// Funci√≥n helper para crear notificaciones
export const createNotification = async (userId, notification) => {
  try {
    const notificationsRef = ref(database, `notificaciones/${userId}`);
    await push(notificationsRef, {
      ...notification,
      timestamp: new Date().toISOString(),
      leida: false
    });
  } catch (error) {
    console.error('Error al crear notificaci√≥n:', error);
  }
};

export default NotificationSystem;
