// src/components/PasswordRecovery.js
import React, { useState } from 'react';
import { firestore } from '../config/firebase';
import { collection, query, where, getDocs, doc, updateDoc } from 'firebase/firestore';

const PasswordRecovery = ({ onClose, onSuccess }) => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    email: '',
    pin: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [userData, setUserData] = useState(null);
  const [temporaryPassword, setTemporaryPassword] = useState('');

  // Funci√≥n para generar contrase√±a temporal
  const generateTemporaryPassword = () => {
    const chars = 'ABCDEFGHJKLMNPQRSTUVWXYZabcdefghjkmnpqrstuvwxyz23456789';
    let password = 'Temp'; // Prefijo para identificar f√°cilmente
    for (let i = 0; i < 6; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return password;
  };

  // Verificar email y PIN
  const handleVerifyPin = async (e) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }
    
    console.log('Iniciando verificaci√≥n de PIN...'); // Debug
    console.log('Email:', formData.email);
    console.log('PIN:', formData.pin);
    
    // Validaciones b√°sicas
    if (!formData.email || !formData.pin) {
      setMessage({ 
        type: 'error', 
        text: '‚ùå Por favor completa todos los campos' 
      });
      return;
    }

    if (formData.pin.length !== 4) {
      setMessage({ 
        type: 'error', 
        text: '‚ùå El PIN debe tener 4 d√≠gitos' 
      });
      return;
    }

    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Buscar usuario por email
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', formData.email.toLowerCase().trim()));
      const querySnapshot = await getDocs(q);

      console.log('Usuarios encontrados:', querySnapshot.size); // Debug

      if (querySnapshot.empty) {
        setMessage({ 
          type: 'error', 
          text: '‚ùå No existe un usuario con ese correo electr√≥nico' 
        });
        setLoading(false);
        return;
      }

      const userDoc = querySnapshot.docs[0];
      const user = { id: userDoc.id, ...userDoc.data() };
      
      console.log('Usuario encontrado:', user.name); // Debug
      console.log('PIN almacenado (codificado):', user.recoveryPin); // Debug

      // Verificar PIN - intentar ambos formatos
      let pinCorrecto = false;
      
      if (user.recoveryPin) {
        try {
          // Intentar decodificar base64
          const storedPin = atob(user.recoveryPin);
          console.log('PIN decodificado:', storedPin); // Debug
          pinCorrecto = (formData.pin === storedPin);
        } catch (e) {
          // Si falla la decodificaci√≥n, comparar directamente
          console.log('Comparando PIN directamente'); // Debug
          pinCorrecto = (formData.pin === user.recoveryPin);
        }
      } else {
        setMessage({ 
          type: 'error', 
          text: '‚ùå Este usuario no tiene PIN configurado. Contacta al administrador.' 
        });
        setLoading(false);
        return;
      }

      if (!pinCorrecto) {
        setMessage({ 
          type: 'error', 
          text: '‚ùå PIN incorrecto. Intenta nuevamente.' 
        });
        setLoading(false);
        return;
      }

      console.log('PIN correcto! Generando contrase√±a temporal...'); // Debug

      // Generar contrase√±a temporal
      const tempPass = generateTemporaryPassword();
      console.log('Contrase√±a temporal generada:', tempPass); // Debug
      
      // Guardar en Firestore
      await updateDoc(doc(firestore, 'users', user.id), {
        tempPassword: btoa(tempPass), // Codificar en base64
        passwordResetAt: new Date().toISOString(),
        requirePasswordChange: true,
        resetMethod: 'PIN'
      });

      console.log('Contrase√±a guardada en Firestore'); // Debug

      // Actualizar estado
      setUserData(user);
      setTemporaryPassword(tempPass);
      setStep(2);
      setMessage({ 
        type: 'success', 
        text: '‚úÖ PIN verificado correctamente' 
      });

    } catch (error) {
      console.error('Error completo:', error); // Debug
      setMessage({ 
        type: 'error', 
        text: '‚ùå Error al verificar los datos: ' + error.message 
      });
    } finally {
      setLoading(false);
    }
  };

  // Copiar al portapapeles
  const copyToClipboard = () => {
    navigator.clipboard.writeText(temporaryPassword)
      .then(() => {
        setMessage({ 
          type: 'success', 
          text: '‚úÖ Contrase√±a copiada al portapapeles' 
        });
        setTimeout(() => {
          setMessage({ type: '', text: '' });
        }, 3000);
      })
      .catch(err => {
        console.error('Error al copiar:', err);
        // Fallback para navegadores antiguos
        const textArea = document.createElement("textarea");
        textArea.value = temporaryPassword;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        setMessage({ 
          type: 'success', 
          text: '‚úÖ Contrase√±a copiada' 
        });
      });
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000,
      padding: '20px'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        padding: '30px',
        maxWidth: '450px',
        width: '100%',
        boxShadow: '0 10px 40px rgba(0,0,0,0.2)',
        position: 'relative'
      }}>
        {/* Bot√≥n cerrar fuera del formulario */}
        <button
          type="button"
          onClick={(e) => {
            e.preventDefault();
            e.stopPropagation();
            onClose();
          }}
          style={{
            position: 'absolute',
            top: '15px',
            right: '15px',
            background: 'transparent',
            border: 'none',
            fontSize: '24px',
            cursor: 'pointer',
            color: '#6b7280',
            padding: '5px',
            width: '30px',
            height: '30px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        >
          ‚úñ
        </button>

        {/* Header */}
        <div style={{ marginBottom: '25px' }}>
          <h2 style={{ 
            margin: 0, 
            fontSize: '24px', 
            color: '#1f2937' 
          }}>
            üîê Recuperar Contrase√±a
          </h2>
        </div>

        {/* Indicador de pasos */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          marginBottom: '25px',
          gap: '10px'
        }}>
          <div style={{
            flex: 1,
            height: '4px',
            background: step >= 1 ? '#3b82f6' : '#e5e7eb',
            borderRadius: '2px',
            transition: 'all 0.3s'
          }} />
          <div style={{
            flex: 1,
            height: '4px',
            background: step >= 2 ? '#22c55e' : '#e5e7eb',
            borderRadius: '2px',
            transition: 'all 0.3s'
          }} />
        </div>

        {/* Mensajes */}
        {message.text && (
          <div style={{
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            background: message.type === 'success' ? '#dcfce7' : '#fee2e2',
            border: `1px solid ${message.type === 'success' ? '#86efac' : '#fecaca'}`,
            color: message.type === 'success' ? '#166534' : '#991b1b',
            fontSize: '14px'
          }}>
            {message.text}
          </div>
        )}

        {/* PASO 1: Verificar Email y PIN */}
        {step === 1 && (
          <div>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Correo Electr√≥nico
              </label>
              <input
                type="email"
                required
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                placeholder="tu@correo.com"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px',
                  outline: 'none'
                }}
                autoFocus
              />
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                PIN de Seguridad (4 d√≠gitos)
              </label>
              <input
                type="text"
                required
                maxLength="4"
                value={formData.pin}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, '');
                  setFormData({...formData, pin: value});
                }}
                onKeyPress={(e) => {
                  if (e.key === 'Enter' && formData.pin.length === 4) {
                    e.preventDefault();
                    handleVerifyPin(e);
                  }
                }}
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '24px',
                  textAlign: 'center',
                  letterSpacing: '15px',
                  fontWeight: 'bold'
                }}
              />
              <small style={{ 
                color: '#6b7280', 
                fontSize: '12px',
                display: 'block',
                marginTop: '5px'
              }}>
                Ingresa tu PIN de 4 d√≠gitos
              </small>
            </div>

            <button
              type="button"
              onClick={handleVerifyPin}
              disabled={loading || formData.pin.length !== 4 || !formData.email}
              style={{
                width: '100%',
                padding: '14px',
                background: loading || formData.pin.length !== 4 || !formData.email ? '#9ca3af' : 
                          'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: loading || formData.pin.length !== 4 || !formData.email ? 'not-allowed' : 'pointer'
              }}
            >
              {loading ? 'Verificando...' : 'Verificar PIN ‚Üí'}
            </button>
          </div>
        )}

        {/* PASO 2: Mostrar contrase√±a temporal */}
        {step === 2 && (
          <div>
            <div style={{
              marginBottom: '20px',
              padding: '15px',
              background: '#dcfce7',
              borderRadius: '8px',
              border: '1px solid #22c55e'
            }}>
              <p style={{ 
                margin: 0, 
                fontSize: '16px', 
                color: '#166534',
                fontWeight: '600'
              }}>
                ‚úÖ Recuperaci√≥n Exitosa
              </p>
              <p style={{ 
                margin: '8px 0 0 0', 
                fontSize: '14px', 
                color: '#166534' 
              }}>
                Usuario: <strong>{userData?.name}</strong>
              </p>
            </div>

            <div style={{
              background: '#f0f9ff',
              border: '2px dashed #3b82f6',
              borderRadius: '8px',
              padding: '20px',
              marginBottom: '20px',
              textAlign: 'center'
            }}>
              <p style={{
                margin: '0 0 15px 0',
                fontSize: '14px',
                color: '#1e40af',
                fontWeight: '600'
              }}>
                Tu contrase√±a temporal es:
              </p>
              <div style={{
                background: 'white',
                padding: '15px',
                borderRadius: '6px',
                margin: '10px 0',
                border: '1px solid #dbeafe'
              }}>
                <code style={{
                  fontSize: '22px',
                  fontWeight: 'bold',
                  color: '#1e293b',
                  letterSpacing: '2px',
                  fontFamily: 'monospace',
                  userSelect: 'all'
                }}>
                  {temporaryPassword}
                </code>
              </div>
              <button
                type="button"
                onClick={copyToClipboard}
                style={{
                  padding: '10px 20px',
                  background: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  fontWeight: '500',
                  cursor: 'pointer'
                }}
              >
                üìã Copiar Contrase√±a
              </button>
            </div>

            <div style={{
              background: '#fef3c7',
              padding: '12px',
              borderRadius: '6px',
              marginBottom: '20px',
              fontSize: '13px',
              color: '#92400e'
            }}>
              <strong>‚ö†Ô∏è Importante:</strong> Esta contrase√±a es temporal. 
              Deber√°s cambiarla al iniciar sesi√≥n.
            </div>

            <button
              type="button"
              onClick={() => {
                onSuccess();
                onClose();
              }}
              style={{
                width: '100%',
                padding: '14px',
                background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer'
              }}
            >
              ‚úÖ Ir a Iniciar Sesi√≥n
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default PasswordRecovery;
