// src/components/PasswordRecovery.js
import React, { useState } from 'react';
import { firestore } from '../config/firebase';
import { collection, query, where, getDocs, doc, updateDoc } from 'firebase/firestore';

const PasswordRecovery = ({ onClose, onSuccess }) => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    email: '',
    pin: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [userData, setUserData] = useState(null);

  // Función simple de hash (para producción usar bcrypt)
  const simpleHash = (text) => {
    return btoa(text); // Codificación base64 simple
  };

  // Verificar email y PIN
  const handleVerifyPin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', formData.email));
      const querySnapshot = await getDocs(q);

      if (querySnapshot.empty) {
        setMessage({ 
          type: 'error', 
          text: '❌ No existe un usuario con ese correo electrónico' 
        });
        setLoading(false);
        return;
      }

      const userDoc = querySnapshot.docs[0];
      const user = { id: userDoc.id, ...userDoc.data() };

      // Verificar PIN
      const storedPin = user.recoveryPin ? atob(user.recoveryPin) : null;
      
      if (!storedPin) {
        setMessage({ 
          type: 'error', 
          text: '❌ Este usuario no tiene PIN configurado' 
        });
        setLoading(false);
        return;
      }

      if (formData.pin !== storedPin) {
        setMessage({ 
          type: 'error', 
          text: '❌ PIN incorrecto' 
        });
        setLoading(false);
        return;
      }

      // PIN correcto
      setUserData(user);
      setStep(2);
      setMessage({ 
        type: 'success', 
        text: '✅ PIN verificado. Ahora crea tu nueva contraseña.' 
      });

    } catch (error) {
      console.error('Error:', error);
      setMessage({ 
        type: 'error', 
        text: '❌ Error al verificar los datos' 
      });
    } finally {
      setLoading(false);
    }
  };

  // Actualizar contraseña
  const handleUpdatePassword = async (e) => {
    e.preventDefault();
    
    if (formData.newPassword !== formData.confirmPassword) {
      setMessage({ 
        type: 'error', 
        text: '❌ Las contraseñas no coinciden' 
      });
      return;
    }

    if (formData.newPassword.length < 6) {
      setMessage({ 
        type: 'error', 
        text: '❌ La contraseña debe tener al menos 6 caracteres' 
      });
      return;
    }

    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Guardar la nueva contraseña hasheada en Firestore
      await updateDoc(doc(firestore, 'users', userData.id), {
        tempPassword: simpleHash(formData.newPassword),
        passwordResetAt: new Date().toISOString(),
        requirePasswordChange: true,
        resetMethod: 'PIN'
      });

      setMessage({ 
        type: 'success', 
        text: '✅ ¡Contraseña actualizada exitosamente! Ya puedes iniciar sesión con tu nueva contraseña.' 
      });

      // Cerrar modal después de 3 segundos
      setTimeout(() => {
        onSuccess();
        onClose();
      }, 3000);

    } catch (error) {
      console.error('Error:', error);
      setMessage({ 
        type: 'error', 
        text: '❌ Error al actualizar la contraseña' 
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000,
      padding: '20px'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        padding: '30px',
        maxWidth: '400px',
        width: '100%',
        boxShadow: '0 10px 40px rgba(0,0,0,0.2)'
      }}>
        {/* Header */}
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '25px'
        }}>
          <h2 style={{ 
            margin: 0, 
            fontSize: '24px', 
            color: '#1f2937' 
          }}>
            🔐 Recuperar Contraseña
          </h2>
          <button
            onClick={onClose}
            style={{
              background: 'transparent',
              border: 'none',
              fontSize: '24px',
              cursor: 'pointer',
              color: '#6b7280'
            }}
          >
            ✖
          </button>
        </div>

        {/* Indicador de pasos */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          marginBottom: '25px',
          gap: '10px'
        }}>
          <div style={{
            width: '100px',
            height: '4px',
            background: step >= 1 ? '#3b82f6' : '#e5e7eb',
            borderRadius: '2px',
            transition: 'all 0.3s'
          }} />
          <div style={{
            width: '100px',
            height: '4px',
            background: step >= 2 ? '#22c55e' : '#e5e7eb',
            borderRadius: '2px',
            transition: 'all 0.3s'
          }} />
        </div>

        {/* Mensajes */}
        {message.text && (
          <div style={{
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            background: message.type === 'success' ? '#dcfce7' : '#fee2e2',
            border: `1px solid ${message.type === 'success' ? '#86efac' : '#fecaca'}`,
            color: message.type === 'success' ? '#166534' : '#991b1b',
            fontSize: '14px',
            animation: 'slideIn 0.3s ease'
          }}>
            {message.text}
          </div>
        )}

        {/* PASO 1: Verificar Email y PIN */}
        {step === 1 && (
          <form onSubmit={handleVerifyPin}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Correo Electrónico
              </label>
              <input
                type="email"
                required
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                placeholder="tu@correo.com"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px'
                }}
                autoFocus
              />
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                PIN de Seguridad
              </label>
              <input
                type="text"
                required
                maxLength="4"
                pattern="[0-9]{4}"
                value={formData.pin}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, '');
                  setFormData({...formData, pin: value});
                }}
                placeholder="••••"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '24px',
                  textAlign: 'center',
                  letterSpacing: '15px',
                  fontWeight: 'bold'
                }}
              />
              <small style={{ 
                color: '#6b7280', 
                fontSize: '12px',
                display: 'block',
                marginTop: '5px'
              }}>
                Ingresa tu PIN de 4 dígitos
              </small>
            </div>

            <button
              type="submit"
              disabled={loading || formData.pin.length !== 4}
              style={{
                width: '100%',
                padding: '14px',
                background: loading || formData.pin.length !== 4 ? '#9ca3af' : 
                          'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: loading || formData.pin.length !== 4 ? 'not-allowed' : 'pointer',
                transition: 'all 0.3s'
              }}
            >
              {loading ? (
                <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
                  <span className="spinner"></span>
                  Verificando...
                </span>
              ) : 'Verificar PIN →'}
            </button>
          </form>
        )}

        {/* PASO 2: Nueva Contraseña */}
        {step === 2 && (
          <form onSubmit={handleUpdatePassword}>
            <div style={{
              marginBottom: '20px',
              padding: '15px',
              background: 'linear-gradient(135deg, #eff6ff 0%, #e0f2fe 100%)',
              borderRadius: '8px',
              border: '1px solid #3b82f6'
            }}>
              <p style={{ 
                margin: 0, 
                fontSize: '14px', 
                color: '#1e40af',
                fontWeight: '600'
              }}>
                ✅ Usuario Verificado
              </p>
              <p style={{ 
                margin: '8px 0 0 0', 
                fontSize: '14px', 
                color: '#1e40af' 
              }}>
                👤 {userData?.name}
              </p>
              <p style={{ 
                margin: '4px 0 0 0', 
                fontSize: '13px', 
                color: '#64748b' 
              }}>
                📧 {userData?.email}
              </p>
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Nueva Contraseña
              </label>
              <input
                type="password"
                required
                minLength="6"
                value={formData.newPassword}
                onChange={(e) => setFormData({...formData, newPassword: e.target.value})}
                placeholder="Mínimo 6 caracteres"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px'
                }}
                autoFocus
              />
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Confirmar Contraseña
              </label>
              <input
                type="password"
                required
                minLength="6"
                value={formData.confirmPassword}
                onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                placeholder="Repite la contraseña"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px'
                }}
              />
            </div>

            <button
              type="submit"
              disabled={loading}
              style={{
                width: '100%',
                padding: '14px',
                background: loading ? '#9ca3af' : 
                          'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: loading ? 'not-allowed' : 'pointer',
                transition: 'all 0.3s'
              }}
            >
              {loading ? (
                <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
                  <span className="spinner"></span>
                  Actualizando contraseña...
                </span>
              ) : '✅ Cambiar Contraseña'}
            </button>
          </form>
        )}

        <style>{`
          @keyframes slideIn {
            from {
              opacity: 0;
              transform: translateY(-10px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
          
          .spinner {
            width: 16px;
            height: 16px;
            border: 2px solid #ffffff;
            border-top-color: transparent;
            border-radius: 50%;
            animation: spin 0.6s linear infinite;
            display: inline-block;
          }
          
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    </div>
  );
};

export default PasswordRecovery;
