// src/services/pdfExportService.js
import jsPDF from 'jspdf';
import 'jspdf-autotable';

// ========== CONFIGURACI√ìN DE ESTILOS ==========
const COLORS = {
  primary: '#667eea',
  secondary: '#764ba2',
  success: '#22c55e',
  warning: '#f59e0b',
  danger: '#ef4444',
  dark: '#1f2937',
  gray: '#6b7280'
};

const FONTS = {
  title: 20,
  subtitle: 16,
  heading: 14,
  normal: 11,
  small: 9
};

// ========== SERVICIO DE EXPORTACI√ìN PDF ==========
export const pdfExportService = {
  
  // ========== EXPORTAR USUARIOS A PDF ==========
  exportUsersToPDF: (users, currentUser) => {
    const doc = new jsPDF();
    const pageWidth = doc.internal.pageSize.width;
    const pageHeight = doc.internal.pageSize.height;
    const margins = { top: 20, left: 15, right: 15, bottom: 20 };
    
    // Fecha y hora actual
    const now = new Date();
    const fecha = now.toLocaleDateString('es-CL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
    const hora = now.toLocaleTimeString('es-CL', {
      hour: '2-digit',
      minute: '2-digit'
    });

    // ========== HEADER ==========
    // Logo o t√≠tulo del sistema
    doc.setFillColor(102, 126, 234);
    doc.rect(0, 0, pageWidth, 40, 'F');
    
    // T√≠tulo principal
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(FONTS.title);
    doc.setFont(undefined, 'bold');
    doc.text('SISTEMA MAPAQUILL√ìN', pageWidth / 2, 20, { align: 'center' });
    
    // Subt√≠tulo
    doc.setFontSize(FONTS.subtitle);
    doc.setFont(undefined, 'normal');
    doc.text('Reporte de Usuarios', pageWidth / 2, 30, { align: 'center' });
    
    // ========== INFORMACI√ìN DEL REPORTE ==========
    doc.setTextColor(COLORS.dark);
    doc.setFontSize(FONTS.small);
    doc.setFont(undefined, 'normal');
    
    // Cuadro de informaci√≥n
    doc.setFillColor(245, 247, 250);
    doc.rect(margins.left, 50, pageWidth - margins.left - margins.right, 25, 'F');
    
    doc.text(`Fecha de generaci√≥n: ${fecha}`, margins.left + 5, 60);
    doc.text(`Hora: ${hora}`, margins.left + 5, 67);
    doc.text(`Generado por: ${currentUser.name || currentUser.email}`, pageWidth / 2, 60);
    doc.text(`Total de usuarios: ${users.length}`, pageWidth / 2, 67);
    
    // ========== ESTAD√çSTICAS DE USUARIOS ==========
    const stats = {
      superadmins: users.filter(u => u.role === 'superadmin').length,
      admins: users.filter(u => u.role === 'admin').length,
      trabajadores: users.filter(u => u.role === 'trabajador').length,
      juntasVecinos: users.filter(u => u.role === 'junta_vecinos').length,
      activos: users.filter(u => u.active !== false).length,
      inactivos: users.filter(u => u.active === false).length
    };
    
    // Cuadro de estad√≠sticas
    let yPosition = 85;
    doc.setFontSize(FONTS.heading);
    doc.setFont(undefined, 'bold');
    doc.text('üìä Estad√≠sticas de Usuarios', margins.left, yPosition);
    
    yPosition += 10;
    doc.setFontSize(FONTS.normal);
    doc.setFont(undefined, 'normal');
    
    // Crear grid de estad√≠sticas
    const statsData = [
      ['Super Admins', stats.superadmins, 'Administradores', stats.admins],
      ['Trabajadores', stats.trabajadores, 'Juntas de Vecinos', stats.juntasVecinos],
      ['Usuarios Activos', stats.activos, 'Usuarios Inactivos', stats.inactivos]
    ];
    
    doc.autoTable({
      startY: yPosition,
      head: [],
      body: statsData,
      theme: 'grid',
      styles: {
        fontSize: 10,
        cellPadding: 3
      },
      columnStyles: {
        0: { fontStyle: 'bold', fillColor: [240, 242, 245] },
        2: { fontStyle: 'bold', fillColor: [240, 242, 245] }
      }
    });
    
    // ========== TABLA DE USUARIOS ==========
    yPosition = doc.lastAutoTable.finalY + 15;
    
    doc.setFontSize(FONTS.heading);
    doc.setFont(undefined, 'bold');
    doc.text('üë• Lista Detallada de Usuarios', margins.left, yPosition);
    
    // Preparar datos para la tabla
    const tableHeaders = [
      ['#', 'Nombre', 'Email', 'Rol', 'Tel√©fono', 'Localidad', 'Estado', 'Fecha Registro']
    ];
    
    const tableData = users.map((user, index) => {
      const fechaRegistro = user.createdAt 
        ? new Date(user.createdAt).toLocaleDateString('es-CL')
        : 'No disponible';
      
      const rol = {
        'superadmin': 'üîê Super Admin',
        'admin': 'üë®‚Äçüíº Administrador',
        'trabajador': 'üë∑ Trabajador',
        'junta_vecinos': 'üèòÔ∏è Junta Vecinos'
      }[user.role] || user.role;
      
      return [
        index + 1,
        user.name || 'Sin nombre',
        user.email,
        rol,
        user.phone || '-',
        user.localidad || '-',
        user.active === false ? '‚ùå Inactivo' : '‚úÖ Activo',
        fechaRegistro
      ];
    });
    
    // Generar tabla con autoTable
    doc.autoTable({
      startY: yPosition + 7,
      head: tableHeaders,
      body: tableData,
      theme: 'striped',
      headStyles: {
        fillColor: [102, 126, 234],
        textColor: 255,
        fontSize: 10,
        fontStyle: 'bold'
      },
      bodyStyles: {
        fontSize: 9
      },
      alternateRowStyles: {
        fillColor: [245, 247, 250]
      },
      columnStyles: {
        0: { cellWidth: 10, halign: 'center' },
        1: { cellWidth: 35 },
        2: { cellWidth: 45 },
        3: { cellWidth: 28 },
        4: { cellWidth: 25 },
        5: { cellWidth: 25 },
        6: { cellWidth: 20, halign: 'center' },
        7: { cellWidth: 25, halign: 'center' }
      },
      didDrawPage: function(data) {
        // Footer en cada p√°gina
        doc.setFontSize(FONTS.small);
        doc.setTextColor(COLORS.gray);
        doc.text(
          `P√°gina ${data.pageNumber} de ${doc.internal.getNumberOfPages()}`,
          pageWidth / 2,
          pageHeight - 10,
          { align: 'center' }
        );
      }
    });
    
    // ========== PIE DE P√ÅGINA FINAL ==========
    const finalY = doc.lastAutoTable.finalY;
    if (finalY < pageHeight - 40) {
      doc.setDrawColor(200, 200, 200);
      doc.line(margins.left, finalY + 10, pageWidth - margins.right, finalY + 10);
      
      doc.setFontSize(FONTS.small);
      doc.setTextColor(COLORS.gray);
      doc.setFont(undefined, 'italic');
      doc.text(
        'Este documento fue generado autom√°ticamente por el Sistema MapaQuill√≥n',
        pageWidth / 2,
        finalY + 20,
        { align: 'center' }
      );
      doc.text(
        '¬© 2024 Municipalidad de Quill√≥n - Todos los derechos reservados',
        pageWidth / 2,
        finalY + 27,
        { align: 'center' }
      );
    }
    
    // Guardar el PDF
    doc.save(`usuarios_mapaquillon_${now.getTime()}.pdf`);
  },

  // ========== EXPORTAR VEH√çCULOS A PDF ==========
  exportVehiclesToPDF: (vehiculos, tiposVehiculo, currentUser) => {
    const doc = new jsPDF('landscape'); // Orientaci√≥n horizontal para m√°s espacio
    const pageWidth = doc.internal.pageSize.width;
    const pageHeight = doc.internal.pageSize.height;
    const margins = { top: 20, left: 15, right: 15, bottom: 20 };
    
    // Fecha y hora actual
    const now = new Date();
    const fecha = now.toLocaleDateString('es-CL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
    const hora = now.toLocaleTimeString('es-CL', {
      hour: '2-digit',
      minute: '2-digit'
    });

    // ========== HEADER ==========
    doc.setFillColor(59, 130, 246);
    doc.rect(0, 0, pageWidth, 40, 'F');
    
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(FONTS.title);
    doc.setFont(undefined, 'bold');
    doc.text('SISTEMA MAPAQUILL√ìN', pageWidth / 2, 20, { align: 'center' });
    
    doc.setFontSize(FONTS.subtitle);
    doc.setFont(undefined, 'normal');
    doc.text('Reporte de Flota Vehicular', pageWidth / 2, 30, { align: 'center' });
    
    // ========== INFORMACI√ìN DEL REPORTE ==========
    doc.setTextColor(COLORS.dark);
    doc.setFontSize(FONTS.small);
    doc.setFont(undefined, 'normal');
    
    doc.setFillColor(245, 247, 250);
    doc.rect(margins.left, 50, pageWidth - margins.left - margins.right, 25, 'F');
    
    doc.text(`Fecha de generaci√≥n: ${fecha}`, margins.left + 5, 60);
    doc.text(`Hora: ${hora}`, margins.left + 5, 67);
    doc.text(`Generado por: ${currentUser.name || currentUser.email}`, pageWidth / 2, 60);
    doc.text(`Total de veh√≠culos: ${vehiculos.length}`, pageWidth / 2, 67);
    
    // ========== ESTAD√çSTICAS DE FLOTA ==========
    const stats = {
      total: vehiculos.length,
      disponibles: vehiculos.filter(v => v.estado === 'disponible').length,
      enUso: vehiculos.filter(v => v.estado === 'en_uso').length,
      mantenimiento: vehiculos.filter(v => v.estado === 'mantenimiento').length,
      fueraServicio: vehiculos.filter(v => v.estado === 'fuera_servicio').length
    };
    
    // Calcular porcentajes
    const porcentajes = {
      disponibles: ((stats.disponibles / stats.total) * 100).toFixed(1),
      enUso: ((stats.enUso / stats.total) * 100).toFixed(1),
      mantenimiento: ((stats.mantenimiento / stats.total) * 100).toFixed(1),
      fueraServicio: ((stats.fueraServicio / stats.total) * 100).toFixed(1)
    };
    
    let yPosition = 85;
    doc.setFontSize(FONTS.heading);
    doc.setFont(undefined, 'bold');
    doc.text('üìä Estado de la Flota', margins.left, yPosition);
    
    yPosition += 10;
    
    // Tabla de estad√≠sticas
    const statsData = [
      ['Estado', 'Cantidad', 'Porcentaje', 'Indicador'],
      ['‚úÖ Disponibles', stats.disponibles, `${porcentajes.disponibles}%`, '‚ñà‚ñà‚ñà‚ñà‚ñà‚ñà‚ñà‚ñà‚ñà'],
      ['üîß En Uso', stats.enUso, `${porcentajes.enUso}%`, '‚ñà‚ñà‚ñà‚ñà‚ñà'],
      ['üîß En Mantenimiento', stats.mantenimiento, `${porcentajes.mantenimiento}%`, '‚ñà‚ñà‚ñà'],
      ['‚ùå Fuera de Servicio', stats.fueraServicio, `${porcentajes.fueraServicio}%`, '‚ñà‚ñà']
    ];
    
    doc.autoTable({
      startY: yPosition,
      head: [statsData[0]],
      body: statsData.slice(1),
      theme: 'grid',
      headStyles: {
        fillColor: [59, 130, 246],
        textColor: 255,
        fontSize: 10,
        fontStyle: 'bold'
      },
      styles: {
        fontSize: 10,
        cellPadding: 4
      },
      columnStyles: {
        0: { cellWidth: 50 },
        1: { cellWidth: 30, halign: 'center' },
        2: { cellWidth: 30, halign: 'center' },
        3: { cellWidth: 40, halign: 'left' }
      }
    });
    
    // ========== ESTAD√çSTICAS POR TIPO DE VEH√çCULO ==========
    yPosition = doc.lastAutoTable.finalY + 15;
    
    doc.setFontSize(FONTS.heading);
    doc.setFont(undefined, 'bold');
    doc.text('üöõ Distribuci√≥n por Tipo de Veh√≠culo', margins.left, yPosition);
    
    yPosition += 10;
    
    // Agrupar veh√≠culos por tipo
    const vehiculosPorTipo = {};
    vehiculos.forEach(v => {
      const tipoNombre = tiposVehiculo.find(t => t.id === v.tipo)?.nombre || v.tipo || 'Sin tipo';
      if (!vehiculosPorTipo[tipoNombre]) {
        vehiculosPorTipo[tipoNombre] = {
          total: 0,
          disponibles: 0,
          enUso: 0,
          mantenimiento: 0
        };
      }
      vehiculosPorTipo[tipoNombre].total++;
      if (v.estado === 'disponible') vehiculosPorTipo[tipoNombre].disponibles++;
      if (v.estado === 'en_uso') vehiculosPorTipo[tipoNombre].enUso++;
      if (v.estado === 'mantenimiento') vehiculosPorTipo[tipoNombre].mantenimiento++;
    });
    
    const tiposData = Object.entries(vehiculosPorTipo).map(([tipo, stats]) => [
      tipo,
      stats.total,
      stats.disponibles,
      stats.enUso,
      stats.mantenimiento
    ]);
    
    if (tiposData.length > 0) {
      doc.autoTable({
        startY: yPosition,
        head: [['Tipo de Veh√≠culo', 'Total', 'Disponibles', 'En Uso', 'Mantenimiento']],
        body: tiposData,
        theme: 'striped',
        headStyles: {
          fillColor: [59, 130, 246],
          textColor: 255,
          fontSize: 10,
          fontStyle: 'bold'
        },
        styles: {
          fontSize: 9
        },
        columnStyles: {
          0: { cellWidth: 60 },
          1: { cellWidth: 25, halign: 'center' },
          2: { cellWidth: 30, halign: 'center' },
          3: { cellWidth: 25, halign: 'center' },
          4: { cellWidth: 35, halign: 'center' }
        }
      });
    }
    
    // ========== LISTA DETALLADA DE VEH√çCULOS ==========
    yPosition = doc.lastAutoTable ? doc.lastAutoTable.finalY + 15 : yPosition + 15;
    
    // Verificar si necesitamos una nueva p√°gina
    if (yPosition > pageHeight - 60) {
      doc.addPage();
      yPosition = margins.top;
    }
    
    doc.setFontSize(FONTS.heading);
    doc.setFont(undefined, 'bold');
    doc.text('üìã Inventario Detallado de Veh√≠culos', margins.left, yPosition);
    
    // Preparar datos para la tabla
    const tableHeaders = [
      ['#', 'Nombre', 'Tipo', 'Marca', 'Modelo', 'A√±o', 'Patente', 'Estado', '√öltimo Mant.', 'Pr√≥ximo Mant.']
    ];
    
    const tableData = vehiculos.map((vehiculo, index) => {
      const tipo = tiposVehiculo.find(t => t.id === vehiculo.tipo);
      const tipoDisplay = tipo ? `${tipo.icon || ''} ${tipo.nombre}` : vehiculo.tipo || 'Sin tipo';
      
      const estadoDisplay = {
        'disponible': '‚úÖ Disponible',
        'en_uso': 'üîß En Uso',
        'mantenimiento': 'üîß Mantenimiento',
        'fuera_servicio': '‚ùå Fuera Servicio'
      }[vehiculo.estado] || vehiculo.estado || 'Desconocido';
      
      const ultimoMant = vehiculo.ultimoMantenimiento 
        ? new Date(vehiculo.ultimoMantenimiento).toLocaleDateString('es-CL')
        : '-';
      
      const proximoMant = vehiculo.proximoMantenimiento
        ? new Date(vehiculo.proximoMantenimiento).toLocaleDateString('es-CL')
        : '-';
      
      return [
        index + 1,
        vehiculo.nombre || 'Sin nombre',
        tipoDisplay,
        vehiculo.marca || '-',
        vehiculo.modelo || '-',
        vehiculo.a√±o || '-',
        vehiculo.patente || '-',
        estadoDisplay,
        ultimoMant,
        proximoMant
      ];
    });
    
    // Generar tabla con autoTable
    doc.autoTable({
      startY: yPosition + 7,
      head: tableHeaders,
      body: tableData,
      theme: 'striped',
      headStyles: {
        fillColor: [59, 130, 246],
        textColor: 255,
        fontSize: 9,
        fontStyle: 'bold'
      },
      bodyStyles: {
        fontSize: 8
      },
      alternateRowStyles: {
        fillColor: [245, 247, 250]
      },
      columnStyles: {
        0: { cellWidth: 10, halign: 'center' },
        1: { cellWidth: 35 },
        2: { cellWidth: 35 },
        3: { cellWidth: 25 },
        4: { cellWidth: 25 },
        5: { cellWidth: 15, halign: 'center' },
        6: { cellWidth: 25 },
        7: { cellWidth: 28 },
        8: { cellWidth: 25, halign: 'center' },
        9: { cellWidth: 25, halign: 'center' }
      },
      didDrawPage: function(data) {
        // Footer en cada p√°gina
        doc.setFontSize(FONTS.small);
        doc.setTextColor(COLORS.gray);
        doc.text(
          `P√°gina ${data.pageNumber} de ${doc.internal.getNumberOfPages()}`,
          pageWidth / 2,
          pageHeight - 10,
          { align: 'center' }
        );
      }
    });
    
    // ========== SECCI√ìN DE MANTENIMIENTOS PR√ìXIMOS ==========
    const mantenimientosPendientes = vehiculos
      .filter(v => v.proximoMantenimiento)
      .map(v => ({
        ...v,
        diasRestantes: Math.ceil((new Date(v.proximoMantenimiento) - new Date()) / (1000 * 60 * 60 * 24))
      }))
      .filter(v => v.diasRestantes <= 30)
      .sort((a, b) => a.diasRestantes - b.diasRestantes);
    
    if (mantenimientosPendientes.length > 0) {
      // Verificar si necesitamos una nueva p√°gina
      if (doc.lastAutoTable.finalY > pageHeight - 60) {
        doc.addPage();
        yPosition = margins.top;
      } else {
        yPosition = doc.lastAutoTable.finalY + 15;
      }
      
      doc.setFontSize(FONTS.heading);
      doc.setFont(undefined, 'bold');
      doc.setTextColor(COLORS.warning);
      doc.text('‚ö†Ô∏è Mantenimientos Pr√≥ximos (30 d√≠as)', margins.left, yPosition);
      
      const mantData = mantenimientosPendientes.map(v => [
        v.nombre,
        v.patente || '-',
        new Date(v.proximoMantenimiento).toLocaleDateString('es-CL'),
        v.diasRestantes <= 0 ? 'VENCIDO' : `${v.diasRestantes} d√≠as`,
        v.diasRestantes <= 7 ? 'üî¥ Urgente' : v.diasRestantes <= 15 ? 'üü° Pronto' : 'üü¢ Programado'
      ]);
      
      doc.autoTable({
        startY: yPosition + 7,
        head: [['Veh√≠culo', 'Patente', 'Fecha Programada', 'D√≠as Restantes', 'Prioridad']],
        body: mantData,
        theme: 'grid',
        headStyles: {
          fillColor: [245, 158, 11],
          textColor: 255,
          fontSize: 9,
          fontStyle: 'bold'
        },
        bodyStyles: {
          fontSize: 9
        },
        columnStyles: {
          3: { halign: 'center' },
          4: { halign: 'center' }
        }
      });
    }
    
    // ========== PIE DE P√ÅGINA FINAL ==========
    const finalY = doc.lastAutoTable.finalY;
    if (finalY < pageHeight - 30) {
      doc.setDrawColor(200, 200, 200);
      doc.line(margins.left, finalY + 10, pageWidth - margins.right, finalY + 10);
      
      doc.setFontSize(FONTS.small);
      doc.setTextColor(COLORS.gray);
      doc.setFont(undefined, 'italic');
      doc.text(
        'Este documento fue generado autom√°ticamente por el Sistema MapaQuill√≥n',
        pageWidth / 2,
        finalY + 20,
        { align: 'center' }
      );
    }
    
    // Guardar el PDF
    doc.save(`vehiculos_mapaquillon_${now.getTime()}.pdf`);
  },

  // ========== EXPORTAR REPORTE COMBINADO ==========
  exportCombinedReport: (data, currentUser) => {
    const { users, vehiculos, tiposVehiculo, zonas, trabajadores } = data;
    const doc = new jsPDF('landscape');
    const pageWidth = doc.internal.pageSize.width;
    const pageHeight = doc.internal.pageSize.height;
    
    // Fecha y hora actual
    const now = new Date();
    const fecha = now.toLocaleDateString('es-CL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      weekday: 'long'
    });

    // ========== PORTADA ==========
    doc.setFillColor(102, 126, 234);
    doc.rect(0, 0, pageWidth, pageHeight, 'F');
    
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(35);
    doc.setFont(undefined, 'bold');
    doc.text('SISTEMA MAPAQUILL√ìN', pageWidth / 2, 60, { align: 'center' });
    
    doc.setFontSize(22);
    doc.setFont(undefined, 'normal');
    doc.text('Reporte Ejecutivo Completo', pageWidth / 2, 80, { align: 'center' });
    
    doc.setFontSize(16);
    doc.text(`${fecha}`, pageWidth / 2, 100, { align: 'center' });
    
    // Cuadro de resumen en portada
    doc.setFillColor(255, 255, 255, 0.9);
    doc.roundedRect(50, 120, pageWidth - 100, 60, 5, 5, 'F');
    
    doc.setTextColor(31, 41, 55);
    doc.setFontSize(14);
    const resumenText = [
      `üë• ${users?.length || 0} Usuarios Registrados`,
      `üöõ ${vehiculos?.length || 0} Veh√≠culos en Flota`,
      `üó∫Ô∏è ${zonas?.length || 0} Zonas Activas`,
      `üë∑ ${trabajadores?.length || 0} Trabajadores Operativos`
    ];
    
    resumenText.forEach((text, index) => {
      doc.text(text, pageWidth / 2, 140 + (index * 10), { align: 'center' });
    });
    
    // Pie de portada
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(12);
    doc.text(`Generado por: ${currentUser.name || currentUser.email}`, pageWidth / 2, pageHeight - 30, { align: 'center' });
    doc.text('¬© 2024 Municipalidad de Quill√≥n', pageWidth / 2, pageHeight - 20, { align: 'center' });
    
    // ========== P√ÅGINAS DE CONTENIDO ==========
    // Agregar p√°ginas con cada secci√≥n
    
    // P√°gina 2: Resumen Ejecutivo
    doc.addPage();
    let yPos = 30;
    
    doc.setTextColor(31, 41, 55);
    doc.setFontSize(20);
    doc.setFont(undefined, 'bold');
    doc.text('Resumen Ejecutivo', 15, yPos);
    
    // ... Continuar con el contenido detallado ...
    
    // Guardar el PDF
    doc.save(`reporte_completo_mapaquillon_${now.getTime()}.pdf`);
  }
};

export default pdfExportService;
