// src/components/ZonesManagement.js
import React, { useState, useEffect, useRef } from 'react';
import { database } from '../config/firebase';
import { ref, push, set, update, remove, onValue } from 'firebase/database';

const ZonesManagement = ({ currentUser, onLogout, onViewChange, currentView = 'zones' }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [zonas, setZonas] = useState([]);
  const [trabajadores, setTrabajadores] = useState([]);
  const [selectedZona, setSelectedZona] = useState(null);
  const [isCreatingZona, setIsCreatingZona] = useState(false);
  const [showZonaForm, setShowZonaForm] = useState(false);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [isMobile, setIsMobile] = useState(false);
  
  // Form states
  const [newZona, setNewZona] = useState({
    nombre: '',
    descripcion: '',
    direccion: '',
    lat: null,
    lng: null,
    trabajadorAsignado: '',
    prioridad: 'normal',
    fechaLimite: '',
    estado: 'pendiente'
  });

  const markersRef = useRef({});
  const mapClickListenerRef = useRef(null);
  const API_KEY = 'AIzaSyA7DrdL36n5cx-RNzuXGAQggFeGCheHDbY';

  // Detectar si es móvil
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Cargar Google Maps
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve) => {
        if (window.google && window.google.maps) {
          resolve(window.google.maps);
          return;
        }

        const existingScript = document.querySelector('script[src*="maps.googleapis.com"]');
        if (existingScript) {
          existingScript.addEventListener('load', () => {
            resolve(window.google.maps);
          });
          return;
        }

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${API_KEY}&libraries=places,geometry&v=weekly`;
        script.async = true;
        script.defer = true;
        script.onload = () => resolve(window.google.maps);
        document.head.appendChild(script);
      });
    };

    const initMap = async () => {
      const maps = await loadGoogleMaps();
      
      if (!mapRef.current) return;

      const mapInstance = new maps.Map(mapRef.current, {
        center: { lat: -36.7333, lng: -72.4667 },
        zoom: 13,
        mapTypeId: 'roadmap'
      });

      setMap(mapInstance);
      setMapLoaded(true);
    };

    initMap();
  }, []);

  // Cargar zonas y trabajadores desde Firebase
  useEffect(() => {
    // Escuchar zonas
    const zonasRef = ref(database, 'zonas_asignadas');
    const unsubZonas = onValue(zonasRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const zonasArray = Object.entries(data).map(([id, zona]) => ({
          id,
          ...zona
        }));
        setZonas(zonasArray);
      } else {
        setZonas([]);
      }
    });

    // Escuchar trabajadores
    const trabajadoresRef = ref(database, 'trabajadores');
    const unsubTrabajadores = onValue(trabajadoresRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const trabajadoresArray = Object.entries(data).map(([id, trabajador]) => ({
          id,
          ...trabajador
        }));
        setTrabajadores(trabajadoresArray);
      } else {
        setTrabajadores([]);
      }
    });

    return () => {
      unsubZonas();
      unsubTrabajadores();
    };
  }, []);

  // Actualizar marcadores en el mapa
  useEffect(() => {
    if (!map || !window.google) return;

    // Limpiar marcadores anteriores
    Object.values(markersRef.current).forEach(marker => marker.setMap(null));
    markersRef.current = {};

    // Crear marcadores para cada zona
    zonas.forEach(zona => {
      const marker = new window.google.maps.Marker({
        position: { lat: zona.lat, lng: zona.lng },
        map: map,
        title: zona.nombre,
        icon: {
          path: 'M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7z',
          fillColor: zona.estado === 'completado' ? '#22c55e' : 
                     zona.estado === 'en_progreso' ? '#3b82f6' : 
                     zona.prioridad === 'alta' ? '#ef4444' : '#f59e0b',
          fillOpacity: 1,
          strokeColor: '#ffffff',
          strokeWeight: 2,
          scale: 2,
          anchor: new window.google.maps.Point(12, 24)
        }
      });

      // InfoWindow para cada marcador
      const infoWindow = new window.google.maps.InfoWindow({
        content: `
          <div style="padding: 10px;">
            <h3 style="margin: 0 0 10px 0;">${zona.nombre}</h3>
            <p style="margin: 5px 0;"><strong>Dirección:</strong> ${zona.direccion}</p>
            <p style="margin: 5px 0;"><strong>Descripción:</strong> ${zona.descripcion}</p>
            <p style="margin: 5px 0;"><strong>Trabajador:</strong> ${
              zona.trabajadorAsignado ? 
              trabajadores.find(t => t.id === zona.trabajadorAsignado)?.nombre || 'Sin asignar' : 
              'Sin asignar'
            }</p>
            <p style="margin: 5px 0;"><strong>Estado:</strong> ${zona.estado}</p>
            <p style="margin: 5px 0;"><strong>Prioridad:</strong> ${zona.prioridad}</p>
          </div>
        `
      });

      marker.addListener('click', () => {
        infoWindow.open(map, marker);
        setSelectedZona(zona);
      });

      markersRef.current[zona.id] = marker;
    });
  }, [map, zonas, trabajadores]);

  // Activar modo creación de zona
  const startCreatingZona = () => {
    if (!map) return;
    
    setIsCreatingZona(true);
    map.setOptions({ draggableCursor: 'crosshair' });
    
    // Escuchar click en el mapa
    mapClickListenerRef.current = map.addListener('click', (event) => {
      const lat = event.latLng.lat();
      const lng = event.latLng.lng();
      
      // Obtener dirección aproximada usando Geocoding
      const geocoder = new window.google.maps.Geocoder();
      geocoder.geocode({ location: { lat, lng } }, (results, status) => {
        if (status === 'OK' && results[0]) {
          setNewZona({
            ...newZona,
            lat,
            lng,
            direccion: results[0].formatted_address
          });
        } else {
          setNewZona({
            ...newZona,
            lat,
            lng,
            direccion: `${lat.toFixed(6)}, ${lng.toFixed(6)}`
          });
        }
      });
      
      // Crear marcador temporal
      Object.values(markersRef.current).forEach(marker => {
        if (marker.temp) marker.setMap(null);
      });
      
      const tempMarker = new window.google.maps.Marker({
        position: { lat, lng },
        map: map,
        title: 'Nueva zona',
        icon: {
          path: window.google.maps.SymbolPath.CIRCLE,
          scale: 10,
          fillColor: '#ef4444',
          fillOpacity: 0.8,
          strokeColor: '#ffffff',
          strokeWeight: 2
        },
        temp: true
      });
      
      markersRef.current.temp = tempMarker;
      setShowZonaForm(true);
      cancelCreatingZona();
    });
  };

  // Cancelar modo creación
  const cancelCreatingZona = () => {
    setIsCreatingZona(false);
    if (map) {
      map.setOptions({ draggableCursor: null });
      if (mapClickListenerRef.current) {
        window.google.maps.event.removeListener(mapClickListenerRef.current);
        mapClickListenerRef.current = null;
      }
    }
  };

  // Guardar nueva zona
  const saveZona = async () => {
    if (!newZona.nombre || !newZona.lat || !newZona.lng) {
      alert('Por favor completa todos los campos requeridos');
      return;
    }

    try {
      const zonaData = {
        ...newZona,
        createdBy: currentUser.uid,
        createdAt: new Date().toISOString(),
        ultimaActualizacion: new Date().toISOString()
      };

      const zonasRef = ref(database, 'zonas_asignadas');
      const newZonaRef = push(zonasRef);
      await set(newZonaRef, zonaData);

      // Si hay un trabajador asignado, notificarle
      if (newZona.trabajadorAsignado) {
        const notificacionData = {
          tipo: 'nueva_zona',
          mensaje: `Se te ha asignado una nueva zona: ${newZona.nombre}`,
          zonaId: newZonaRef.key,
          trabajadorId: newZona.trabajadorAsignado,
          timestamp: new Date().toISOString(),
          leido: false
        };
        
        const notifRef = ref(database, 'notificaciones');
        await push(notifRef, notificacionData);
      }

      // Limpiar formulario
      setNewZona({
        nombre: '',
        descripcion: '',
        direccion: '',
        lat: null,
        lng: null,
        trabajadorAsignado: '',
        prioridad: 'normal',
        fechaLimite: '',
        estado: 'pendiente'
      });
      
      // Limpiar marcador temporal
      if (markersRef.current.temp) {
        markersRef.current.temp.setMap(null);
        delete markersRef.current.temp;
      }
      
      setShowZonaForm(false);
      alert('✅ Zona creada exitosamente');
    } catch (error) {
      console.error('Error al crear zona:', error);
      alert('❌ Error al crear la zona');
    }
  };

  // Eliminar zona
  const deleteZona = async (zonaId) => {
    if (window.confirm('¿Estás seguro de eliminar esta zona?')) {
      try {
        await remove(ref(database, `zonas_asignadas/${zonaId}`));
        alert('✅ Zona eliminada');
      } catch (error) {
        console.error('Error al eliminar zona:', error);
        alert('❌ Error al eliminar la zona');
      }
    }
  };

  // Actualizar estado de zona
  const updateZonaStatus = async (zonaId, newStatus) => {
    try {
      await update(ref(database, `zonas_asignadas/${zonaId}`), {
        estado: newStatus,
        ultimaActualizacion: new Date().toISOString()
      });
    } catch (error) {
      console.error('Error al actualizar estado:', error);
    }
  };

  return (
    <div style={{ display: 'flex', height: '100vh', position: 'relative', flexDirection: 'column' }}>
      {/* Header con navegación */}
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        zIndex: 1003,
        boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
        padding: isMobile ? '10px' : '12px 20px'
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          width: '100%'
        }}>
          {/* Izquierda - Título */}
          <div style={{ flex: '0 0 auto' }}>
            <h1 style={{ 
              margin: 0, 
              fontSize: isMobile ? '16px' : '20px', 
              fontWeight: '600'
            }}>
              🗺️ Gestión de Zonas
            </h1>
          </div>

          {/* Derecha - Botones de navegación */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '10px'
          }}>
            {/* Botones de navegación */}
            {onViewChange && (
              <>
                {/* Botón Panel Admin solo para superadmin */}
                {currentUser && currentUser.role === 'superadmin' && (
                  <button
                    onClick={() => onViewChange('admin')}
                    style={{
                      padding: isMobile ? '6px 10px' : '6px 12px',
                      background: currentView === 'admin' ? 'rgba(255,255,255,0.25)' : 'rgba(255,255,255,0.1)',
                      color: 'white',
                      border: '1px solid rgba(255,255,255,0.3)',
                      borderRadius: '5px',
                      cursor: 'pointer',
                      fontSize: isMobile ? '11px' : '13px',
                      fontWeight: currentView === 'admin' ? '600' : '400',
                      whiteSpace: 'nowrap'
                    }}
                  >
                    🔐 Panel Admin
                  </button>
                )}
                <button
                  onClick={() => onViewChange('dashboard')}
                  style={{
                    padding: isMobile ? '6px 10px' : '6px 12px',
                    background: currentView === 'dashboard' ? 'rgba(255,255,255,0.25)' : 'rgba(255,255,255,0.1)',
                    color: 'white',
                    border: '1px solid rgba(255,255,255,0.3)',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    fontSize: isMobile ? '11px' : '13px',
                    fontWeight: currentView === 'dashboard' ? '600' : '400',
                    whiteSpace: 'nowrap'
                  }}
                >
                  📊 Dashboard GPS
                </button>
                <button
                  onClick={() => onViewChange('zones')}
                  style={{
                    padding: isMobile ? '6px 10px' : '6px 12px',
                    background: currentView === 'zones' ? 'rgba(255,255,255,0.25)' : 'rgba(255,255,255,0.1)',
                    color: 'white',
                    border: '1px solid rgba(255,255,255,0.3)',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    fontSize: isMobile ? '11px' : '13px',
                    fontWeight: currentView === 'zones' ? '600' : '400',
                    whiteSpace: 'nowrap'
                  }}
                >
                  🗺️ Gestión Zonas
                </button>
                
<button
  onClick={() => onViewChange('fleet')}
  style={{
    padding: isMobile ? '6px 10px' : '6px 12px',
    background: currentView === 'fleet' ? 'rgba(255,255,255,0.25)' : 'rgba(255,255,255,0.1)',
    color: 'white',
    border: '1px solid rgba(255,255,255,0.3)',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: isMobile ? '11px' : '13px',
    fontWeight: currentView === 'fleet' ? '600' : '400',
    whiteSpace: 'nowrap'
  }}
>
  🚛 Panel Flota
</button>
              </>
            )}

            {/* Botón Cerrar Sesión */}
            <button
              onClick={onLogout}
              style={{
                padding: isMobile ? '6px 10px' : '6px 14px',
                background: '#ef4444',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                fontSize: isMobile ? '12px' : '13px',
                fontWeight: '600',
                boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
                whiteSpace: 'nowrap'
              }}
            >
              🚪 {isMobile ? 'Salir' : 'Cerrar Sesión'}
            </button>
          </div>
        </div>

        {/* Botones de navegación móvil - Segunda línea */}
        {isMobile && onViewChange && (
          <div style={{
            marginTop: '8px',
            display: 'flex',
            gap: '8px'
          }}>
            {currentUser && currentUser.role === 'superadmin' && (
              <button
                onClick={() => onViewChange('admin')}
                style={{
                  flex: 1,
                  padding: '6px',
                  background: currentView === 'admin' ? 'rgba(255,255,255,0.3)' : 'rgba(255,255,255,0.1)',
                  color: 'white',
                  border: '1px solid rgba(255,255,255,0.3)',
                  borderRadius: '4px',
                  fontSize: '11px'
                }}
              >
                🔐 Admin
              </button>
            )}
            <button
              onClick={() => onViewChange('dashboard')}
              style={{
                flex: 1,
                padding: '6px',
                background: currentView === 'dashboard' ? 'rgba(255,255,255,0.3)' : 'rgba(255,255,255,0.1)',
                color: 'white',
                border: '1px solid rgba(255,255,255,0.3)',
                borderRadius: '4px',
                fontSize: '11px'
              }}
            >
              📊 Dashboard
            </button>
            <button
              onClick={() => onViewChange('zones')}
              style={{
                flex: 1,
                padding: '6px',
                background: currentView === 'zones' ? 'rgba(255,255,255,0.3)' : 'rgba(255,255,255,0.1)',
                color: 'white',
                border: '1px solid rgba(255,255,255,0.3)',
                borderRadius: '4px',
                fontSize: '11px'
              }}
            >
              🗺️ Zonas
            </button>
          </div>
        )}
      </div>

      {/* Contenido principal con margen superior para el header */}
      <div style={{ 
        display: 'flex', 
        flex: 1, 
        marginTop: isMobile && onViewChange ? 
                   (currentUser && currentUser.role === 'superadmin' ? '90px' : '85px') : 
                   '60px',
        position: 'relative'
      }}>
        {/* Panel lateral */}
        <div style={{
          width: isMobile ? '100%' : '400px',
          background: 'white',
          borderRight: '1px solid #e5e7eb',
          overflowY: 'auto',
          display: 'flex',
          flexDirection: 'column',
          position: isMobile ? 'absolute' : 'relative',
          height: '100%',
          zIndex: isMobile ? 1002 : 1001
        }}>
          {/* Header del panel */}
          <div style={{
            padding: '20px',
            borderBottom: '1px solid #e5e7eb',
            background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
            color: 'white'
          }}>
            <h2 style={{ margin: '0 0 10px 0', fontSize: '18px' }}>
              📍 Crear y Administrar Zonas
            </h2>
            <button
              onClick={startCreatingZona}
              disabled={isCreatingZona}
              style={{
                width: '100%',
                padding: '10px',
                background: isCreatingZona ? '#9ca3af' : 'white',
                color: isCreatingZona ? 'white' : '#22c55e',
                border: 'none',
                borderRadius: '6px',
                fontSize: '14px',
                fontWeight: '500',
                cursor: isCreatingZona ? 'not-allowed' : 'pointer'
              }}
            >
              {isCreatingZona ? '📍 Haz clic en el mapa...' : '➕ Crear Nueva Zona'}
            </button>
          </div>

          {/* Formulario de nueva zona */}
          {showZonaForm && (
            <div style={{
              padding: '20px',
              background: '#f0f9ff',
              borderBottom: '2px solid #3b82f6'
            }}>
              <h3 style={{ margin: '0 0 15px 0', color: '#1e40af' }}>
                Nueva Zona de Trabajo
              </h3>
              
              <div style={{ marginBottom: '12px' }}>
                <label style={{ display: 'block', marginBottom: '4px', fontSize: '13px', fontWeight: '500' }}>
                  Nombre de la zona *
                </label>
                <input
                  type="text"
                  value={newZona.nombre}
                  onChange={(e) => setNewZona({ ...newZona, nombre: e.target.value })}
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #d1d5db',
                    borderRadius: '4px',
                    fontSize: '14px'
                  }}
                  placeholder="Ej: Reparación Calle Principal"
                />
              </div>

              <div style={{ marginBottom: '12px' }}>
                <label style={{ display: 'block', marginBottom: '4px', fontSize: '13px', fontWeight: '500' }}>
                  Descripción
                </label>
                <textarea
                  value={newZona.descripcion}
                  onChange={(e) => setNewZona({ ...newZona, descripcion: e.target.value })}
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #d1d5db',
                    borderRadius: '4px',
                    fontSize: '14px',
                    minHeight: '60px'
                  }}
                  placeholder="Detalles del trabajo a realizar..."
                />
              </div>

              <div style={{ marginBottom: '12px' }}>
                <label style={{ display: 'block', marginBottom: '4px', fontSize: '13px', fontWeight: '500' }}>
                  Dirección
                </label>
                <input
                  type="text"
                  value={newZona.direccion}
                  onChange={(e) => setNewZona({ ...newZona, direccion: e.target.value })}
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #d1d5db',
                    borderRadius: '4px',
                    fontSize: '14px'
                  }}
                />
              </div>

              <div style={{ marginBottom: '12px' }}>
                <label style={{ display: 'block', marginBottom: '4px', fontSize: '13px', fontWeight: '500' }}>
                  Asignar a trabajador
                </label>
                <select
                  value={newZona.trabajadorAsignado}
                  onChange={(e) => setNewZona({ ...newZona, trabajadorAsignado: e.target.value })}
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #d1d5db',
                    borderRadius: '4px',
                    fontSize: '14px'
                  }}
                >
                  <option value="">-- Sin asignar --</option>
                  {trabajadores.map(trabajador => (
                    <option key={trabajador.id} value={trabajador.id}>
                      {trabajador.nombre || trabajador.email}
                    </option>
                  ))}
                </select>
              </div>

              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px', marginBottom: '12px' }}>
                <div>
                  <label style={{ display: 'block', marginBottom: '4px', fontSize: '13px', fontWeight: '500' }}>
                    Prioridad
                  </label>
                  <select
                    value={newZona.prioridad}
                    onChange={(e) => setNewZona({ ...newZona, prioridad: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '8px',
                      border: '1px solid #d1d5db',
                      borderRadius: '4px',
                      fontSize: '14px'
                    }}
                  >
                    <option value="baja">🟢 Baja</option>
                    <option value="normal">🟡 Normal</option>
                    <option value="alta">🔴 Alta</option>
                    <option value="urgente">🚨 Urgente</option>
                  </select>
                </div>

                <div>
                  <label style={{ display: 'block', marginBottom: '4px', fontSize: '13px', fontWeight: '500' }}>
                    Fecha límite
                  </label>
                  <input
                    type="date"
                    value={newZona.fechaLimite}
                    onChange={(e) => setNewZona({ ...newZona, fechaLimite: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '8px',
                      border: '1px solid #d1d5db',
                      borderRadius: '4px',
                      fontSize: '14px'
                    }}
                  />
                </div>
              </div>

              <div style={{ 
                padding: '8px', 
                background: '#e0e7ff', 
                borderRadius: '4px',
                marginBottom: '12px',
                fontSize: '12px'
              }}>
                📍 <strong>Ubicación:</strong> {newZona.lat?.toFixed(6)}, {newZona.lng?.toFixed(6)}
              </div>

              <div style={{ display: 'flex', gap: '8px' }}>
                <button
                  onClick={saveZona}
                  style={{
                    flex: 1,
                    padding: '10px',
                    background: '#22c55e',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: 'pointer'
                  }}
                >
                  ✅ Guardar Zona
                </button>
                <button
                  onClick={() => {
                    setShowZonaForm(false);
                    if (markersRef.current.temp) {
                      markersRef.current.temp.setMap(null);
                      delete markersRef.current.temp;
                    }
                  }}
                  style={{
                    flex: 1,
                    padding: '10px',
                    background: '#ef4444',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: 'pointer'
                  }}
                >
                  ❌ Cancelar
                </button>
              </div>
            </div>
          )}

          {/* Lista de zonas */}
          <div style={{ flex: 1, padding: '20px' }}>
            <h3 style={{ margin: '0 0 15px 0', fontSize: '16px', color: '#1f2937' }}>
              📋 Zonas Activas ({zonas.length})
            </h3>
            
            {zonas.length === 0 ? (
              <div style={{
                textAlign: 'center',
                padding: '40px 20px',
                color: '#6b7280'
              }}>
                <p>No hay zonas creadas</p>
                <p style={{ fontSize: '12px', marginTop: '8px' }}>
                  Haz clic en "Crear Nueva Zona" para comenzar
                </p>
              </div>
            ) : (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
                {zonas.map(zona => {
                  const trabajador = trabajadores.find(t => t.id === zona.trabajadorAsignado);
                  return (
                    <div
                      key={zona.id}
                      style={{
                        padding: '12px',
                        border: '1px solid #e5e7eb',
                        borderRadius: '8px',
                        background: selectedZona?.id === zona.id ? '#f0f9ff' : 'white',
                        cursor: 'pointer'
                      }}
                      onClick={() => {
                        setSelectedZona(zona);
                        if (map && markersRef.current[zona.id]) {
                          map.panTo({ lat: zona.lat, lng: zona.lng });
                          map.setZoom(16);
                        }
                      }}
                    >
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
                        <div style={{ flex: 1 }}>
                          <h4 style={{ margin: '0 0 4px 0', fontSize: '14px', fontWeight: '600' }}>
                            {zona.nombre}
                          </h4>
                          <p style={{ margin: '0 0 4px 0', fontSize: '12px', color: '#6b7280' }}>
                            📍 {zona.direccion}
                          </p>
                          <p style={{ margin: '0 0 4px 0', fontSize: '12px', color: '#6b7280' }}>
                            👷 {trabajador ? trabajador.nombre : 'Sin asignar'}
                          </p>
                          <div style={{ display: 'flex', gap: '6px', marginTop: '6px' }}>
                            <span style={{
                              padding: '2px 6px',
                              borderRadius: '4px',
                              fontSize: '11px',
                              background: zona.estado === 'completado' ? '#dcfce7' :
                                        zona.estado === 'en_progreso' ? '#dbeafe' :
                                        '#fef3c7',
                              color: zona.estado === 'completado' ? '#166534' :
                                     zona.estado === 'en_progreso' ? '#1e40af' :
                                     '#92400e'
                            }}>
                              {zona.estado === 'completado' ? '✅ Completado' :
                               zona.estado === 'en_progreso' ? '🔧 En progreso' :
                               '⏳ Pendiente'}
                            </span>
                            <span style={{
                              padding: '2px 6px',
                              borderRadius: '4px',
                              fontSize: '11px',
                              background: zona.prioridad === 'urgente' ? '#fee2e2' :
                                        zona.prioridad === 'alta' ? '#fee2e2' :
                                        zona.prioridad === 'normal' ? '#fef3c7' :
                                        '#dcfce7',
                              color: zona.prioridad === 'urgente' ? '#991b1b' :
                                     zona.prioridad === 'alta' ? '#991b1b' :
                                     zona.prioridad === 'normal' ? '#92400e' :
                                     '#166534'
                            }}>
                              {zona.prioridad === 'urgente' ? '🚨 Urgente' :
                               zona.prioridad === 'alta' ? '🔴 Alta' :
                               zona.prioridad === 'normal' ? '🟡 Normal' :
                               '🟢 Baja'}
                            </span>
                          </div>
                        </div>
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            deleteZona(zona.id);
                          }}
                          style={{
                            padding: '4px 8px',
                            background: '#ef4444',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            fontSize: '11px',
                            cursor: 'pointer'
                          }}
                        >
                          🗑️
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>

        {/* Mapa */}
        <div style={{ flex: 1, position: 'relative' }}>
          <div ref={mapRef} style={{ width: '100%', height: '100%' }} />
          
          {!mapLoaded && (
            <div style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              textAlign: 'center'
            }}>
              <div style={{
                width: '50px',
                height: '50px',
                border: '4px solid #f3f3f3',
                borderTop: '4px solid #667eea',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite'
              }}></div>
              <p style={{ marginTop: '10px', color: '#666' }}>Cargando mapa...</p>
            </div>
          )}

          {isCreatingZona && (
            <div style={{
              position: 'absolute',
              top: '20px',
              left: '50%',
              transform: 'translateX(-50%)',
              background: 'white',
              padding: '12px 20px',
              borderRadius: '8px',
              boxShadow: '0 4px 20px rgba(0,0,0,0.15)',
              display: 'flex',
              alignItems: 'center',
              gap: '10px'
            }}>
              <span style={{ fontSize: '14px', fontWeight: '500' }}>
                📍 Haz clic en el mapa para seleccionar la ubicación
              </span>
              <button
                onClick={cancelCreatingZona}
                style={{
                  padding: '6px 12px',
                  background: '#6b7280',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  fontSize: '12px',
                  cursor: 'pointer'
                }}
              >
                Cancelar
              </button>
            </div>
          )}
        </div>
      </div>

      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default ZonesManagement;
