// src/components/PreventiveMaintenanceModule.js

import React, { useState, useEffect, useCallback } from 'react';
import { 
  ref as databaseRef,
  set as databaseSet,
  push,
  update,
  remove,
  onValue,
  off
} from 'firebase/database';
import { database } from '../config/firebase';

const PreventiveMaintenanceModule = ({ vehiculos, currentUser, isMobile }) => {
  // Estados principales
  const [maintenanceRecords, setMaintenanceRecords] = useState([]);
  const [showMaintenanceForm, setShowMaintenanceForm] = useState(false);
  const [selectedVehicle, setSelectedVehicle] = useState('');
  const [editingMaintenance, setEditingMaintenance] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  
  // Estado para el paso actual del formulario
  const [currentStep, setCurrentStep] = useState(1);
  
  // Estado para vista activa
  const [activeView, setActiveView] = useState('dashboard');
  
  // Estado para búsqueda y filtros
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('todos');
  
  // Estado para tipo de repuesto
  const [tipoRepuesto, setTipoRepuesto] = useState('original');
  
  // Estado para servicios preventivos
  const [serviciosPreventivos, setServiciosPreventivos] = useState([{
    id: Date.now(),
    numero: 1,
    servicio: '',
    codigo: '',
    detalle: '',
    manoObra: 0,
    costoRepuesto: 0,
    cantidadRepuesto: 1,
    repuestoDescripcion: ''
  }]);

  // Cálculos automáticos
  const calcularTotales = useCallback(() => {
    const subtotalManoObra = serviciosPreventivos.reduce((sum, s) => sum + (parseFloat(s.manoObra) || 0), 0);
    const subtotalRepuestos = serviciosPreventivos.reduce((sum, s) => sum + ((parseFloat(s.costoRepuesto) || 0) * (parseInt(s.cantidadRepuesto) || 1)), 0);
    const valorNeto = subtotalManoObra + subtotalRepuestos;
    const iva = valorNeto * 0.19;
    const total = valorNeto + iva;
    
    return {
      subtotalManoObra,
      subtotalRepuestos,
      valorNeto,
      iva,
      total
    };
  }, [serviciosPreventivos]);

  // Cargar registros de mantenimiento
  useEffect(() => {
    const maintenanceRef = databaseRef(database, 'mantenimientos');
    const unsubscribe = onValue(maintenanceRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const maintenanceArray = Object.entries(data).map(([id, maintenance]) => ({
          id,
          ...maintenance
        }));
        setMaintenanceRecords(maintenanceArray);
      } else {
        setMaintenanceRecords([]);
      }
    });

    return () => off(maintenanceRef);
  }, []);

  // Funciones auxiliares
  const agregarServicio = () => {
    const nuevoNumero = serviciosPreventivos.length + 1;
    setServiciosPreventivos([...serviciosPreventivos, {
      id: Date.now(),
      numero: nuevoNumero,
      servicio: '',
      codigo: '',
      detalle: '',
      manoObra: 0,
      costoRepuesto: 0,
      cantidadRepuesto: 1,
      repuestoDescripcion: ''
    }]);
  };

  const actualizarServicio = (id, campo, valor) => {
    setServiciosPreventivos(serviciosPreventivos.map(s => 
      s.id === id ? { ...s, [campo]: valor } : s
    ));
  };

  const eliminarServicio = (id) => {
    if (serviciosPreventivos.length > 1) {
      const nuevosServicios = serviciosPreventivos.filter(s => s.id !== id);
      const serviciosRenumerados = nuevosServicios.map((s, index) => ({
        ...s,
        numero: index + 1
      }));
      setServiciosPreventivos(serviciosRenumerados);
    }
  };

  // Guardar mantenimiento
  const handleSaveMaintenance = async (e) => {
    e.preventDefault();
    
    if (!selectedVehicle) {
      setMessage({ type: 'error', text: '❌ Debe seleccionar un vehículo' });
      return;
    }

    setLoading(true);
    
    try {
      const totales = calcularTotales();
      const vehiculo = vehiculos.find(v => v.id === selectedVehicle);
      
      const maintenanceData = {
        vehiculoId: selectedVehicle,
        vehiculoNombre: vehiculo?.nombre || '',
        vehiculoPatente: vehiculo?.patente || '',
        tipoRepuesto,
        serviciosPreventivos,
        totales,
        fechaMantenimiento: new Date().toISOString(),
        kilometrajeActual: vehiculo?.kilometraje || 0,
        createdBy: currentUser.uid,
        createdAt: new Date().toISOString()
      };

      if (editingMaintenance) {
        const maintenanceRef = databaseRef(database, `mantenimientos/${editingMaintenance.id}`);
        await update(maintenanceRef, {
          ...maintenanceData,
          updatedAt: new Date().toISOString(),
          updatedBy: currentUser.uid
        });
        
        setMessage({ type: 'success', text: '✅ Mantenimiento actualizado exitosamente' });
      } else {
        const maintenanceRef = databaseRef(database, 'mantenimientos');
        const newMaintenanceRef = push(maintenanceRef);
        await databaseSet(newMaintenanceRef, maintenanceData);
        
        const vehicleRef = databaseRef(database, `vehiculos/${selectedVehicle}`);
        await update(vehicleRef, {
          ultimoMantenimiento: new Date().toISOString().split('T')[0],
          kilometrajeUltimoMantenimiento: vehiculo?.kilometraje || 0
        });
        
        setMessage({ type: 'success', text: '✅ Mantenimiento registrado exitosamente' });
      }

      resetForm();
      
    } catch (error) {
      console.error('Error al guardar mantenimiento:', error);
      setMessage({ type: 'error', text: '❌ Error al guardar mantenimiento' });
    } finally {
      setLoading(false);
    }
  };

  const handleEditMaintenance = (maintenance) => {
    setEditingMaintenance(maintenance);
    setSelectedVehicle(maintenance.vehiculoId);
    setTipoRepuesto(maintenance.tipoRepuesto);
    setServiciosPreventivos(maintenance.serviciosPreventivos);
    setShowMaintenanceForm(true);
    setCurrentStep(1);
  };

  const handleDeleteMaintenance = async (maintenanceId) => {
    if (window.confirm('¿Está seguro de eliminar este registro de mantenimiento?')) {
      try {
        const maintenanceRef = databaseRef(database, `mantenimientos/${maintenanceId}`);
        await remove(maintenanceRef);
        setMessage({ type: 'success', text: '✅ Mantenimiento eliminado' });
      } catch (error) {
        setMessage({ type: 'error', text: '❌ Error al eliminar mantenimiento' });
      }
    }
  };

  const resetForm = () => {
    setShowMaintenanceForm(false);
    setEditingMaintenance(null);
    setSelectedVehicle('');
    setTipoRepuesto('original');
    setCurrentStep(1);
    setServiciosPreventivos([{
      id: Date.now(),
      numero: 1,
      servicio: '',
      codigo: '',
      detalle: '',
      manoObra: 0,
      costoRepuesto: 0,
      cantidadRepuesto: 1,
      repuestoDescripcion: ''
    }]);
  };

  const totales = calcularTotales();

  // Filtrar registros
  const filteredRecords = maintenanceRecords.filter(record => {
    const matchesSearch = record.vehiculoNombre.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         record.vehiculoPatente.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filterStatus === 'todos' || record.tipoRepuesto === filterStatus;
    return matchesSearch && matchesFilter;
  });

  // Estadísticas mejoradas
  const getStats = () => {
    const hoy = new Date();
    const inicioMes = new Date(hoy.getFullYear(), hoy.getMonth(), 1);
    
    return {
      total: maintenanceRecords.length,
      esteMes: maintenanceRecords.filter(m => new Date(m.fechaMantenimiento) >= inicioMes).length,
      costoTotal: maintenanceRecords.reduce((sum, m) => sum + (m.totales?.total || 0), 0),
      promedioMensual: Math.round(maintenanceRecords.reduce((sum, m) => sum + (m.totales?.total || 0), 0) / 12)
    };
  };

  const stats = getStats();

  return (
    <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
      {/* Header mejorado */}
      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: '20px',
        padding: '30px',
        marginBottom: '30px',
        color: 'white',
        boxShadow: '0 10px 30px rgba(102, 126, 234, 0.2)'
      }}>
        <h1 style={{ margin: 0, fontSize: '32px', fontWeight: '700' }}>
          🔧 Centro de Mantenimientos
        </h1>
        <p style={{ margin: '10px 0 0 0', opacity: 0.9, fontSize: '16px' }}>
          Gestiona y programa los mantenimientos preventivos de tu flota
        </p>
      </div>

      {/* Mensajes */}
      {message.text && (
        <div style={{
          padding: '16px',
          borderRadius: '12px',
          marginBottom: '20px',
          background: message.type === 'success' ? '#dcfce7' : '#fee2e2',
          border: `2px solid ${message.type === 'success' ? '#86efac' : '#fecaca'}`,
          color: message.type === 'success' ? '#166534' : '#991b1b',
          fontWeight: '500',
          animation: 'slideIn 0.3s ease'
        }}>
          {message.text}
        </div>
      )}

      {/* Dashboard de estadísticas */}
      {!showMaintenanceForm && (
        <>
          <div style={{
            display: 'grid',
            gridTemplateColumns: `repeat(auto-fit, minmax(${isMobile ? '150px' : '250px'}, 1fr))`,
            gap: '20px',
            marginBottom: '30px'
          }}>
            <div style={{
              background: 'white',
              padding: '25px',
              borderRadius: '16px',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              borderTop: '4px solid #3b82f6',
              transition: 'transform 0.3s ease',
              cursor: 'pointer'
            }}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <div style={{ color: '#6b7280', fontSize: '14px', fontWeight: '500' }}>Total Mantenimientos</div>
                  <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#1f2937' }}>{stats.total}</div>
                </div>
                <div style={{ fontSize: '40px', opacity: 0.2 }}>📊</div>
              </div>
            </div>

            <div style={{
              background: 'white',
              padding: '25px',
              borderRadius: '16px',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              borderTop: '4px solid #22c55e',
              transition: 'transform 0.3s ease',
              cursor: 'pointer'
            }}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <div style={{ color: '#6b7280', fontSize: '14px', fontWeight: '500' }}>Este Mes</div>
                  <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#1f2937' }}>{stats.esteMes}</div>
                </div>
                <div style={{ fontSize: '40px', opacity: 0.2 }}>📅</div>
              </div>
            </div>

            <div style={{
              background: 'white',
              padding: '25px',
              borderRadius: '16px',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              borderTop: '4px solid #f59e0b',
              transition: 'transform 0.3s ease',
              cursor: 'pointer'
            }}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <div style={{ color: '#6b7280', fontSize: '14px', fontWeight: '500' }}>Inversión Total</div>
                  <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#1f2937' }}>
                    ${stats.costoTotal.toLocaleString('es-CL')}
                  </div>
                </div>
                <div style={{ fontSize: '40px', opacity: 0.2 }}>💰</div>
              </div>
            </div>

            <div style={{
              background: 'white',
              padding: '25px',
              borderRadius: '16px',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              borderTop: '4px solid #8b5cf6',
              transition: 'transform 0.3s ease',
              cursor: 'pointer'
            }}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <div style={{ color: '#6b7280', fontSize: '14px', fontWeight: '500' }}>Promedio Mensual</div>
                  <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#1f2937' }}>
                    ${stats.promedioMensual.toLocaleString('es-CL')}
                  </div>
                </div>
                <div style={{ fontSize: '40px', opacity: 0.2 }}>📈</div>
              </div>
            </div>
          </div>

          {/* Barra de acciones */}
          <div style={{
            background: 'white',
            padding: '20px',
            borderRadius: '16px',
            marginBottom: '30px',
            boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            flexWrap: 'wrap',
            gap: '15px'
          }}>
            <div style={{ display: 'flex', gap: '15px', flexWrap: 'wrap' }}>
              <button
                onClick={() => {
                  setShowMaintenanceForm(true);
                  setCurrentStep(1);
                }}
                style={{
                  padding: '12px 24px',
                  background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '12px',
                  fontSize: '16px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  boxShadow: '0 4px 15px rgba(34, 197, 94, 0.3)',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateY(-2px)';
                  e.currentTarget.style.boxShadow = '0 6px 20px rgba(34, 197, 94, 0.4)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = '0 4px 15px rgba(34, 197, 94, 0.3)';
                }}>
                <span style={{ fontSize: '20px' }}>➕</span>
                Nuevo Mantenimiento
              </button>
            </div>

            <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
              <input
                type="text"
                placeholder="🔍 Buscar vehículo..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                  padding: '10px 15px',
                  border: '2px solid #e5e7eb',
                  borderRadius: '10px',
                  fontSize: '14px',
                  width: '200px',
                  transition: 'border-color 0.3s ease',
                  outline: 'none'
                }}
                onFocus={(e) => e.currentTarget.style.borderColor = '#3b82f6'}
                onBlur={(e) => e.currentTarget.style.borderColor = '#e5e7eb'}
              />
              
              <select
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
                style={{
                  padding: '10px 15px',
                  border: '2px solid #e5e7eb',
                  borderRadius: '10px',
                  fontSize: '14px',
                  cursor: 'pointer',
                  transition: 'border-color 0.3s ease',
                  outline: 'none'
                }}
                onFocus={(e) => e.currentTarget.style.borderColor = '#3b82f6'}
                onBlur={(e) => e.currentTarget.style.borderColor = '#e5e7eb'}>
                <option value="todos">Todos</option>
                <option value="original">Originales</option>
                <option value="alternativo">Alternativos</option>
              </select>
            </div>
          </div>
        </>
      )}

      {/* Formulario mejorado con pasos */}
      {showMaintenanceForm && (
        <div style={{
          background: 'white',
          borderRadius: '20px',
          boxShadow: '0 10px 40px rgba(0,0,0,0.1)',
          overflow: 'hidden'
        }}>
          {/* Indicador de progreso */}
          <div style={{
            background: 'linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)',
            padding: '30px',
            borderBottom: '2px solid #e5e7eb'
          }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
              <h2 style={{ margin: 0, fontSize: '24px', fontWeight: '600', color: '#1f2937' }}>
                {editingMaintenance ? '✏️ Editar Mantenimiento' : '➕ Nuevo Mantenimiento'}
              </h2>
              <button
                onClick={resetForm}
                style={{
                  padding: '8px 16px',
                  background: '#ef4444',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '14px',
                  cursor: 'pointer'
                }}>
                ✖ Cancelar
              </button>
            </div>

            {/* Pasos */}
            <div style={{ display: 'flex', justifyContent: 'space-between', maxWidth: '600px', margin: '0 auto' }}>
              {[
                { num: 1, label: 'Vehículo', icon: '🚛' },
                { num: 2, label: 'Servicios', icon: '🔧' },
                { num: 3, label: 'Resumen', icon: '💰' }
              ].map((step, index) => (
                <div key={step.num} style={{ flex: 1, display: 'flex', alignItems: 'center' }}>
                  <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    flex: 1
                  }}>
                    <div style={{
                      width: '50px',
                      height: '50px',
                      borderRadius: '50%',
                      background: currentStep >= step.num ? 
                        'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)' : 
                        '#e5e7eb',
                      color: currentStep >= step.num ? 'white' : '#6b7280',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '20px',
                      fontWeight: 'bold',
                      boxShadow: currentStep === step.num ? '0 4px 15px rgba(59, 130, 246, 0.3)' : 'none',
                      transition: 'all 0.3s ease',
                      cursor: currentStep >= step.num ? 'pointer' : 'default'
                    }}
                    onClick={() => currentStep >= step.num && setCurrentStep(step.num)}>
                      {step.icon}
                    </div>
                    <span style={{
                      marginTop: '8px',
                      fontSize: '13px',
                      fontWeight: '500',
                      color: currentStep >= step.num ? '#1f2937' : '#9ca3af'
                    }}>
                      {step.label}
                    </span>
                  </div>
                  {index < 2 && (
                    <div style={{
                      height: '2px',
                      background: currentStep > step.num ? '#3b82f6' : '#e5e7eb',
                      flex: 1,
                      margin: '0 10px',
                      transition: 'background 0.3s ease'
                    }} />
                  )}
                </div>
              ))}
            </div>
          </div>

          <form onSubmit={handleSaveMaintenance} style={{ padding: '30px' }}>
            {/* Paso 1: Selección de vehículo */}
            {currentStep === 1 && (
              <div style={{ animation: 'fadeIn 0.3s ease' }}>
                <h3 style={{ marginBottom: '20px', color: '#1f2937', fontSize: '20px' }}>
                  Selecciona el vehículo
                </h3>
                
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '15px' }}>
                  {vehiculos.map(v => (
                    <div
                      key={v.id}
                      onClick={() => setSelectedVehicle(v.id)}
                      style={{
                        padding: '20px',
                        border: `3px solid ${selectedVehicle === v.id ? '#3b82f6' : '#e5e7eb'}`,
                        borderRadius: '12px',
                        cursor: 'pointer',
                        background: selectedVehicle === v.id ? '#eff6ff' : 'white',
                        transition: 'all 0.3s ease'
                      }}
                      onMouseEnter={(e) => {
                        if (selectedVehicle !== v.id) {
                          e.currentTarget.style.borderColor = '#93c5fd';
                          e.currentTarget.style.background = '#f0f9ff';
                        }
                      }}
                      onMouseLeave={(e) => {
                        if (selectedVehicle !== v.id) {
                          e.currentTarget.style.borderColor = '#e5e7eb';
                          e.currentTarget.style.background = 'white';
                        }
                      }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <div>
                          <h4 style={{ margin: '0 0 5px 0', fontSize: '18px', color: '#1f2937' }}>
                            {v.nombre}
                          </h4>
                          <p style={{ margin: '0', fontSize: '14px', color: '#6b7280' }}>
                            {v.marca} {v.modelo} • Patente: {v.patente}
                          </p>
                          <p style={{ margin: '5px 0 0 0', fontSize: '13px', color: '#9ca3af' }}>
                            Kilometraje: {v.kilometraje?.toLocaleString('es-CL') || 0} km
                          </p>
                        </div>
                        {selectedVehicle === v.id && (
                          <div style={{ fontSize: '30px', color: '#3b82f6' }}>✓</div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>

                <div style={{ marginTop: '30px' }}>
                  <h3 style={{ marginBottom: '15px', color: '#1f2937', fontSize: '18px' }}>
                    Tipo de repuestos a utilizar
                  </h3>
                  
                  <div style={{ display: 'flex', gap: '15px' }}>
                    <label style={{
                      flex: 1,
                      padding: '20px',
                      border: `3px solid ${tipoRepuesto === 'original' ? '#22c55e' : '#e5e7eb'}`,
                      borderRadius: '12px',
                      cursor: 'pointer',
                      background: tipoRepuesto === 'original' ? '#f0fdf4' : 'white',
                      transition: 'all 0.3s ease',
                      textAlign: 'center'
                    }}>
                      <input
                        type="radio"
                        name="tipoRepuesto"
                        value="original"
                        checked={tipoRepuesto === 'original'}
                        onChange={(e) => setTipoRepuesto(e.target.value)}
                        style={{ display: 'none' }}
                      />
                      <div style={{ fontSize: '30px', marginBottom: '10px' }}>✅</div>
                      <div style={{ fontSize: '16px', fontWeight: '600', color: '#15803d' }}>
                        Repuestos Originales
                      </div>
                      <div style={{ fontSize: '13px', color: '#6b7280', marginTop: '5px' }}>
                        Garantía oficial del fabricante
                      </div>
                    </label>

                    <label style={{
                      flex: 1,
                      padding: '20px',
                      border: `3px solid ${tipoRepuesto === 'alternativo' ? '#f59e0b' : '#e5e7eb'}`,
                      borderRadius: '12px',
                      cursor: 'pointer',
                      background: tipoRepuesto === 'alternativo' ? '#fef3c7' : 'white',
                      transition: 'all 0.3s ease',
                      textAlign: 'center'
                    }}>
                      <input
                        type="radio"
                        name="tipoRepuesto"
                        value="alternativo"
                        checked={tipoRepuesto === 'alternativo'}
                        onChange={(e) => setTipoRepuesto(e.target.value)}
                        style={{ display: 'none' }}
                      />
                      <div style={{ fontSize: '30px', marginBottom: '10px' }}>🔄</div>
                      <div style={{ fontSize: '16px', fontWeight: '600', color: '#92400e' }}>
                        Repuestos Alternativos
                      </div>
                      <div style={{ fontSize: '13px', color: '#6b7280', marginTop: '5px' }}>
                        Menor costo, garantía limitada
                      </div>
                    </label>
                  </div>
                </div>

                <div style={{ marginTop: '30px', display: 'flex', justifyContent: 'flex-end' }}>
                  <button
                    type="button"
                    onClick={() => setCurrentStep(2)}
                    disabled={!selectedVehicle}
                    style={{
                      padding: '12px 30px',
                      background: selectedVehicle ? 
                        'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)' : 
                        '#e5e7eb',
                      color: selectedVehicle ? 'white' : '#9ca3af',
                      border: 'none',
                      borderRadius: '10px',
                      fontSize: '16px',
                      fontWeight: '600',
                      cursor: selectedVehicle ? 'pointer' : 'not-allowed',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px'
                    }}>
                    Continuar
                    <span style={{ fontSize: '18px' }}>→</span>
                  </button>
                </div>
              </div>
            )}

            {/* Paso 2: Servicios */}
            {currentStep === 2 && (
              <div style={{ animation: 'fadeIn 0.3s ease' }}>
                <h3 style={{ marginBottom: '20px', color: '#1f2937', fontSize: '20px' }}>
                  Detalle de servicios realizados
                </h3>

                {serviciosPreventivos.map((servicio, index) => (
                  <div key={servicio.id} style={{
                    marginBottom: '20px',
                    padding: '20px',
                    background: '#f9fafb',
                    borderRadius: '12px',
                    border: '2px solid #e5e7eb'
                  }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
                      <h4 style={{ margin: 0, fontSize: '16px', fontWeight: '600', color: '#374151' }}>
                        Servicio #{servicio.numero}
                      </h4>
                      {serviciosPreventivos.length > 1 && (
                        <button
                          type="button"
                          onClick={() => eliminarServicio(servicio.id)}
                          style={{
                            padding: '6px 12px',
                            background: '#ef4444',
                            color: 'white',
                            border: 'none',
                            borderRadius: '6px',
                            fontSize: '12px',
                            cursor: 'pointer'
                          }}>
                          🗑️ Eliminar
                        </button>
                      )}
                    </div>

                    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
                      <div>
                        <label style={{ display: 'block', marginBottom: '5px', fontSize: '13px', fontWeight: '500', color: '#374151' }}>
                          Tipo de servicio
                        </label>
                        <input
                          type="text"
                          value={servicio.servicio}
                          onChange={(e) => actualizarServicio(servicio.id, 'servicio', e.target.value)}
                          placeholder="Ej: Cambio de aceite"
                          style={{
                            width: '100%',
                            padding: '10px',
                            border: '1px solid #d1d5db',
                            borderRadius: '8px',
                            fontSize: '14px'
                          }}
                        />
                      </div>

                      <div>
                        <label style={{ display: 'block', marginBottom: '5px', fontSize: '13px', fontWeight: '500', color: '#374151' }}>
                          Código
                        </label>
                        <input
                          type="text"
                          value={servicio.codigo}
                          onChange={(e) => actualizarServicio(servicio.id, 'codigo', e.target.value)}
                          placeholder="Código interno"
                          style={{
                            width: '100%',
                            padding: '10px',
                            border: '1px solid #d1d5db',
                            borderRadius: '8px',
                            fontSize: '14px'
                          }}
                        />
                      </div>

                      <div>
                        <label style={{ display: 'block', marginBottom: '5px', fontSize: '13px', fontWeight: '500', color: '#374151' }}>
                          Mano de obra ($)
                        </label>
                        <input
                          type="number"
                          value={servicio.manoObra}
                          onChange={(e) => actualizarServicio(servicio.id, 'manoObra', e.target.value)}
                          placeholder="0"
                          min="0"
                          style={{
                            width: '100%',
                            padding: '10px',
                            border: '1px solid #d1d5db',
                            borderRadius: '8px',
                            fontSize: '14px'
                          }}
                        />
                      </div>

                      <div style={{ gridColumn: 'span 3' }}>
                        <label style={{ display: 'block', marginBottom: '5px', fontSize: '13px', fontWeight: '500', color: '#374151' }}>
                          Descripción del servicio
                        </label>
                        <textarea
                          value={servicio.detalle}
                          onChange={(e) => actualizarServicio(servicio.id, 'detalle', e.target.value)}
                          placeholder="Descripción detallada del trabajo realizado..."
                          rows={2}
                          style={{
                            width: '100%',
                            padding: '10px',
                            border: '1px solid #d1d5db',
                            borderRadius: '8px',
                            fontSize: '14px',
                            resize: 'none'
                          }}
                        />
                      </div>

                      <div>
                        <label style={{ display: 'block', marginBottom: '5px', fontSize: '13px', fontWeight: '500', color: '#374151' }}>
                          Repuesto
                        </label>
                        <input
                          type="text"
                          value={servicio.repuestoDescripcion}
                          onChange={(e) => actualizarServicio(servicio.id, 'repuestoDescripcion', e.target.value)}
                          placeholder="Nombre del repuesto"
                          style={{
                            width: '100%',
                            padding: '10px',
                            border: '1px solid #d1d5db',
                            borderRadius: '8px',
                            fontSize: '14px'
                          }}
                        />
                      </div>

                      <div>
                        <label style={{ display: 'block', marginBottom: '5px', fontSize: '13px', fontWeight: '500', color: '#374151' }}>
                          Cantidad
                        </label>
                        <input
                          type="number"
                          value={servicio.cantidadRepuesto}
                          onChange={(e) => actualizarServicio(servicio.id, 'cantidadRepuesto', e.target.value)}
                          min="1"
                          style={{
                            width: '100%',
                            padding: '10px',
                            border: '1px solid #d1d5db',
                            borderRadius: '8px',
                            fontSize: '14px'
                          }}
                        />
                      </div>

                      <div>
                        <label style={{ display: 'block', marginBottom: '5px', fontSize: '13px', fontWeight: '500', color: '#374151' }}>
                          Costo unitario ($)
                        </label>
                        <input
                          type="number"
                          value={servicio.costoRepuesto}
                          onChange={(e) => actualizarServicio(servicio.id, 'costoRepuesto', e.target.value)}
                          placeholder="0"
                          min="0"
                          style={{
                            width: '100%',
                            padding: '10px',
                            border: '1px solid #d1d5db',
                            borderRadius: '8px',
                            fontSize: '14px'
                          }}
                        />
                      </div>
                    </div>

                    <div style={{
                      marginTop: '15px',
                      padding: '10px',
                      background: 'white',
                      borderRadius: '8px',
                      textAlign: 'right',
                      fontSize: '16px',
                      fontWeight: '600',
                      color: '#059669'
                    }}>
                      Subtotal: ${((parseFloat(servicio.manoObra) || 0) + ((parseFloat(servicio.costoRepuesto) || 0) * (parseInt(servicio.cantidadRepuesto) || 1))).toLocaleString('es-CL')}
                    </div>
                  </div>
                ))}

                <button
                  type="button"
                  onClick={agregarServicio}
                  style={{
                    width: '100%',
                    padding: '15px',
                    background: 'white',
                    border: '2px dashed #3b82f6',
                    borderRadius: '12px',
                    color: '#3b82f6',
                    fontSize: '15px',
                    fontWeight: '600',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '8px',
                    transition: 'all 0.3s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.background = '#eff6ff';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.background = 'white';
                  }}>
                  ➕ Agregar otro servicio
                </button>

                <div style={{ marginTop: '30px', display: 'flex', justifyContent: 'space-between' }}>
                  <button
                    type="button"
                    onClick={() => setCurrentStep(1)}
                    style={{
                      padding: '12px 30px',
                      background: 'white',
                      color: '#6b7280',
                      border: '2px solid #e5e7eb',
                      borderRadius: '10px',
                      fontSize: '16px',
                      fontWeight: '600',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px'
                    }}>
                    <span style={{ fontSize: '18px' }}>←</span>
                    Anterior
                  </button>

                  <button
                    type="button"
                    onClick={() => setCurrentStep(3)}
                    style={{
                      padding: '12px 30px',
                      background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                      color: 'white',
                      border: 'none',
                      borderRadius: '10px',
                      fontSize: '16px',
                      fontWeight: '600',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px'
                    }}>
                    Continuar
                    <span style={{ fontSize: '18px' }}>→</span>
                  </button>
                </div>
              </div>
            )}

            {/* Paso 3: Resumen */}
            {currentStep === 3 && (
              <div style={{ animation: 'fadeIn 0.3s ease' }}>
                <h3 style={{ marginBottom: '20px', color: '#1f2937', fontSize: '20px' }}>
                  Resumen del mantenimiento
                </h3>

                {/* Información del vehículo */}
                <div style={{
                  marginBottom: '20px',
                  padding: '20px',
                  background: '#f0f9ff',
                  borderRadius: '12px',
                  border: '2px solid #0284c7'
                }}>
                  <h4 style={{ margin: '0 0 10px 0', color: '#0c4a6e', fontSize: '16px' }}>
                    🚛 Vehículo
                  </h4>
                  {vehiculos.filter(v => v.id === selectedVehicle).map(v => (
                    <div key={v.id}>
                      <p style={{ margin: '5px 0', fontSize: '15px', color: '#334155' }}>
                        <strong>{v.nombre}</strong> - {v.marca} {v.modelo}
                      </p>
                      <p style={{ margin: '5px 0', fontSize: '14px', color: '#64748b' }}>
                        Patente: {v.patente} | Kilometraje: {v.kilometraje?.toLocaleString('es-CL') || 0} km
                      </p>
                    </div>
                  ))}
                </div>

                {/* Resumen de servicios */}
                <div style={{
                  marginBottom: '20px',
                  padding: '20px',
                  background: '#fefce8',
                  borderRadius: '12px',
                  border: '2px solid #ca8a04'
                }}>
                  <h4 style={{ margin: '0 0 15px 0', color: '#713f12', fontSize: '16px' }}>
                    🔧 Servicios realizados
                  </h4>
                  {serviciosPreventivos.map((s, i) => (
                    <div key={s.id} style={{
                      padding: '10px',
                      marginBottom: '10px',
                      background: 'white',
                      borderRadius: '8px'
                    }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <strong style={{ fontSize: '14px' }}>#{i + 1} {s.servicio || 'Sin nombre'}</strong>
                        <span style={{ fontWeight: '600', color: '#059669' }}>
                          ${((parseFloat(s.manoObra) || 0) + ((parseFloat(s.costoRepuesto) || 0) * (parseInt(s.cantidadRepuesto) || 1))).toLocaleString('es-CL')}
                        </span>
                      </div>
                      {s.detalle && (
                        <p style={{ margin: '5px 0 0 0', fontSize: '13px', color: '#6b7280' }}>
                          {s.detalle}
                        </p>
                      )}
                    </div>
                  ))}
                </div>

                {/* Resumen de costos */}
                <div style={{
                  padding: '25px',
                  background: tipoRepuesto === 'original' ? '#f0fdf4' : '#fef3c7',
                  borderRadius: '12px',
                  border: `3px solid ${tipoRepuesto === 'original' ? '#22c55e' : '#f59e0b'}`
                }}>
                  <h4 style={{
                    margin: '0 0 20px 0',
                    color: tipoRepuesto === 'original' ? '#166534' : '#78350f',
                    fontSize: '18px',
                    textAlign: 'center'
                  }}>
                    💰 Total con repuestos {tipoRepuesto === 'original' ? 'ORIGINALES' : 'ALTERNATIVOS'}
                  </h4>

                  <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '10px' }}>
                    <div style={{ fontSize: '15px', color: '#6b7280' }}>Subtotal Mano de Obra:</div>
                    <div style={{ textAlign: 'right', fontSize: '15px', fontWeight: '500' }}>
                      ${totales.subtotalManoObra.toLocaleString('es-CL')}
                    </div>

                    <div style={{ fontSize: '15px', color: '#6b7280' }}>Subtotal Repuestos:</div>
                    <div style={{ textAlign: 'right', fontSize: '15px', fontWeight: '500' }}>
                      ${totales.subtotalRepuestos.toLocaleString('es-CL')}
                    </div>

                    <div style={{ 
                      paddingTop: '10px',
                      borderTop: '1px solid #e5e7eb',
                      fontWeight: '600',
                      fontSize: '16px'
                    }}>
                      VALOR NETO:
                    </div>
                    <div style={{ 
                      paddingTop: '10px',
                      borderTop: '1px solid #e5e7eb',
                      textAlign: 'right',
                      fontWeight: '600',
                      fontSize: '16px'
                    }}>
                      ${totales.valorNeto.toLocaleString('es-CL')}
                    </div>

                    <div style={{ fontSize: '15px', color: '#6b7280' }}>IVA (19%):</div>
                    <div style={{ textAlign: 'right', fontSize: '15px' }}>
                      ${totales.iva.toLocaleString('es-CL')}
                    </div>

                    <div style={{
                      paddingTop: '15px',
                      borderTop: '2px solid',
                      borderColor: tipoRepuesto === 'original' ? '#22c55e' : '#f59e0b',
                      fontSize: '22px',
                      fontWeight: 'bold',
                      color: tipoRepuesto === 'original' ? '#166534' : '#78350f'
                    }}>
                      TOTAL:
                    </div>
                    <div style={{
                      paddingTop: '15px',
                      borderTop: '2px solid',
                      borderColor: tipoRepuesto === 'original' ? '#22c55e' : '#f59e0b',
                      textAlign: 'right',
                      fontSize: '22px',
                      fontWeight: 'bold',
                      color: tipoRepuesto === 'original' ? '#166534' : '#78350f'
                    }}>
                      ${totales.total.toLocaleString('es-CL')}
                    </div>
                  </div>
                </div>

                <div style={{ marginTop: '30px', display: 'flex', justifyContent: 'space-between' }}>
                  <button
                    type="button"
                    onClick={() => setCurrentStep(2)}
                    style={{
                      padding: '12px 30px',
                      background: 'white',
                      color: '#6b7280',
                      border: '2px solid #e5e7eb',
                      borderRadius: '10px',
                      fontSize: '16px',
                      fontWeight: '600',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px'
                    }}>
                    <span style={{ fontSize: '18px' }}>←</span>
                    Anterior
                  </button>

                  <button
                    type="submit"
                    disabled={loading}
                    style={{
                      padding: '12px 40px',
                      background: loading ? '#9ca3af' : 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                      color: 'white',
                      border: 'none',
                      borderRadius: '10px',
                      fontSize: '16px',
                      fontWeight: '600',
                      cursor: loading ? 'not-allowed' : 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px',
                      boxShadow: '0 4px 15px rgba(34, 197, 94, 0.3)'
                    }}>
                    {loading ? '⏳ Guardando...' : editingMaintenance ? '✅ Actualizar Mantenimiento' : '✅ Guardar Mantenimiento'}
                  </button>
                </div>
              </div>
            )}
          </form>

          <style>{`
            @keyframes fadeIn {
              from { opacity: 0; transform: translateY(10px); }
              to { opacity: 1; transform: translateY(0); }
            }
            @keyframes slideIn {
              from { transform: translateX(-20px); opacity: 0; }
              to { transform: translateX(0); opacity: 1; }
            }
          `}</style>
        </div>
      )}

      {/* Lista de mantenimientos mejorada */}
      {!showMaintenanceForm && (
        <div style={{
          background: 'white',
          borderRadius: '20px',
          boxShadow: '0 10px 40px rgba(0,0,0,0.1)',
          overflow: 'hidden'
        }}>
          <div style={{
            padding: '25px',
            borderBottom: '2px solid #e5e7eb',
            background: 'linear-gradient(135deg, #f9fafb 0%, #f3f4f6 100%)'
          }}>
            <h2 style={{ margin: 0, color: '#1f2937', fontSize: '22px', fontWeight: '600' }}>
              📋 Historial de Mantenimientos
            </h2>
          </div>

          {filteredRecords.length === 0 ? (
            <div style={{
              padding: '60px',
              textAlign: 'center'
            }}>
              <div style={{ fontSize: '60px', marginBottom: '20px', opacity: 0.2 }}>🔧</div>
              <h3 style={{ color: '#9ca3af', fontSize: '20px', margin: '0 0 10px 0' }}>
                No hay mantenimientos registrados
              </h3>
              <p style={{ color: '#d1d5db', fontSize: '16px' }}>
                Comienza agregando tu primer mantenimiento preventivo
              </p>
            </div>
          ) : (
            <div style={{ padding: '20px' }}>
              {filteredRecords.map((record) => (
                <div key={record.id} style={{
                  marginBottom: '15px',
                  padding: '20px',
                  background: '#f9fafb',
                  borderRadius: '12px',
                  border: '2px solid #e5e7eb',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.borderColor = '#3b82f6';
                  e.currentTarget.style.transform = 'translateX(5px)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.borderColor = '#e5e7eb';
                  e.currentTarget.style.transform = 'translateX(0)';
                }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
                    <div style={{ flex: 1 }}>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '10px' }}>
                        <h3 style={{ margin: 0, fontSize: '18px', color: '#1f2937' }}>
                          {record.vehiculoNombre}
                        </h3>
                        <span style={{
                          padding: '4px 10px',
                          borderRadius: '6px',
                          fontSize: '12px',
                          fontWeight: '600',
                          background: record.tipoRepuesto === 'original' ? '#dcfce7' : '#fef3c7',
                          color: record.tipoRepuesto === 'original' ? '#166534' : '#78350f'
                        }}>
                          {record.tipoRepuesto === 'original' ? '✅ Original' : '🔄 Alternativo'}
                        </span>
                      </div>
                      
                      <div style={{ display: 'flex', gap: '20px', fontSize: '14px', color: '#6b7280' }}>
                        <span>📅 {new Date(record.fechaMantenimiento).toLocaleDateString('es-CL')}</span>
                        <span>🚗 {record.vehiculoPatente}</span>
                        <span>🔧 {record.serviciosPreventivos?.length || 0} servicios</span>
                        <span>📍 {record.kilometrajeActual?.toLocaleString('es-CL') || 0} km</span>
                      </div>
                    </div>

                    <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
                      <div style={{ textAlign: 'right' }}>
                        <div style={{ fontSize: '12px', color: '#9ca3af' }}>Total</div>
                        <div style={{ fontSize: '22px', fontWeight: 'bold', color: '#059669' }}>
                          ${record.totales?.total?.toLocaleString('es-CL') || 0}
                        </div>
                      </div>

                      <div style={{ display: 'flex', gap: '8px' }}>
                        <button
                          onClick={() => handleEditMaintenance(record)}
                          style={{
                            padding: '8px',
                            background: '#3b82f6',
                            color: 'white',
                            border: 'none',
                            borderRadius: '8px',
                            fontSize: '16px',
                            cursor: 'pointer',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            width: '36px',
                            height: '36px'
                          }}>
                          ✏️
                        </button>
                        <button
                          onClick={() => handleDeleteMaintenance(record.id)}
                          style={{
                            padding: '8px',
                            background: '#ef4444',
                            color: 'white',
                            border: 'none',
                            borderRadius: '8px',
                            fontSize: '16px',
                            cursor: 'pointer',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            width: '36px',
                            height: '36px'
                          }}>
                          🗑️
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default PreventiveMaintenanceModule;
