// src/components/PasswordRecovery.js
import React, { useState } from 'react';
import { firestore } from '../config/firebase';
import { collection, query, where, getDocs, doc, updateDoc } from 'firebase/firestore';

const PasswordRecovery = ({ onClose, onSuccess }) => {
  const [step, setStep] = useState(1); // 1: Email/PIN, 2: Mostrar contrase√±a temporal
  const [formData, setFormData] = useState({
    email: '',
    pin: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [userData, setUserData] = useState(null);
  const [temporaryPassword, setTemporaryPassword] = useState('');

  // Funci√≥n para generar contrase√±a temporal aleatoria
  const generateTemporaryPassword = () => {
    const chars = 'ABCDEFGHJKLMNPQRSTUVWXYZabcdefghjkmnpqrstuvwxyz23456789!@#';
    let password = '';
    for (let i = 0; i < 10; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return password;
  };

  // Funci√≥n simple de hash (para producci√≥n usar bcrypt)
  const simpleHash = (text) => {
    return btoa(text); // Codificaci√≥n base64 simple
  };

  // Verificar email y PIN y generar contrase√±a temporal
  const handleVerifyPin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Buscar usuario por email
      const usersRef = collection(firestore, 'users');
      const q = query(usersRef, where('email', '==', formData.email.toLowerCase().trim()));
      const querySnapshot = await getDocs(q);

      if (querySnapshot.empty) {
        setMessage({ 
          type: 'error', 
          text: '‚ùå No existe un usuario con ese correo electr√≥nico' 
        });
        setLoading(false);
        return;
      }

      const userDoc = querySnapshot.docs[0];
      const user = { id: userDoc.id, ...userDoc.data() };

      // Verificar PIN
      const storedPin = user.recoveryPin ? atob(user.recoveryPin) : null;
      
      if (!storedPin) {
        setMessage({ 
          type: 'error', 
          text: '‚ùå Este usuario no tiene PIN configurado. Contacta al administrador.' 
        });
        setLoading(false);
        return;
      }

      if (formData.pin !== storedPin) {
        setMessage({ 
          type: 'error', 
          text: '‚ùå PIN incorrecto. Intenta nuevamente.' 
        });
        setLoading(false);
        return;
      }

      // PIN correcto - Generar contrase√±a temporal
      const tempPass = generateTemporaryPassword();
      
      // Guardar la contrase√±a temporal en Firestore
      await updateDoc(doc(firestore, 'users', user.id), {
        tempPassword: simpleHash(tempPass),
        passwordResetAt: new Date().toISOString(),
        requirePasswordChange: true,
        resetMethod: 'PIN'
      });

      // Guardar datos para mostrar
      setUserData(user);
      setTemporaryPassword(tempPass);
      setStep(2);
      setMessage({ 
        type: 'success', 
        text: '‚úÖ PIN verificado correctamente' 
      });

    } catch (error) {
      console.error('Error:', error);
      setMessage({ 
        type: 'error', 
        text: '‚ùå Error al verificar los datos. Intenta nuevamente.' 
      });
    } finally {
      setLoading(false);
    }
  };

  // Funci√≥n para copiar contrase√±a al portapapeles
  const copyToClipboard = () => {
    navigator.clipboard.writeText(temporaryPassword);
    setMessage({ 
      type: 'success', 
      text: '‚úÖ Contrase√±a copiada al portapapeles' 
    });
    setTimeout(() => {
      setMessage({ type: '', text: '' });
    }, 3000);
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000,
      padding: '20px'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        padding: '30px',
        maxWidth: '450px',
        width: '100%',
        boxShadow: '0 10px 40px rgba(0,0,0,0.2)'
      }}>
        {/* Header */}
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '25px'
        }}>
          <h2 style={{ 
            margin: 0, 
            fontSize: '24px', 
            color: '#1f2937' 
          }}>
            üîê Recuperar Contrase√±a
          </h2>
          <button
            onClick={onClose}
            type="button"
            style={{
              background: 'transparent',
              border: 'none',
              fontSize: '24px',
              cursor: 'pointer',
              color: '#6b7280',
              padding: '5px'
            }}
          >
            ‚úñ
          </button>
        </div>

        {/* Indicador de pasos */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          marginBottom: '25px',
          gap: '10px'
        }}>
          <div style={{
            width: '150px',
            height: '4px',
            background: step >= 1 ? '#3b82f6' : '#e5e7eb',
            borderRadius: '2px',
            transition: 'all 0.3s'
          }} />
          <div style={{
            width: '150px',
            height: '4px',
            background: step >= 2 ? '#22c55e' : '#e5e7eb',
            borderRadius: '2px',
            transition: 'all 0.3s'
          }} />
        </div>

        {/* Mensajes */}
        {message.text && (
          <div style={{
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            background: message.type === 'success' ? '#dcfce7' : '#fee2e2',
            border: `1px solid ${message.type === 'success' ? '#86efac' : '#fecaca'}`,
            color: message.type === 'success' ? '#166534' : '#991b1b',
            fontSize: '14px',
            animation: 'slideIn 0.3s ease'
          }}>
            {message.text}
          </div>
        )}

        {/* PASO 1: Verificar Email y PIN */}
        {step === 1 && (
          <form onSubmit={handleVerifyPin}>
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                Correo Electr√≥nico
              </label>
              <input
                type="email"
                required
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                placeholder="tu@correo.com"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '16px',
                  outline: 'none',
                  transition: 'border-color 0.2s'
                }}
                autoFocus
              />
            </div>

            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: '#374151',
                fontSize: '14px',
                fontWeight: '500'
              }}>
                PIN de Seguridad
              </label>
              <input
                type="text"
                required
                maxLength="4"
                pattern="[0-9]{4}"
                value={formData.pin}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, '');
                  setFormData({...formData, pin: value});
                }}
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '24px',
                  textAlign: 'center',
                  letterSpacing: '15px',
                  fontWeight: 'bold',
                  outline: 'none',
                  transition: 'border-color 0.2s'
                }}
              />
              <small style={{ 
                color: '#6b7280', 
                fontSize: '12px',
                display: 'block',
                marginTop: '5px'
              }}>
                Ingresa tu PIN de 4 d√≠gitos
              </small>
            </div>

            <button
              type="submit"
              disabled={loading || formData.pin.length !== 4}
              style={{
                width: '100%',
                padding: '14px',
                background: loading || formData.pin.length !== 4 ? '#9ca3af' : 
                          'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: loading || formData.pin.length !== 4 ? 'not-allowed' : 'pointer',
                transition: 'all 0.3s'
              }}
            >
              {loading ? (
                <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
                  <span className="spinner"></span>
                  Verificando...
                </span>
              ) : 'Verificar PIN ‚Üí'}
            </button>
          </form>
        )}

        {/* PASO 2: Mostrar contrase√±a temporal */}
        {step === 2 && (
          <div>
            <div style={{
              marginBottom: '20px',
              padding: '15px',
              background: 'linear-gradient(135deg, #dcfce7 0%, #d9f99d 100%)',
              borderRadius: '8px',
              border: '1px solid #22c55e'
            }}>
              <p style={{ 
                margin: 0, 
                fontSize: '14px', 
                color: '#166534',
                fontWeight: '600'
              }}>
                ‚úÖ Recuperaci√≥n Exitosa
              </p>
              <p style={{ 
                margin: '8px 0 0 0', 
                fontSize: '14px', 
                color: '#166534' 
              }}>
                Usuario: <strong>{userData?.name}</strong>
              </p>
              <p style={{ 
                margin: '4px 0 0 0', 
                fontSize: '13px', 
                color: '#64748b' 
              }}>
                üìß {userData?.email}
              </p>
            </div>

            <div style={{
              background: '#f0f9ff',
              border: '2px dashed #3b82f6',
              borderRadius: '8px',
              padding: '20px',
              marginBottom: '20px',
              textAlign: 'center'
            }}>
              <p style={{
                margin: '0 0 10px 0',
                fontSize: '14px',
                color: '#1e40af',
                fontWeight: '600'
              }}>
                Tu contrase√±a temporal es:
              </p>
              <div style={{
                background: 'white',
                padding: '15px',
                borderRadius: '6px',
                margin: '10px 0',
                border: '1px solid #dbeafe',
                position: 'relative'
              }}>
                <code style={{
                  fontSize: '20px',
                  fontWeight: 'bold',
                  color: '#1e293b',
                  letterSpacing: '2px',
                  fontFamily: 'monospace'
                }}>
                  {temporaryPassword}
                </code>
              </div>
              <button
                type="button"
                onClick={copyToClipboard}
                style={{
                  padding: '10px 20px',
                  background: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  fontWeight: '500',
                  cursor: 'pointer',
                  marginTop: '10px'
                }}
              >
                üìã Copiar Contrase√±a
              </button>
            </div>

            <div style={{
              background: '#fef3c7',
              border: '1px solid #f59e0b',
              borderRadius: '8px',
              padding: '15px',
              marginBottom: '20px'
            }}>
              <h4 style={{ 
                margin: '0 0 10px 0', 
                color: '#92400e', 
                fontSize: '14px',
                fontWeight: '600'
              }}>
                ‚ö†Ô∏è Importante:
              </h4>
              <ul style={{ 
                margin: 0, 
                paddingLeft: '20px', 
                fontSize: '13px', 
                color: '#78350f' 
              }}>
                <li>Esta contrase√±a es temporal y de un solo uso</li>
                <li>Deber√°s cambiarla al iniciar sesi√≥n</li>
                <li>An√≥tala o c√≥piala antes de cerrar esta ventana</li>
                <li>Por seguridad, no podr√°s verla nuevamente</li>
              </ul>
            </div>

            <button
              type="button"
              onClick={() => {
                onSuccess();
                onClose();
              }}
              style={{
                width: '100%',
                padding: '14px',
                background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s'
              }}
            >
              ‚úÖ Ir a Iniciar Sesi√≥n
            </button>
          </div>
        )}

        <style>{`
          @keyframes slideIn {
            from {
              opacity: 0;
              transform: translateY(-10px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
          
          .spinner {
            width: 16px;
            height: 16px;
            border: 2px solid #ffffff;
            border-top-color: transparent;
            border-radius: 50%;
            animation: spin 0.6s linear infinite;
            display: inline-block;
          }
          
