// src/components/SistemaTrabajadoresFirebase.js
import React, { useEffect, useRef, useState } from 'react';
import { trabajadoresService } from '../services/firebaseservices';
import { database } from '../config/firebase';
import { ref, onValue, update, set } from 'firebase/database';

const SistemaTrabajadoresFirebase = ({ currentUser, onLogout }) => {
  // Log para debugging
  console.log('üîç Current User Data:', currentUser);
  
  // Estados principales
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [userPosition, setUserPosition] = useState(null);
  const [selectedZona, setSelectedZona] = useState(null);
  const [isTracking, setIsTracking] = useState(false);
  const [isNavigating, setIsNavigating] = useState(false);
  const [workStatus, setWorkStatus] = useState('idle'); // idle, traveling, working, completed
  const [workLog, setWorkLog] = useState({
    startTime: null,
    arrivalTime: null,
    endTime: null,
    zona: null
  });
  
  // Estados para zonas y notificaciones
  const [zonasAsignadas, setZonasAsignadas] = useState([]);
  const [notificaciones, setNotificaciones] = useState([]);
  const [showPanel, setShowPanel] = useState(true); // Estado para mostrar/ocultar panel
  const [isMobile, setIsMobile] = useState(false);
  
  // Referencias para marcadores y servicios
  const userMarkerRef = useRef(null);
  const zonaMarkerRef = useRef(null);
  const directionsServiceRef = useRef(null);
  const directionsRendererRef = useRef(null);
  const watchIdRef = useRef(null);
  const lastUpdateRef = useRef(null);
  
  // Estados de UI
  const [mapLoaded, setMapLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [distance, setDistance] = useState(null);
  const [duration, setDuration] = useState(null);
  const [showWorkLog, setShowWorkLog] = useState(false);
  const [gpsStatus, setGpsStatus] = useState('inactive'); // inactive, activating, active, error

  const API_KEY = 'AIzaSyA7DrdL36n5cx-RNzuXGAQggFeGCheHDbY';

  // Detectar si es m√≥vil
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Cargar zonas asignadas desde Firebase
  useEffect(() => {
    if (currentUser && currentUser.uid) {
      console.log('üîÑ Cargando zonas asignadas para trabajador:', currentUser.uid);
      
      // Escuchar zonas asignadas al trabajador
      const zonasRef = ref(database, 'zonas_asignadas');
      const unsubZonas = onValue(zonasRef, (snapshot) => {
        const data = snapshot.val();
        console.log('üì¶ Datos de zonas recibidos:', data);
        
        if (data) {
          // Filtrar solo las zonas asignadas a este trabajador
          const zonasArray = Object.entries(data)
            .filter(([id, zona]) => {
              console.log(`Zona ${id}: asignada a ${zona.trabajadorAsignado}, yo soy ${currentUser.uid}`);
              return zona.trabajadorAsignado === currentUser.uid && zona.estado !== 'completado';
            })
            .map(([id, zona]) => ({
              id,
              ...zona
            }));
          
          console.log('‚úÖ Zonas asignadas filtradas:', zonasArray);
          setZonasAsignadas(zonasArray);
        } else {
          console.log('‚ö†Ô∏è No hay zonas en Firebase');
          setZonasAsignadas([]);
        }
      });

      // Escuchar notificaciones
      const notifRef = ref(database, 'notificaciones');
      const unsubNotif = onValue(notifRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
          const notifArray = Object.entries(data)
            .filter(([id, notif]) => notif.trabajadorId === currentUser.uid && !notif.leido)
            .map(([id, notif]) => ({
              id,
              ...notif
            }));
          setNotificaciones(notifArray);
        }
      });

      return () => {
        unsubZonas();
        unsubNotif();
      };
    }
  }, [currentUser]);

  // Registrar trabajador al montar el componente
  useEffect(() => {
    if (currentUser && currentUser.uid) {
      console.log('üîÑ Registrando trabajador al iniciar...');
      
      // Crear o actualizar el trabajador en Realtime Database
      trabajadoresService.createOrUpdateTrabajador(currentUser.uid, {
        nombre: currentUser.name || currentUser.email || 'Trabajador',
        email: currentUser.email,
        telefono: currentUser.phone || '',
        vehicleId: currentUser.vehicleId || null,
        estado: 'inactivo',
        rol: 'trabajador',
        ubicacion: null,
        createdAt: new Date().toISOString(),
        ultimaActualizacion: new Date().toISOString()
      }).then(() => {
        console.log('‚úÖ Trabajador registrado en Realtime Database al iniciar');
      }).catch(error => {
        console.error('‚ùå Error al registrar trabajador al iniciar:', error);
      });
    }
  }, [currentUser]);

  // Funci√≥n para cargar Google Maps
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve, reject) => {
        if (window.google && window.google.maps) {
          resolve(window.google.maps);
          return;
        }

        const existingScript = document.querySelector('script[src*="maps.googleapis.com"]');
        if (existingScript) {
          existingScript.addEventListener('load', () => {
            if (window.google && window.google.maps) {
              resolve(window.google.maps);
            } else {
              reject(new Error('Google Maps no disponible'));
            }
          });
          return;
        }

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${API_KEY}&libraries=places,geometry&v=weekly`;
        script.async = true;
        script.defer = true;

        script.onload = () => {
          if (window.google && window.google.maps) {
            resolve(window.google.maps);
          } else {
            reject(new Error('Google Maps no disponible'));
          }
        };

        script.onerror = () => {
          reject(new Error('Error al cargar Google Maps'));
        };

        document.head.appendChild(script);
      });
    };

    const initMap = async () => {
      try {
        const maps = await loadGoogleMaps();
        
        if (!mapRef.current) return;

        const mapInstance = new maps.Map(mapRef.current, {
          center: { lat: -36.7333, lng: -72.4667 },
          zoom: 13,
          mapTypeId: 'roadmap',
          zoomControl: !isMobile,
          mapTypeControl: false,
          scaleControl: false,
          streetViewControl: false,
          rotateControl: false,
          fullscreenControl: true,
          gestureHandling: 'greedy' // Mejor para m√≥viles
        });

        setMap(mapInstance);
        
        // Inicializar servicios de direcciones
        // Inicializar servicios de direcciones
directionsServiceRef.current = new maps.DirectionsService();
directionsRendererRef.current = new maps.DirectionsRenderer({
  map: mapInstance,
  suppressMarkers: false, // Mostrar marcadores A y B
  polylineOptions: {
    strokeColor: '#4285F4',
    strokeWeight: 6,
    strokeOpacity: 0.8
  },
  // Agregar estas opciones para mejor visualizaci√≥n
  markerOptions: {
    startMarker: {
      label: 'A',
      title: 'Tu ubicaci√≥n'
    },
    endMarker: {
      label: 'B', 
      title: 'Zona de trabajo'
    }
  },
  preserveViewport: false // Ajustar vista autom√°ticamente a la ruta
});

        setMapLoaded(true);
      } catch (err) {
        setError('Error al cargar el mapa: ' + err.message);
      }
    };

    initMap();
  }, [isMobile]);

  // üî• FUNCI√ìN CR√çTICA - Actualizar posici√≥n del usuario Y ENVIAR A FIREBASE
  const updateUserPosition = (position) => {
    console.log('üîÑ Actualizando posici√≥n:', position);
    setUserPosition(position);
    
    // Mostrar precisi√≥n en el UI
    if (window.google && map) {
      // Opcional: Mostrar c√≠rculo de precisi√≥n en el mapa
      const accuracy = position.accuracy || 100;
      console.log('üìç Precisi√≥n actual:', accuracy, 'metros');
    }
    
    // üî• ENVIAR UBICACI√ìN A FIREBASE - ESTO ES LO QUE FALTABA
    // Solo actualizar si ha pasado al menos 5 segundos desde la √∫ltima actualizaci√≥n
    const ahora = Date.now();
    if (!lastUpdateRef.current || (ahora - lastUpdateRef.current) > 5000) {
      console.log('üìç Enviando ubicaci√≥n a Firebase:', position);
      
      if (currentUser && currentUser.uid) {
        // Actualizar ubicaci√≥n en Firebase
        trabajadoresService.updateTrabajadorLocation(currentUser.uid, {
          lat: position.lat,
          lng: position.lng,
          timestamp: new Date().toISOString(),
          accuracy: position.accuracy || null // Guardar precisi√≥n tambi√©n
        }).then(() => {
          console.log('‚úÖ Ubicaci√≥n enviada a Firebase exitosamente');
          console.log('   - Trabajador ID:', currentUser.uid);
          console.log('   - Posici√≥n:', position);
        }).catch(error => {
          console.error('‚ùå Error al enviar ubicaci√≥n a Firebase:', error);
          console.error('   - Detalles:', error.message);
        });
        
        // Tambi√©n actualizar el estado del trabajador si es necesario
        const estadoActual = workStatus === 'traveling' ? 'en_camino' : 
                            workStatus === 'working' ? 'trabajando' : 
                            'disponible';
        
        // Actualizar datos adicionales del trabajador
        trabajadoresService.updateTrabajadorData(currentUser.uid, {
          nombre: currentUser.name || currentUser.email,
          email: currentUser.email,
          estado: estadoActual,
          zonaDestino: selectedZona ? selectedZona.nombre : null,
          ultimaActualizacion: new Date().toISOString()
        }).then(() => {
          console.log('‚úÖ Datos del trabajador actualizados');
        }).catch(error => {
          console.error('‚ùå Error al actualizar datos del trabajador:', error);
        });
        
        lastUpdateRef.current = ahora;
      }
    }
    
    // Actualizar marcador en el mapa local
    if (!map || !window.google) return;

    // Crear o actualizar marcador del usuario
    if (!userMarkerRef.current) {
      userMarkerRef.current = new window.google.maps.Marker({
        position: position,
        map: map,
        title: 'Tu ubicaci√≥n',
        icon: {
          path: window.google.maps.SymbolPath.CIRCLE,
          scale: 10,
          fillColor: '#4285F4',
          fillOpacity: 1,
          strokeColor: '#ffffff',
          strokeWeight: 3
        },
        zIndex: 1000
      });
      
      // Centrar mapa en usuario
      map.setCenter(position);
      map.setZoom(15);
    } else {
      userMarkerRef.current.setPosition(position);
    }

    // Si est√° navegando, actualizar distancia pero NO cambiar autom√°ticamente a working
    if (isNavigating && selectedZona) {
      const distance = window.google.maps.geometry.spherical.computeDistanceBetween(
        new window.google.maps.LatLng(position.lat, position.lng),
        new window.google.maps.LatLng(selectedZona.lat, selectedZona.lng)
      );
      
      // Solo mostrar distancia, no cambiar estado autom√°ticamente
      console.log('üìè Distancia a la zona:', Math.round(distance), 'metros');
    }
  };

  // Funci√≥n para activar/desactivar GPS
  const toggleGPS = () => {
    if (isTracking) {
      // Detener tracking
      if (watchIdRef.current) {
        navigator.geolocation.clearWatch(watchIdRef.current);
        watchIdRef.current = null;
      }
      setIsTracking(false);
      setGpsStatus('inactive');
      
      // Mantener √∫ltima posici√≥n para la pr√≥xima activaci√≥n
      console.log('üìç GPS desactivado, √∫ltima ubicaci√≥n guardada:', userPosition);
      
      // Limpiar marcador de usuario
      if (userMarkerRef.current) {
        userMarkerRef.current.setMap(null);
        userMarkerRef.current = null;
      }
      
      // Limpiar ubicaci√≥n en Firebase al desactivar GPS (sin await)
      if (currentUser && currentUser.uid) {
        trabajadoresService.updateTrabajadorData(currentUser.uid, {
          estado: 'inactivo',
          // NO borrar la ubicaci√≥n, solo marcar como inactivo
          // ubicacion: null, // COMENTADO - mantener √∫ltima ubicaci√≥n
          ultimaActualizacion: new Date().toISOString()
        }).then(() => {
          console.log('üìç GPS desactivado - Estado actualizado a inactivo');
        }).catch(error => {
          console.error('Error al actualizar estado en Firebase:', error);
        });
        
        // üî• NUEVO: Si hay un veh√≠culo asignado y est√° en uso, liberarlo
        if (currentUser.vehicleId && (workStatus === 'traveling' || workStatus === 'working')) {
          const vehicleRef = ref(database, `vehiculos/${currentUser.vehicleId}`);
          update(vehicleRef, {
            estado: 'disponible',
            operadorEnRuta: null,
            zonaDestino: null,
            enZonaTrabajo: false,
            ultimaActualizacion: new Date().toISOString()
          }).then(() => {
            console.log('‚úÖ Veh√≠culo liberado al desactivar GPS');
          }).catch(error => {
            console.error('Error al liberar veh√≠culo:', error);
          });
        }
      }
      
      // Mantener √∫ltima posici√≥n conocida
      console.log('üìç √öltima ubicaci√≥n conocida guardada:', userPosition);
    } else {
      // Iniciar tracking
      if (!navigator.geolocation) {
        alert('Tu navegador no soporta geolocalizaci√≥n');
        setGpsStatus('error');
        return;
      }

      console.log('üîÑ Iniciando GPS...');
      setIsTracking(true);
      setGpsStatus('activating');
      
      // Registrar el trabajador en Firebase si no existe (sin bloquear)
      if (currentUser && currentUser.uid) {
        console.log('üìù Registrando trabajador en Realtime Database...');
        console.log('   - User ID:', currentUser.uid);
        console.log('   - Datos:', currentUser);
        
        trabajadoresService.createOrUpdateTrabajador(currentUser.uid, {
          nombre: currentUser.name || currentUser.email || 'Trabajador',
          email: currentUser.email,
          telefono: currentUser.phone || '',
          estado: 'disponible',
          rol: 'trabajador',
          vehicleId: currentUser.vehicleId || null,
          createdAt: new Date().toISOString()
        }).then(() => {
          console.log('‚úÖ Trabajador registrado en Realtime Database');
          console.log('   - Path: trabajadores/' + currentUser.uid);
        }).catch(error => {
          console.error('‚ùå Error al registrar trabajador:', error);
          console.error('   - Detalles:', error.message);
        });
      } else {
        console.error('‚ö†Ô∏è No hay currentUser o uid disponible');
      }
      
      // Obtener posici√≥n inicial con ALTA PRECISI√ìN
      console.log('üìç Intentando obtener ubicaci√≥n GPS de alta precisi√≥n...');
      
      // Si ya tenemos una posici√≥n previa, usarla mientras obtenemos la nueva
      if (userPosition) {
        console.log('üìç Usando √∫ltima ubicaci√≥n conocida mientras se obtiene GPS de alta precisi√≥n');
        setGpsStatus('active');
        
        // Intentar obtener ubicaci√≥n de ALTA PRECISI√ìN en segundo plano
        navigator.geolocation.getCurrentPosition(
          (position) => {
            console.log('üìç GPS de alta precisi√≥n actualizado:', position.coords);
            console.log('   - Precisi√≥n:', position.coords.accuracy, 'metros');
            const pos = {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
              accuracy: position.coords.accuracy
            };
            updateUserPosition(pos);
          },
          (error) => {
            console.log('‚ö†Ô∏è GPS de alta precisi√≥n no disponible, usando precisi√≥n normal');
          },
          {
            enableHighAccuracy: true, // üî¥ ALTA PRECISI√ìN
            timeout: 20000, // M√°s tiempo para alta precisi√≥n
            maximumAge: 0 // No usar cach√©, queremos ubicaci√≥n fresca
          }
        );
      } else {
        // Primera vez - intentar con alta precisi√≥n
        setGpsStatus('activating');
        
        navigator.geolocation.getCurrentPosition(
          (position) => {
            console.log('‚úÖ GPS de alta precisi√≥n obtenido:', position.coords);
            console.log('   - Precisi√≥n:', position.coords.accuracy, 'metros');
            const pos = {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
              accuracy: position.coords.accuracy
            };
            updateUserPosition(pos);
            setGpsStatus('active');
          },
          (error) => {
            console.warn('‚ö†Ô∏è Alta precisi√≥n fall√≥, intentando con precisi√≥n normal:', error.message);
            
            if (error.code === error.PERMISSION_DENIED) {
              alert('Por favor, habilita los permisos de ubicaci√≥n en tu navegador');
              setIsTracking(false);
              setGpsStatus('error');
              return;
            }
            
            // Segundo intento con precisi√≥n normal
            navigator.geolocation.getCurrentPosition(
              (position) => {
                console.log('üìç GPS con precisi√≥n normal:', position.coords);
                console.log('   - Precisi√≥n:', position.coords.accuracy, 'metros');
                const pos = {
                  lat: position.coords.latitude,
                  lng: position.coords.longitude,
                  accuracy: position.coords.accuracy
                };
                updateUserPosition(pos);
                setGpsStatus('active');
              },
              (error2) => {
                console.error('‚ùå No se pudo obtener GPS:', error2);
                // √öltima opci√≥n: ubicaci√≥n de respaldo
                const testPosition = {
                  lat: -36.7508 + (Math.random() - 0.5) * 0.001,
                  lng: -72.4698 + (Math.random() - 0.5) * 0.001,
                  accuracy: 100
                };
                updateUserPosition(testPosition);
                setGpsStatus('active');
              },
              {
                enableHighAccuracy: false, // Precisi√≥n normal
                timeout: 10000,
                maximumAge: 30000
              }
            );
          },
          {
            enableHighAccuracy: true, // üî¥ ALTA PRECISI√ìN primero
            timeout: 15000, // 15 segundos para alta precisi√≥n
            maximumAge: 0 // No usar cach√©
          }
        );
      }

      // Seguimiento continuo con ALTA PRECISI√ìN
      const gpsWatchId = navigator.geolocation.watchPosition(
        (position) => {
          console.log('üìç GPS - Actualizaci√≥n:', position.coords);
          console.log('   - Precisi√≥n:', position.coords.accuracy, 'metros');
          
          // Solo actualizar si la precisi√≥n es mejor que 150 metros
          if (position.coords.accuracy <= 150) {
            const pos = {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
              accuracy: position.coords.accuracy
            };
            updateUserPosition(pos);
            setGpsStatus('active');
          } else {
            console.log('‚ö†Ô∏è Precisi√≥n insuficiente, esperando mejor se√±al...');
          }
        },
        (error) => {
          console.log('‚è±Ô∏è Esperando se√±al GPS...', error.message);
        },
        {
          enableHighAccuracy: true, // üî¥ ALTA PRECISI√ìN
          timeout: 30000,
          maximumAge: 0, // Siempre ubicaci√≥n fresca
          distanceFilter: 5 // Actualizar cada 5 metros de movimiento
        }
      );
      
      watchIdRef.current = gpsWatchId;
    }
  };

  // Seleccionar zona de trabajo
  const selectZona = (zona) => {
    setSelectedZona(zona);
    
    if (!map || !window.google) return;

    // Limpiar marcador anterior
    if (zonaMarkerRef.current) {
      zonaMarkerRef.current.setMap(null);
    }

    // Crear marcador de zona
    zonaMarkerRef.current = new window.google.maps.Marker({
      position: { lat: zona.lat, lng: zona.lng },
      map: map,
      title: zona.nombre,
      icon: {
        path: window.google.maps.SymbolPath.BACKWARD_CLOSED_ARROW,
        scale: 8,
        fillColor: '#22c55e',
        fillOpacity: 1,
        strokeColor: '#ffffff',
        strokeWeight: 2
      }
    });

    // Ajustar vista para mostrar ambos puntos
    if (userPosition) {
      const bounds = new window.google.maps.LatLngBounds();
      bounds.extend(userPosition);
      bounds.extend({ lat: zona.lat, lng: zona.lng });
      map.fitBounds(bounds, 100);
    } else {
      map.setCenter({ lat: zona.lat, lng: zona.lng });
      map.setZoom(15);
    }
  };

  // Iniciar navegaci√≥n
  const startNavigation = async () => {
    if (!selectedZona || !userPosition) {
      alert('Debes activar el GPS y seleccionar una zona primero');
      return;
    }

    setIsNavigating(true);
    setWorkStatus('traveling');
    setWorkLog({
      ...workLog,
      startTime: new Date().toISOString(),
      zona: selectedZona
    });

    // üî• Actualizar estado en Firebase y en la zona
    try {
      // Actualizar estado del trabajador
      await trabajadoresService.updateTrabajadorData(currentUser.uid, {
        estado: 'en_camino',
        zonaDestino: selectedZona.nombre,
        recorridoInicio: new Date().toISOString()
      });
      
      // Actualizar estado de la zona a "en_progreso"
      if (selectedZona.id) {
        await update(ref(database, `zonas_asignadas/${selectedZona.id}`), {
          estado: 'en_progreso',
          ultimaActualizacion: new Date().toISOString()
        });
      }
      
      // üî• NUEVO: Actualizar estado del veh√≠culo asignado
      if (currentUser.vehicleId) {
        const vehicleRef = ref(database, `vehiculos/${currentUser.vehicleId}`);
        await update(vehicleRef, {
          estado: 'en_uso',
          operadorEnRuta: currentUser.name || currentUser.email,
          zonaDestino: selectedZona.nombre,
          ultimaActualizacion: new Date().toISOString()
        });
        console.log('‚úÖ Veh√≠culo marcado como en uso');
      }
      
      console.log('‚úÖ Estado actualizado a "en_camino" en Firebase');
    } catch (error) {
      console.error('Error al actualizar estado en Firebase:', error);
    }

    // Calcular y mostrar ruta
const request = {
  origin: userPosition,
  destination: { lat: selectedZona.lat, lng: selectedZona.lng },
  travelMode: window.google.maps.TravelMode.DRIVING,
  // Agregar estas opciones para mejor c√°lculo de ruta
  unitSystem: window.google.maps.UnitSystem.METRIC,
  region: 'CL', // Chile
  avoidHighways: false,
  avoidTolls: false,
  optimizeWaypoints: true,
  provideRouteAlternatives: true // Obtener rutas alternativas
};

directionsServiceRef.current.route(request, (result, status) => {
  if (status === 'OK') {
    console.log('‚úÖ Ruta calculada exitosamente');
    console.log('Rutas disponibles:', result.routes.length);
    
    // Usar la mejor ruta disponible
    directionsRendererRef.current.setDirections(result);
    
    const route = result.routes[0];
    const leg = route.legs[0];
    
    setDistance(leg.distance.text);
    setDuration(leg.duration.text);
    
    // Informaci√≥n adicional de la ruta
    console.log('üìç Distancia:', leg.distance.text);
    console.log('‚è±Ô∏è Duraci√≥n estimada:', leg.duration.text);
    console.log('üìç Direcci√≥n inicio:', leg.start_address);
