          </div>
        )}

        {/* Estado: Completado */}
        {workStatus === 'completed' && (
          <div>
            <div style={{
              background: '#e0e7ff',
              padding: '12px',
              borderRadius: '8px',
              marginBottom: '12px',
              border: '1px solid #c7d2fe',
              textAlign: 'center'
            }}>
              <div style={{ fontSize: '16px', fontWeight: '500', color: '#312e81' }}>
                ‚úÖ Trabajo Completado
              </div>
            </div>
            <button
              onClick={resetWork}
              style={{
                ...styles.button,
                background: '#22c55e',
                color: 'white'
              }}
            >
              üîÑ Nuevo Trabajo
            </button>
          </div>
        )}
      </div>

      {/* Bot√≥n flotante cuando el panel est√° oculto */}
      {!showPanel && workStatus !== 'idle' && (
        <button
          onClick={() => setShowPanel(true)}
          style={{
            ...styles.floatingButton,
            background: workStatus === 'traveling' ? '#f59e0b' :
                      workStatus === 'working' ? '#3b82f6' :
                      '#22c55e',
            color: 'white',
            border: 'none'
          }}
        >
          {workStatus === 'traveling' && 'üöó'}
          {workStatus === 'working' && 'üîß'}
          {workStatus === 'completed' && '‚úÖ'}
        </button>
      )}

      {/* Modal de resumen de trabajo */}
      {showWorkLog && workLog.zona && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.5)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 2000,
          padding: '20px'
        }}>
          <div style={{
            background: 'white',
            borderRadius: '12px',
            padding: isMobile ? '15px' : '20px',
            maxWidth: isMobile ? '100%' : '400px',
            width: '100%',
            maxHeight: '80vh',
            overflow: 'auto'
          }}>
            <h3 style={{ margin: '0 0 15px 0', color: '#1f2937', fontSize: isMobile ? '18px' : '20px' }}>
              üìã Resumen del Trabajo
            </h3>
            
            <div style={{ fontSize: isMobile ? '13px' : '14px', lineHeight: '1.8' }}>
              <div style={{ marginBottom: '15px' }}>
                <strong>{workLog.zona.nombre}</strong><br/>
                üìç {workLog.zona.direccion}
              </div>
              
              <div style={{
                background: '#f8fafc',
                padding: '12px',
                borderRadius: '8px',
                marginBottom: '15px'
              }}>
                <div>‚è±Ô∏è Tiempo trabajado: {calculateDuration(workLog.arrivalTime, workLog.endTime)}</div>
                <div style={{
                  marginTop: '10px',
                  padding: '8px',
                  background: '#dcfce7',
                  borderRadius: '6px',
                  textAlign: 'center',
                  fontWeight: 'bold',
                  color: '#166534'
                }}>
                  ‚úÖ COMPLETADO
                </div>
              </div>
              
              <button
                onClick={() => setShowWorkLog(false)}
                style={{
                  ...styles.button,
                  background: '#6b7280',
                  color: 'white'
                }}
              >
                Cerrar
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Indicador de carga */}
      {!mapLoaded && (
        <div style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          textAlign: 'center',
          zIndex: 1001
        }}>
          <div style={{
            width: '50px',
            height: '50px',
            border: '4px solid #f3f3f3',
            borderTop: '4px solid #22c55e',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite'
          }}></div>
          <p style={{ marginTop: '10px', color: '#666' }}>Cargando mapa...</p>
        </div>
      )}

      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default SistemaTrabajadoresFirebase;
