// src/components/MapaQuillonFirebase.js
import React, { useEffect, useRef, useState } from 'react';
import { vehiculosService, zonasService, trabajadoresService, reportesService } from '../services/firebaseservices';

const MapaQuillonFirebase = ({ currentUser, onLogout, onViewChange }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [vehiculos, setVehiculos] = useState([]);
  const [zonas, setZonas] = useState([]);
  const [trabajadores, setTrabajadores] = useState([]);
  const [selectedItemId, setSelectedItemId] = useState(null);
  const [selectedItemType, setSelectedItemType] = useState(null);
  const [filtroActivo, setFiltroActivo] = useState('todos');
  const [mapLoaded, setMapLoaded] = useState(false);
  const [showStats, setShowStats] = useState(false); // Oculto por defecto en mÃ³vil
  const [showMenu, setShowMenu] = useState(false); // MenÃº mÃ³vil
  const [isMobile, setIsMobile] = useState(false);
  const [realtimeStats, setRealtimeStats] = useState({
    vehiculos: { total: 0, activos: 0, enMantenimiento: 0 },
    zonas: { total: 0, activas: 0, completadas: 0, pendientes: 0 },
    trabajadores: { total: 0, enRuta: 0, trabajando: 0, disponibles: 0 }
  });
  
  const markersRef = useRef({});
  const infoWindowRef = useRef(null);
  const unsubscribesRef = useRef([]);
  const dataRef = useRef({ vehiculos: [], zonas: [], trabajadores: [] });
  
  const API_KEY = 'AIzaSyA7DrdL36n5cx-RNzuXGAQggFeGCheHDbY';

  // Detectar si es mÃ³vil
  useEffect(() => {
    const checkMobile = () => {
      const mobile = window.innerWidth <= 768;
      setIsMobile(mobile);
      // En mÃ³vil, ocultar stats por defecto
      if (mobile && showStats) {
        setShowStats(false);
      }
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Cargar Google Maps
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve, reject) => {
        if (window.google && window.google.maps) {
          resolve(window.google.maps);
          return;
        }

        const existingScript = document.querySelector('script[src*="maps.googleapis.com"]');
        if (existingScript) {
          existingScript.addEventListener('load', () => {
            if (window.google && window.google.maps) {
              resolve(window.google.maps);
            } else {
              reject(new Error('Google Maps no disponible'));
            }
          });
          return;
        }

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${API_KEY}&libraries=places,geometry&v=weekly`;
        script.async = true;
        script.defer = true;

        script.onload = () => {
          if (window.google && window.google.maps) {
            resolve(window.google.maps);
          }
        };

        script.onerror = () => reject(new Error('Error al cargar Google Maps'));
        document.head.appendChild(script);
      });
    };

    const initMap = async () => {
      try {
        const maps = await loadGoogleMaps();
        
        if (!mapRef.current) return;

        const mapInstance = new maps.Map(mapRef.current, {
          center: { lat: -36.7333, lng: -72.4667 },
          zoom: 13,
          mapTypeId: 'roadmap',
          styles: [
            {
              featureType: 'poi.business',
              stylers: [{ visibility: 'off' }]
            }
          ],
          // Controles optimizados para mÃ³vil
          zoomControl: !isMobile,
          mapTypeControl: false,
          scaleControl: false,
          streetViewControl: false,
          rotateControl: false,
          fullscreenControl: true,
          gestureHandling: isMobile ? 'greedy' : 'cooperative'
        });

        setMap(mapInstance);
        infoWindowRef.current = new maps.InfoWindow();
        
        // Agregar listener para cerrar InfoWindow cuando se hace clic en el mapa
        mapInstance.addListener('click', () => {
          if (infoWindowRef.current) {
            infoWindowRef.current.close();
            setSelectedItemId(null);
            setSelectedItemType(null);
          }
        });
        
        setMapLoaded(true);
      } catch (err) {
        console.error('Error al cargar mapa:', err);
      }
    };

    initMap();
  }, [isMobile]);

  // Suscribirse a datos de Firebase en tiempo real
  useEffect(() => {
    // Suscribirse a vehÃ­culos
    const unsubVehiculos = vehiculosService.subscribeToVehiculos((vehiculosData) => {
      dataRef.current.vehiculos = vehiculosData;
      setVehiculos(vehiculosData);
    });
    unsubscribesRef.current.push(unsubVehiculos);

    // Suscribirse a zonas_asignadas
    const unsubZonas = zonasService.subscribeToZonasAsignadas ? 
      zonasService.subscribeToZonasAsignadas((zonasData) => {
        dataRef.current.zonas = zonasData;
        setZonas(zonasData);
      }) :
      zonasService.subscribeToZonas((zonasData) => {
        dataRef.current.zonas = zonasData;
        setZonas(zonasData);
      });
    unsubscribesRef.current.push(unsubZonas);

    // Suscribirse a trabajadores
    const unsubTrabajadores = trabajadoresService.subscribeToTrabajadores((trabajadoresData) => {
      dataRef.current.trabajadores = trabajadoresData;
      setTrabajadores(trabajadoresData);
    });
    unsubscribesRef.current.push(unsubTrabajadores);

    // Suscribirse a estadÃ­sticas en tiempo real
    reportesService.getRealtimeStats((stats) => {
      setRealtimeStats(stats);
    });

    // Limpiar suscripciones al desmontar
    return () => {
      unsubscribesRef.current.forEach(unsub => unsub && unsub());
    };
  }, []);

  // FunciÃ³n para actualizar contenido del InfoWindow
  const updateInfoWindowContent = () => {
    if (!infoWindowRef.current || !selectedItemId || !selectedItemType) return;
    
    let item = null;
    let content = null;
    
    // Buscar el item actualizado
    if (selectedItemType === 'vehiculo') {
      item = dataRef.current.vehiculos.find(v => v.id === selectedItemId);
      if (item) content = createVehiculoInfoWindow(item);
    } else if (selectedItemType === 'zona') {
      item = dataRef.current.zonas.find(z => z.id === selectedItemId);
      if (item) content = createZonaInfoWindow(item, dataRef.current.trabajadores);
    } else if (selectedItemType === 'trabajador') {
      item = dataRef.current.trabajadores.find(t => t.id === selectedItemId);
      if (item) content = createTrabajadorInfoWindow(item);
    }
    
    // Actualizar contenido solo si el InfoWindow estÃ¡ abierto
    if (content && infoWindowRef.current.getMap()) {
      infoWindowRef.current.setContent(content);
    }
  };

  // Actualizar InfoWindow cuando cambien los datos
  useEffect(() => {
    updateInfoWindowContent();
  }, [vehiculos, zonas, trabajadores]);

  // FunciÃ³n para crear icono segÃºn tipo y estado
  const getIcon = (tipo, estado) => {
    if (!window.google) return null;
    
    const iconConfigs = {
      vehiculo: {
        activo: { color: '#22c55e' },
        trabajando: { color: '#3b82f6' },
        en_ruta: { color: '#f59e0b' },
        mantenimiento: { color: '#ef4444' },
        inactivo: { color: '#6b7280' }
      },
      zona: {
        en_progreso: { color: '#3b82f6' },
        pendiente: { color: '#f59e0b' },
        completado: { color: '#22c55e' },
        activa: { color: '#3b82f6' },
        pausada: { color: '#f59e0b' },
        completada: { color: '#22c55e' }
      },
      trabajador: {
        en_camino: { color: '#8b5cf6' },
        trabajando: { color: '#10b981' },
        disponible: { color: '#06b6d4' },
        inactivo: { color: '#6b7280' }
      }
    };

    const config = iconConfigs[tipo]?.[estado] || { color: '#6b7280' };
    
    if (tipo === 'trabajador') {
      return {
        path: window.google.maps.SymbolPath.CIRCLE,
        scale: isMobile ? 10 : 8,
        fillColor: config.color,
        fillOpacity: 1,
        strokeColor: '#ffffff',
        strokeWeight: 2
      };
    }

    return {
      path: 'M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7z',
      fillColor: config.color,
      fillOpacity: 1,
      strokeColor: '#ffffff',
      strokeWeight: 2,
      scale: isMobile ? 2.2 : (tipo === 'vehiculo' ? 1.8 : 2),
      anchor: new window.google.maps.Point(12, 24)
    };
  };

  // Crear/actualizar marcadores - OPTIMIZADO
  useEffect(() => {
    if (!map || !window.google) return;

    // FunciÃ³n para crear o actualizar un marcador
    const createOrUpdateMarker = (key, position, icon, title, clickHandler) => {
      if (markersRef.current[key]) {
        // Actualizar marcador existente
        const marker = markersRef.current[key];
        marker.setPosition(position);
        marker.setIcon(icon);
        marker.setTitle(title);
      } else {
        // Crear nuevo marcador
        const marker = new window.google.maps.Marker({
          position: position,
          map: map,
          title: title,
          icon: icon
        });

        marker.addListener('click', clickHandler);
        markersRef.current[key] = marker;
      }
    };

    // FunciÃ³n para ocultar marcadores no necesarios
    const hideUnusedMarkers = (activeKeys) => {
      Object.keys(markersRef.current).forEach(key => {
        if (!activeKeys.includes(key)) {
          markersRef.current[key].setMap(null);
          delete markersRef.current[key];
        }
      });
    };

    const activeKeys = [];

    // Actualizar marcadores de vehÃ­culos
    if (filtroActivo === 'todos' || filtroActivo === 'vehiculos') {
      vehiculos.forEach(vehiculo => {
        if (vehiculo.lat && vehiculo.lng) {
          const key = `vehiculo_${vehiculo.id}`;
          activeKeys.push(key);
          
          createOrUpdateMarker(
            key,
            { lat: vehiculo.lat, lng: vehiculo.lng },
            getIcon('vehiculo', vehiculo.estado),
            vehiculo.nombre,
            () => {
              const content = createVehiculoInfoWindow(vehiculo);
              infoWindowRef.current.setContent(content);
              infoWindowRef.current.open(map, markersRef.current[key]);
              setSelectedItemId(vehiculo.id);
              setSelectedItemType('vehiculo');
            }
          );
        }
      });
    }

    // Actualizar marcadores de zonas
    if (filtroActivo === 'todos' || filtroActivo === 'zonas') {
      zonas.forEach(zona => {
        if (zona.lat && zona.lng) {
          const key = `zona_${zona.id}`;
          activeKeys.push(key);
          
          createOrUpdateMarker(
            key,
            { lat: zona.lat, lng: zona.lng },
            getIcon('zona', zona.estado || 'pendiente'),
            zona.nombre,
            () => {
              const content = createZonaInfoWindow(zona, trabajadores);
              infoWindowRef.current.setContent(content);
