// src/components/SuperAdmin.js
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { auth, firestore, database } from '../config/firebase';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { doc, setDoc, collection, getDocs, deleteDoc, updateDoc } from 'firebase/firestore';
import { ref, set, push, update, remove, onValue, off } from 'firebase/database';
import { pdfExportService } from '../services/pdfExportService';

// COMPONENTE MODAL SEPARADO - FUERA DEL COMPONENTE PRINCIPAL
const TiposVehiculoModal = React.memo(({ 
  showTipoModal, 
  setShowTipoModal, 
  editingTipo, 
  setEditingTipo,
  tipoFormData,
  setTipoFormData,
  handleSaveTipoVehiculo,
  handleEditTipo,
  handleDeleteTipoVehiculo,
  tiposVehiculo,
  vehiculos,
  loading,
  isMobile 
}) => {
  if (!showTipoModal) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0,0,0,0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000,
      padding: '20px'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        maxWidth: '900px',
        width: '100%',
        maxHeight: '90vh',
        overflow: 'auto',
        padding: '30px'
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '25px'
        }}>
          <h2 style={{ margin: 0, color: '#1f2937', fontSize: '24px' }}>
            ‚öôÔ∏è Gesti√≥n de Tipos de Veh√≠culos
          </h2>
          <button
            onClick={() => {
              setShowTipoModal(false);
              setEditingTipo(null);
              setTipoFormData({
                nombre: '',
                icon: 'üöõ',
                descripcion: '',
                categoria: 'general'
              });
            }}
            style={{
              padding: '8px',
              background: '#6b7280',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontSize: '18px'
            }}
          >
            ‚úñ
          </button>
        </div>

        <div style={{
          background: '#f9fafb',
          padding: '20px',
          borderRadius: '8px',
          marginBottom: '25px'
        }}>
          <h3 style={{ 
            margin: '0 0 15px 0', 
            color: '#374151', 
            fontSize: '18px' 
          }}>
            {editingTipo ? '‚úèÔ∏è Editar Tipo' : '‚ûï Crear Nuevo Tipo'}
          </h3>
          
          <form onSubmit={handleSaveTipoVehiculo}>
            <div style={{
              display: 'grid',
              gridTemplateColumns: isMobile ? '1fr' : '1fr 1fr',
              gap: '15px'
            }}>
              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '6px',
                  color: '#374151',
                  fontSize: '14px',
                  fontWeight: '500'
                }}>
                  Nombre del Tipo *
                </label>
                <input
                  type="text"
                  required
                  value={tipoFormData.nombre}
                  onChange={(e) => setTipoFormData(prev => ({...prev, nombre: e.target.value}))}
                  placeholder="Ej: Retroexcavadora, Cami√≥n Tolva, etc."
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '1px solid #d1d5db',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                />
              </div>

              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '6px',
                  color: '#374151',
                  fontSize: '14px',
                  fontWeight: '500'
                }}>
                  √çcono
                </label>
                <div style={{ display: 'flex', gap: '10px' }}>
                  <select
                    value={tipoFormData.icon}
                    onChange={(e) => setTipoFormData(prev => ({...prev, icon: e.target.value}))}
                    style={{
                      flex: 1,
                      padding: '10px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '16px'
                    }}
                  >
                    <optgroup label="Veh√≠culos">
                      <option value="üöó">üöó Auto</option>
                      <option value="üöö">üöö Cami√≥n</option>
                      <option value="üöõ">üöõ Cami√≥n Grande</option>
                    </optgroup>
                    <optgroup label="Maquinaria">
                      <option value="üöú">üöú Maquinaria Pesada</option>
                    </optgroup>
                  </select>
                  <div style={{
                    width: '50px',
                    height: '42px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    background: '#f3f4f6',
                    borderRadius: '6px',
                    fontSize: '24px'
                  }}>
                    {tipoFormData.icon}
                  </div>
                </div>
              </div>

              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '6px',
                  color: '#374151',
                  fontSize: '14px',
                  fontWeight: '500'
                }}>
                  Categor√≠a
                </label>
                <select
                  value={tipoFormData.categoria}
                  onChange={(e) => setTipoFormData(prev => ({...prev, categoria: e.target.value}))}
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '1px solid #d1d5db',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                >
                  <option value="general">General</option>
                  <option value="liviano">Veh√≠culo Liviano</option>
                  <option value="pesado">Veh√≠culo Pesado</option>
                  <option value="maquinaria">Maquinaria</option>
                  <option value="especial">Especial</option>
                  <option value="emergencia">Emergencia</option>
                </select>
              </div>

              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '6px',
                  color: '#374151',
                  fontSize: '14px',
                  fontWeight: '500'
                }}>
                  Descripci√≥n (Opcional)
                </label>
                <input
                  type="text"
                  value={tipoFormData.descripcion}
                  onChange={(e) => setTipoFormData(prev => ({...prev, descripcion: e.target.value}))}
                  placeholder="Descripci√≥n breve del tipo"
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '1px solid #d1d5db',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                />
              </div>
            </div>

            <div style={{ marginTop: '20px', display: 'flex', gap: '10px' }}>
              <button
                type="submit"
                disabled={loading}
                style={{
                  padding: '10px 20px',
                  background: loading ? '#9ca3af' : '#22c55e',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  fontWeight: '500',
                  cursor: loading ? 'not-allowed' : 'pointer'
                }}
              >
                {loading ? 'Guardando...' : editingTipo ? '‚úÖ Actualizar' : '‚úÖ Crear Tipo'}
              </button>
              
              {editingTipo && (
                <button
                  type="button"
                  onClick={() => {
                    setEditingTipo(null);
                    setTipoFormData({
                      nombre: '',
                      icon: 'üöõ',
                      descripcion: '',
                      categoria: 'general'
                    });
                  }}
                  style={{
                    padding: '10px 20px',
                    background: '#6b7280',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: 'pointer'
                  }}
                >
                  Cancelar Edici√≥n
                </button>
              )}
            </div>
          </form>
        </div>

        <div>
          <h3 style={{ 
            margin: '0 0 15px 0', 
            color: '#374151', 
            fontSize: '18px' 
          }}>
            üìã Tipos Existentes ({tiposVehiculo.length})
          </h3>

          {tiposVehiculo.length === 0 ? (
            <div style={{
              padding: '40px',
              textAlign: 'center',
              background: '#f9fafb',
              borderRadius: '8px',
              color: '#6b7280'
            }}>
              <p style={{ fontSize: '16px', marginBottom: '10px' }}>
                No hay tipos de veh√≠culos creados
              </p>
              <p style={{ fontSize: '14px' }}>
                Crea tu primer tipo usando el formulario de arriba
              </p>
            </div>
          ) : (
            <div style={{
              display: 'grid',
              gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fill, minmax(250px, 1fr))',
              gap: '15px'
            }}>
              {tiposVehiculo.map(tipo => {
                const vehiculosConTipo = vehiculos.filter(v => v.tipo === tipo.id).length;
                
                return (
                  <div key={tipo.id} style={{
                    background: 'white',
                    border: '1px solid #e5e7eb',
                    borderRadius: '8px',
                    padding: '15px',
                    position: 'relative'
                  }}>
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      marginBottom: '10px'
                    }}>
                      <span style={{ fontSize: '24px' }}>{tipo.icon}</span>
                      <div style={{ flex: 1 }}>
                        <h4 style={{ 
                          margin: 0, 
                          fontSize: '16px', 
                          color: '#1f2937' 
                        }}>
                          {tipo.nombre}
                        </h4>
                        {tipo.categoria && (
                          <span style={{
                            fontSize: '12px',
                            color: '#6b7280',
                            background: '#f3f4f6',
                            padding: '2px 6px',
                            borderRadius: '4px',
                            display: 'inline-block',
                            marginTop: '4px'
                          }}>
                            {tipo.categoria}
                          </span>
                        )}
                      </div>
                    </div>
                    
                    {tipo.descripcion && (
                      <p style={{ 
                        margin: '0 0 10px 0', 
                        fontSize: '13px', 
                        color: '#6b7280' 
                      }}>
                        {tipo.descripcion}
                      </p>
                    )}
                    
                    <div style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                      paddingTop: '10px',
                      borderTop: '1px solid #f3f4f6'
                    }}>
                      <span style={{
                        fontSize: '12px',
                        color: '#6b7280'
                      }}>
                        {vehiculosConTipo} veh√≠culo(s)
                      </span>
                      
                      <div style={{ display: 'flex', gap: '5px' }}>
                        <button
                          onClick={() => handleEditTipo(tipo)}
                          style={{
                            padding: '6px 10px',
                            background: '#3b82f6',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            fontSize: '12px',
                            cursor: 'pointer'
                          }}
                        >
                          ‚úèÔ∏è
                        </button>
                        <button
                          onClick={() => handleDeleteTipoVehiculo(tipo)}
                          disabled={vehiculosConTipo > 0}
                          style={{
                            padding: '6px 10px',
                            background: vehiculosConTipo > 0 ? '#d1d5db' : '#ef4444',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            fontSize: '12px',
                            cursor: vehiculosConTipo > 0 ? 'not-allowed' : 'pointer',
                            opacity: vehiculosConTipo > 0 ? 0.5 : 1
                          }}
                          title={vehiculosConTipo > 0 ? 'No se puede eliminar, hay veh√≠culos usando este tipo' : 'Eliminar tipo'}
                        >
                          üóëÔ∏è
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
});

const SuperAdmin = ({ currentUser, onLogout, onViewChange, currentView = 'admin' }) => {
  // ========== ESTADOS PARA USUARIOS ==========
  const [solicitudParaZona, setSolicitudParaZona] = useState(null);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    role: 'trabajador',
    phone: '',
    vehicleId: '',
    localidad: ''
  });
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [activeTab, setActiveTab] = useState('users');
  const [isMobile, setIsMobile] = useState(false);
  const [showEditUserForm, setShowEditUserForm] = useState(false);
  
  // ========== ESTADOS PARA VEH√çCULOS ==========
  const [vehiculos, setVehiculos] = useState([]);
  const [showVehicleForm, setShowVehicleForm] = useState(false);
  const [editingVehicle, setEditingVehicle] = useState(null);
  const [vehicleFormData, setVehicleFormData] = useState({
  // DATOS EXISTENTES
  nombre: '',
  tipo: '',
  marca: '',
  modelo: '',
  a√±o: new Date().getFullYear(),
  patente: '',
  color: '',
  numeroChasis: '',
  numeroMotor: '',
  kilometraje: 0,
  capacidadCombustible: 0,
  tipoCombustible: 'gasolina',
  ultimoMantenimiento: '',
  proximoMantenimiento: '',
  kilometrajeUltimoMantenimiento: 0,
  kilometrajeProximoMantenimiento: 0,
  fechaRevisionTecnica: '',
  fechaSeguro: '',
  fechaPermisoCirculacion: '',
  operadorAsignado: '',
  estado: 'disponible',
  lat: null,
  lng: null,
  observaciones: '',
  
  // NUEVOS CAMPOS - DATOS GENERALES
  vin: '',
  proveedor: '',
  modalidadAdquisicion: 'compra', // compra, leasing, arriendo, donacion
  valorAdquisicion: 0,
  fechaAdquisicion: '',
  
  // ESPECIFICACIONES T√âCNICAS
  cilindrada: '',
  potencia: '',
  transmision: 'manual', // manual, automatica
  traccion: '4x2', // 4x2, 4x4, 6x4
  capacidadPasajeros: 0,
  capacidadCarga: 0,
  pesoBrutoVehicular: 0,
  largo: 0,
  ancho: 0,
  alto: 0,
  numeroEjes: 2,
  equipamientoAdicional: '',
  
  // DOCUMENTACI√ìN LEGAL
  numeroPermisoCirculacion: '',
  vigenciaPermisoCirculacion: '',
  fechaRevisionGases: '',
  resultadoRevisionTecnica: 'aprobado', // aprobado, rechazado, pendiente
  vigenciaSOAP: '',
  numeroPolizaSOAP: '',
  seguroComplementario: false,
  numeroPolizaComplementario: '',
  vigenciaSeguroComplementario: '',
  multasRegistradas: '',
  
  // CONTROL DE OPERACI√ìN
  horasUso: 0, // Para maquinaria
  promedioUsoMensual: 0,
  consumoPromedio: 0, // L/100km o L/hr
  proveedorCombustible: '',
  tieneGPS: false,
  dispositivoGPS: '',
  
  // MANTENIMIENTO
  tallerMantenimiento: '',
  costoUltimoMantenimiento: 0,
  historialReparaciones: [],
  
  // COSTOS Y CICLO DE VIDA
  costoMantencionAcumulado: 0,
  costoCombustibleMensual: 0,
  costoCombustibleAnual: 0,
  costoSeguroAnual: 0,
  vidaUtilEstimada: 10, // a√±os
  fechaEstimadaReemplazo: '',
  estadoPatrimonial: 'activo' // activo, baja, en_proceso_baja
});

  // ========== ESTADOS PARA TIPOS DE VEH√çCULOS DIN√ÅMICOS ==========
  const [tiposVehiculo, setTiposVehiculo] = useState([]);
  const [showTipoModal, setShowTipoModal] = useState(false);
  const [editingTipo, setEditingTipo] = useState(null);
  const [tipoFormData, setTipoFormData] = useState({
    nombre: '',
    icon: 'üöõ',
    descripcion: '',
    categoria: 'general'
  });

  // ========== ESTADOS PARA SOLICITUDES ==========
  const [solicitudes, setSolicitudes] = useState([]);
  const [showSolicitudDetails, setShowSolicitudDetails] = useState(null);

  // ========== ESTADOS PARA SIDEBAR ==========
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [expandedMenus, setExpandedMenus] = useState(['gestion']);

  // ========== DETECTAR SI ES M√ìVIL ==========
  useEffect(() => {
    const checkMobile = () => {
      const mobile = window.innerWidth <= 768;
      setIsMobile(mobile);
      if (mobile) {
        setIsSidebarOpen(false);
      }
    };
    
    checkMobile();
    window.addEventListener('resize', checkMobile);
    
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // ========== CARGAR DATOS INICIALES ==========
  useEffect(() => {
    loadUsers();
    
    // Suscripciones a Firebase
    const unsubscribeVehiculos = loadVehiculos();
    const unsubscribeSolicitudes = loadSolicitudes();
    const unsubscribeTipos = loadTiposVehiculo();
    
    // Cleanup
    return () => {
      if (unsubscribeVehiculos) unsubscribeVehiculos();
      if (unsubscribeSolicitudes) unsubscribeSolicitudes();
      if (unsubscribeTipos) unsubscribeTipos();
    };
  }, []);

  // ========== FUNCIONES DE CARGA DE DATOS ==========
  const loadUsers = async () => {
    try {
      const usersSnapshot = await getDocs(collection(firestore, 'users'));
      const usersList = [];
      usersSnapshot.forEach((doc) => {
        usersList.push({ id: doc.id, ...doc.data() });
      });
      setUsers(usersList);
    } catch (error) {
      console.error('Error al cargar usuarios:', error);
    }
  };

  const loadVehiculos = useCallback(() => {
    const vehiculosRef = ref(database, 'vehiculos');
    const unsubscribe = onValue(vehiculosRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const vehiculosArray = Object.entries(data).map(([id, vehiculo]) => ({
          id,
          ...vehiculo
        }));
        setVehiculos(vehiculosArray);
      } else {
        setVehiculos([]);
      }
    });
    
    return () => off(vehiculosRef);
  }, []);

  const loadSolicitudes = useCallback(() => {
    const solicitudesRef = ref(database, 'solicitudes_maquinaria');
    const unsubscribe = onValue(solicitudesRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const solicitudesArray = Object.entries(data)
          .map(([id, sol]) => ({ id, ...sol }))
          .sort((a, b) => {
            if (a.estado === 'pendiente' && b.estado !== 'pendiente') return -1;
            if (a.estado !== 'pendiente' && b.estado === 'pendiente') return 1;
            return new Date(b.fechaSolicitud) - new Date(a.fechaSolicitud);
          });
        setSolicitudes(solicitudesArray);
      } else {
        setSolicitudes([]);
      }
    });
    
    return () => off(solicitudesRef);
  }, []);

  const loadTiposVehiculo = useCallback(() => {
    const tiposRef = ref(database, 'tipos_vehiculo');
    const unsubscribe = onValue(tiposRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const tiposArray = Object.entries(data).map(([id, tipo]) => ({
          id,
          ...tipo
        }));
        setTiposVehiculo(tiposArray);
      } else {
        setTiposVehiculo([]);
      }
    });
    
    return () => off(tiposRef);
  }, []);

  // ========== FUNCIONES PARA TIPOS DE VEH√çCULOS - MEMOIZADAS ==========
  const handleSaveTipoVehiculo = useCallback(async (e) => {
    e.preventDefault();
    
    if (!tipoFormData.nombre.trim()) {
      setMessage({ type: 'error', text: 'El nombre del tipo es requerido' });
      return;
    }

    setLoading(true);
    
    try {
      if (editingTipo) {
        const tipoRef = ref(database, `tipos_vehiculo/${editingTipo.id}`);
        await update(tipoRef, {
          nombre: tipoFormData.nombre,
          icon: tipoFormData.icon,
          descripcion: tipoFormData.descripcion || '',
          categoria: tipoFormData.categoria || 'general',
          updatedAt: new Date().toISOString(),
          updatedBy: currentUser.uid
        });
        
        setMessage({ 
          type: 'success', 
          text: `‚úÖ Tipo "${tipoFormData.nombre}" actualizado exitosamente` 
        });
      } else {
        const tipoId = tipoFormData.nombre.toLowerCase()
          .normalize("NFD")
          .replace(/[\u0300-\u036f]/g, "")
          .replace(/\s+/g, '_')
          .replace(/[^a-z0-9_]/g, '');

        const tipoRef = ref(database, `tipos_vehiculo/${tipoId}`);
        await set(tipoRef, {
          nombre: tipoFormData.nombre,
          icon: tipoFormData.icon,
          descripcion: tipoFormData.descripcion || '',
          categoria: tipoFormData.categoria || 'general',
          createdAt: new Date().toISOString(),
          createdBy: currentUser.uid
        });
        
        setMessage({ 
          type: 'success', 
          text: `‚úÖ Tipo "${tipoFormData.nombre}" creado exitosamente` 
        });
      }
      
      setTipoFormData({
        nombre: '',
        icon: 'üöõ',
        descripcion: '',
        categoria: 'general'
      });
      setEditingTipo(null);
      setShowTipoModal(false);
      
    } catch (error) {
      console.error('Error al guardar tipo:', error);
      setMessage({ 
        type: 'error', 
        text: 'Error al guardar el tipo de veh√≠culo' 
      });
    } finally {
      setLoading(false);
    }
  }, [tipoFormData, editingTipo, currentUser.uid]);

  const handleDeleteTipoVehiculo = useCallback(async (tipo) => {
    const vehiculosConTipo = vehiculos.filter(v => v.tipo === tipo.id);
    
    if (vehiculosConTipo.length > 0) {
      setMessage({ 
        type: 'error', 
        text: `‚ùå No se puede eliminar "${tipo.nombre}". Hay ${vehiculosConTipo.length} veh√≠culo(s) usando este tipo` 
      });
      return;
    }

    if (window.confirm(`¬øEst√°s seguro de eliminar el tipo "${tipo.nombre}"?`)) {
      try {
        const tipoRef = ref(database, `tipos_vehiculo/${tipo.id}`);
        await remove(tipoRef);
        
        setMessage({ 
          type: 'success', 
          text: `‚úÖ Tipo "${tipo.nombre}" eliminado exitosamente` 
        });
      } catch (error) {
        console.error('Error al eliminar tipo:', error);
        setMessage({ 
          type: 'error', 
          text: 'Error al eliminar el tipo de veh√≠culo' 
        });
      }
    }
  }, [vehiculos]);

  const handleEditTipo = useCallback((tipo) => {
    setEditingTipo(tipo);
    setTipoFormData({
      nombre: tipo.nombre,
      icon: tipo.icon || 'üöõ',
      descripcion: tipo.descripcion || '',
      categoria: tipo.categoria || 'general'
    });
    setShowTipoModal(true);
  }, []);

  // ========== FUNCIONES PARA SOLICITUDES ==========
  const handleResponseSolicitud = async (solicitudId, estado, respuesta) => {
    try {
      const solicitudRef = ref(database, `solicitudes_maquinaria/${solicitudId}`);
      await update(solicitudRef, {
        estado,
        respuesta,
